INSERT INTO [t7776] VALUES (1,'مثلي ومثل الأنبياء من قبلي كمثل رجل ابتنى بيوتا فأحسنها وأجملها وأكملها إلا موضع لبنة من','3','0','0');
INSERT INTO [t7776] VALUES (2,'مثل البخيل والمتصدق كمثل رجلين عليهما جبتان أو جنتان من حديد إلى ثدييهما أو إلى تراقيهما،','4','0','0');
INSERT INTO [t7776] VALUES (3,'مثلي كمثل رجل استوقد نارا فلما أضاءت ما حولها جعل الفراش وهذه الدواب التي يقعن في النار','5','0','0');
INSERT INTO [t7776] VALUES (4,'في الجنة شجرة يسير الراكب في ظلها مائة عام لا يقطعها','6','0','0');
INSERT INTO [t7776] VALUES (5,'الظن أكذب الحديث، ولا تناجشوا، ولا تحاسدوا، ولا تنافسوا، ولا تباغضوا، ولا تدابروا، وكونوا','7','0','0');
INSERT INTO [t7776] VALUES (6,'في الجمعة ساعة لا يوافقها مسلم وهو يصلي يسأل ربه شيئا إلا آتاه إياه','8','0','0');
INSERT INTO [t7776] VALUES (7,'الملائكة يتعاقبون فيكم , ملائكة بالليل وملائكة بالنهار ويجتمعون في صلاة الفجر، وصلاة','9','0','0');
INSERT INTO [t7776] VALUES (8,'الملائكة تصلي على أحدكم ما دام في مصلاه الذي صلى فيه، وتقول: اللهم اغفر له، اللهم ارحمه','10','0','0');
INSERT INTO [t7776] VALUES (9,'إذا قال أحدكم: آمين والملائكة في السماء: آمين، فوافق إحداها الأخرى؛ غفر له ما تقدم من','11','0','0');
INSERT INTO [t7776] VALUES (10,'ويلك اركبها: ويلك اركبها','12','0','0');
INSERT INTO [t7776] VALUES (11,'إذا قاتل أحدكم فليتجنب الوجه','13','0','0');
INSERT INTO [t7776] VALUES (12,'رحمتي سبقت غضبي','15','0','0');
INSERT INTO [t7776] VALUES (13,'لو تعلمون ما أعلم لبكيتم كثيرا ولضحكتم قليلا','16','0','0');
INSERT INTO [t7776] VALUES (14,'الصيام جنة، فإذا كان أحدكم يوما صائما، فلا يجهل ولا يرفث فإن امرؤ قاتله أو شاتمه فليقل','17','0','0');
INSERT INTO [t7776] VALUES (15,'لخلوف فم الصائم أطيب عند الله من ريح المسك، يذر شهوته وطعامه وشرابه من جراي، فالصيام لي','18','0','0');
INSERT INTO [t7776] VALUES (16,'فأمر بها فأحرقت في النار، فأوحى الله إليه: فهلا نملة واحدة','19','0','0');
INSERT INTO [t7776] VALUES (17,'ما قعدت خلف سرية تغزو في سبيل الله، ولكن لا أجد سعة فأحملهم، ولا يجدون سعة فيتبعوني، ولا','20','0','0');
INSERT INTO [t7776] VALUES (18,'أؤخر دعوتي شفاعة لأمتي يوم القيامة','21','0','0');
INSERT INTO [t7776] VALUES (19,'من أحب لقاء الله أحب الله لقاءه، ومن لم يحب لقاء الله لم يحب الله','22','0','0');
INSERT INTO [t7776] VALUES (20,'من أطاعني فقد أطاع الله، ومن يعصني فقد عصى الله، ومن يطع الأمير فقد أطاعني ومن يعص الأمير','23','0','0');
INSERT INTO [t7776] VALUES (21,'يكثر فيكم المال فيفيض حتى يهم رب المال من يتقبل منه صدقته. قال: ويقبض العلم ويقترب الزمان','24','0','0');
INSERT INTO [t7776] VALUES (22,'تقتتل فئتان عظيمتان تكون بينهما مقتلة عظيمة ودعواهما واحدة','25','0','0');
INSERT INTO [t7776] VALUES (23,'ينبعث دجالون كذابون قريب من ثلاثين كلهم يزعم أنه رسول الله','26','0','0');
INSERT INTO [t7776] VALUES (24,'تطلع الشمس من مغربها فإذا طلعت ورآها الناس آمنوا أجمعون، وذلك حين لا ينفع نفسا إيمانها لم','27','0','0');
INSERT INTO [t7776] VALUES (25,'أدبر الشيطان له ضراط حتى لا يسمع التأذين، فإذا قضي التأذين أقبل حتى إذا ثوب بها أدبر حتى','28','0','0');
INSERT INTO [t7776] VALUES (26,'يمين الله ملأى لا يغيضها نفقة، سحاء الليل والنهار، أرأيتم ما أنفق منذ خلق السماء والأرض','29','0','0');
INSERT INTO [t7776] VALUES (27,'يراني أحب إليه من مثل أهله وماله معهم','30','0','0');
INSERT INTO [t7776] VALUES (28,'يهلك كسرى ثم لا كسرى بعده، وقيصر ليهلكن ثم لا يكون قيصر بعده، ولتنفقن كنوزهما في سبيل','31','0','0');
INSERT INTO [t7776] VALUES (29,'أعددت لعبادي الصالحين ما لا عين رأت، ولا أذن سمعت، ولا خطر على قلب بشر','32','0','0');
INSERT INTO [t7776] VALUES (30,'هلك الذين قبلكم بسؤالهم واختلافهم على أنبيائهم، فإذا نهيتكم عن شيء فاجتنبوه، وإذا أمرتكم','33','0','0');
INSERT INTO [t7776] VALUES (31,'إذا نودي للصلاة صلاة الصبح، وأحدكم جنب فلا يصوم يومئذ','34','0','0');
INSERT INTO [t7776] VALUES (32,'لله تسعة وتسعون اسما مائة إلا واحدا، من أحصاها دخل الجنة، إنه وتر يحب','35','0','0');
INSERT INTO [t7776] VALUES (33,'إذا نظر أحدكم إلى من هو فضل عليه في المال والخلق، فلينظر إلى من هو أسفل منه ممن فضل','36','0','0');
INSERT INTO [t7776] VALUES (34,'طهور إناء أحدكم إذا ولغ الكلب فيه فليغسله سبع مرات','37','0','0');
INSERT INTO [t7776] VALUES (35,'يستعدوا لي بحزم من حطب، ثم آمر رجلا يصلي بالناس ثم أحرق بيوتا على من','38','0','0');
INSERT INTO [t7776] VALUES (36,'نصرت بالرعب، وأوتيت جوامع الكلم','39','0','0');
INSERT INTO [t7776] VALUES (37,'إذا انقطع شسع نعل أحدكم أو شراكه فلا يمش في إحداهما بنعل واحدة والأخرى حافية، ليحفهما','40','0','0');
INSERT INTO [t7776] VALUES (38,'لا يأتي ابن آدم النذر بشيء لم أكن قد قدرته، ولكن يلقيه النذر وقد قدرته له، أستخرج به من','41','0','0');
INSERT INTO [t7776] VALUES (39,'أنفق أنفق عليك، وسمى الحرب خدعة','42','0','0');
INSERT INTO [t7776] VALUES (40,'آمنت بالله وكذبت عيني','43','0','0');
INSERT INTO [t7776] VALUES (41,'خازن أضع حيث أمرت','44','0','0');
INSERT INTO [t7776] VALUES (42,'الإمام ليؤتم به، فلا تختلفوا عليه، فإذا كبر فكبروا، وإذا ركع فاركعوا، وإذا قال: سمع الله','45','0','0');
INSERT INTO [t7776] VALUES (43,'إقامة الصف من حسن الصلاة','46','0','0');
INSERT INTO [t7776] VALUES (44,'تحاج آدم وموسى، فقال له موسى: أنت آدم الذي أغويت الناس فأخرجتهم من الجنة إلى الأرض، فقال','47','0','0');
INSERT INTO [t7776] VALUES (45,'لا غنى لي عن بركتك','48','0','0');
INSERT INTO [t7776] VALUES (46,'لا يأكل إلا من عمل يده','49','0','0');
INSERT INTO [t7776] VALUES (47,'رؤيا الرجل الصالح جزء من ستة وأربعين جزءا من النبوة','50','0','0');
INSERT INTO [t7776] VALUES (48,'يسلم الصغير على الكبير، والمار على القاعد، والقليل على الكثير','51','0','0');
INSERT INTO [t7776] VALUES (49,'لا أزال أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوا: لا إله إلا الله فقد عصموا','52','0','0');
INSERT INTO [t7776] VALUES (50,'تحاجت الجنة والنار، فقالت النار: أوثرت بالمتكبرين والمتجبرين، وقالت الجنة: فما لي لا','53','0','0');
INSERT INTO [t7776] VALUES (51,'إذا استجمر أحدكم فليوتر','54','0','0');
INSERT INTO [t7776] VALUES (52,'إذا تحدث عبدي بأن يعمل حسنة فأنا أكتبها له حسنة ما لم يعملها، فإذا عملها فأنا أكتبها له','55','0','0');
INSERT INTO [t7776] VALUES (53,'لقيد سوط أحدكم من الجنة خير له مما بين السماء والأرض','56','0','0');
INSERT INTO [t7776] VALUES (54,'أدنى مقعد أحدكم من الجنة أن هيئ له أن يقال له: تمن، فيتمنى ويتمنى فيقال له: هل تمنيت؟','57','0','0');
INSERT INTO [t7776] VALUES (55,'لولا الهجرة لكنت امرأ من الأنصار، ولو يندفع الناس في شعبة أو في واد والأنصار في شعبة','58','0','0');
INSERT INTO [t7776] VALUES (56,'لولا بنو إسرائيل لم يخبث الطعام، ولم يخنز اللحم، ولولا حواء لم تخن أنثى زوجها','59','0','0');
INSERT INTO [t7776] VALUES (57,'خلق الله آدم على صورته , طوله ستون ذراعا فلما خلقه قال: اذهب فسلم على أولئك النفر - وهم','60','0','0');
INSERT INTO [t7776] VALUES (58,'جاء ملك الموت إلى موسى فقال له: أجب ربك، قال: فلطم موسى عين ملك الموت ففقأها، قال: فرجع','61','0','0');
INSERT INTO [t7776] VALUES (59,'كانت بنو إسرائيل يغتسلون عراة ينظر بعضهم إلى سوأة بعض، وكان موسى يغتسل وحده فقالوا: والله','62','0','0');
INSERT INTO [t7776] VALUES (60,'ليس الغنى من كثرة العرض، ولكن الغنى غنى النفس','63','0','0');
INSERT INTO [t7776] VALUES (61,'من الظلم مطل الغني، وإن أتبع أحدكم على مليء فليتبع','64','0','0');
INSERT INTO [t7776] VALUES (62,'أغيظ رجل على الله يوم القيامة وأخبثه وأغيظه عليه رجل كان يسمى ملك الأملاك لا ملك إلا الله','65','0','0');
INSERT INTO [t7776] VALUES (63,'خسف به الأرض، فهو يتجلجل فيها إلى يوم القيامة','66','0','0');
INSERT INTO [t7776] VALUES (64,'أنا عند ظن عبدي بي','67','0','0');
INSERT INTO [t7776] VALUES (65,'من يولد يولد على هذه الفطرة، فأبواه يهودانه وينصرانه، كما تنتجون البهيمة، هل تجدون فيها','68','0','0');
INSERT INTO [t7776] VALUES (66,'في الإنسان عظما لا تأكله الأرض أبدا، فيه يركب يوم القيامة. قالوا: أي عظم؟ قال: عجب الذنب.','69','0','0');
INSERT INTO [t7776] VALUES (67,'إياكم والوصال، إياكم والوصال قالوا: فإنك تواصل يا رسول الله. قال: إني لست في ذاكم مثلكم،','70','0','0');
INSERT INTO [t7776] VALUES (68,'إذا استيقظ أحدكم فلا يضع يده على الوضوء حتى يغسلها، إنه لا يدري أحدكم أين باتت','71','0','0');
INSERT INTO [t7776] VALUES (69,'تعدل بين الاثنين صدقة، وتعين الرجل في دابته وتحمله عليها، أو ترفع له عليها متاعه صدقة،','72','0','0');
INSERT INTO [t7776] VALUES (70,'رب النعم لم يعط حقها تسلط عليه يوم القيامة تخبط وجهه بأخفافها','73','0','0');
INSERT INTO [t7776] VALUES (71,'يكون كنز أحدكم يوم القيامة شجاعا أقرع يفر منه صاحبه ويطلبه ويقول: أنا كنزك، قال: والله لن','74','0','0');
INSERT INTO [t7776] VALUES (72,'لا يبال في الماء الدائم الذي لا يجري، ثم يغتسل به','75','0','0');
INSERT INTO [t7776] VALUES (73,'ليس المسكين هذا الطواف الذي يطوف على الناس، ترده اللقمة واللقمتان والتمرة والتمرتان، إنما','76','0','0');
INSERT INTO [t7776] VALUES (74,'لا تصوم المرأة وبعلها شاهد إلا بإذنه، ولا تأذن في بيته وهو شاهد إلا بإذنه، وما أنفقت من','77','0','0');
INSERT INTO [t7776] VALUES (75,'لا يتمنى أحدكم الموت ولا يدعو به من قبل أن يأتيه، إنه إذا مات أحدكم انقطع عمله، أو قال','78','0','0');
INSERT INTO [t7776] VALUES (76,'لا يقل أحدكم للعنب: الكرم، إنما الكرم الرجل المسلم','79','0','0');
INSERT INTO [t7776] VALUES (77,'اشترى رجل من رجل عقارا فوجد الرجل الذي اشترى العقار في عقاره جرة فيها ذهب، فقال له الذي','80','0','0');
INSERT INTO [t7776] VALUES (78,'لله أشد فرحا بتوبة عبده إذا تاب من أحدكم براحلته إذا وجدها','81','0','0');
INSERT INTO [t7776] VALUES (79,'إذا تلقاني عبدي بشبر تلقيته بذراع، وإذا تلقاني بذراع تلقيته بباع، وإذا تلقاني بباع جئته ,','82','0','0');
INSERT INTO [t7776] VALUES (80,'إذا توضأ أحدكم فليستنشق بمنخريه من ماء ثم لينتثر','83','0','0');
INSERT INTO [t7776] VALUES (81,'لو أن عندي أحدا ذهبا لأحببت ألا يأتي علي ثلاث ليال وعندي منه دينار أجد من يتقبله مني، ليس','84','0','0');
INSERT INTO [t7776] VALUES (82,'إذا جاءكم الصانع بطعامكم قد أغنى عنكم حره ودخانه فادعوه فليأكل معكم، وإلا فألقموه في يده','85','0','0');
INSERT INTO [t7776] VALUES (83,'لا يقل أحدكم اسق ربك أو أطعم ربك وضئ ربك ولا يقل أحدكم: ربي، وليقل: سيدي، مولاي، ولا يقل','86','0','0');
INSERT INTO [t7776] VALUES (84,'أول زمرة تلج الجنة صورهم على صورة القمر ليلة البدر، لا يبصقون فيها، ولا يمتخطون، ولا','87','0','0');
INSERT INTO [t7776] VALUES (85,'فأي المؤمنين آذيته أو شتمته أو جلدته أو لعنته فاجعلها صلاة وزكاة وقربة تقربه بها يوم','88','0','0');
INSERT INTO [t7776] VALUES (86,'لم تحل الغنائم لمن كان قبلنا، ذلك بأن الله رأى ضعفنا وعجزنا فطيبها لنا','89','0','0');
INSERT INTO [t7776] VALUES (87,'دخلت امرأة النار من جراء هرة لها أو هرة ربطتها، فلا هي أطعمتها ولا هي أرسلتها تتقهم من','90','0','0');
INSERT INTO [t7776] VALUES (88,'لا يسرق سارق وهو حين يسرق مؤمن، ولا يزني زان وهو حين يزني مؤمن، ولا يشرب الحدود أحدكم،','91','0','0');
INSERT INTO [t7776] VALUES (89,'لا يسمع بي أحد من هذه الأمة، ولا يهودي ولا نصراني، ومات ولم يؤمن بالذي أرسلت به إلا كان','92','0','0');
INSERT INTO [t7776] VALUES (90,'التسبيح للقوم، والتصفيق للنساء في الصلاة','93','0','0');
INSERT INTO [t7776] VALUES (91,'كل كلم يكلم به المسلم في سبيل الله يكون يوم القيامة كهيئتها إذا طعنت يفجر دما، اللون لون','94','0','0');
INSERT INTO [t7776] VALUES (92,'لا تزالون تستفتون حتى يقول أحدكم: هذا الله، خلق الخلق، فمن خلق الله؟','95','0','0');
INSERT INTO [t7776] VALUES (93,'فأجد التمرة ساقطة على فراشي أو في بيتي فأرفعها لآكلها، ثم أخشى أن تكون من الصدقة','96','0','0');
INSERT INTO [t7776] VALUES (94,'لأن يلج أحدكم بيمينه في أهله، آثم له عند الله من أن يعطي كفارته التي فرض الله عز','97','0','0');
INSERT INTO [t7776] VALUES (95,'إذا أكره الاثنان على اليمين فاستحباها فأسهم بينهما','98','0','0');
INSERT INTO [t7776] VALUES (96,'اشترى لقحة مصراة أو شاة مصراة فهو بخير النظرين بعد أن يحلبها، إما هي وإلا فليردها وصاعا','99','0','0');
INSERT INTO [t7776] VALUES (97,'الشيخ شاب على حب اثنتين: طول الحياة، وكثرة المال','100','0','0');
INSERT INTO [t7776] VALUES (98,'لا يشير أحدكم إلى أخيه بالسلاح، فإنه لا يدري أحدكم لعل الشيطان أن ينزع في يده؛ فيقع في','101','0','0');
INSERT INTO [t7776] VALUES (99,'اشتد غضب الله على قوم فعلوا برسول الله - صلى الله عليه وسلم - وهو حينئذ يشير إلى','102','0','0');
INSERT INTO [t7776] VALUES (100,'اشتد غضب الله على رجل يقتله رسول الله - صلى الله عليه وسلم - في سبيل الله عز','103','0','0');
INSERT INTO [t7776] VALUES (101,'على ابن آدم نصيب من الزنا أدرك ذلك لا محالة. قال: فالعين زنيتها النظر وتصديقها الإعراض،','104','0','0');
INSERT INTO [t7776] VALUES (102,'إذا أحسن أحدكم إسلامه فكل حسنة يعملها تكتب بعشر أمثالها إلى سبعمائة ضعف، وكل سيئة يعملها','105','0','0');
INSERT INTO [t7776] VALUES (103,'إذا أم أحدكم للناس فليخفف الصلاة؛ فإن فيهم الكبير وفيهم الضعيف وفيهم السقيم، وإن قام','106','0','0');
INSERT INTO [t7776] VALUES (104,'فإن عملها فاكتبوها له بمثلها، وإن تركها فاكتبوها له حسنة، إنما تركها من','107','0','0');
INSERT INTO [t7776] VALUES (105,'كذبني عبدي ولم يكن ذلك له، وشتمني عبدي ولم يكن ذلك له، أما تكذيبه إياي أن يقول: لن يعيدنا','108','0','0');
INSERT INTO [t7776] VALUES (106,'أبردوا عن الحر في الصلاة؛ فإن شدة الحر من فيح جهنم','109','0','0');
INSERT INTO [t7776] VALUES (107,'لا تقبل صلاة أحدكم إذا أحدث حتى يتوضأ','110','0','0');
INSERT INTO [t7776] VALUES (108,'إذا نودي بالصلاة فأتوها وأنتم تمشون وعليكم السكينة، فما أدركتم فصلوا، وما سبقتم','111','0','0');
INSERT INTO [t7776] VALUES (109,'يضحك الله لرجلين يقتل أحدهما الآخر، كلاهما يدخل الجنة، قالوا: وكيف يا رسول الله؟ قال:','112','0','0');
INSERT INTO [t7776] VALUES (110,'لا يبيع أحدكم على بيع أخيه، ولا يخطب على خطبة أخيه','113','0','0');
INSERT INTO [t7776] VALUES (111,'الكافر يأكل في سبعة أمعاء، والمؤمن يأكل في معى واحد','114','0','0');
INSERT INTO [t7776] VALUES (112,'سمي خضر؛ لأنه جلس على فروة بيضاء، فإذا هي تهتز تحته خضراء','115','0','0');
INSERT INTO [t7776] VALUES (113,'لا ينظر إلى المسبل يوم القيامة، يعني إزاره','116','0','0');
INSERT INTO [t7776] VALUES (114,'ادخلوا الباب سجدا وقولوا حطة يغفر لكم خطاياكم. فبدلوا، فدخلوا الباب يزحفون على أستاههم،','117','0','0');
INSERT INTO [t7776] VALUES (115,'إذا قام أحدكم من الليل فاستعجم القرآن على لسانه فلم يدر ما يقول','118','0','0');
INSERT INTO [t7776] VALUES (116,'لا يقل ابن آدم يا خيبة الدهر فإني أنا الدهر أرسل الليل والنهار فإذا شئت','119','0','0');
INSERT INTO [t7776] VALUES (117,'نعما للمملوك أن يتوفاه الله بحسن طاعة ربه وطاعة سيده، نعما له نعما له','120','0','0');
INSERT INTO [t7776] VALUES (118,'فلا يبصق أمامه؛ فإنه يناجي الله ما دام في مصلاه، ولا عن يمينه؛ فإن عن يمينه ملكا، ولكن','121','0','0');
INSERT INTO [t7776] VALUES (119,'إذا قلت للناس أنصتوا وهم يتكلمون فقد لغوت على نفسك، يعني يوم الجمعة','122','0','0');
INSERT INTO [t7776] VALUES (120,'أنا أولى الناس بالمؤمنين في كتاب الله، فأيكم ترك دينا أو ضيعة فادعوني فإني وليه، وأيكم ما','123','0','0');
INSERT INTO [t7776] VALUES (121,'اللهم اغفر لي إن شئت، أو ارحمني إن شئت، أو ارزقني إن شئت، ليعزم المسألة؛ إنه يفعل ما','124','0','0');
INSERT INTO [t7776] VALUES (122,'فلم تحل الغنائم لأحد من قبلنا؛ ذلك بأن الله رأى ضعفنا وعجزنا فطيبها','125','0','0');
INSERT INTO [t7776] VALUES (123,'أنزع على حوض أسقي الناس، فأتاني أبو بكر فأخذ الدلو من يدي ليريحني فنزع دلوين وفي نزعه ضعف','126','0','0');
INSERT INTO [t7776] VALUES (124,'لا تقوم الساعة حتى تقاتلوا جور كرمان، قوما من الأعاجم حمر الوجوه، فطس الأنوف، صغار','127','0','0');
INSERT INTO [t7776] VALUES (125,'الخيلاء والفخر في أهل الخيل والإبل، والسكينة في أهل الغنم','128','0','0');
INSERT INTO [t7776] VALUES (126,'لا تقوم الساعة حتى تقاتلوا قوما نعالهم الشعر','129','0','0');
INSERT INTO [t7776] VALUES (127,'الناس تبع لقريش في هذه الشأن - أراه يعني الإمارة - مسلمهم تبع لمسلمهم، وكافرهم تبع','130','0','0');
INSERT INTO [t7776] VALUES (128,'خير نساء ركبن الإبل نساء قريش، أحناه على ولد في صغره، وأرعاه على زوج في ذات','131','0','0');
INSERT INTO [t7776] VALUES (129,'العين حق، ونهى عن الوشم','132','0','0');
INSERT INTO [t7776] VALUES (130,'لا يزال أحدكم في صلاة ما كانت تحبسه، ولا يمنعه أن يخرج إلا انتظارها','133','0','0');
INSERT INTO [t7776] VALUES (131,'اليد العليا خير من اليد السفلى، وابدأ بمن تعول','134','0','0');
INSERT INTO [t7776] VALUES (132,'أنا أولى الناس بعيسى ابن مريم في الأولى والآخرة، قالوا: كيف يا رسول الله؟ قال: الأنبياء','135','0','0');
INSERT INTO [t7776] VALUES (133,'أوتيت من خزائن الأرض، فوضع في يدي سواران من ذهب فكبرا علي وأهماني، فأوحي إلي أن انفخهما،','136','0','0');
INSERT INTO [t7776] VALUES (134,'ليس أحد منكم بمنجيه عمله، ولكن سددوا وقاربوا. قالوا: ولا أنت يا رسول الله؟ قال: ولا أنا','137','0','0');
INSERT INTO [t7776] VALUES (135,'نهى رسول الله صلى الله عليه وسلم عن بيعتين ولبستين، أن يحتبي أحدكم في الثوب الواحد ليس','138','0','0');
INSERT INTO [t7776] VALUES (136,'العجماء جرحها جبار، والبئر جبار، والمعدن جبار، والنار جبار، وفي الركاز','139','0','0');
INSERT INTO [t7776] VALUES (137,'أيما قرية أتيتموها وأقمتم، فيها مسهمكم - وأظنه قال فهي لكم أو نحوه من الكلام - وأيما قرية','140','0','0');
