INSERT INTO [t8203] VALUES (16,'باب في كراهية إضاعة المال وهو الإنفاق في معصية الله أو في معروف. روينا عن عبد الله بن مسعود أنه قال: " النفقة في غير حق هو التبذير ". وروينا في معناه، عن عبد الله بن عباس: " هو التبذير ".','100','0','0');
INSERT INTO [t8203] VALUES (17,'باب في فضل الإنفاق بالمعروف وكراهية البخل والإمساك قال الله عز وجل في مدح المنفقين: وسارعوا إلى مغفرة من ربكم وجنة عرضها السموات والأرض أعدت للمتقين، الذين ينفقون في السراء والضراء وقال في ذم البخلاء: والله لا يحب كل مختال فخور، الذين يبخلون ويأمرون','102','0','0');
INSERT INTO [t8203] VALUES (18,'باب في التعاون على البر والتقوى قال الله عز وجل: وتعاونوا على البر والتقوى ولا تعاونوا على الإثم والعدوان.','107','0','0');
