INSERT INTO [t8204] VALUES (1,'من يرد الله به خيرا يفقهه في الدين قال محمد بن الحسين: يدل على أنه من لم يتفقه في دينه','8','0','0');
INSERT INTO [t8204] VALUES (2,'عليكم بالعلم قبل أن يقبض، وقبل أن يرفع ثم جمع بين أصبعيه الوسطى والتي تلي الإبهام، ثم','9','0','0');
INSERT INTO [t8204] VALUES (3,'الأعمال بالنية، وإنما لامرئ ما نوى، فمن كانت هجرته إلى الله ورسوله فهجرته إلى الله','10','0','0');
INSERT INTO [t8204] VALUES (4,'بني الإسلام على خمس: شهادة أن لا إله إلا الله، وأن محمدا رسول الله، وإقام الصلاة، وإيتاء','11','0','0');
INSERT INTO [t8204] VALUES (5,'أخبرني عن الإسلام، وما الإسلام؟ قال: أن تشهد أن لا إله إلا الله، وأن محمدا رسول الله،','12','0','0');
INSERT INTO [t8204] VALUES (6,'إن خلق أحدكم يجمع في بطن أمه أربعين ليلة، ثم يكون علقة مثل ذلك، ثم يكون مضغة مثل ذلك، ثم','13','0','0');
INSERT INTO [t8204] VALUES (7,'ما منكم من نفس منفوسة إلا وقد كتب مكانها من الجنة والنار، وإلا وقد كتبت شقية أو سعيدة','14','0','0');
INSERT INTO [t8204] VALUES (8,'أوصيكم بتقوى الله، والسمع والطاعة، وإن كان عبدا حبشيا، فإنه من يعش منكم بعدي سيرى اختلافا','15','0','0');
INSERT INTO [t8204] VALUES (9,'ونزل القرآن من سبعة أبواب، على سبعة أحرف: زاجر، وآمر، وحلال، وحرام، ومحكم، ومتشابه،','16','0','0');
INSERT INTO [t8204] VALUES (10,'أبو بكر في الجنة، وعمر في الجنة، وعثمان في الجنة، وعلي في الجنة، وطلحة في الجنة، والزبير','17','0','0');
INSERT INTO [t8204] VALUES (11,'إن الله عز وجل اختارني واختار لي أصحابا، فجعل لي منهم وزراء وأنصارا وأصهارا، فمن سبهم','18','0','0');
INSERT INTO [t8204] VALUES (12,'الإيمان قول باللسان، وعمل بالأركان، ويقين بالقلب قال محمد بن الحسين: هذا الحديث أصل كبير','19','0','0');
INSERT INTO [t8204] VALUES (13,'ليأتين على أمتي ما أتى على بني إسرائيل؛ تفرق بنو إسرائيل على ثنتين وسبعين ملة، وستفترق','20','0','0');
INSERT INTO [t8204] VALUES (14,'أصول البدع أربع: الروافض، والخوارج، والقدرية، والمرجئة، ثم تتشعب كل فرقة ثماني عشرة','21','0','0');
INSERT INTO [t8204] VALUES (15,'هذا وظيفة الوضوء الذي لا يقبل الله عز وجل صلاة إلا به، ثم توضأ مرتين مرتين فقال: هذا وضوء','22','0','0');
INSERT INTO [t8204] VALUES (16,'فأفرغ من الإناء على يديه فغسلهما ثلاثا، ثم مضمض واستنشق ثلاثا من الكف الذي يأخذ به الماء،','23','0','0');
INSERT INTO [t8204] VALUES (17,'وضعت للنبي صلى الله عليه وسلم غسلا فاغتسل من الجنابة، فكفأ الإناء بشماله على يمينه فغسل','24','0','0');
INSERT INTO [t8204] VALUES (18,'خمس من جاء بهن يوم القيامة مع إيمان دخل الجنة: من حافظ على الصلوات الخمس: على وضوئهن،','25','0','0');
INSERT INTO [t8204] VALUES (19,'إذا قام إلى الصلاة كبر ثم قرأ، فإذا ركع أمكن كفيه من ركبتيه، وفرج بين أصابعه، ثم هصر','26','0','0');
INSERT INTO [t8204] VALUES (20,'إذا أردت الصلاة فتوضأ فأحسن الوضوء، ثم قم فاستقبل القبلة، ثم كبر، ثم اقرأ، ثم اركع حتى','27','0','0');
INSERT INTO [t8204] VALUES (21,'ترون هذا لو مات على هذا لمات على غير ملة محمد صلى الله عليه وسلم؟ نقر صلاته كما ينقر','28','0','0');
INSERT INTO [t8204] VALUES (22,'من رمى سهما في سبيل الله فبلغ أخطأ أو أصاب كان سهمه ذلك كله كعدل رقبة من ولد إسماعيل، ومن','29','0','0');
INSERT INTO [t8204] VALUES (23,'من توضأ كما أمر، وصلى كما أمر، غفر له ما تقدم من عمل، أكذلك يا عقبة؟ قال: نعم قال محمد بن','30','0','0');
INSERT INTO [t8204] VALUES (24,'أيما رجل له مال لم يعط حق الله تبارك وتعالى منه إلا جعله الله عز وجل شجاعا قرعا على صاحبه','31','0','0');
INSERT INTO [t8204] VALUES (25,'ما من صاحب إبل، ولا بقر، ولا غنم، لا يؤدي زكاتها، إلا جاءت يوم القيامة أعظم ما كانت','32','0','0');
INSERT INTO [t8204] VALUES (26,'ليس فيما دون خمس أواق صدقة، وليس فيما دون خمس ذود صدقة، وليس فيما دون خمس أوسق صدقة قال','33','0','0');
INSERT INTO [t8204] VALUES (27,'في خمس من الإبل شاة، وفي عشر شاتان، وفي خمس عشرة ثلاث شياه، وفي عشرين أربع شياه، وفي خمس','34','0','0');
INSERT INTO [t8204] VALUES (28,'من صام رمضان إيمانا واحتسابا غفر الله له ما تقدم من ذنبه، ومن قام ليلة القدر إيمانا','35','0','0');
INSERT INTO [t8204] VALUES (29,'كان الصوم ثلاثة أيام في كل شهر، ثم نسخ ذلك بالذي أنزل الله تعالى من صيام شهر رمضان، وهذا','36','0','0');
INSERT INTO [t8204] VALUES (30,'الشهر تسع وعشرون، فلا تصوموا حتى تروه، ولا تفطروا حتى تروه، فإن غم عليكم فاقدروا له قال','37','0','0');
INSERT INTO [t8204] VALUES (31,'الهلال إذا حال دون منظره غيم فينبغي أن يعتقد من الليل أنه يصبح صائما، لأنه لا يدري من','38','0','0');
INSERT INTO [t8204] VALUES (32,'إذا كان ليلة ثلاثين من شعبان نظر إلى الهلال، فإن حال دونه سحاب أو قتر أصبح صائما، وإن لم','39','0','0');
INSERT INTO [t8204] VALUES (33,'من أراد الحج فليعجل، فإنه قد يمرض المريض، وتضل الضالة، وتعرض الحاجة قال محمد بن الحسين:','40','0','0');
INSERT INTO [t8204] VALUES (34,'من لم يمنعه من الحج حاجة ظاهرة، ولا مرض حابس، ولا سلطان جائر، فمات ولم يحج، فليمت إن شاء','41','0','0');
INSERT INTO [t8204] VALUES (35,'والسبيل أن يصح بدن العبد، ويكون له ثمن زاد وراحلة من غير أن يجحف به، ثم قال: {ومن كفر فإن','42','0','0');
INSERT INTO [t8204] VALUES (36,'رباط يوم في سبيل الله خير من قيام شهر وصيامه، ومن مات مرابطا في سبيل الله كان له أجر','43','0','0');
INSERT INTO [t8204] VALUES (37,'جاهدوا في سبيل الله القريب والبعيد، في الحضر والسفر، فإن الجهاد باب من أبواب الجنة، وإنه','44','0','0');
INSERT INTO [t8204] VALUES (38,'إن أولياء الله تبارك وتعالى المصلون وأن رسول الله صلى الله عليه وسلم قال: من يقم الصلوات','45','0','0');
INSERT INTO [t8204] VALUES (39,'هي إحدى عشرة كبيرة، منها أربعة في الرأس، وهي: الشرك بالله، وقذف المحصنات، واليمين','46','0','0');
INSERT INTO [t8204] VALUES (40,'ما نملك لك من الله شيئا وذرفت عيناه، فقلت: صلى الله عليك أتبكي؟ أو لم تنه عن البكاء؟ قال:','47','0','0');
INSERT INTO [t8204] VALUES (41,'الدين النصيحة، إن الدين النصيحة، إن الدين النصيحة ثلاث مرات قال: لمن يا رسول الله؟ قال:','48','0','0');
INSERT INTO [t8204] VALUES (42,'الحلال بين، والحرام بين، وبينهما شبهات لا يعلمها كثير من الناس، فمن اتقى الشبهات فقد','49','0','0');
INSERT INTO [t8204] VALUES (43,'سبعة في ظل الله عز وجل يوم لا ظل إلا ظله: إمام مقتصد، وشاب نشأ في عبادة الله عز وجل','50','0','0');
INSERT INTO [t8204] VALUES (44,'أمرتني بالصلاة، فما الصلاة؟ قال: خير موضوع، فاستكثر أو استقل قال: قلت: يا رسول الله فأي','51','0','0');
INSERT INTO [t8204] VALUES (45,'من حفظ على أمتي أربعين حديثا من أمر دينها بعثه الله عز وجل يوم القيامة في زمرة الفقهاء','52','0','0');
