INSERT INTO [t8206] VALUES (1,'الكتاب الأول: الإيمان','2','0','0');
INSERT INTO [t8206] VALUES (2,'المجلد الأول','2','1','0');
INSERT INTO [t8206] VALUES (3,'مقدمة','2','2','0');
INSERT INTO [t8206] VALUES (4,'باب ذكر الأخبار والآثار التي دعتنا إلى جمع هذا الكتاب وتأليفه قال الشيخ: " أستوفق الله لصواب القول وصالح العمل، وأسأله عصمة من الزلل، وأن يجعل ما يوفقنا له من ذلك واصلا بنا إليه، ومزلفنا لديه، وأن يجعل ما علمنا حجة لنا، وبركة علينا، وعلى من عرفنا','30','2','0');
INSERT INTO [t8206] VALUES (5,'باب ذكر ما افترضه الله تعالى نصا في التنزيل من طاعة الرسول صلى الله عليه وسلم " أما بعد: فإن الله عز وجل بعث محمدا صلى الله عليه وسلم رحمة للعالمين ومهيمنا على النبيين ونذيرا بين يدي عذاب شديد بكتاب أحكمت آياته وفصلت بيناته لا يأتيه الباطل من بين يديه،','63','2','0');
INSERT INTO [t8206] VALUES (6,'باب ذكر ما جاءت به السنة من طاعة رسول الله صلى الله عليه وسلم والتحذير من طوائف يعارضون سنن رسول الله صلى الله عليه وسلم بالقرآن قال الشيخ: " وليعلم المؤمنون من أهل العقل والعلم أن قوما يريدون إبطال الشريعة ودروس آثار العلم والسنة، فهم يموهون على من قل','68','2','0');
INSERT INTO [t8206] VALUES (7,'باب ذكر ما نطق به الكتاب نصا في محكم التنزيل بلزوم الجماعة والنهي عن الفرقة أما بعد: فاعلموا يا إخواني وفقنا الله وإياكم للسداد والائتلاف، وعصمنا وإياكم من الشتات والاختلاف، أن الله عز وجل قد أعلمنا اختلاف الأمم الماضين قبلنا وإنهم تفرقوا واختلفوا','118','2','0');
INSERT INTO [t8206] VALUES (8,'باب ذكر ما أمر به النبي صلى الله عليه وسلم من لزوم الجماعة والتحذير من الفرقة','126','2','0');
INSERT INTO [t8206] VALUES (9,'باب ما أمر به من التمسك بالسنة والجماعة، والأخذ بها، وفضل من لزمها','165','2','0');
INSERT INTO [t8206] VALUES (10,'باب ذكر افتراق الأمم في دينهم وعلى كم تفترق هذه الأمة وإخبار النبي صلى الله عليه وسلم لنا بذلك قال الشيخ: " قد ذكرت في أول هذا الكتاب ما قصه الله عز وجل علينا في كتابه من اختلاف الأمم، وتفرق أهل الكتاب، وتحذيره إيانا من ذلك، وأنا أذكر الآن ما جاءت به','288','2','0');
INSERT INTO [t8206] VALUES (11,'باب ترك السؤال عما لا يغني والبحث والتنقير عما لا يضر جهله والتحذير من قوم يتعمقون في المسائل ويتعمدون إدخال الشكوك على المسلمين قال الشيخ: " اعلموا إخواني أني فكرت في السبب الذي أخرج أقواما من السنة والجماعة، واضطرهم إلى البدعة والشناعة، وفتح باب','314','2','0');
INSERT INTO [t8206] VALUES (12,'المجلد الثاني','375','1','0');
INSERT INTO [t8206] VALUES (13,'باب التحذير من صحبة قوم يمرضون القلوب ويفسدون الإيمان قد أعلمتك يا أخي - عصمني الله وإياك من الفتن , ووقانا وإياك جميع المحن - أن الذي أورد القلوب حمامها , وأورثها الشك بعد اتقائها هو البحث والتنقير , وكثرة السؤال , عما لا تؤمن فتنته , وقد كفي العقلاء','375','12','0');
INSERT INTO [t8206] VALUES (14,'باب ذم المراء والخصومات في الدين , والتحذير من أهل الجدال والكلام','539','12','0');
INSERT INTO [t8206] VALUES (15,'باب التحذير من استماع كلام قوم يريدون نقض الإسلام , ومحو شرائعه فيكنون عن ذلك بالطعن على فقهاء المسلمين , وعيبهم بالاختلاف فإن قال قائل: قد ذكرت نهي النبي صلى الله عليه وسلم عن الفرقة , وتحذيره أمته ذلك , وحضه إياهم على الجماعة والتمسك بالسنة , وقلت','724','12','0');
INSERT INTO [t8206] VALUES (16,'باب إعلام النبي صلى الله عليه وسلم لأمته ركوب طريق الأمم قبلهم , وتحذيره إياهم ذلك','746','12','0');
INSERT INTO [t8206] VALUES (17,'باب إعلام النبي صلى الله عليه وسلم أمته أمر الفتن الجارية , وأمره لهم بلزوم البيوت , وفضل القعود , ولزوم العقلاء بيوتهم , وتخوفهم على قلوبهم من اتباع الهوى , وصيانتهم لألسنتهم وأديانهم','762','12','0');
INSERT INTO [t8206] VALUES (18,'باب تحذير النبي صلى الله عليه وسلم لأمته من قوم يتجادلون بمتشابه القرآن وما يجب على الناس من الحذر منهم','812','12','0');
INSERT INTO [t8206] VALUES (19,'باب النهي عن المراء في القرآن','829','12','0');
INSERT INTO [t8206] VALUES (20,'باب معرفة الإيمان , وكيف نزل به القرآن وترتيب الفرائض , وأن الإيمان قول وعمل رب يسر وأعن بعونك، الحمد لله رب العالمين، الرحمن الرحيم، مالك يوم الدين الذي هو ربنا وبه نستعين وإياه نسأل أن يهدينا إلى الصراط المستقيم الذي أنعم عليهم بهدى القرآن فاتبعوه','854','12','0');
INSERT INTO [t8206] VALUES (21,'باب معرفة اليوم الذي نزلت فيه هذه الآية','861','12','0');
INSERT INTO [t8206] VALUES (22,'باب معرفة الإسلام وعلى كم بني','866','12','0');
INSERT INTO [t8206] VALUES (23,'باب معرفة الإسلام والإيمان وسؤال جبريل النبي صلى الله عليه وسلم','872','12','0');
INSERT INTO [t8206] VALUES (24,'باب فضائل الإيمان وعلى كم شعبة هو وأخلاق المؤمنين وصفاتهم','877','12','0');
INSERT INTO [t8206] VALUES (25,'باب كفر تارك الصلاة , ومانع الزكاة , وإباحة قتالهم وقتلهم إذا فعلوا ذلك','911','12','0');
INSERT INTO [t8206] VALUES (26,'باب ذكر الأفعال والأقوال التي تورث النفاق , وعلامات المنافقين','940','12','0');
INSERT INTO [t8206] VALUES (27,'باب ذكر الذنوب التي من ارتكبها فارقه الإيمان , فإن تاب راجعه','992','12','0');
INSERT INTO [t8206] VALUES (28,'باب ذكر الذنوب التي تصير بصاحبها إلى كفر غير خارج عن الملة','1025','12','0');
INSERT INTO [t8206] VALUES (29,'باب: الإيمان خوف ورجاء وتخوف العقلاء من المؤمنين على أنفسهم سلب الإيمان وخوفهم النفاق على من أمن ذلك على نفسه. بذلك نزل القرآن , وجاءت به السنة. قال الله عز وجل: أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم أقرب ويرجون رحمته ويخافون عذابه. وقال عز','1099','12','0');
INSERT INTO [t8206] VALUES (30,'باب بيان الإيمان وفرضه وأنه تصديق بالقلب وإقرار باللسان وعمل بالجوارح والحركات، لا يكون العبد مؤمنا إلا بهذه الثلاث. قال الشيخ: اعلموا رحمكم الله أن الله جل ثناؤه , وتقدست أسماؤه فرض على القلب المعرفة به , والتصديق له ولرسله ولكتبه , وبكل ما جاءت به','1108','12','0');
INSERT INTO [t8206] VALUES (31,'باب ذكر الآيات من كتاب الله عز وجل في ذلك قال الله عز وجل: وبشر الذين آمنوا وعملوا الصالحات أن لهم جنات تجري من تحتها الأنهار كلما رزقوا منها من ثمرة رزقا قالوا هذا الذي رزقنا من قبل الآية. وقال الله عز وجل: إن الذين آمنوا وعملوا الصالحات وأقاموا','1121','12','0');
INSERT INTO [t8206] VALUES (32,'باب زيادة الإيمان ونقصانه , وما دل على الفاضل فيه والمفضول اعلموا رحمكم الله أن الله عز وجل تفضل بالإيمان على من سبقت له الرحمة في كتابه , ومن أحب أن يسعده , ثم جعل المؤمنين في الإيمان متفاضلين , ورفع بعضهم فوق بعض درجات , ثم جعله فيهم يزيد ويقوى','1174','12','0');
INSERT INTO [t8206] VALUES (33,'باب الاستثناء في الإيمان قال الشيخ: اعلموا رحمنا الله وإياكم أن من شأن المؤمنين وصفاتهم وجود الإيمان فيهم , ودوام الإشفاق على إيمانهم , وشدة الحذر على أديانهم , فقلوبهم وجلة من خوف السلب , قد أحاط بهم الوجل، لا يدرون ما الله صانع بهم في بقية أعمارهم ,','1232','12','0');
INSERT INTO [t8206] VALUES (34,'باب سؤال الرجل لغيره أمؤمن أنت , وكيف الجواب له , وكراهية العلماء هذا السؤال وتبديع السائل عن ذلك','1261','12','0');
