INSERT INTO [t8206] VALUES (98,'باب الإيمان بأن القرآن كلام الله غير مخلوق، خلافا على الطائفة الواقفة التي وقفت وشكت وقالت: لا نقول: مخلوق، ولا: غير مخلوق','2269','95','0');
INSERT INTO [t8206] VALUES (99,'باب ذكر اللفظية والتحذير من رأيهم ومقالاتهم واعلموا رحمكم الله أن صنفا من الجهمية اعتقدوا بمكر قلوبهم، وخبث آرائهم، وقبيح أهوائهم، أن القرآن مخلوق، فكنوا عن ذلك ببدعة اخترعوها، تمويها وبهرجة على العامة، ليخفى كفرهم، ويستغمض إلحادهم على من قل علمه','2327','95','0');
