INSERT INTO [t8206] VALUES (103,'باب اتضاح الحجة في أن القرآن كلام الله غير مخلوق من قول التابعين، وفقهاء المسلمين والبدلاء والصالحين، رحمة الله عليهم أجمعين، وتكفير من قال: إن القرآن مخلوق، وبيان ردته وزندقته','2392','102','0');
INSERT INTO [t8206] VALUES (104,'باب بيان كفرهم وضلالهم وخروجهم عن الملة وإباحة قتلهم','2450','102','0');
