INSERT INTO [t8207] VALUES (1,'القوم إذا رأوا الظالم فلم يأخذوا على يديه، والمنكر فلم يغيروه، عمهم الله','1','0','0');
INSERT INTO [t8207] VALUES (2,'بل ائتمروا بالمعروف وتناهوا عن المنكر، حتى إذا رأيت شحا مطاعا وهوى متبعا، وإعجاب كل ذي','2','0','0');
INSERT INTO [t8207] VALUES (3,'إذا رأيت أمتي تهاب الظالم أن تقول له: إنك ظالم، فقد تودع منهم','3','0','0');
INSERT INTO [t8207] VALUES (4,'إن من كان قبلكم كان إذا عمل العامل فيهم بالخطيئة نهاه الناهي تعذيرا، فإذا كان الغد جالسه','4','0','0');
INSERT INTO [t8207] VALUES (5,'أيما قوم عمل فيهم بالمعاصي هم أعز وأكثر، لم يغيروا، إلا عمهم الله','5','0','0');
INSERT INTO [t8207] VALUES (6,'ما من قوم يكون بين أظهرهم من يعمل بالمعاصي هم أعز وأمنع لم يغيروا عليه، إلا أصابهم الله','6','0','0');
INSERT INTO [t8207] VALUES (7,'أيها الناس إن الله عز وجل يقول لكم: مروا بالمعروف، وانهوا عن المنكر، قبل أن تدعوني فلا','7','0','0');
INSERT INTO [t8207] VALUES (8,'لتأمرن بالمعروف، ولتنهون عن المنكر أو ليسلطن الله عليكم شراركم، فليسومنكم سوء العذاب، ثم','8','0','0');
INSERT INTO [t8207] VALUES (9,'لا يمنعن أحدكم مخافة الناس أن يتكلم بحق إذا علمه قال أبو سعيد: فما زال بنا البلاء حتى','9','0','0');
INSERT INTO [t8207] VALUES (10,'من رأى منكرا فاستطاع أن يغيره بيده فليفعل، فإن لم يستطع بيده فبلسانه، فإن لم يستطع بلسانه','10','0','0');
INSERT INTO [t8207] VALUES (11,'ما منعك إذا رأيت المنكر أن تنكره؟ ، قال رسول الله صلى الله عليه وسلم: فإذا لقن الله عبدا','11','0','0');
INSERT INTO [t8207] VALUES (12,'لتأمرن بالمعروف، ولتنهون عن المنكر، ولتحاضن على الخير، أو ليسحتنكم الله جميعا بعذاب، أو','12','0','0');
INSERT INTO [t8207] VALUES (13,'الأمر بالمعروف والنهي عن المنكر؛ فإنه دين الله الذي بعث الله به أنبياءه إلى عباده، وقد','13','0','0');
INSERT INTO [t8207] VALUES (14,'من غفلتك عن نفسك إعراضك عن الله، بأن ترى ما يسخطه فتجاوزه، لا تأمر فيه، ولا تنهى، خوفا','14','0','0');
INSERT INTO [t8207] VALUES (15,'من ترك الأمر بالمعروف والنهي عن المنكر من مخافة المخلوقين نزعت منه هيبة الطاعة، فلو أمر','15','0','0');
INSERT INTO [t8207] VALUES (16,'لا يمنعن أحدكم مخافة الناس أن يتكلم بالحق إذا رآه أو علمه أو رآه أو','16','0','0');
INSERT INTO [t8207] VALUES (17,'لعن الله من ليس منا أعظم من أحد، لتأمرن بالمعروف ولتنهون عن المنكر، أو لتقتتلن فليظهرن','17','0','0');
INSERT INTO [t8207] VALUES (18,'الجهاد على أربع شعب: على الأمر بالمعروف والنهي عن المنكر، والصدق في المواطن، وشنآن','18','0','0');
INSERT INTO [t8207] VALUES (19,'مروا بالمعروف وإن لم تعملوا به كله، وانهوا عن المنكر وإن لم تناهوا عنه','19','0','0');
INSERT INTO [t8207] VALUES (20,'لتأمرن بالمعروف ولتنهون عن المنكر، ولتأخذن على يدي المسيء، ولتأطرنه على الحق أطرا، أو','20','0','0');
INSERT INTO [t8207] VALUES (21,'يجاء بالرجل يوم القيامة فيلقى في النار، فتندلق أقتابه، فيدور بها كما يدور الحمار برحاه،','21','0','0');
INSERT INTO [t8207] VALUES (22,'أتقاهم للرب، وأوصلهم للرحم، وآمرهم بالمعروف، وأنهاهم عن المنكر','22','0','0');
INSERT INTO [t8207] VALUES (23,'لم يكن يقر (. . .) سخطا لله','23','0','0');
INSERT INTO [t8207] VALUES (24,'أيها الناس، لا تغرنكم هذه الآية التي في المائدة: {يا أيها الذين آمنوا عليكم أنفسكم لا','24','0','0');
INSERT INTO [t8207] VALUES (25,'لا تقوم الساعة حتى يأخذ الله شريطته من أهل الأرض، فيبقى عجاج لا يعرفون معروفا، ولا ينكرون','25','0','0');
INSERT INTO [t8207] VALUES (26,'يأتي على الناس زمان يذوب فيه قلب المؤمن، كما يذوب الملح في الماء، قيل: مم ذاك؟، قال: مما','26','0','0');
INSERT INTO [t8207] VALUES (27,'إذا سمعوا المنكر فلم يغيروه أوشك أن يعمهم الله بعقاب','27','0','0');
INSERT INTO [t8207] VALUES (28,'إذا كان البخل في خياركم، والعلم في رذالكم، والإدهان في قرائكم، والملك في','28','0','0');
INSERT INTO [t8207] VALUES (29,'كيف بك إذا بقيت في حثالة من الناس، قد مرجت عهودهم وأماناتهم، واختلفوا فصاروا كذا - وشبك','29','0','0');
INSERT INTO [t8207] VALUES (30,'أنتم اليوم على بينة من ربكم تأمرون بالمعروف وتنهون عن المنكر، وتجاهدون في سبيل الله،','30','0','0');
INSERT INTO [t8207] VALUES (31,'وإذا وقع القول عليهم} [النمل: ٨٢] ، قال: إذا لم يأمروا بالمعروف ولم ينهوا عن','31','0','0');
INSERT INTO [t8207] VALUES (32,'لأتيحن لهم فتنة يصير الحليم فيهم حيرانا','32','0','0');
INSERT INTO [t8207] VALUES (33,'غشيتكم سكرتان: سكرة الجهل وسكرة حب العيش، فعند ذلك لا تأمرون بمعروف ولا تنهون عن','33','0','0');
INSERT INTO [t8207] VALUES (34,'سيكون آخر الزمان رجراجة من الناس لا يعرفون حقا، ولا ينكرون منكرا، يتراكبون كما تتراكب','34','0','0');
INSERT INTO [t8207] VALUES (35,'لا يحل لعين مؤمنة ترى الله يعصى فتطرف حتى تغيره','35','0','0');
INSERT INTO [t8207] VALUES (36,'القوم عرضوا السيف فحال السيف دون الكلام، قلت: يا أبا سعيد، هل تعرف لمتكلم فضلا؟، قال: ما','36','0','0');
INSERT INTO [t8207] VALUES (37,'ألا لا يمنعن أحدكم هيبة الناس أن يقول بحق إذا رآه أو شهده، فإنه لا يقرب من أجل ولا يباعد','37','0','0');
INSERT INTO [t8207] VALUES (38,'ليس للمؤمن أن يذل نفسه، قيل: يا رسول الله، وما إذلاله لنفسه؟، قال: يتعرض من البلاء ما لا','38','0','0');
INSERT INTO [t8207] VALUES (39,'الأمر بالمعروف والنهي عن المنكر، من فرائض الله تبارك وتعالى','39','0','0');
INSERT INTO [t8207] VALUES (40,'الرجل يأمر والديه بالمعروف وينهاهما عن المنكر،؟ قال: يأمرهما إن قبلا، وإن كرها سكت','40','0','0');
INSERT INTO [t8207] VALUES (41,'أطعم الجائع، واسق الظمآن، ومر بالمعروف وانه عن المنكر، فإن لم تطق، فكف لسانك إلا من','41','0','0');
INSERT INTO [t8207] VALUES (42,'أوصاني رسول الله صلى الله عليه وسلم بقول الحق وإن كان مرا، وأوصاني أن لا تأخذني في الله','42','0','0');
INSERT INTO [t8207] VALUES (43,'الناس إذا رأوا منكرا لم يغيروه، يوشك أن يعمهم الله بعقاب','43','0','0');
INSERT INTO [t8207] VALUES (44,'أيها الناس، مروا بالمعروف، وانهوا عن المنكر، قبل أن تدعوا الله فلا يستجيب لكم، وقبل أن','44','0','0');
INSERT INTO [t8207] VALUES (45,'يا فتى، ما هذا أجر نعمة الله عندك','45','0','0');
INSERT INTO [t8207] VALUES (46,'الله يراكما، سترنا الله وإياكما','46','0','0');
INSERT INTO [t8207] VALUES (47,'عشوا قنديلا، وابعثوا به إلى مواليه','47','0','0');
INSERT INTO [t8207] VALUES (48,'يا ابن أخي إن لي إليك حاجة، قال: فما ذاك يا عم؟ قال: ترفع إزارك، قال: نعم، ونعمة عين،','48','0','0');
INSERT INTO [t8207] VALUES (49,'أن رجلا، كان يقال له: عقيب كان يعبد الله وكان في ذلك الزمان ملك يعذب الناس بالمثلات، فقال','49','0','0');
INSERT INTO [t8207] VALUES (50,'كان ثلاثة نفر اجتمعوا فقالوا عن هذا الرجل يفعل ويفعل - يعنون ملكهم - فقالوا: فيأتيه واحد','50','0','0');
INSERT INTO [t8207] VALUES (51,'أخبروني عن قوم أرادوا سفرا فحادوا النهار عن الطريق وناموا الليل، فمتى يقطعون سفرهم؟ قال:','51','0','0');
INSERT INTO [t8207] VALUES (52,'رأى العمري العابد رجلا من آل علي يمشي يخطر، فأسرع إليه، فأخذ بيده، فقال: يا هذا، إن الذي','52','0','0');
INSERT INTO [t8207] VALUES (53,'تأمر من يقبل منك، أرأيت إن لقيت سلطانا أكنت تقول له: اتق الله؟ لو قلت هذا لأهلكت أهل بيتك','53','0','0');
INSERT INTO [t8207] VALUES (54,'أمرنا بالمعروف ونهينا عن المنكر فدعونا نصبر على ما أصابنا، فندخل في وصية الرجل','54','0','0');
INSERT INTO [t8207] VALUES (55,'أن الحجاج بن أيمن ابن أم أيمن، - وكان أيمن أخا أسامة لأمه، وهو رجل من الأنصار - فدخل','55','0','0');
INSERT INTO [t8207] VALUES (56,'كذبت والله ما صليت، والله لا تريم حتى تصلي، قال: فقمت فصليت فأتممت الركوع والسجود، فقال','56','0','0');
INSERT INTO [t8207] VALUES (57,'قم صل، فقال: قد صليت، قال: لا والله، لا تبرح حتى تصلي، قال: ما لك ولهذا يا أعرج؟ قال:','57','0','0');
INSERT INTO [t8207] VALUES (58,'إذا رأوا الرجل لا يحسن الصلاة علموه قال سفيان: أخشى أن لا يسعهم إلا','58','0','0');
INSERT INTO [t8207] VALUES (59,'أنصح الناس إليك من خاف الله فيك','59','0','0');
INSERT INTO [t8207] VALUES (60,'كان الحسن بن حي إذا أراد أن يعظ، أخا له كتبه في لوح وناوله','60','0','0');
INSERT INTO [t8207] VALUES (61,'من وعظ أخاه فيما بينه وبينه فهي نصيحة، ومن وعظه على رءوس الناس فإنما','61','0','0');
INSERT INTO [t8207] VALUES (62,'رأيتك في الجمعة تلتفت، لا تفعل','62','0','0');
INSERT INTO [t8207] VALUES (63,'كان من قبلكم إذا رأى من أخيه شيئا يأمره في رفق، فيؤجر في أمره ونهيه وإن أحد هؤلاء يخرق','63','0','0');
INSERT INTO [t8207] VALUES (64,'رأى فضيل بن عياض رجلا يفقع أصابعه في الصلاة، فزبره ونهره، فقال له الرجل: يا هذا، ينبغي','64','0','0');
INSERT INTO [t8207] VALUES (65,'إن الله لا يعذب العامة بعمل الخاصة حتى يروا المنكر بين ظهرانيهم وهم قادرون على أن ينكروه،','65','0','0');
INSERT INTO [t8207] VALUES (66,'إن الله لا يعذب العامة بذنب الخاصة، ولكن إذا عمل المنكر جهارا، استحقوا العقوبة','66','0','0');
INSERT INTO [t8207] VALUES (67,'ما ترك قوم الأمر بالمعروف والنهي عن المنكر إلا لم ترفع أعمالهم، ولم يسمع','67','0','0');
INSERT INTO [t8207] VALUES (68,'إن قوما ركبوا البحر في سفينة فاقتسموا فأصاب كل رجل مكانا، فأخذ رجل منهم الفأس فنقر مكانه،','68','0','0');
INSERT INTO [t8207] VALUES (69,'أوحى الله عز وجل إلى نبي من أنبياء بني إسرائيل، يقال له: أرميا أن قم بين ظهراني قومك فإن','69','0','0');
INSERT INTO [t8207] VALUES (70,'إذا أعظمت أمتي الدنيا نزعت منها هيبة الإسلام، وإذا تركت الأمر بالمعروف والنهي عن المنكر','70','0','0');
INSERT INTO [t8207] VALUES (71,'النصح لله، قيل: فالأمر بالمعروف، والنهي عن المنكر؟، قال: جهده إذا نصح أن لا يأمر ولا','71','0','0');
INSERT INTO [t8207] VALUES (72,'إن الرجل ليدخل المدخل الذي يجب عليه أن يتكلم فيه لله فلا يتكلم، فلا يعود قلبه إلى ما كان','72','0','0');
INSERT INTO [t8207] VALUES (73,'بعث الله عز وجل ملكين إلى أهل قرية أن دمراها بمن فيها، فوجدا فيها رجلا قائما يصلي في مسجد','73','0','0');
INSERT INTO [t8207] VALUES (74,'به فابدأ، فإنه لم يتمعر وجهه في ساعة قط','74','0','0');
INSERT INTO [t8207] VALUES (75,'لم يغضبوا لغضبي، وكانوا يؤاكلونهم ويشاربونهم','75','0','0');
INSERT INTO [t8207] VALUES (76,'لما أصاب داود الخطيئة، قال: يا رب، اغفر لي، قال: قد غفرتها لك، وألزمت عارها بني إسرائيل،','76','0','0');
INSERT INTO [t8207] VALUES (77,'أوحى الله إلى نبي من أنبياء بني إسرائيل: قل لقومك: لا يدخلوا مدخل أعدائي، ولا يطعموا','77','0','0');
INSERT INTO [t8207] VALUES (78,'يوشك من عاش منكم أن يرى منكرا لا يستطيع فيه غير أن يعلم الله من قلبه أنه له','78','0','0');
INSERT INTO [t8207] VALUES (79,'نعوذ بالله أن نأمر، الناس بالبر وننسى أنفسنا، وتلا {أتأمرون الناس بالبر وتنسون أنفسكم}','79','0','0');
INSERT INTO [t8207] VALUES (80,'آمر السلطان بالمعروف وأنهاه عن المنكر؟ قال: إن خفت أن يقتلك فلا، قال: ثم عدت، فقال لي مثل','80','0','0');
INSERT INTO [t8207] VALUES (81,'من كان عليه سلطان فأراد أن يذله نزع الله ربقة الإسلام من عنقه حتى يعود فيكون فيمن','81','0','0');
INSERT INTO [t8207] VALUES (82,'أمرنا رسول الله صلى الله عليه وسلم أن لا يغلبونا على ثلاث: أن نأمر بالمعروف، وننهى عن','82','0','0');
INSERT INTO [t8207] VALUES (83,'من رأى منكرا فلينكره بقلبه، فقلت: من هذا المتكلم؟ فقالوا: هذا أبو سعيد','83','0','0');
INSERT INTO [t8207] VALUES (84,'كيف أنتم إذا كثرت أمراؤكم وطغت نساؤكم؟ قالوا: وإن ذلك لكائن يا رسول الله؟، قال: نعم، وأشد','84','0','0');
INSERT INTO [t8207] VALUES (85,'رأى جدي زبيد بيد جارية من الحي دفا فأخذه فضرب به الأرض حتى كسره','85','0','0');
INSERT INTO [t8207] VALUES (86,'رأى غلاما معه زمارة من قصب، فأخذها فشقها','86','0','0');
INSERT INTO [t8207] VALUES (87,'بينا حبر من أحبار بني إسرائيل متكئ على سريره، إذ رأى بعض بنيه يغامز النساء، فقال: مهلا يا','87','0','0');
INSERT INTO [t8207] VALUES (88,'إن قيام المؤمن بحق الله لم يبق له طريقا، والله إنا لنأمر بالمعروف وننهى عن المنكر،','88','0','0');
INSERT INTO [t8207] VALUES (89,'مر من يقبل منك','89','0','0');
INSERT INTO [t8207] VALUES (90,'تأمر من لا يقبل منك؟، قال: يكون معذرة','90','0','0');
INSERT INTO [t8207] VALUES (91,'ستغربلون حتى تصيروا في حثالة في قوم قد مرجت عهودهم، وخربت أماناتهم، قال: فكيف بنا؟ قال:','91','0','0');
INSERT INTO [t8207] VALUES (92,'ما ترك قوم القتال في سبيل الله إلا ضربهم الله بذل، ولا قر قوم المنكر بين أظهرهم إلا عمهم','92','0','0');
INSERT INTO [t8207] VALUES (93,'يأتي على الناس زمان خيرهم لا يأمر بالمعروف ولا ينهى عن المنكر','93','0','0');
INSERT INTO [t8207] VALUES (94,'أنتم اليوم على بينة من أمركم، تأمرون بالمعروف، وتنهون عن المنكر، وتجاهدون في سبيل الله،','94','0','0');
INSERT INTO [t8207] VALUES (95,'من كان له جار يعمل بالمعاصي فلم ينهه فهو شريكه','95','0','0');
INSERT INTO [t8207] VALUES (96,'لا يدخلوا مدخل أعدائي، ولا يطعموا مطاعم أعدائي، ولا يلبسوا ملابس أعدائي، ولا يركبوا مراكب','96','0','0');
INSERT INTO [t8207] VALUES (97,'عظ نفسك، فإن اتعظت فعظ الناس، وإلا فاستحي مني','97','0','0');
INSERT INTO [t8207] VALUES (98,'إذا كنت ممن يأمر بالمعروف فكن من آخذ الناس به وإلا هلكت، وإذا كنت ممن ينهى عن المنكر فكن','98','0','0');
INSERT INTO [t8207] VALUES (99,'إني أنا الله، تسميت بشديد الغضب، لآخذن مطيعكم بعاصيكم حتى لا أعصى علانية بين','99','0','0');
INSERT INTO [t8207] VALUES (100,'أما والله ليسلطن عليكم اليهود، والنصارى فليطؤن رقابكم','100','0','0');
INSERT INTO [t8207] VALUES (101,'لا يأتي على الناس الزمان إلا الذي بعده شر منه','101','0','0');
INSERT INTO [t8207] VALUES (102,'يصبح على كل سلامى من ابن آدم صدقة، تسليمه على من لقي صدقة، وأمره بالمعروف صدقة، ونهيه عن','102','0','0');
INSERT INTO [t8207] VALUES (103,'يأتي على الناس زمان يذوب فيه قلب المؤمن كما يذوب الملح في الماء، قيل: مم ذاك؟، قال: مما','103','0','0');
INSERT INTO [t8207] VALUES (104,'لا تكن له فتنة قال: أفرأيت إن أمرني بمعصية الله عز وجل؟، قال: ذاك الذي تريد، فكن حينئذ','104','0','0');
INSERT INTO [t8207] VALUES (105,'كل الذي قلتم منذ اليوم أعرف منه مثل ما تعرفون، وأنكر منه ما تنكرون، وأنا مثل الذي أنتم','105','0','0');
INSERT INTO [t8207] VALUES (106,'الأمر يا عمرو بالمعروف نافلة ... والقائمون به لله أنصار والتاركون له عجزا لهم عذر ...','106','0','0');
INSERT INTO [t8207] VALUES (107,'ليس للمؤمن أن يذل نفسه، قيل: يا رسول الله، وكيف يذل نفسه؟، قال: يتعرض من البلاء لما لا','107','0','0');
INSERT INTO [t8207] VALUES (108,'أرأيت إن رأيت شرطيا أو مسلحا أو سلطانا يظلم، أنهاه؟، قال: إن قدرت فافعل، قلت: أما الكلام','108','0','0');
INSERT INTO [t8207] VALUES (109,'ليس هذا زمان كلام، هذا زمان بكاء وتضرع، واستكانة ودعاء لجميع أمة محمد صلى الله عليه وسلم،','109','0','0');
INSERT INTO [t8207] VALUES (110,'أنا لا، أنهى إن (. . . . . .) ، إنما أخاف أن يبتلى فلا يصبر','110','0','0');
INSERT INTO [t8207] VALUES (111,'وما عليك لو فاتك الحج، أما بلغك ما لقي يوسف عليه السلام حين استشفع بغيره، قال: فصاح سيار','111','0','0');
INSERT INTO [t8207] VALUES (112,'إنها ستكون هنات وهنات، فبحسب امرئ إذا رأى منكرا لا يستطيع له غير أن يعلم الله أنه له','112','0','0');
INSERT INTO [t8207] VALUES (113,'أخشى الله إن كذبت، وأخشاكم إن صدقت','113','0','0');
INSERT INTO [t8207] VALUES (114,'إن صدقناكم قتلتمونا، وإن كذبناكم خشينا الله عز وجل، فنظر إليه الحجاج، فقال له عبد الملك:','114','0','0');
INSERT INTO [t8207] VALUES (115,'أكره إن تكلمت أن يروا أن ما بي غير الذي بي، وإن سكت رهبت أن آثم','115','0','0');
INSERT INTO [t8207] VALUES (116,'جاهدوا المنافقين بأيديكم، فإن لم تستطيعوا فبألسنتكم، فإن لم تستطيعوا إلا أن تكفهروا في','116','0','0');
INSERT INTO [t8207] VALUES (117,'أخشى أن أشهد مشهدا يدخلني النار','117','0','0');
INSERT INTO [t8207] VALUES (118,'لو أن المرء، لا يعظ أخاه حتى يحكم أمر نفسه، ويكمل الذي خلق له من عبادة ربه، إذن لتواكل','118','0','0');
INSERT INTO [t8207] VALUES (119,'من حضر معصية فكرهها فكأنه غاب عنها، ومن غاب عنها فأحبها فكأنه حضرها','119','0','0');
INSERT INTO [t8207] VALUES (120,'إنما تهلكون إذا لم يعرف لذي شيبة شيبته، ولا لذي سن سنه، وصرتم تمشون على الركبات كأنكم','120','0','0');
INSERT INTO [t8207] VALUES (121,'إني لآمرك بالأمر وما أفعله، ولكن أرجو أن أؤجر فيه','121','0','0');
INSERT INTO [t8207] VALUES (122,'بينما رجل يدور في النار مثلما يدور الحمار في الرحى، إذ ناداه أهل النار: ويلك ما لنا نراك','122','0','0');
