INSERT INTO [t8210] VALUES (1,'ذكر ما وصف الله عز وجل به نفسه ودل على وحدانيته عز وجل، وأنه أحد صمد , لم يلد ولم يولد , ولم يكن له كفوا أحد','1','0','0');
INSERT INTO [t8210] VALUES (2,'ذكر معرفة بدء الخلق قال الله تعالى مخبرا عن وحدانيته وتفرده بالخلق من غير ظهير ولا معين ما أشهدتهم خلق السموات والأرض ولا خلق أنفسهم وما كنت متخذ المضلين عضدا وقال الله عز وجل: أشهدوا خلقهم ستكتب شهادتهم ويسألون','8','0','0');
INSERT INTO [t8210] VALUES (3,'ذكر ما يدل على أن خلق العرش تقدم على خلق الأشياء','11','0','0');
INSERT INTO [t8210] VALUES (4,'ذكر ما يدل على أن الله قدر مقادير كل شيء قبل خلق الخلق قال الله تعالى: إنا كل شيء خلقناه بقدر الآية','13','0','0');
INSERT INTO [t8210] VALUES (5,'ذكر ما يستدل به أولو الألباب من الآيات الواضحة التي جعلها الله , عز وجل , دليلا لعباده من خلقه على معرفة وحدانيته من انتظام صنعته , وبدائع حكمته , في خلق السموات والأرض , وما أحكم فيها وخلق الإنسان. . . . . . . . . والأرواح وما ركب فيها. قال الله , عز','18','0','0');
INSERT INTO [t8210] VALUES (6,'ذكر الآيات المتفقة المنتظمة الدالة على توحيد الله , عز وجل في صفة خلق السموات التي ذكرها في كتابه وبينها على لسان رسوله صلى الله عليه وسلم تنبيها لخلقه. قال الله , عز وجل: ومن آياته خلق السموات والأرض ومن آياته أن تقوم السماء والأرض بأمره الآية وقال عز','25','0','0');
INSERT INTO [t8210] VALUES (7,'إخبار النبي صلى الله عليه وسلم عن ليلة المعراج. . . . . . . . سماء فوق سماء ووصفه ذلك لأصحابه , رضوان الله عليهم.','28','0','0');
INSERT INTO [t8210] VALUES (8,'ذكر ما يدل على أن النبي صلى الله عليه وسلم عرج ببدنه يقظانا وأن قريشا أنكرت ذلك , ولو كان رؤيا لم تنكر عليه','30','0','0');
INSERT INTO [t8210] VALUES (9,'ذكر آية أخرى تدل على وحدانية الله تعالى وبديع صنعته في خلق الشمس والقمر قال الله , عز وجل ,: والشمس تجري لمستقر لها ذلك تقدير العزيز العليم الآية وقال تعالى: وجعلنا الليل والنهار آيتين فمحونا آية الليل وجعلنا آية النهار مبصرة الآية وقال تعالى: وسخر','35','0','0');
INSERT INTO [t8210] VALUES (10,'ذكر آية أخرى تدل على وحدانية الله , عز وجل , وعظيم قدرته في خلق النجوم قال الله تعالى: والنجوم مسخرات بأمره وقال تعالى: ولقد زينا السماء الدنيا بمصابيح الآية وقال الله تعالى: إنا زينا السماء الدنيا بزينة الكواكب وحفظا من كل شيطان مارد','43','0','0');
INSERT INTO [t8210] VALUES (11,'ذكر آية تدل على وحدانية الخالق من لطيف صنعته وبديع حكمته في تكوير ساعات الليل على النهار وإيلاج النهار على الليل. قال الله عز وجل: يولج الليل في النهار ويولج النهار في الليل الآية وقال تعالى: يكور الليل على النهار ويكور النهار على الليل الآية وقال','50','0','0');
INSERT INTO [t8210] VALUES (12,'ذكر آية أخرى تدل على وحدانية الله , عز وجل في إمساكه السحاب في جو السماء قال الله , عز وجل: مخبرا عما عجز عن وصفه المخلوق وتاهت فيه العقول والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت وأسماء السحاب في كتاب الله تعالى: المزن، والعنان، والصوب','55','0','0');
INSERT INTO [t8210] VALUES (13,'ذكر آية أخرى تدل على وحدانية الله عز وجل مما عجز عن وصفه المخلوق وتاهت فيه العقول قوله عز وجل: أو كصيب من السماء فيه ظلمات ورعد وبرق','58','0','0');
INSERT INTO [t8210] VALUES (14,'ذكر آية أخرى تدل على وحدانية الله , وأنه مرسل الرياح والريح قال الله تعالى: إن في خلق السموات والأرض. . . . . . وبث فيها من كل دابة وتصريف الرياح. . . الآية وقال: وأرسلنا الرياح لواقح وقال: وهو الذي أرسل الرياح. . . وقال: ومن آياته أن يرسل الرياح','62','0','0');
INSERT INTO [t8210] VALUES (15,'ذكر الفرق بين الريح والرياح ومن قال: إن الله يرسل الريح للنقمة، والرياح للرحمة، ومن قال: معنى الرياح والريح واحد. قال الله عز وجل: فأرسلنا عليهم ريحا وجنودا لم تروها وقال تعالى: فأرسلنا عليهم ريحا صرصرا الآية وروي عن ابن عباس رضي الله عنهما , أن','66','0','0');
INSERT INTO [t8210] VALUES (16,'ذكر الآيات التي تدل على وحدانيته في خلق الأرض وما فيها قال الله , عز وجل: ومن آياته خلق السموات والأرض وما بث فيهما من دابة وقال تعالى: خلق السموات والأرض بالحق وقال تعالى: ومن آياته أن تقوم السماء والأرض بأمره وقال تعالى: الذي جعل لكم الأرض مهدا','71','0','0');
INSERT INTO [t8210] VALUES (17,'ذكر آية أخرى تدل على وحدانية الله في خلق الجبال وما أخبر عما فيها من المنافع ووصف ألوانها قال الله , عز وجل: مخبرا عن بديع حكمته في خلق الجبال وأنها رواسي وأوتادا والجبال أرساها، ووالجبال أوتادا وقال تعالى: وألقى في الأرض رواسي أن تميد بكم ثم أخبر عن','80','0','0');
INSERT INTO [t8210] VALUES (18,'ذكر آية أخرى تدل على وحدانية الله تعالى من لطيف صنعته في خلق الماء الذي جعله الله , عز وجل , حياة لجميع خلقه. قال الله تعالى: وجعلنا من الماء كل شيء حي وقال تعالى: وأنزلنا من السماء ماء وقال تعالى: أفرأيتم الماء الذي تشربون أأنتم أنزلتموه من المزن أم','84','0','0');
INSERT INTO [t8210] VALUES (19,'ذكر آية أخرى تدل على وحدانية الله تعالى وأنه منزل الماء من المزن وفالق الحب والنوى , ومنبت النبات وألوان الأشجار التي تحمل ألوان الثمار مختلفة الأطعمة والألوان من أزواج شتى من كل زوج بهيج قال الله , عز وجل , مخبرا عن لطيف قدرته وحسن صنعته من خلقه: أخرج','87','0','0');
INSERT INTO [t8210] VALUES (20,'ذكر الآيات الدالة على وحدانية الله , عز وجل , وأنه خالق الخلق ومنشيها من تراب آدم , عليه السلام , ثم من نطفة ولده وخلق منها زوجها حواء قال الله عز وجل منبها عباده على وحدانيته وربوبيته وبديع صنعته لخلقه: ومن آياته أن خلقكم من تراب ثم إذا أنتم بشر','93','0','0');
INSERT INTO [t8210] VALUES (21,'بيان قوله: وخلق منها زوجها','100','0','0');
INSERT INTO [t8210] VALUES (22,'ذكر آية تدل على وحدانية الله , عز وجل , من انتقال الخلق من حال إلى حال فقال الله , عز وجل: ولقد خلقنا الإنسان من سلالة من طين. . . . . . إلى قوله: فتبارك الله أحسن الخالقين وقال , عز وجل: ألم نخلقكم من ماء مهين فجعلناه في قرار مكين وقال: قتل الإنسان','105','0','0');
INSERT INTO [t8210] VALUES (23,'ذكر خلق آدم , عليه السلام , وطوله ووقت خروجه من الجنة','107','0','0');
INSERT INTO [t8210] VALUES (24,'ذكر آية أخرى تدل على وحدانية الله وأنه مخرج النطفة إلى الرحم بنقلهم من حال إلى حال قال الله تعالى: فلينظر الإنسان مم خلق، خلق من ماء دافق، يخرج من بين الصلب والترائب إنه على رجعه لقادر قال ابن عباس: الترائب: أربعة أضلاع من ذا الجانب، وأربعة أضلاع','111','0','0');
INSERT INTO [t8210] VALUES (25,'ذكر آية تدل على وحدانية الخالق وأنه المقر في الأرحام ما يشاء قال الله , عز وجل: ألم نخلقكم من ماء مهين، فجعلناه في قرار مكين الآية وقال ربك عز وجل: وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا وقال: ونقر في الأرحام ما نشاء بيان ذلك من','114','0','0');
INSERT INTO [t8210] VALUES (26,'ذكر آية تدل على وحدانية الخالق وأنه ناقل أحوال النطفة إلى العلقة وإلى المضغة إلى العظام إلى إنشائه بشرا سويا. بيان ذلك من الأثر','119','0','0');
INSERT INTO [t8210] VALUES (27,'ذكر آية تدل على وحدانية الخالق وأنه يخرج من النطفة الميتة بشرا حيا إذا شاء وأن الممني يتمنى الولد فلا يقدر الرب , عز وجل , ويكره ويعزل , فيقدر. قال الله تعالى منبها على قدرته على ذلك: أفرأيتم ما تمنون أأنتم تخلقونه أم نحن الخالقون','125','0','0');
INSERT INTO [t8210] VALUES (28,'ذكر آية تدل على وحدانية الخالق وإحكام صنعته في خلق الرحم والمشيمة في مدة استقرار النطفة فيها إلى التارات التي تمر عليها إلى أن تصير بشرا حيا. قال الله , عز وجل: مخلقة وغير مخلقة لنبين لكم ونقر في الأرحام ما نشاء إلى أجل مسمى وقال: فمستقر ومستودع وقال','129','0','0');
INSERT INTO [t8210] VALUES (29,'ذكر آية تدل على وحدانية الخالق، وأن الأنثى تحمل وتضع بإذنه قال الله تعالى منبها على قدرته وتقديره في أعمار خلقه: وما تحمل من أنثى ولا تضع إلا بعلمه وما يعمر من معمر ولا ينقص من عمره إلا في كتاب. وقال: هو الذي خلقكم من تراب إلى قوله: ثم لتكونوا شيوخا','135','0','0');
INSERT INTO [t8210] VALUES (30,'ذكر آية تدل على وحدانية الخالق بأنه خلق الخلق وجعلهم سميعا وبصيرا يسمعون ويبصرون وهي من الأسماء المستعارة من أسماء الله تعالى لخلقه ليعرفوا نعمة الله تعالى عليهم بذلك، فتسمى بالسميع البصير وسمى عبده سميعا بصيرا. فاتفقت الأسماء واختلفت المعاني إذ لم','142','0','0');
INSERT INTO [t8210] VALUES (31,'ذكر آية تدل على وحدانية الخالق وإحكام صنعته في مصالح خلقه قال الله عز وجل: وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا وقال عز وجل: يا أيها الناس إنا خلقناكم من ذكر وأنثى الآية بيان ذلك من الأثر','145','0','0');
INSERT INTO [t8210] VALUES (32,'ذكر الآيات التي تدل على وحدانية الخالق من تقلب أحوال العبد وأنه المدبر لذلك من حال الصحة والمرض والموت والحياة والنوم والانتباه والفقر والغنى والعجز والقدرة. قال الله تعالى منبها على قدرته عن أحوال العبيد وعجزهم إلا بمعونة الله , عز وجل فقال: ألا له','149','0','0');
INSERT INTO [t8210] VALUES (33,'ذكر آيات تدل على وحدانية الخالق وأنه مقلب القلوب على ما يشاء قال الله عز وجل , مخبرا على قدرته وعلمه بما في قلوب العباد واعلموا أن الله يحول بين المرء وقلبه وقال: ونقلب أفئدتهم وأبصارهم الآية. وقال منبها على دعائه: ربنا لا تزغ قلوبنا بعد إذ هديتنا','156','0','0');
INSERT INTO [t8210] VALUES (34,'ذكر آية تدل على وحدانية الله , عز وجل , وأنه مقلب القلوب يحول بين المرء وقلبه إلى ما يريد من السعادة والشقاء قال الله تعالى: واعلموا أن الله يحول بين المرء وقلبه وقال: ونقلب أفئدتهم وأبصارهم الآية قال ابن عباس: يحول بين المرء وقلبه يحول بين المؤمن','163','0','0');
INSERT INTO [t8210] VALUES (35,'ذكر آية تدل على وحدانية الخالق وأن الأرواح بيده في حال الموت والحياة والنوم والانتباه. قال الله تعالى مخبرا عن قدرته على ذلك: الله يتوفى الأنفس حين موتها والتي لم تمت في منامها فيمسك التي قضى عليها الموت ويرسل الأخرى إلى أجل مسمى بيان ذلك من الأثر','171','0','0');
INSERT INTO [t8210] VALUES (36,'ذكر آية تدل على وحدانية الخالق وأنه الرزاق المغني المفقر قال الله تعالى ذاكرا لنعمه على عباده: الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم وقال: الآية أولم يروا أن الله يبسط الرزق لمن يشاء ويقدر إن في ذلك لآيات لقوم يؤمنون بيان ذلك من الأثر','179','0','0');
INSERT INTO [t8210] VALUES (37,'ذكر آية تدل على وحدانية الخالق وأنه الممرض المداوي الشافي لعباده قال الله , عز وجل , مخبرا عن إيمان نبيه وخليله: وإذا مرضت فهو يشفين والذي يميتني ثم يحيين','185','0','0');
INSERT INTO [t8210] VALUES (38,'ذكر آية تدل على وحدانية الخالق وأنه المبدئ خلقه بلا مثال والمعيد لها بعد فنائها قال الله تعالى مخبرا عن قدرته على إحياء خلقه بعد موتهم وفنائهم وإعادته خلقهم بعد أن يصيروا رميما ورفاتا: ألم يك نطفة من مني يمنى الآية وقال: وضرب لنا مثلا ونسي خلقه وقال ,','188','0','0');
INSERT INTO [t8210] VALUES (39,'ذكر استدلال من لم تبلغه الدعوة ولم يأته رسول قال الله تعالى مخبرا عن إيمان إبراهيم , عليه السلام , بالله , عز وجل قبل الرسالة: إني وجهت وجهي للذي فطر السموات والأرض حنيفا وما أنا من المشركين','192','0','0');
INSERT INTO [t8210] VALUES (40,'ذكر الدليل على أن المجتهد المخطئ في معرفة الله , عز وجل ووحدانيته كالمعاند قال الله تعالى مخبرا عن ضلالتهم ومعاندتهم: قل هل ننبئكم بالأخسرين أعمالا الذين ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعا وقال علي بن أبي طالب , رضي الله عنه لما سئل','194','0','0');
INSERT INTO [t8210] VALUES (41,'ذكر معرفة أسماء الله عز وجل الحسنة التي تسمى بها وأظهرها لعباده للمعرفة والدعاء والذكر قال الله تعالى: ولله الأسماء الحسنى فادعوه بها الآية، وقال: هل تعلم له سميا. قال ابن عباس: " معناه: هل تعلم أحدا يقال له: الله، غيره "، وقال النبي صلى الله','198','0','0');
INSERT INTO [t8210] VALUES (42,'ذكر معرفة اسم الله الأكبر الذي تسمى به وشرفه على الأذكار كلها فقال عز وجل: ولذكر الله أكبر، وقال لنبيه صلى الله عليه وسلم: فاعلم أنه لا إله إلا الله، وقال: ولله الأسماء الحسنى فادعوه بها، وقال: يا أيها الذين آمنوا اذكروا الله ذكرا كثيرا، فاسمه','207','0','0');
INSERT INTO [t8210] VALUES (43,'قول النبي صلى الله عليه وسلم أمرت أن أدعو الناس إلى شهادة أن لا إله إلا الله','210','0','0');
INSERT INTO [t8210] VALUES (44,'قول النبي صلى الله عليه وسلم: " بني الإسلام على شهادة أن لا إله إلا الله "','212','0','0');
INSERT INTO [t8210] VALUES (45,'قول النبي صلى الله عليه وسلم: " من كان يؤمن بالله واليوم الآخر فليقل خيرا أو ليسكت "','214','0','0');
INSERT INTO [t8210] VALUES (46,'قول النبي صلى الله عليه وسلم لرجل: " قل ربي الله ثم استقم "','216','0','0');
INSERT INTO [t8210] VALUES (47,'قول النبي صلى الله عليه وسلم لرجل: " الله يمنعني منك "','218','0','0');
INSERT INTO [t8210] VALUES (48,'قول النبي صلى الله عليه وسلم: " من كان حالفا فليحلف بالله عز وجل ومن حلف بغير الله فقد أشرك "','220','0','0');
INSERT INTO [t8210] VALUES (49,'قول النبي رسول الله صلى الله عليه وسلم: " اذكروا اسم الله على جميع الأمور " قال تعالى: اذكروا الله ذكرا كثيرا','223','0','0');
INSERT INTO [t8210] VALUES (50,'ذكر اسم الله عز وجل على الذبائح وعند الأكل والشرب والوضوء، وقال ابن عباس: " المسلم يكفيه اسمه، فإذا نسي عند الذبح فليسم الله إذا أكل "','228','0','0');
INSERT INTO [t8210] VALUES (51,'قول النبي صلى الله عليه وسلم لأمراء السرايا: " اغزوا بسم الله، قاتلوا من كفر بالله عز وجل "','234','0','0');
INSERT INTO [t8210] VALUES (52,'قول النبي صلى الله عليه وسلم: " إذا قال العبد لا قوة إلا بالله فقال الله تعالى: أسلم عبدي واستسلم "','236','0','0');
INSERT INTO [t8210] VALUES (53,'قول النبي صلى الله عليه وسلم: " بسم الله أرقيك "','238','0','0');
INSERT INTO [t8210] VALUES (54,'قول النبي صلى الله عليه وسلم: " لقنوا أمواتكم لا إله إلا الله " ومن كان آخر كلامه لا إله إلا الله','240','0','0');
INSERT INTO [t8210] VALUES (55,'ومن أسماء الله عز وجل الرحمن الرحيم قال أهل التأويل: هما اسمان رقيقان أحدهما أرق من الآخر، فقوله الرحمن يجمع كل معاني الرحمة من الرأفة والشفقة والحنان واللطف والعطف قال عبد الله بن عباس: قوله عز وجل: هل تعلم له سميا قال: ليس أحد يسمى الرحمن غيره،','248','0','0');
INSERT INTO [t8210] VALUES (56,'ومن أسمائه الرحيم قال أهل التأويل معناه: البالغ في الرحمة أرحم الراحمين، الرفيق الرقيق ويقال إنهما بمعنى رحيم ورحمن وراحم ومثله علام وعليم وعالم، وهو من الأسماء المستعارة لعبيده إذا رحم، اشتق له اسم الرحيم من فعله إذا رحم','251','0','0');
INSERT INTO [t8210] VALUES (57,'الملك والمالك صفة ملكه قال أهل التأويل: اسم الملك يجمع المالك والملك والمليك قال النبي صلى الله عليه وسلم: " لا ملك إلا الله عز وجل " وقد سأل به واستعاذ','256','0','0');
INSERT INTO [t8210] VALUES (58,'ومن أسماء الله عز وجل: الرب، رب كل شيء ومليكه وهو من الأسماء المستعارة لعبده إذا ملك قيل ربه قال الله عز وجل في قصة موسى عليه السلام وفرعون: قال فرعون وما رب العالمين قال رب السموات والأرض وما بينهما إن كنتم موقنين وقال النبي صلى الله عليه وسلم: "','261','0','0');
INSERT INTO [t8210] VALUES (59,'ومن أسماء الله عز وجل: الأحد الصمد قال أهل التأويل: معناه الواحد الأحد الموحد الذي يعبد بتوحيده ويشهد له بالوحدانية','263','0','0');
INSERT INTO [t8210] VALUES (60,'ومن أسماء الله عز وجل: الصمد قال عبد الله بن مسعود: " الصمد الذي قد انتهى سؤدده " وعنه مشهور. وقال أبي بن كعب: " الصمد الذي لا يخرج منه شيء ولم يخرج من شيء الذي لم يلد ولم يولد "، وقال ابن عباس: " الصمد الذي يصمد إليه في الحوائج "، وروي عن ابن','265','0','0');
INSERT INTO [t8210] VALUES (61,'ومن أسماء الله عز وجل: عالم الغيب والشهادة هو الرحمن الرحيم قال أهل التأويل: معنى عالم وعلام وعليم بالخلق وأفعالهم قبل خلقهم، فقال عز وجل عالم الغيب وعلام الغيوب وعليم بذات الصدور ومعنى عالم وعليم ويعلم أي أن له علما والعلم صفة له عز وجل','266','0','0');
INSERT INTO [t8210] VALUES (62,'ومن أسماء الله عز وجل: هو الله الذي لا إله إلا هو الملك القدوس السلام قال أهل التأويل: معنى قوله القدوس: الطهر الطاهر الذي تعالى عن كل دنس','268','0','0');
INSERT INTO [t8210] VALUES (63,'ومن أسماء الله عز وجل: السلام، المؤمن، المهيمن، العزيز، الجبار، المتكبر قال أهل التأويل: معنى المؤمن المصدق الصادقين، دعا خلقه إلى الإيمان به، وقيل: الذي يملك أمان خلقه في الدنيا والآخرة، ويقال: الموحد نفسه يقول شهد الله أنه لا إله إلا هو','272','0','0');
INSERT INTO [t8210] VALUES (64,'ومن أسماء الله عز وجل: العزيز قال أهل التأويل: قوله ولله العزة: وهو رب العزة يعز من يشاء ويذل من يشاء والله العزيز المعز الذي يملك العزة، وهو من الأسماء المعارة لخلقه. قال الله تعالى: قل اللهم مالك الملك تؤتي الملك من تشاء وتنزع الملك ممن تشاء وتعز','277','0','0');
INSERT INTO [t8210] VALUES (65,'ومن أسماء الله عز وجل: الجبار قال أهل التأويل: جبار القلوب على فطرتها شقيها وسعيدها وهو قول علي بن أبي طالب رضي الله عنه وقيل: الجبار المتكبر على خلقه','281','0','0');
INSERT INTO [t8210] VALUES (66,'ومن أسماء الله عز وجل: الخالق، البارئ، المصور قال أهل التأويل: معنى البارئ، هو الخالق الذي خلق النفوس في الأرحام وصورها كما شاء في ظلمات ثلاث، والذارئ مثله، الذي ذرأ الخلق وبرأهم من أمهاتهم، والخالق هو المقدر الفاعل الصانع، وهو البارئ المصور،','284','0','0');
INSERT INTO [t8210] VALUES (67,'ومن أسماء الله عز وجل: المصور قال الله تعالى: هو الذي خلقكم فمنكم كافر ومنكم مؤمن والله بما تعملون بصير خلق السموات والأرض بالحق وصوركم فأحسن صوركم وإليه المصير','288','0','0');
INSERT INTO [t8210] VALUES (68,'ومن أسماء الله عز وجل: الأول والآخر والظاهر والباطن فهي معرفة ذاته قال أهل التأويل: معنى الأول هو الأول بالأولية، وهو خالق أول الأشياء وسماه أول الأشياء، ومعنى الآخر هو الآخر الذي لا يزال آخرا دائما باقيا الوارث لكل شيء بديموميته وبقائه، ومعنى','293','0','0');
INSERT INTO [t8210] VALUES (69,'ومن أسماء الله عز وجل: الأحد الحي القيوم الدائم القائم قال أهل التأويل: معنى الحي حياة لا تشبه حياة الأحياء لا يستدرك بالعقول، ولا تأخذه سنة ولا نوم ولا موت، حييت به القلوب من الكفر والجهل، وهو من الأسماء المستعارة للعبد، تزول عنه بالموت ومعنى','296','0','0');
INSERT INTO [t8210] VALUES (70,'ومن أسماء الله عز وجل: الباعث الباقي','300','0','0');
INSERT INTO [t8210] VALUES (71,'ومن أسماء الله عز وجل: البديع البصير قال الله عز وجل: بديع السموات والأرض','305','0','0');
INSERT INTO [t8210] VALUES (72,'ومن أسماء الله عز وجل: البار قول الله عز وجل: هو البر الرحيم قال الحسن: " بار بعباده، محسن إليهم، معناه لا ينقطع بره وإحسانه "','308','0','0');
INSERT INTO [t8210] VALUES (73,'ومن أسماء الله عز وجل: الباسط، صفة له قول الله عز وجل: بل يداه مبسوطتان، وقال عز وجل: الله يقبض ويبسط، وقال ولو بسط الله الرزق لعباده، وقال: يبسط الرزق لمن يشاء من عباده','311','0','0');
INSERT INTO [t8210] VALUES (74,'ومن أسماء الله عز وجل: التواب الرحيم قال الله عز وجل: هو التواب الرحيم، وقال: وهو الذي يقبل التوبة عن عباده','314','0','0');
INSERT INTO [t8210] VALUES (75,'ومن أسماء الله عز وجل: الجواد الجميل والجليل والجامع والجبار ذكر عن أبي ذر، عن النبي صلى الله عليه وسلم قال: " إن الله يقول: إني جواد ماجد واجد " , وروي عن أنس أن النبي صلى الله عليه وسلم قال: " إن الله أجود الأجودين "','320','0','0');
INSERT INTO [t8210] VALUES (76,'ومن أسماء الله عز وجل: الحق قال الله عز وجل: وأن الله هو الحق المبين وقال الله عز وجل: فالحق والحق أقول وقوله الحق. وقال: ليحق الحق ويبطل الباطل','327','0','0');
INSERT INTO [t8210] VALUES (77,'ومن أسماء الله عز وجل: الحليم قال الله عز وجل في سورة البقرة: ولكن يؤاخذكم بما كسبت قلوبكم والله غفور حليم واعلموا أن الله غفور حليم روي عن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله عز وجل الحليم','329','0','0');
INSERT INTO [t8210] VALUES (78,'ومن أسماء الله عز وجل: الحافظ والحفيظ في سورة البقرة وسورة هود قاله ابن عيينة. وفي حديث أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله عز وجل: الحفيظ والحافظ','331','0','0');
INSERT INTO [t8210] VALUES (79,'ومن أسماء الله عز وجل: الحميد قال أهل التأويل: الحميد اسم الفردانية، لا يحمد ولا يشكر غيره','333','0','0');
INSERT INTO [t8210] VALUES (80,'ومن أسماء الله عز وجل: الحي المحيي الحسيب الحكم قال الله عز وجل: وكفى بالله حسيبا كفى بنا حاسبين وقال النبي صلى الله عليه وسلم: " إن الله حي كريم " وقال: " استحيوا من الله " وقال لرجل من أصحابه: " ما اسمك؟ " فقال: أبو الحكم. فقال: " إن الله هو','337','0','0');
INSERT INTO [t8210] VALUES (81,'ومن أسماء الله عز وجل: الخالق والخلاق قال الله عز وجل: هل من خالق غير الله وقال عز وجل: وهو الخلاق العليم. وفيما روي عن أبي هريرة من أسماء الله الخالق','338','0','0');
INSERT INTO [t8210] VALUES (82,'ومن أسماء الله عز وجل: الخبير قال الله عز وجل: والله خبير في آل عمران','344','0','0');
INSERT INTO [t8210] VALUES (83,'ومن أسماء الله عز وجل: الدائم والدافع والديان قال عمر رضي الله عنه: " ويل لديان الأرض من ديان السماء "، وفيما روي عن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله الدائم والدافع','346','0','0');
INSERT INTO [t8210] VALUES (84,'ومن أسماء الله عز وجل: ذو الجلال والإكرام قاله الله عز وجل في سورة الرحمن','349','0','0');
INSERT INTO [t8210] VALUES (85,'ومن أسماء الله عز وجل: الرءوف الرحيم قال الله عز وجل: في سورة البقرة إن الله بالناس لرءوف رحيم وروي عن أبي هريرة عن النبي صلى الله عليه وسلم في أسماء الله عز وجل الرءوف','353','0','0');
INSERT INTO [t8210] VALUES (86,'ومن أسماء الله عز وجل: الرقيب قال الله عز وجل في سورة النساء إن الله كان عليكم رقيبا وروي عن أبي هريرة عن النبي صلى الله عليه وسلم في أسماء الله عز وجل الرقيب','356','0','0');
INSERT INTO [t8210] VALUES (87,'ومن أسماء الله عز وجل: الرازق والرزاق قال الله عز وجل: إن الله هو الرزاق ذو القوة المتين','358','0','0');
INSERT INTO [t8210] VALUES (88,'ومن أسماء الله عز وجل: الرافع والرفيق والرشيد وروي عن أبي هريرة عن النبي صلى الله عليه وسلم في أسماء الله عز وجل الرافع الرشيد','364','0','0');
INSERT INTO [t8210] VALUES (89,'ومن أسماء الله عز وجل: السيد السلام السميع قال الله عز وجل: أم يحسبون أنا لا نسمع سرهم وقال النبي صلى الله عليه وسلم: " سيد بنى دارا " في حديث أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله عز وجل السيد','371','0','0');
INSERT INTO [t8210] VALUES (90,'ومن أسماء الله عز وجل: السبوح السريع الستار','378','0','0');
INSERT INTO [t8210] VALUES (91,'ومن أسماء الله عز وجل: الشافي الشديد ذكره في سورة الشعراء: وإذا مرضت فهو يشفين وقال عز وجل: وهو شديد المحال وقال النبي صلى الله عليه وسلم: " لا شافي إلا أنت " وفي دعاء النبي صلى الله عليه وسلم " يا ذا الحبل الشديد "','383','0','0');
INSERT INTO [t8210] VALUES (92,'ومن أسماء الله عز وجل: الشهيد والشاهد والشكور والشاكر ذكر الشهيد في آل عمران، وذكر الشاكر في البقرة، روي عن أبي هريرة عن النبي صلى الله عليه وسلم في أسماء الله الشهيد والشكور والشاكر','386','0','0');
INSERT INTO [t8210] VALUES (93,'ومن أسماء الله عز وجل: الصمد والصادق والصاحب والصبور عن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الصادق والصمد والصبور، فاسمه الصادق في سورة مريم وقال ابن عباس: " قوله كهيعص الصاد الصادق "','387','0','0');
INSERT INTO [t8210] VALUES (94,'الصادق والصانع','389','0','0');
INSERT INTO [t8210] VALUES (95,'الصاحب','392','0','0');
INSERT INTO [t8210] VALUES (96,'ومن أسماء الله عز وجل: الطيب والطهر والظاهر فاسمه الظاهر في حديث أبي هريرة في أسماء الله عز وجل','394','0','0');
INSERT INTO [t8210] VALUES (97,'ومن أسماء الله عز وجل: العلي الأعلى العظيم وذكرهما في سورة البقرة، وعن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله العلي العظيم قال أهل المعرفة بالتأويل معنى العلي: تعالى على الخلق وهو أعلى من كل شيء وتعالى في كل شيء، فلا شيء أعلى منه،','396','0','0');
INSERT INTO [t8210] VALUES (98,'ومن أسماء الله عز وجل: العزيز والعدل روي عن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله عز وجل، وعن عبد الله بن مسعود، أن النبي صلى الله عليه وسلم قال في دعائه: " اللهم أنت العدل في قضائك "','400','0','0');
INSERT INTO [t8210] VALUES (99,'ومن أسماء الله عز وجل: العالم العليم العلام وقال الله عز وجل: عالم الغيب فلا يظهر على غيبه أحدا إلا من ارتضى من رسول وقال عز وجل: إن الله عليم خبير وقال: عليم بذات الصدور وقال عز وجل: علام الغيوب وقال عبد الله بن عباس: " قوله عز وجل: وفوق كل ذي','402','0','0');
INSERT INTO [t8210] VALUES (100,'ومن أسماء الله عز وجل: العفو قال الله عز وجل: إن الله لعفو غفور، وروي عن أبي هريرة، عن النبي صلى الله عليه وسلم، أنه ذكر في أسماء الله والله يعصمك من الناس','405','0','0');
INSERT INTO [t8210] VALUES (101,'ومن أسماء الله عز وجل: الغفور والغافر والغفار قال الله عز وجل: غافر الذنب، وقال: وإني لغفار لمن تاب وآمن. قال أهل التأويل: معناها واحد، وهو الستار والعفو والتغطية على الشيء، ومنه المغفر','408','0','0');
INSERT INTO [t8210] VALUES (102,'ومن أسماء الله عز وجل: الغني قال الله عز وجل: هو الغني وروي عن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله الغني','411','0','0');
INSERT INTO [t8210] VALUES (103,'ومن أسماء الله عز وجل: الفاتح والفتاح قال الله عز وجل: وهو الفتاح العليم , وعنده مفاتح الغيب وعن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله الفتاح','412','0','0');
INSERT INTO [t8210] VALUES (104,'ومن أسماء الله عز وجل: فاطر قال الله تعالى: فاطر السموات وكان رسول الله صلى الله عليه وسلم يسأل بهذا الاسم','413','0','0');
INSERT INTO [t8210] VALUES (105,'ومن أسماء الله عز وجل: القدير والقادر والمقتدر قال الله عز وجل: قل هو القادر على أن يبعث عليكم عذابا من فوقكم أو من تحت أرجلكم أو يلبسكم شيعا وقال: وهو على كل شيء قدير وقال: وكان الله على كل شيء مقتدرا قال أهل التأويل: معنى القدير يقدر على كل شيء من','416','0','0');
INSERT INTO [t8210] VALUES (106,'ومن أسماء الله عز وجل: القيوم والقيام والقائم قال الله عز وجل: الحي القيوم وروي عن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله القيوم','419','0','0');
INSERT INTO [t8210] VALUES (107,'ومن أسماء الله عز وجل: القهار والقاهر والقدوس قال الله عز وجل: الواحد القهار وقال: وهو القاهر فوق عباده قال أهل التأويل: القاهر بمعنى القهار فوق خلقه قهرهم بقدرته عليهم، وفي حديث أبي هريرة أن النبي صلى الله عليه وسلم ذكر في أسماء الله القهار والقدوس','422','0','0');
INSERT INTO [t8210] VALUES (108,'ومن أسماء الله عز وجل: القريب والقوي القابض والقديم القاضي قال الله عز وجل: قريب مجيب وقال: وهو القوي وقال: والله يقبض ويبسط , يقضي بالحق وفي حديث أبي هريرة أن النبي صلى الله عليه وسلم ذكر في أسماء الله القوي والقابض والقريب والقديم','424','0','0');
INSERT INTO [t8210] VALUES (109,'ومن أسماء الله عز وجل: الكبير والكريم والكافي والكفيل قال الله عز وجل في سورة سبأ: وهو العلي الكبير وذكر في المؤمن: الكريم قال ابن عباس: " كهيعص كاف كافي "، وعن أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله الكبير والكريم والكافي','426','0','0');
INSERT INTO [t8210] VALUES (110,'ومن أسماء الله عز وجل: اللطيف قال الله عز وجل: وهو اللطيف الخبير وفي حديث أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله عز وجل اللطيف','431','0','0');
INSERT INTO [t8210] VALUES (111,'ومن أسماء الله عز وجل: المجيد الماجد المتكبر المصور المعز المذل قال الله عز وجل: إنه حميد مجيد وذكر النبي صلى الله عليه وسلم في التشهد وفي حديث أبي هريرة، عن النبي صلى الله عليه وسلم: " المؤمن المهيمن المتكبر المصور المعز المذل المغيث المجيب المحيط','433','0','0');
INSERT INTO [t8210] VALUES (112,'المعز','436','0','0');
INSERT INTO [t8210] VALUES (113,'ومن أسماء الله عز وجل: المقدر','440','0','0');
INSERT INTO [t8210] VALUES (114,'ومن أسماء الله عز وجل: المعطي المانع','445','0','0');
INSERT INTO [t8210] VALUES (115,'ومن أسماء الله عز وجل: المعين','448','0','0');
INSERT INTO [t8210] VALUES (116,'ومن أسماء الله عز وجل: المنان والمبين المفضل الموسع المنعم المفرج وفي رواية ابن سيرين، عن أبي هريرة، عن النبي صلى الله عليه وسلم: المنان والمبين','452','0','0');
INSERT INTO [t8210] VALUES (117,'الموسع','456','0','0');
INSERT INTO [t8210] VALUES (118,'المنان','459','0','0');
INSERT INTO [t8210] VALUES (119,'ومن أسماء الله عز وجل: المقسط المعافي المطعم','461','0','0');
INSERT INTO [t8210] VALUES (120,'ومن أسماء الله عز وجل: النور الناصر والنذير قال الله عز وجل: الله نور السماوات والأرض وقال: نعم المولى ونعم النصير في الأنفال، وفي حديث أبي هريرة، عن النبي صلى الله عليه وسلم أنه ذكر أسماء الله وفيه النور والنافع. ومن حديث ابن سيرين عنه ذكر فيه','468','0','0');
INSERT INTO [t8210] VALUES (121,'ومن أسماء الله عز وجل: الواحد، الوتر، الوهاب، الودود، الولي، الوفي وفي حديث أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله الواحد، الوتر، الوهاب، الودود، الواحد، الوكيل، الوارث، الوفي قال أهل المعرفة بالتأويل: معنى الودود: الحب','472','0','0');
INSERT INTO [t8210] VALUES (122,'ومن أسماء الله عز وجل: الهادي قال الله عز وجل: ولو شاء لهداكم أجمعين وفي حديث أبي هريرة، عن النبي صلى الله عليه وسلم في أسماء الله: الهادي','475','0','0');
INSERT INTO [t8210] VALUES (123,'ومن أسماء الله عز وجل: المضافة إلى صفاته وأفعاله قوله عز وجل: ذو الجلال والإكرام قال ابن عباس: " ذو الكبرياء والعظمة ". وقال النبي صلى الله عليه وسلم: " قال الله: الكبرياء ردائي والعظمة إزاري "','481','0','0');
INSERT INTO [t8210] VALUES (124,'ذو الفضل العظيم','485','0','0');
INSERT INTO [t8210] VALUES (125,'ذو القوة المتين، ذو العرش المجيد، ذو الطول والإحسان، ذو الرحمة الواسعة، ذو الجبروت والملكوت، فاطر السماوات والأرض، فالق الحب والنوى، منزل الكتاب، سريع الحساب، علام الغيوب، غافر الذنب، وقابل التوب، فارج الهم، كاشف الكرب، مقلب القلوب','487','0','0');
INSERT INTO [t8210] VALUES (126,'منزل الكتاب سريع الحساب','489','0','0');
INSERT INTO [t8210] VALUES (127,'رب العرش العظيم، رب العرش الكريم، رب السماوات السبع، خير الراحمين، أرحم الراحمين، خير الفاتحين، خير الناصرين، خير الوارثين، خير الفاصلين، خير المنزلين، أحكم الحاكمين، أحسن الخالقين، ولي المؤمنين','491','0','0');
