INSERT INTO [t8216] VALUES (1,'للدنيا أهون على الله من هذه على صاحبها، ولو كانت الدنيا تعدل عند الله جناح بعوضة ما سقى','1','0','0');
INSERT INTO [t8216] VALUES (2,'للدنيا أهون على الله تعالى من هذه على أهلها','2','0','0');
INSERT INTO [t8216] VALUES (3,'للدنيا أهون على الله عز وجل من هذه الشاة على أهلها','3','0','0');
INSERT INTO [t8216] VALUES (4,'الدنيا سجن المؤمن وجنة الكافر','4','0','0');
INSERT INTO [t8216] VALUES (5,'الدنيا سجن المؤمن وجنة الكافر','5','0','0');
INSERT INTO [t8216] VALUES (6,'يجاء بالدنيا يوم القيامة فيقال: ميزوا ما كان منها لله عز وجل، وألقوا سائرها في','6','0','0');
INSERT INTO [t8216] VALUES (7,'الدنيا ملعونة، وملعون ما فيها إلا ما كان منها لله عز وجل','7','0','0');
INSERT INTO [t8216] VALUES (8,'من أحب دنياه أضر بآخرته، ومن أحب آخرته أضر بدنياه، فآثروا ما يبقى على ما','8','0','0');
INSERT INTO [t8216] VALUES (9,'حب الدنيا رأس كل خطيئة','9','0','0');
INSERT INTO [t8216] VALUES (10,'لما بعث محمد صلى الله عليه وسلم أتت إبليس جنوده فقالوا: قد بعث نبي وأخرجت أمته قال: يحبون','10','0','0');
INSERT INTO [t8216] VALUES (11,'هذه الدنيا مثلت لي فقلت لها: إليك عني، ثم رجعت فقالت: إنك إن أفلت مني فلن يفلت مني من','11','0','0');
INSERT INTO [t8216] VALUES (12,'ما الدنيا في الآخرة إلا مثل ما يجعل أحدكم إصبعه في اليم، فلينظر ما يرجع','12','0','0');
INSERT INTO [t8216] VALUES (13,'ما الدنيا في الآخرة إلا كنفجة أرنب','13','0','0');
INSERT INTO [t8216] VALUES (14,'يا عجبا كل العجب للمصدق بدار الخلود وهو يسعى لدار الغرور','14','0','0');
INSERT INTO [t8216] VALUES (15,'لو كانت الدنيا تزن عند الله جناح بعوضة ما سقى فرعون منها شربة ماء','15','0','0');
INSERT INTO [t8216] VALUES (16,'الدنيا دار من لا دار له، ومال من لا مال له، ولها يجمع من لا عقل له','16','0','0');
INSERT INTO [t8216] VALUES (17,'حلالها حساب، وحرامها النار','17','0','0');
INSERT INTO [t8216] VALUES (18,'من صح فيها أمن، ومن سقم فيها ندم، ومن افتقر فيها حزن، ومن استغنى فيها فتن، في حلالها','18','0','0');
INSERT INTO [t8216] VALUES (19,'هذه الدنيا','19','0','0');
INSERT INTO [t8216] VALUES (20,'الدنيا حلوة خضرة، وإن الله مستخلفكم فيها فناظر كيف تعملون، إن بني إسرائيل لما بسطت لهم','20','0','0');
INSERT INTO [t8216] VALUES (21,'ما شبهت الدنيا إلا كرجل نام فرأى في منامه ما يكره وما يحب، فبينما هو كذلك إذ','21','0','0');
INSERT INTO [t8216] VALUES (22,'أحلام النائم','22','0','0');
INSERT INTO [t8216] VALUES (23,'أحلام نوم أو كظل زائل ... إن اللبيب بمثلها لا يخدع','23','0','0');
INSERT INTO [t8216] VALUES (24,'يا أهل لذات دنيا لا بقاء لها ... إن اغترارا بظل زائل حمق','24','0','0');
INSERT INTO [t8216] VALUES (25,'ألا إنما الدنيا كظل بنيته ... ولا بد يوما أن ظلك زائل','25','0','0');
INSERT INTO [t8216] VALUES (26,'وإن امرءا دنياه أكبر همه ... لمستمسك منها بحبل غرور','26','0','0');
INSERT INTO [t8216] VALUES (27,'بؤسا لأزواجك الباقين كيف لا يعتبرون بأزواجك الماضين؟ كيف تهلكينهم واحدا واحدا ولا يكونون','27','0','0');
INSERT INTO [t8216] VALUES (28,'أعوذ بالله من شرك. قالت: فإن أحببت أن تعاذ من شري فابغض الدرهم','28','0','0');
INSERT INTO [t8216] VALUES (29,'رأيت الدنيا - يعني في النوم - عجوزا مشوهة حدباء','29','0','0');
INSERT INTO [t8216] VALUES (30,'رأيت في النوم عجوزا شمطاء مشوهة تصفق بيديها، وخلفها خلق يتبعونها ويصفقون ويرقصون، فلما','30','0','0');
INSERT INTO [t8216] VALUES (31,'لا تتخذوا الدنيا ربا فتتخذكم الدنيا عبيدا، اكنزوا كنزكم عند من لا يضيعه؛ فإن صاحب كنز','31','0','0');
INSERT INTO [t8216] VALUES (32,'أكببت لكم الدنيا على وجهها، فلا تنعشوها بعدي؛ فإن من خبث الدنيا أن الله عز وجل عصي فيها،','32','0','0');
INSERT INTO [t8216] VALUES (33,'كببت لكم الدنيا فلا تنعشوها بعدي؛ فإنه لا خير في دار عصي الله عز وجل فيها، ولا خير في دار','33','0','0');
INSERT INTO [t8216] VALUES (34,'بطحت لكم الدنيا وجلستم على ظهرها، فلا ينازعكم فيها إلا الملوك والنساء، فأما الملوك فلا','34','0','0');
INSERT INTO [t8216] VALUES (35,'ما سكنت الدنيا في قلب عبد إلا التاط قلبه بثلاث: شغل لا ينفك عناؤه، وفقر لا يدرك غناه،','35','0','0');
INSERT INTO [t8216] VALUES (36,'أربع من أعلام الشقاء: قسوة القلب، وجمود العين، وطول الأمل، والحرص على','36','0','0');
INSERT INTO [t8216] VALUES (37,'كيف بدنيا تقطع رقابكم؟ فمن جعل الله عز وجل غناه في قلبه فقد أفلح، ومن لا فليس بنافعته','37','0','0');
INSERT INTO [t8216] VALUES (38,'إذا أحب الله عز وجل عبدا حماه الدنيا كما يحمي أحدكم مريضه الماء','38','0','0');
INSERT INTO [t8216] VALUES (39,'اتقوا السحارة، اتقوا السحارة؛ فإنها تسحر قلوب العلماء، يعني الدنيا','39','0','0');
INSERT INTO [t8216] VALUES (40,'إن الله جل ثناؤه لم يخلق خلقا هو أبغض إليه من الدنيا، وإنه منذ خلقها لم ينظر','40','0','0');
INSERT INTO [t8216] VALUES (41,'ضربت على بني قبة الشيطان، اكشطوها بذكر الله عز وجل','41','0','0');
INSERT INTO [t8216] VALUES (42,'ما أحد من الناس بسط له الدنيا فلم يخف أن يكون قد مكر به فيها، إلا كان قد نقص عقله وعجز','42','0','0');
INSERT INTO [t8216] VALUES (43,'فلما نسوا ما ذكروا به} [الأعراف: ١٦٥] إلى قوله {والحمد لله رب العالمين} [الأنعام: ٤٥]','43','0','0');
INSERT INTO [t8216] VALUES (44,'لكفى به ذنبا أن الله عز وجل يزهدنا في الدنيا ونحن نرغب فيها، فزاهدكم راغب، ومجتهدكم مقصر،','44','0','0');
INSERT INTO [t8216] VALUES (45,'يا ابن داود، لقد آتاك الله ملكا عظيما. قال: فسمع سليمان كلمته، فقال: لتسبيحة في صحيفة','45','0','0');
INSERT INTO [t8216] VALUES (46,'من أصبح وأكبر همه الدنيا فليس من الله عز وجل','46','0','0');
INSERT INTO [t8216] VALUES (47,'أحق الناس بذم الدنيا وقلاها من بسط له فيها وأعطي حاجته منها؛ لأنه يتوقع آفة تعدو على ماله','47','0','0');
INSERT INTO [t8216] VALUES (48,'يحسب الجاهل الشيء الذي هو لا شيء شيئا، والشيء الذي هو الشيء لا شيء، ومن لا يترك الشيء','48','0','0');
INSERT INTO [t8216] VALUES (49,'خلق الله عز وجل الداء والدواء، فالداء الدنيا، والدواء تركها','49','0','0');
INSERT INTO [t8216] VALUES (50,'الدنيا دار ظعن وليست بدار إقامة، وإنما أنزل آدم عليه السلام إليها عقوبة، فاحذرها يا أمير','50','0','0');
INSERT INTO [t8216] VALUES (51,'حب الدنيا أصل كل خطيئة، والمال فيها داء كبير قالوا: وما داؤه؟ قال: لا يسلم من الفخر','51','0','0');
INSERT INTO [t8216] VALUES (52,'الوضاء الحسنة وجوههم، المعجبون بشبابهم؟ أين الملوك الذين بنوا المدائن وحصنوها بالحيطان؟','52','0','0');
INSERT INTO [t8216] VALUES (53,'ما من صباح ولا مساء إلا ومناد ينادي: يا أيها الناس الرحيل الرحيل، وإن تصديق ذلك في كتاب','53','0','0');
INSERT INTO [t8216] VALUES (54,'أبادر ملك الموت رحمك الله قال: فقمت عنه، وقام إلى صلاته','54','0','0');
INSERT INTO [t8216] VALUES (55,'أبادر خروج نفسي','55','0','0');
INSERT INTO [t8216] VALUES (56,'قدم صالح الأعمال، ودع عنك كثرة الأشغال، بادر قبل نزول ما تحاذر، ولا تهتم بأرزاق من تخلف،','56','0','0');
INSERT INTO [t8216] VALUES (57,'التؤدة في كل شيء خير إلا في أمر الآخرة','57','0','0');
INSERT INTO [t8216] VALUES (58,'يتوسد المؤمن ما قدم من عمله في قبره، إن خيرا فخير، وإن شرا فشر، فاغتنموا المبادرة رحمكم','58','0','0');
INSERT INTO [t8216] VALUES (59,'رحم الله امرأ عمل لمثل هذا اليوم، إنكم اليوم تقدرون على ما لا يقدر عليه إخوانكم هؤلاء من','59','0','0');
INSERT INTO [t8216] VALUES (60,'لا تقعدوا فراغا، فإن الموت يطلبكم','60','0','0');
INSERT INTO [t8216] VALUES (61,'أبادر طي الصحيفة','61','0','0');
INSERT INTO [t8216] VALUES (62,'زهرة الحياة الدنيا وزينة المترفين، فلو شئت أن أزينكما بزينة من الدنيا يعرف فرعون حين','62','0','0');
INSERT INTO [t8216] VALUES (63,'أنزلني من نفسك كهمك، واجعلني ذخرا لك في معادك، وتقرب إلي بالنوافل أدنك، وتوكل علي أكفك،','63','0','0');
INSERT INTO [t8216] VALUES (64,'مثل الدنيا والآخرة كمثل رجل له ضرتان، إن أرضى إحداهما أسخط الأخرى','64','0','0');
INSERT INTO [t8216] VALUES (65,'الدنيا والآخرة يجتمعان في قلب العبد، فأيهما غلب كان الآخر تبعا له','65','0','0');
INSERT INTO [t8216] VALUES (66,'إذا كانت الآخرة في القلب جاءت الدنيا تزحمها، وإذا كانت الدنيا في القلب لم تزحمها الآخرة؛','66','0','0');
INSERT INTO [t8216] VALUES (67,'بقدر ما تحزن للدنيا فكذلك يخرج هم الآخرة من قلبك، وبقدر ما تحزن للآخرة فكذلك يخرج هم','67','0','0');
INSERT INTO [t8216] VALUES (68,'يؤتى بالدنيا يوم القيامة في صورة عجوز شمطاء زرقاء، أنيابها بادية مشوه خلقها، فتشرف على','68','0','0');
INSERT INTO [t8216] VALUES (69,'لا يعيذك الله مني حتى تبغض الدرهم. قال: قلت: من أنت؟ قالت: أما تعرفني؟ قلت لا. قالت: أنا','69','0','0');
INSERT INTO [t8216] VALUES (70,'يجاء بالدنيا يوم القيامة تتبختر في زينتها ونضرتها، فتقول، يا رب اجعلني لأخس عبادك دارا،','70','0','0');
INSERT INTO [t8216] VALUES (71,'ما الدنيا؟ إن كنت لبائعها في بعض الحالات كلها بشربة على الظمأ','71','0','0');
INSERT INTO [t8216] VALUES (72,'اجعل الدنيا دارا تبلغك لأثقالك، واجعل نزولك فيها استراحتك، لا تحبسك كالهارب من عدوه،','72','0','0');
INSERT INTO [t8216] VALUES (73,'لا تطلبوا الدنيا بهلكة أنفسكم، واطلبوا الدنيا بترك ما فيها، عراة دخلتموها، وعراة تخرجون','73','0','0');
INSERT INTO [t8216] VALUES (74,'تكفينا خلقان من كان قبلنا','74','0','0');
INSERT INTO [t8216] VALUES (75,'أنا أكرم على الله عز وجل من أن يجعل لي شيئا يشغلني به','75','0','0');
INSERT INTO [t8216] VALUES (76,'الزهد في الدنيا يريح القلب والبدن، والرغبة في الدنيا تطيل الهم والحزن','76','0','0');
INSERT INTO [t8216] VALUES (77,'احذروا الدنيا؛ فإنها أسحر من هاروت وماروت','77','0','0');
INSERT INTO [t8216] VALUES (78,'مثلي ومثل الدنيا كمثل راكب قال في ظل شجرة في يوم صائف، ثم راح وتركها','78','0','0');
INSERT INTO [t8216] VALUES (79,'ما مثلي ومثل الدنيا إلا كراكب سار في يوم صائف، فاستظل تحت شجرة ساعة من نهار، ثم راح','79','0','0');
INSERT INTO [t8216] VALUES (80,'لهم الدنيا ولنا الآخرة؟ قال بلى. قال: فهو إن شاء الله كذلك','80','0','0');
INSERT INTO [t8216] VALUES (81,'ما رأيت شيئا يرد البصر إلا أهبة ثلاثا، فقلت: يا رسول الله، ادع الله أن يوسع عليك، فقد وسع','81','0','0');
INSERT INTO [t8216] VALUES (82,'كانت الدنيا أهون عليهم من التراب الذي تمشون عليه، وما يبالون، أشرقت الدنيا أم غربت، أذهبت','82','0','0');
INSERT INTO [t8216] VALUES (83,'لو كانت الدنيا كلها له ما كان له منها إلا الكفاف، ويقدم ذلك ليوم فقره','83','0','0');
INSERT INTO [t8216] VALUES (84,'المؤمن في الدنيا كالغريب لا ينافس في عزها، ولا يجزع من ذلها، للناس حال - أظنه قال: وله','84','0','0');
INSERT INTO [t8216] VALUES (85,'ليس لابن آدم حق فيما سوى هذه الخصال: بيت يستره، وثوب يواري عورته غليظ، وجلف من الخبز','85','0','0');
INSERT INTO [t8216] VALUES (86,'الزهد في الدنيا يريح القلب والبدن، وإن الرغبة في الدنيا تكثر الهم','86','0','0');
INSERT INTO [t8216] VALUES (87,'طوبى للزاهدين في الدنيا، والراغبين في الآخرة، أولئك قوم اتخذوا أرض الله بساطا، وترابها','87','0','0');
INSERT INTO [t8216] VALUES (88,'كان يأكل الشجر، ويلبس الشعر، ويأكل ما وجد، ولا يسأل عما فقد، ليس له ولد يموت، ولا بيت','88','0','0');
INSERT INTO [t8216] VALUES (89,'تابعنا الأعمال ولم نجد شيئا أبلغ في طلب الآخرة من الزهد في الدنيا','89','0','0');
INSERT INTO [t8216] VALUES (90,'الزهد في الدنيا','90','0','0');
INSERT INTO [t8216] VALUES (91,'الزهد في الدنيا: من لم يغلب الحرام صبره، ولم يستقل الحلال شكره','91','0','0');
INSERT INTO [t8216] VALUES (92,'من زهد في الدنيا هانت عليه المصيبات، ومن ارتقب الموت سارع في الخيرات','92','0','0');
INSERT INTO [t8216] VALUES (93,'من لم يغلب الحرام صبره، ولم يمنع الحلال شكره، قال: معناه: من ترك الحرام، وشكر','93','0','0');
INSERT INTO [t8216] VALUES (94,'عهد إلينا أن يكون بلغة أحدنا من الدنيا كزاد الراكب فلما مات نظر فيما ترك، فإذا قيمته','94','0','0');
INSERT INTO [t8216] VALUES (95,'إن أردت اللحوق بي فليكفك من الدنيا كزاد الراكب، ولا تستخلقي ثوبا حتى ترقعيه، وإياك','95','0','0');
INSERT INTO [t8216] VALUES (96,'إن فتحت عليكم الدنيا فلا تأخذن منها إلا بلاغا، واعلم أن من صلى صلاة الصبح فهو في ذمة الله','96','0','0');
INSERT INTO [t8216] VALUES (97,'أقربكم مني مجلسا يوم القيامة من خرج من الدنيا بهيئة ما تركته فيها، وإنه والله ما منكم من','97','0','0');
INSERT INTO [t8216] VALUES (98,'ما أحد أغنى بالله منا، ما لنا إلا ظل نتوارى به، وثلة من غنم تروح علينا، ومولاة لنا تصدقت','98','0','0');
INSERT INTO [t8216] VALUES (99,'عتيق إلى الحول','99','0','0');
INSERT INTO [t8216] VALUES (100,'أزهد الناس؟ قال: من لم ينس القبر والبلى، وترك أفضل زينة الدنيا، وآثر ما يبقى على ما يفنى،','100','0','0');
INSERT INTO [t8216] VALUES (101,'أزهدكم في الدنيا وأرغبكم في الآخرة','101','0','0');
INSERT INTO [t8216] VALUES (102,'من زهد في الدنيا أسكن الله الحكمة قلبه، وأطلق بها لسانه، وبصره عيوب الدنيا داءها ودواءها،','102','0','0');
INSERT INTO [t8216] VALUES (103,'أفضل الزهد إخفاء الزهد','103','0','0');
INSERT INTO [t8216] VALUES (104,'أعون الأخلاق على الدين الزهادة في الدنيا، وأوشكها ردى اتباع الهوى، ومن اتباع الهوى الرغبة','104','0','0');
INSERT INTO [t8216] VALUES (105,'من رغب في الدنيا وطال أمله فيها أعمى الله قلبه على قدر ذلك، ومن زهد في الدنيا وقصر أمله','105','0','0');
INSERT INTO [t8216] VALUES (106,'لو أن الدنيا بحذافيرها عرضت علي حلالا لا أحاسب بها في الآخرة، لكنت أقذرها كما يقذر أحدكم','106','0','0');
INSERT INTO [t8216] VALUES (107,'ليس الزهادة في الدنيا بتحريم الحلال، ولا بإضاعة المال، ولكن الزهادة في الدنيا أن تكون بما','107','0','0');
INSERT INTO [t8216] VALUES (108,'الزهد في الدنيا أن لا تأسى على ما فات منها، ولا تفرح بما أتاك منها','108','0','0');
INSERT INTO [t8216] VALUES (109,'الزهد في الدنيا قصر الأمل، ليس بأكل الغليظ ولا لبس العباء','109','0','0');
INSERT INTO [t8216] VALUES (110,'أقضى بهم الزهد؟ قال: إلى الأنس به','110','0','0');
INSERT INTO [t8216] VALUES (111,'جعلت لكل شيء مأوى ولم تجعل لي مأوى، فأجابه الجليل تعالى: مأواك عندي في مستقر من رحمتي،','111','0','0');
INSERT INTO [t8216] VALUES (112,'يولم عيسى ويحيى عليهما السلام في الجنة ثلاثمائة سنة، ويدعى في وليمتهما','112','0','0');
INSERT INTO [t8216] VALUES (113,'كانت الدنيا قبل أن أكون فيها، وهي كائنة بعدي، وإنما لي فيها أيام معدودة، فإذا لم أسعد في','113','0','0');
INSERT INTO [t8216] VALUES (114,'أقامني الذي لم يرد أن أصيب من الدنيا شيئا','114','0','0');
INSERT INTO [t8216] VALUES (115,'ما كنت أظن الناس يركبون الشيطان قبل هذا، فأتي بجمله فركبه','115','0','0');
INSERT INTO [t8216] VALUES (116,'هذا سيبلغنا المقيل','116','0','0');
INSERT INTO [t8216] VALUES (117,'كنتم أذل الناس، وأحقر الناس، فأعزكم الله بالدين، مهما تطلبون العز بغيره أذلكم الله عز','117','0','0');
INSERT INTO [t8216] VALUES (118,'العمل الذي يحبك الله عز وجل عليه فازهد في الدنيا، وأما العمل الذي يحبك الناس عليه فانبذ','118','0','0');
INSERT INTO [t8216] VALUES (119,'الدنيا غنيمة الآخرة','119','0','0');
INSERT INTO [t8216] VALUES (120,'حبك للدنيا؟ قال: خذع','120','0','0');
INSERT INTO [t8216] VALUES (121,'الزاهد من قدر فترك','121','0','0');
INSERT INTO [t8216] VALUES (122,'الزهد الرضا عن الله عز وجل','122','0','0');
INSERT INTO [t8216] VALUES (123,'الفقيه الزاهد في الدنيا، الراغب في الآخرة، الدائب في العبادة، البصير','123','0','0');
INSERT INTO [t8216] VALUES (124,'ازهد ونم. قال: وقال سفيان: يا بكر، خذ من الدنيا لبدنك، وخذ من الآخرة لقلبك قال أبو نصر:','124','0','0');
INSERT INTO [t8216] VALUES (125,'الزهد ثلاثة أصناف، فزهد فرض، وزهد فضل، وزهد سلامة فالزهد الفرض: الزهد في الحرام، والزهد','125','0','0');
INSERT INTO [t8216] VALUES (126,'الزاهد في الدنيا؟ قال: من إذا أنعم عليه شكر، وإذا ابتلي صبر. قلت: يا أبا محمد قد أنعم','126','0','0');
INSERT INTO [t8216] VALUES (127,'لنا بيتا نوجه إليه صالح متاعنا. قال: إنه لا بد لك من متاع ما دمت ها هنا. قال: إن صاحب','127','0','0');
INSERT INTO [t8216] VALUES (128,'يكفيني صاع من طعام في كل جمعة، وشربة من ماء في كل يوم','128','0','0');
INSERT INTO [t8216] VALUES (129,'عليك بالزهد يبصرك الله تعالى عورات الدنيا، وعليك بالورع يخفف الله عز وجل حسابك، ودع ما','129','0','0');
INSERT INTO [t8216] VALUES (130,'إن كنت إنما أريدكم لهذا إني إذا لشقي','130','0','0');
INSERT INTO [t8216] VALUES (131,'ما يسرني أن لي جميع ما حوت عليه البصرة من الأموال والثمرة بفلسين','131','0','0');
INSERT INTO [t8216] VALUES (132,'لا يجوز لأحد أن يظهر للناس الزهد والشهوات في قلبه، فإذا لم يبق في قلبه من شهوات الدنيا','132','0','0');
INSERT INTO [t8216] VALUES (133,'أرادوا بالزهد لتفرغ قلوبهم للآخرة','133','0','0');
INSERT INTO [t8216] VALUES (134,'ازهد في الدنيا','134','0','0');
INSERT INTO [t8216] VALUES (135,'أدلك على من هو أزهد مني؟ قال: ومن هو؟ قال: أنت قال: كيف ذاك؟ قال: لأنك زهدت في الجنة وما','135','0','0');
INSERT INTO [t8216] VALUES (136,'من رضي بالدنيا كلها عوضا من الآخرة، فذاك الذي رضي بأقل مما رضيت به','136','0','0');
INSERT INTO [t8216] VALUES (137,'زهدنا الله وإياك زهد من أمكنه الحرام والذنوب في الخلوات فعلم أن الله يراه','137','0','0');
INSERT INTO [t8216] VALUES (138,'لئن حلفتم لي على رجل منكم أنه أزهدكم، لأحلفن لكم أنه خيركم','138','0','0');
INSERT INTO [t8216] VALUES (139,'من زهد على حقيقة كانت مؤونته في الدنيا خفيفة، ومن لم يعرف ثواب الأعمال ثقلت عليه في جميع','139','0','0');
INSERT INTO [t8216] VALUES (140,'لتحببن إليكم الدنيا حتى تتعبدوا لها ولأهلها، وليأتينكم زمان تكره فيه الموعظة، وحتى يختفي','140','0','0');
INSERT INTO [t8216] VALUES (141,'عبدت بنو إسرائيل الأصنام بعد عبادتهم الرحمن بحبهم الدنيا','141','0','0');
INSERT INTO [t8216] VALUES (142,'البدن إذا سقم لم ينجع فيه طعام ولا شراب ولا نوم ولا راحة، وكذلك القلب إذا علقه حب الدنيا','142','0','0');
INSERT INTO [t8216] VALUES (143,'بقدر ما تحزن للدنيا فكذلك يخرج هم الآخرة من قلبك، وبقدر ما تحزن للآخرة فكذلك يخرج هم','143','0','0');
INSERT INTO [t8216] VALUES (144,'اتخذوا الدنيا ظئرا، واتخذوا الآخرة أما، ألم تروا إلى الصبي يلقى على الظئر، فإذا ترعرع','144','0','0');
INSERT INTO [t8216] VALUES (145,'أوحي إلى الدنيا أنه من تركك فاخدميه، ومن آثرك فاستخدميه','145','0','0');
INSERT INTO [t8216] VALUES (146,'بحسبكم بقاء الآخرة من فناء الدنيا. بأي العملين حللت إبقاء الدارين فبت مع دار البقاء، إن','146','0','0');
INSERT INTO [t8216] VALUES (147,'سميت الدنيا، لأنها دنية، وإنما سمي المال لأنه يميل بأهله','147','0','0');
INSERT INTO [t8216] VALUES (148,'ضعف الناس والذنوب والشيطان، قال: وجعل يعرض بأمور لا توافق الرجل في نفسه، فلما رأى ذلك','148','0','0');
INSERT INTO [t8216] VALUES (149,'لا أعلم خليقة يكابد من هذا الأمر ما يكابد هذا الإنسان قال: وقال سعيد أخوه: يكابد مضايق','149','0','0');
INSERT INTO [t8216] VALUES (150,'الدنيا حلوة خضرة، وإن الله مستخلفكم فيها فناظر كيف تعملون، ألا فاتقوا الدنيا واتقوا','150','0','0');
INSERT INTO [t8216] VALUES (151,'للدنيا أهون على الله عز وجل من هذه على أهلها قال الحسن: أخبرنا من شهد','151','0','0');
INSERT INTO [t8216] VALUES (152,'انظروا إلى دجاجهم، وبطهم، وثمارهم','152','0','0');
INSERT INTO [t8216] VALUES (153,'ما الدنيا في الآخرة إلا كرجل وضع إصبعه في اليم، فلينظر بم رجعت إليه','153','0','0');
INSERT INTO [t8216] VALUES (154,'الدنيا مقيل لرائح ... قضى وطرا من حاجة ثم هجرا','154','0','0');
INSERT INTO [t8216] VALUES (155,'الدنيا غنيمة الأكياس، وغفلة الجهال، لم يعرفوها حتى اخرجوا منها، فسألوا الرجعة فلم','155','0','0');
INSERT INTO [t8216] VALUES (156,'إنا أخلصناهم بخالصة ذكرى الدار} [ص: ٤٦] قال: أخلصناهم بذكر الآخرة','156','0','0');
INSERT INTO [t8216] VALUES (157,'ما شاء صنع، وما شاء رفع، وما شاء وضع، وما شاء أعطى، ومن شاء منع، إن الدنيا دار غرور،','157','0','0');
INSERT INTO [t8216] VALUES (158,'كانوا أزهد منكم في الدنيا، وأرغب منكم في الآخرة','158','0','0');
INSERT INTO [t8216] VALUES (159,'ألا أيها الطالب ... أمرا ليس يلحقه ويا من طال بالدنيا ... وزهرتها تعلقه أما ينفك ذا أمل','159','0','0');
INSERT INTO [t8216] VALUES (160,'ألم ترها تلهي بنيها عشية ... وتترك في الصبح المجالس نوحا','160','0','0');
INSERT INTO [t8216] VALUES (161,'زهرة الدنيا غرور ولو تحلت بكل زينة والخير الأكبر غدا في الآخرةفنحن بين مسارع','161','0','0');
INSERT INTO [t8216] VALUES (162,'لو رأت أعين الزاهدين ثواب ما أعد الله لأهل الإعراض عن الدنيا لذابت أنفسهم شوقا واشتياقا','162','0','0');
INSERT INTO [t8216] VALUES (163,'استدبرت الدنيا منذ يوم نزلتها، واستقبلت الآخرة، فأنت إلى دار تقرب منها أقرب منك إلى دار','163','0','0');
INSERT INTO [t8216] VALUES (164,'مثل الدنيا مثل الحية: لين مسها تقتل بسمها، فأعرض عما يعجبك فيها لقلة ما يصحبك منها، وضع','164','0','0');
INSERT INTO [t8216] VALUES (165,'إن سرك أن تجد حلاوة العبادة، وتبلغ ذروة سنامها، فاجعل بينك وبين شهوات الدنيا حائطا من','165','0','0');
INSERT INTO [t8216] VALUES (166,'لا يستقيم حب الآخرة والدنيا في قلب المؤمن','166','0','0');
INSERT INTO [t8216] VALUES (167,'مثل الذي يريد أن يجمع له الآخرة والدنيا مثل عبد له ربان لا يدري أيهما','167','0','0');
INSERT INTO [t8216] VALUES (168,'لم يبق من الدنيا إلا فتنة منتظرة، وكل محزن','168','0','0');
INSERT INTO [t8216] VALUES (169,'من أحب الدنيا وسرته ذهب خوف الآخرة من قلبه، وما من عبد يزداد علما ويزداد على الدنيا حرصا،','169','0','0');
INSERT INTO [t8216] VALUES (170,'الدنيا كلها قليل، وقد ذهب أكثر القليل، وبقي قليل من القليل ممم الرقم غير موجود فى الحديث','170','0','0');
INSERT INTO [t8216] VALUES (171,'ما أكثر أشباه الدنيا منها','171','0','0');
INSERT INTO [t8216] VALUES (172,'إذا رأيت كلما طلبت شيئا من أمر الآخرة وابتغيته يسر لك، وإذا أردت شيئا من أمر الدنيا','172','0','0');
INSERT INTO [t8216] VALUES (173,'بؤسا لمحب الدنيا، أتحب ما أبغض الله عز وجل؟','173','0','0');
INSERT INTO [t8216] VALUES (174,'لا تحزن أن يعجل لك كثير مما تحب من أمر دنياك إذا كنت ذا رغبة في أمر آخرتك حدثنا عبد الله،','174','0','0');
INSERT INTO [t8216] VALUES (175,'من أخذ الرغيف؟ قال: ما أدري. قال: ثم انتهيا إلى وادي ماء، فأخذ عيسى بيد الرجل فمشيا على','175','0','0');
INSERT INTO [t8216] VALUES (176,'مثلي ومثلكم ومثل الدنيا كمثل قوم سلكوا مفازة غبراء، حتى إذا لم يدروا ما سلكوا منها أكثر','176','0','0');
INSERT INTO [t8216] VALUES (177,'مثل الدنيا كمثل الماشي في الماء، هل يستطيع الذي يمشي في الماء ألا تبتل','177','0','0');
INSERT INTO [t8216] VALUES (178,'صاحب الدنيا لا يلتذ العبادة، ولا يجد حلاوتها مع ما يجد من حب الدنيا بحق أقول لكم: إن','178','0','0');
INSERT INTO [t8216] VALUES (179,'الدنيا بحر عميق يغرق فيه ناس كثير، فلتكن سفينتك فيها تقوى الله تعالى، وحشوها الإيمان','179','0','0');
INSERT INTO [t8216] VALUES (180,'ويل لصاحب الدنيا كيف يموت ويتركها، وتغره ويأمنها، وتخذله ويثق بها؟ ويل للمغترين كيف أرتهم','180','0','0');
INSERT INTO [t8216] VALUES (181,'ليس شيء خيرا لنا من أن لا نمتحن بالدنيا','181','0','0');
INSERT INTO [t8216] VALUES (182,'ليست لك بدار، أخرج منها همك، وفارقها بعقلك، فبئست الدار هي، إلا لعامل فيها، فنعمت الدار','182','0','0');
INSERT INTO [t8216] VALUES (183,'كلف الناس بالدنيا، ولم ينالوا منها فوق قسمتهم، وأعرضوا عن الآخرة، وببغيتها يرجو العباد','183','0','0');
INSERT INTO [t8216] VALUES (184,'الدنيا تحتنا','184','0','0');
INSERT INTO [t8216] VALUES (185,'إذا رأيت العبد دنياه تزداد وآخرته تنقص، مقيما على ذلك، راضيا به فذلك المغبون الذي يلعب','185','0','0');
INSERT INTO [t8216] VALUES (186,'أربع لا تجتمع في أحد من الناس إلا تعجب: الصمت وهو أول العبادة، والتواضع لله عز وجل،','186','0','0');
INSERT INTO [t8216] VALUES (187,'من سره أن ينظر إلى الدنيا بحذافيرها فلينظر إلى هذه المزبلة، ثم قال: ولو أن الدنيا تعدل','187','0','0');
INSERT INTO [t8216] VALUES (188,'الدنيا جعلت قليلا فما بقي منها إلا قليل من قليل أنشدني أحمد بن موسى الثقفي: [البحر الهزج]','188','0','0');
INSERT INTO [t8216] VALUES (189,'الدنيا قد آذنت بصرم، وولت حذاء، ولم يبق منها إلا صبابة كصبابة الإناء، وإنكم مفارقوها لا','189','0','0');
INSERT INTO [t8216] VALUES (190,'ما رأيت قوما قط أرغب فيما كان رسول الله صلى الله عليه وسلم يزهد فيه منكم، ترغبون في','190','0','0');
INSERT INTO [t8216] VALUES (191,'الدنيا سجن المؤمن وسنته، فإذا فارق الدنيا فارق السجن والسنة','191','0','0');
INSERT INTO [t8216] VALUES (192,'الدنيا جنة الكافر، وسجن المؤمن، وإنما مثل المؤمن حين تخرج نفسه كمثل رجل كان في سجن فأخرج','192','0','0');
INSERT INTO [t8216] VALUES (193,'ما بقي من الدنيا بلاء وفتنة، وإنما مثل عمل أحدكم كمثل الوعاء، إذا طاب أعلاه طاب أسفله،','193','0','0');
INSERT INTO [t8216] VALUES (194,'فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور} [لقمان: ٣٣] قال: من قال ذا؟ قال: من','194','0','0');
INSERT INTO [t8216] VALUES (195,'الدنيا كثيرة الأشغال، لا يفتح رجل على نفسه باب شغل إلا أوشك ذلك الباب أن يفتح عليه عشرة','195','0','0');
INSERT INTO [t8216] VALUES (196,'المؤمن من يعلم أن ما قاله الله عز وجل كما قال، والمؤمن أحسن الناس عملا، وأشد الناس خوفا،','196','0','0');
INSERT INTO [t8216] VALUES (197,'قطعت عظيم السفر وبقي أقله، فاذكر يا أخي، المصادر والموارد، فقد أوحي إلى نبيك محمد صلى','197','0','0');
INSERT INTO [t8216] VALUES (198,'ناجى موسى عليه السلام، فقال: يا موسى إنه لم يتصنع لي المتصنعون بمثل الزهد في الدنيا، ولم','198','0','0');
INSERT INTO [t8216] VALUES (199,'أزهدكم في الدنيا، وأرغبكم في الآخرة','199','0','0');
INSERT INTO [t8216] VALUES (200,'ما أهونك على الأبرار الذين تصنعت لهم، وتزينت لهم إني قد قذفت في قلوبهم بغضك والصدود عنك،','200','0','0');
INSERT INTO [t8216] VALUES (201,'ما طالب الدنيا من حلالها وجميلها وحسنها عند الله بالمحمود ولا المغبوط، فكيف من طلبها من','201','0','0');
INSERT INTO [t8216] VALUES (202,'تريدون بقاء الدنيا وقد أبى الله عز وجل إلا فناءها، وإنما فناء الدنيا بذهاب','202','0','0');
INSERT INTO [t8216] VALUES (203,'دار بالبلاء محفوفة، وبالفناء معروفة، وبالغدر موصوفة، فكل ما فيها إلى زوال، وهي بين أهلها','203','0','0');
INSERT INTO [t8216] VALUES (204,'بقدر ما تفرح للدنيا كذلك تخرج حلاوة الآخرة من قلبك','204','0','0');
INSERT INTO [t8216] VALUES (205,'كلوا خبز الشعير، والماء القراح ونبات الأرض، فإنكم لا تقومون بشكره، واعلموا أن حلاوة','205','0','0');
INSERT INTO [t8216] VALUES (206,'ما أعطى الله الدنيا من أعطاها إياها إلا اختبارا، ولا زواها عمن زواها عنه إلا اختبارا،','206','0','0');
INSERT INTO [t8216] VALUES (207,'يكفي أهل الدنيا ما يعاينون من كثرة الفجائع وتتابع المصائب في المال والإخوان، والنقص في','207','0','0');
INSERT INTO [t8216] VALUES (208,'خمسة من علامة الشقاء: قسوة القلب، وجمود العين، وقلة الحياء، والرغبة في الدنيا، وطول','208','0','0');
INSERT INTO [t8216] VALUES (209,'أعطاكم الدنيا لتطلبوا بها الآخرة، ولم يعطكموها لتركنوا إليها، إن الدنيا تفنى، والآخرة','209','0','0');
INSERT INTO [t8216] VALUES (210,'لدار صدق لمن صدقها، ودار عافية لمن فهم عنها، ودار غنى لمن تزود منها، ومسجد أحباء الله عز','210','0','0');
INSERT INTO [t8216] VALUES (211,'يا ويح العابدين أما يستحيون من طلب الدنيا، وقد ضمن لهم الرزق، وكفي الراغب منها الطلب،','211','0','0');
INSERT INTO [t8216] VALUES (212,'لا تغبطوا حريصا على ثروة، ولا سعة في مكسب، ولا مال، وانظروا إليه بعين المقت له في فعاله،','212','0','0');
INSERT INTO [t8216] VALUES (213,'طلب أحدهما الدنيا بحلالها فأصابها، فوصل فيها رحمه، وقدم فيها لنفسه، وجانب الآخر الدنيا؟','213','0','0');
INSERT INTO [t8216] VALUES (214,'ولو بسط الله الرزق لعباده لبغوا في الأرض} [الشورى: ٢٧] ، وذلك أنهم قالوا: لو أن لنا','214','0','0');
INSERT INTO [t8216] VALUES (215,'لا تتخذوا الضيعة فترغبوا في الدنيا. قال عبد الله: وبراذان ما براذان وبالمدينة ما','215','0','0');
INSERT INTO [t8216] VALUES (216,'أيحزن عبدي أن أقبض عنه الدنيا، وذلك أقرب له مني، أو يفرح عبدي أن أبسط له الدنيا، وذلك','216','0','0');
INSERT INTO [t8216] VALUES (217,'الزهد في الدنيا راحة القلب والبدن','217','0','0');
INSERT INTO [t8216] VALUES (218,'ينبئهم بما عملوا ليجزي الذين أساءوا بما عملوا ويجزي الذين أحسنوا بالحسنى، فإنه لا معقب','218','0','0');
INSERT INTO [t8216] VALUES (219,'الساعة قد اقتربت، وإن القمر قد انشق، وإن الدنيا قد آذنت بفراق، وإن المضمار اليوم وغدا','219','0','0');
INSERT INTO [t8216] VALUES (220,'يحشر الناس يوم القيامة كلهم عراة ما خلا أهل الزهد','220','0','0');
INSERT INTO [t8216] VALUES (221,'حب الدنيا يعمي ويصم','221','0','0');
INSERT INTO [t8216] VALUES (222,'الدنيا دار مرحلة، وجعل الخير والشر فيها فتنة لأهلها ليبلوهم أيهم أحسن عملا، فهم يتقلبون','222','0','0');
INSERT INTO [t8216] VALUES (223,'الدنيا أمد، والآخرة أبد','223','0','0');
INSERT INTO [t8216] VALUES (224,'ليس من حبك الدنيا طلبك ما يصلحك فيها، ومن زهدك فيها ترك الحاجة يسدها عنك تركها، ومن أحب','224','0','0');
INSERT INTO [t8216] VALUES (225,'الدنيا بحر عميق يغرق فيه ناس كثير، فلتكن سفينتك فيها تقوى الله تعالى، وحشوها الإيمان','225','0','0');
INSERT INTO [t8216] VALUES (226,'ما في أيديكم أسلاب الهالكين، وسيتركها الباقون كما تركها الماضون، ألا ترون أنكم في كل يوم','226','0','0');
INSERT INTO [t8216] VALUES (227,'الدار التي أصبحنا فيها دار بالبلاء محفوفة، وبالفناء موصوفة، كل ما فيها إلى زوال ونفاد،','227','0','0');
INSERT INTO [t8216] VALUES (228,'يا خاطب الدنيا على نفسها ... إن لها في كل يوم خليل ما أقتل الدنيا لخطابها ... تقتلهم قدما','228','0','0');
INSERT INTO [t8216] VALUES (229,'بعث محمد صلى الله عليه وسلم قال: فجعل يرسل شياطينه إلى أصحاب النبي صلى الله عليه وسلم','229','0','0');
INSERT INTO [t8216] VALUES (230,'تشتد حاجتي في الدنيا وليست بداري؟ أم كيف أجمع لها وفي غيرها قراري وخلدي؟ أم كيف يشتد حرصي','230','0','0');
INSERT INTO [t8216] VALUES (231,'اللهم زهدنا في الدنيا، ووسع علينا منها، ولا تزو بها عنا وترغبنا فيها','231','0','0');
INSERT INTO [t8216] VALUES (232,'ألا حر كريم يغضب على الدنيا؟','232','0','0');
INSERT INTO [t8216] VALUES (233,'لم ينافسوا أهل الدنيا في دنياهم، ولم ينافسوهم في عزها، ولم يجزعوا لذلها، أخذوا صفوها،','233','0','0');
INSERT INTO [t8216] VALUES (234,'تطلبون الدنيا وهي مدبرة عنكم، ولكم من الإحداث ما لكم، فقيسوا أمركم','234','0','0');
INSERT INTO [t8216] VALUES (235,'كانوا أزهد في الدنيا وأرغب في الآخرة منكم','235','0','0');
INSERT INTO [t8216] VALUES (236,'تهون على الدنيا الملامة، إنه حريص على استخلاصها من يلومها أنشدني أبو إسحاق القرشي التيمي:','236','0','0');
INSERT INTO [t8216] VALUES (237,'ذا القرنين لقي ملكا من الملائكة فقال: علمني علما أزدد به إيمانا ويقينا، فقال له: إنك لا','237','0','0');
INSERT INTO [t8216] VALUES (238,'الزاهدون في الدنيا، والراغبون في الآخرة؟ قال مسروق: ما كنت لأعطي عليهما','238','0','0');
INSERT INTO [t8216] VALUES (239,'الزاهدون في الدنيا، الراغبون في الآخرة؟ فأراه قبر النبي صلى الله عليه وسلم وأبي بكر وعمر،','239','0','0');
INSERT INTO [t8216] VALUES (240,'الدنيا دار من لا دار له، ومال من لا مال له، ولها يجمع من لا عقل له','240','0','0');
INSERT INTO [t8216] VALUES (241,'لوددت أني من الدنيا فرد كالراكب الغادي الرائح','241','0','0');
INSERT INTO [t8216] VALUES (242,'ما من مسلم رزق رزقا يوما بيوم لا يعلم أنه قد خير له إلا عاجز، أو قال: غبي','242','0','0');
INSERT INTO [t8216] VALUES (243,'الدنيا ملعونة، ملعون ما فيها إلا ذكر الله عز وجل، وما أدى إليه','243','0','0');
INSERT INTO [t8216] VALUES (244,'لو كانت لي الدنيا كلها فسلبنيها بشربة يوم القيامة لافتديت بها أنشدني أحمد بن موسى الثقفي:','244','0','0');
INSERT INTO [t8216] VALUES (245,'هي الدنيا تعذب من هواها ... وتورث قلبه حزنا وداء','245','0','0');
INSERT INTO [t8216] VALUES (246,'أرى أشقياء الناس لا يسأمونها ... على أنهم فيها عراة وجوع أراها وإن كانت تحب كأنها ...','246','0','0');
INSERT INTO [t8216] VALUES (247,'كيف يفرح بالدنيا من يومه يهدم شهره، وشهره يهدم سنته، وسنته تهدم عمره؟ كيف يفرح بالدنيا من','247','0','0');
INSERT INTO [t8216] VALUES (248,'الأيام سهام والناس أغراض، والدهر يرميك كل يوم بسهامه ويتخرمك بلياليه وأيامه، حتى يستغرق','248','0','0');
INSERT INTO [t8216] VALUES (249,'الدنيا وقتك الذي يرجع إليك فيه طرفك، لأن ما مضى عنك فقد فاتك إدراكه، وما لم يأت فلا علم','249','0','0');
INSERT INTO [t8216] VALUES (250,'ما الفقر أخشى عليكم، ولكني أخشى عليكم أن تبسط الدنيا عليكم، كما بسطت على من قبلكم،','250','0','0');
INSERT INTO [t8216] VALUES (251,'لست أخشى عليكم أن تشركوا بعدي، ولكني أخشى عليكم الدنيا أن تنافسوها قال عقبة: فكانت آخر','251','0','0');
INSERT INTO [t8216] VALUES (252,'بلينا بالضراء مع رسول الله فصبرنا، وبلينا بالسراء فلم نصبر','252','0','0');
INSERT INTO [t8216] VALUES (253,'بينا أنا نائم أوفيت على جبل، فبينا أنا عليه طلعت علي ثلة من هذه الأمة قد سدت الأفق، حتى','253','0','0');
INSERT INTO [t8216] VALUES (254,'ما عبد الله بشيء أفضل من الزهد في الدنيا','254','0','0');
INSERT INTO [t8216] VALUES (255,'من زهد في الدنيا تهاون بالمصيبات، ومن ارتقب الموت سارع إلى الخيرات','255','0','0');
INSERT INTO [t8216] VALUES (256,'أين تذهبون؟ بل أين يراد بكم وحادي الموت في أثر الأنفاس حثيث موضع، وعلى اجتياح الأرواح من','256','0','0');
INSERT INTO [t8216] VALUES (257,'على الأسقام والأمراض أسست هذه الدار، فهبك تصح من الأسقام، وتبرأ من الأمراض، هل تقدر على','257','0','0');
INSERT INTO [t8216] VALUES (258,'عجبت من الناس أنهم ينظرون إلى الموتى في كل يوم ينقلون، وهم في الدنيا في غفلة يلعبون، ثم','258','0','0');
INSERT INTO [t8216] VALUES (259,'كفى بذكر الموت مزهدا في الدنيا، ومرغبا في الآخرة قال أبو بكر: قال بعض حكماء الشعراء:','259','0','0');
INSERT INTO [t8216] VALUES (260,'الله تعالى يحمي عبده المؤمن من الدنيا، كما يحمي الراعي الشفيق غنمه عن مراتع','260','0','0');
INSERT INTO [t8216] VALUES (261,'مسكين ابن آدم رضي بدار حلالها حساب، وحرامها عذاب، إن أخذه من حله حوسب بنعيمه، وإن أخذه من','261','0','0');
INSERT INTO [t8216] VALUES (262,'ما تكاملت المروءة في امرئ قط إلا لذي المعروف، وهانت عليه الدنيا','262','0','0');
INSERT INTO [t8216] VALUES (263,'فكأنك بالدنيا لم تكن، وبالآخرة لم تزل','263','0','0');
INSERT INTO [t8216] VALUES (264,'لن تدركوا ما تريدون إلا بترككم ما تشتهون، ولا تنالون ما تأملون إلا بصبركم على ما تكرهون،','264','0','0');
INSERT INTO [t8216] VALUES (265,'اتقوا فضول الدنيا، فإنها رجس عند الله عز وجل','265','0','0');
INSERT INTO [t8216] VALUES (266,'كانت الدنيا ولم أكن فيها، وتكون ولا أكون فيها، وإنما لي فيها أيامي التي أنا فيها، فإن','266','0','0');
INSERT INTO [t8216] VALUES (267,'فرب كلمة قد أحيت سامعها بعد الموت، ورفعته بعد الضعة، ونعشته بعد الصرعة، وأغنته بعد الفقر،','267','0','0');
INSERT INTO [t8216] VALUES (268,'أترون هذه هينة على أهلها؟ ، قالوا: نعم قال: الدنيا أهون على الله من هذه على أهلها قال بعض','268','0','0');
INSERT INTO [t8216] VALUES (269,'يا مجلس القوم الذين ... بهم تفرقت المنازل أصبحت بعد عمارة ... قفرا تخرقك','269','0','0');
INSERT INTO [t8216] VALUES (270,'وأجهشت للتوباذ حين رأيته ... ونادى بأعلى صوته ودعاني فقلت له: أين الذين عهدتهم ... بجزعك','270','0','0');
INSERT INTO [t8216] VALUES (271,'ما أتت علينا ساعة من أعمارنا إلا ونحن نؤثر الدنيا على ما سواها، ثم لا تزداد لنا إلا','271','0','0');
INSERT INTO [t8216] VALUES (272,'المقام فيها ببر الأمهات والأولاد يسير حتى يصيروا منها إلى دارين: إحداهما فرقة لا تواصل','272','0','0');
INSERT INTO [t8216] VALUES (273,'حبكم للدنيا؟ قال: حب الصبي لأمه، إذا أقبلت فرحنا، وإذا أدبرت حزنا وبكينا. قال: فما بال','273','0','0');
INSERT INTO [t8216] VALUES (274,'لا يصبر عن شهوات الدنيا إلا من كان في قلبه ما يشغله من الآخرة بلغني عن بعض الحكماء، قال:','274','0','0');
INSERT INTO [t8216] VALUES (275,'تعملون لدنيا صغيرة، وتتركون الآخرة الكبيرة، وعلى كلكم يمر الموت','275','0','0');
INSERT INTO [t8216] VALUES (276,'من كان صغير الدنيا أعظم في عينه من كبير الآخرة، كيف يرجو أن يصنع له في دنياه','276','0','0');
INSERT INTO [t8216] VALUES (277,'تحملني على المكان الخشن، وتركب على المكان الوطيء، ولكن اركب أنت، وأكون أنا خلفك، قال:','277','0','0');
INSERT INTO [t8216] VALUES (278,'الزهد في الدنيا يريح القلب والبدن، والرغبة في الدنيا تكثر الهم والحزن','278','0','0');
INSERT INTO [t8216] VALUES (279,'جعل الشر كله في بيت، وجعل مفتاحه حب الدنيا، وجعل الخير كله في بيت، وجعل مفتاحه الزهد في','279','0','0');
INSERT INTO [t8216] VALUES (280,'القنوع هو الزهد، وهو الغنى','280','0','0');
INSERT INTO [t8216] VALUES (281,'الدنيا والآخرة في قلب ابن آدم ككفتي الميزان، بقدر ما ترجح إحداهما تخف','281','0','0');
INSERT INTO [t8216] VALUES (282,'ليست بدار إقامة، وإنما أهبط إليها آدم عقوبة، فبحسب من لا يدري ثواب الله أنه ثواب، وبحسب','282','0','0');
INSERT INTO [t8216] VALUES (283,'من كانت الدنيا أكبر همه طال غدا في القيامة غمه','283','0','0');
INSERT INTO [t8216] VALUES (284,'إن أقل الناس هما في الآخرة أقلهم هما في الدنيا أنشدني سليمان بن أبي شيخ: ما زالت الدنيا','284','0','0');
INSERT INTO [t8216] VALUES (285,'اصطلحنا على حب الدنيا، فلا يأمر بعضنا بعضا، ولا ينهى بعضنا بعضا، ولا يدعنا الله عز وجل','285','0','0');
INSERT INTO [t8216] VALUES (286,'الزهد في الدنيا لا يقيم الرجل على راحة تستريح إليها نفسه','286','0','0');
INSERT INTO [t8216] VALUES (287,'الورع يبلغ بالعبد إلى الزهد في الدنيا، والزهد يبلغ به حب الله تعالى','287','0','0');
INSERT INTO [t8216] VALUES (288,'لم أصبح أملك على الناس إلا سبعة دراهم من لحاء شجر فتلته بيدي، وبنعمة ربي أحدث لو أن','288','0','0');
INSERT INTO [t8216] VALUES (289,'الدنيا والآخرة إناءان، أيهما أكفأت كان الشغل فيه','289','0','0');
INSERT INTO [t8216] VALUES (290,'مثل القلب مثل الإناء إذا ملأته ثم زدت فيه شيئا فاض، فكذلك القلب إذا امتلأ من حب الدنيا لم','290','0','0');
INSERT INTO [t8216] VALUES (291,'يسير الدنيا يشغل عن كثير الآخرة','291','0','0');
INSERT INTO [t8216] VALUES (292,'الدنيا دار فناء، ومنزل بلغة، رغبت عنها السعداء، وانتزعت من أيدي الأشقياء، فأشقى الناس','292','0','0');
INSERT INTO [t8216] VALUES (293,'الزاهد: الذي إذا رأى أحدا قال هو أفضل مني','293','0','0');
INSERT INTO [t8216] VALUES (294,'لو أن علماءنا عفا الله عنا وعنهم نصحوا الله في عباده، فقالوا: يا عباد الله اسمعوا ما','294','0','0');
INSERT INTO [t8216] VALUES (295,'ما تأخذ إلا من كيسك','295','0','0');
INSERT INTO [t8216] VALUES (296,'أيحزن عبدي المؤمن أن أزوي عنه الدنيا وهو أقرب له مني؟ ويفرح أن أبسط له الدنيا وهو أبعد له','296','0','0');
INSERT INTO [t8216] VALUES (297,'لا يصيب عبد من الدنيا شيئا إلا نقص من درجاته عند الله تعالى، وإن كان عليه','297','0','0');
INSERT INTO [t8216] VALUES (298,'ما رأيت أحدا أعظم الدنيا فقرت عينه فيها، ولا انتفع بها، وما حقرها أحد إلا تمتع','298','0','0');
INSERT INTO [t8216] VALUES (299,'عامة الزهد في الدنيا، يعني إذا لم تحب ثناء الناس، ولم تبال بذمهم','299','0','0');
INSERT INTO [t8216] VALUES (300,'أهينوا الدنيا، فوالله ما هي لأحد بأهنأ منها لمن أهانها','300','0','0');
INSERT INTO [t8216] VALUES (301,'إذا أراد الله بعبد خيرا أعطاه من الدنيا عطية، ثم يمسك، فإذا أنفد أعاد عليه، فإذا هان عليه','301','0','0');
INSERT INTO [t8216] VALUES (302,'أخاف أن تدفق علي الدنيا دفقة فتغرقني','302','0','0');
INSERT INTO [t8216] VALUES (303,'أمسك عني الدنيا','303','0','0');
INSERT INTO [t8216] VALUES (304,'ليس لأبدانكم ثمن إلا الجنة، فلا تبيعوها إلا بها','304','0','0');
INSERT INTO [t8216] VALUES (305,'من علامة المريدين الزاهدين في الدنيا تركهم كل خليط لا يريد ما يريدون','305','0','0');
INSERT INTO [t8216] VALUES (306,'أمران لو لم نعذب إلا بهما كنا مستحقين بهما العذاب من الله عز وجل: أحدنا يزاد الشيء من','306','0','0');
INSERT INTO [t8216] VALUES (307,'لو أن رجلا صام الدهر لا يفطر، وقام الليل لا يفتر، وتصدق بماله، وجاهد في سبيل الله، واجتنب','307','0','0');
INSERT INTO [t8216] VALUES (308,'إذا عظمت أمتي الدنيا نزع منها هيبة الإسلام، وإذا تركت الأمر بالمعروف والنهي عن المنكر','308','0','0');
INSERT INTO [t8216] VALUES (309,'رهبة العبد من الله على قدر علمه بالله، وزهادته في الدنيا على قدر رغبته في','309','0','0');
INSERT INTO [t8216] VALUES (310,'لا تزال نفس ابن آدم شابة في حب الدنيا والدرهم، ولو التقت ترقوتاه من الكبر، إلا الذين','310','0','0');
INSERT INTO [t8216] VALUES (311,'اشتدت مؤونة الدنيا ومؤونة الآخرة، فأما مؤونة الآخرة فإنك لا تجد لها أعوانا، وأما مؤونة','311','0','0');
INSERT INTO [t8216] VALUES (312,'جعلت الدنيا فتنة ونكالا، فاجعل حظي من جميعها، ونصيبي من قسمها، وشرفي من سلطانها سلوا','312','0','0');
INSERT INTO [t8216] VALUES (313,'أنت طالب ومطلوب، يطلبك من لا تفوته، وتطلب ما قد كفيته، فكأن ما قد غاب عنك قد كشف لك، وما','313','0','0');
INSERT INTO [t8216] VALUES (314,'آذنتك ببين، وانطوت لك على حين أنشدني عمر بن علي بن هارون: إنما الدنيا جدود ... فعزيز','314','0','0');
INSERT INTO [t8216] VALUES (315,'ما أعجب يا أمير المؤمنين مما نحن فيه، كيف غلب علينا؟ وأعجب مما نصير إليه كيف غفلتنا عنه؟','315','0','0');
INSERT INTO [t8216] VALUES (316,'الدنيا من أولها إلى آخرها قليل، وإن الذي بقي منها في جنب الذي مضى منها قليل، وإنما لك','316','0','0');
INSERT INTO [t8216] VALUES (317,'الذي نخاف من شر الدنيا أعظم من الشر الذي نحن فيه منها، وإنما يرجح شر الدنيا لنا عند','317','0','0');
INSERT INTO [t8216] VALUES (318,'المعاد لو كان واحدا ما غلبونا على الدنيا، كأنه يعزي نفسه','318','0','0');
INSERT INTO [t8216] VALUES (319,'إن لم تدعوا الدنيا رغبة في الآخرة فاتركوها أنفا أن تكون مبارة ومبارك أكثرها فيها منكم','319','0','0');
INSERT INTO [t8216] VALUES (320,'لتدعنها غير محمودين، ولا معروف لكم ذلك','320','0','0');
INSERT INTO [t8216] VALUES (321,'إذا أردت أن تعرف قدر الدنيا فانظر عند من هي قرأت في كتاب داود بن رشيد بخطه، حدثني أبو عبد','321','0','0');
INSERT INTO [t8216] VALUES (322,'رأس ما هو مصلحك ومصلح به على يديك الزهد في الدنيا، وإنما الزهد باليقين، واليقين بالتفكر،','322','0','0');
INSERT INTO [t8216] VALUES (323,'طالب الدنيا مثل شارب ماء البحر، كلما ازداد شربا ازداد عطشا حتى يقتله','323','0','0');
INSERT INTO [t8216] VALUES (324,'لو أن عبدا أشغل نفسه نفسا من أنفاسه فأصاب بذلك النفس الدنيا بما فيها لكان هو المغبون في','324','0','0');
INSERT INTO [t8216] VALUES (325,'ازهدوا في الدنيا تمشوا فيها بلا هم','325','0','0');
INSERT INTO [t8216] VALUES (326,'كانت الدنيا بأيديهم كانوا فيه لله خزانا، لم ينفقوا في شهواتهم ولا لذاتهم، كانوا إذا ورد','326','0','0');
INSERT INTO [t8216] VALUES (327,'يسمون الدنيا خنزيرة، ولو وجدوا لها اسما شرا منه سموها به، وكانوا إذا أقبلت إلى أحدهم دنيا','327','0','0');
INSERT INTO [t8216] VALUES (328,'ما الفقر أخشى عليكم، ولكني أخشى أن تبسط الدنيا عليكم كما بسطت على من كان قبلكم،','328','0','0');
INSERT INTO [t8216] VALUES (329,'بت أفكر، فكبست البحر الأخضر بالذهب الأحمر، ثم نظرت فإذا الذي يكفيني من ذلك رغيفان وطمران','329','0','0');
INSERT INTO [t8216] VALUES (330,'حق على الله أن لا يرفع شيئا في الدنيا إلا وضعه','330','0','0');
INSERT INTO [t8216] VALUES (331,'للدنيا أهون على الله من هذه على أهلها','331','0','0');
INSERT INTO [t8216] VALUES (332,'من كانت نيته الآخرة جمع الله له شمله، وجعل غناه في قلبه، وأتته الدنيا وهي راغمة، ومن كانت','332','0','0');
INSERT INTO [t8216] VALUES (333,'من كانت نيته طلب الآخرة جعل الله غناه في قلبه، وجمع له شمله، وأتته الدنيا وهي راغمة، ومن','333','0','0');
INSERT INTO [t8216] VALUES (334,'من كانت الدنيا همه وسدمه، لها يشخص، ولها ينصب، وإياها ينوي، جعل الله عز وجل الفقر بين','334','0','0');
INSERT INTO [t8216] VALUES (335,'ذكر الدنيا فقال: إنها ملعونة، ملعون ما فيها، إلا ما كان لله عز وجل، أو ما ابتغي به وجهه','335','0','0');
INSERT INTO [t8216] VALUES (336,'فلو كانت الدنيا تعدل عند الله عز وجل شيئا ما أعطى كافرا منها قدر جناح','336','0','0');
INSERT INTO [t8216] VALUES (337,'ما ذئبان جائعان ضاريان في غنم تفرقت، أحدهما في أولها، والآخر في آخرها بأسرع فيها فسادا من','337','0','0');
INSERT INTO [t8216] VALUES (338,'من أحب الدنيا وسرته خرج خوف الآخرة من قلبه، ومن ازداد علما ثم ازداد على الدنيا حرصا لم','338','0','0');
INSERT INTO [t8216] VALUES (339,'يجاء بصاحب الدنيا الذي قد أطاع الله فيها وماله بين يديه، كلما تكفأ به الصراط قال له ماله:','339','0','0');
INSERT INTO [t8216] VALUES (340,'الدنيا موقوفة ما بين السماء والأرض كالشن البالي، تنادي ربها منذ يوم خلقها إلى يوم يفنيها:','340','0','0');
INSERT INTO [t8216] VALUES (341,'شركم عملا عالم يختار الدنيا، ود لو أن الناس كلهم كانوا في عمله مثله، ما أحب إلى عبيد','341','0','0');
INSERT INTO [t8216] VALUES (342,'أما ترضى أن تكون لهم الدنيا ولنا الآخرة؟ ما أنا والدنيا إلا كمثل رجل مر في يوم صائف','342','0','0');
INSERT INTO [t8216] VALUES (343,'من عمل لله تعالى كفاه الله الناس، ومن عمل لآخرته كفاه الله دنياه، ومن أصلح سريرته أصلح','343','0','0');
INSERT INTO [t8216] VALUES (344,'الزهد: الرضا','344','0','0');
INSERT INTO [t8216] VALUES (345,'الورع أول الزهد، والقناعة أول الرضا. قال أحمد: وقلت لأبي هشام بن عبد الملك المغازلي: أي','345','0','0');
INSERT INTO [t8216] VALUES (346,'عزم له على الزهد فيها، فترك ذلك أجمع، وأقبل على العبادة والتنسك','346','0','0');
INSERT INTO [t8216] VALUES (347,'استرحنا من صحبة الملوك، نمد أرجلنا إذا شئنا، ونتكئ إذا شئنا، ونعمل ما','347','0','0');
INSERT INTO [t8216] VALUES (348,'من ذا الذي يبني على موج البحر دارا، تلكم الدنيا فلا تتخذوها قرارا','348','0','0');
INSERT INTO [t8216] VALUES (349,'الدنيا مختمرة متنكرة، حتى إذا هبطت ديار الهالكين كشفت قناعها وانحسرت، فانتصبها العاملون','349','0','0');
INSERT INTO [t8216] VALUES (350,'لم يبق من الدنيا فيما مضى منها إلا كما بقي من يومكم هذا فيما مضى منه','350','0','0');
INSERT INTO [t8216] VALUES (351,'مثل هذه الدنيا مثل ثوب شق من أوله إلى آخره، فبقي متعلقا بخيط في آخره، فيوشك ذلك الخيط أن','351','0','0');
INSERT INTO [t8216] VALUES (352,'أكثر ما أخاف عليكم ما يخرج الله لكم من بركات الأرض. فقيل: ما بركات الأرض؟ قال: زهرة','352','0','0');
INSERT INTO [t8216] VALUES (353,'معاوية عاش أربعين سنة، عشرين أميرا، وعشرين خليفة، هذه جثوته عليها ثمامة نابتة، لله در ابن','353','0','0');
INSERT INTO [t8216] VALUES (354,'عشرين سنة أميرا، وعشرين سنة خليفة، ثم صرت إلى هذا، هل الدهر والأيام إلا ما ترى؟ رزية مال،','354','0','0');
INSERT INTO [t8216] VALUES (355,'من علامة الزاهدين في الدنيا تركهم كل خليط لا يريد ما يريدون','355','0','0');
INSERT INTO [t8216] VALUES (356,'الدخول في الدنيا هين، لكن التخلص منها شديد','356','0','0');
INSERT INTO [t8216] VALUES (357,'عجبت لمن يعلم أن الموت حق كيف يفرح؟ وعجبت لمن يعلم أن النار حق كيف يضحك؟ وعجبت لمن يرى','357','0','0');
INSERT INTO [t8216] VALUES (358,'إذا كان القدر حقا فالحرص باطل، وإذا كان الغدر في الناس طباعا فالثقة بكل أحد عجز، وإذا كان','358','0','0');
INSERT INTO [t8216] VALUES (359,'كيف وجدت الدنيا ولذتها؟ قال: كرجل دخل بيتا له بابان، فقام في وسط البيت هنية، ثم خرج من','359','0','0');
INSERT INTO [t8216] VALUES (360,'لا تؤخر عمل اليوم لغد فتدارك عليك الأعمال فتضيع، فإن للناس نفرة عن سلطانهم، أعوذ بالله أن','360','0','0');
INSERT INTO [t8216] VALUES (361,'حتى تبعر فيه الغنم','361','0','0');
INSERT INTO [t8216] VALUES (362,'ما رأيت أزهد في الدنيا من علي بن أبي طالب رضي الله عنه','362','0','0');
INSERT INTO [t8216] VALUES (363,'وسم الدنيا بالوحشة، ليكون أنس المطيعين به','363','0','0');
INSERT INTO [t8216] VALUES (364,'خلقتم لأمر، إن كنتم تصدقون به إنكم لحمقى، وإن كنتم تكذبون به إنكم لهلكى، إنما خلقتم','364','0','0');
INSERT INTO [t8216] VALUES (365,'لا تغرنكم الدنيا والمهلة فيها، فعن قليل عنها تنقلون، وإلى غيرها ترتحلون، فالله الله عباد','365','0','0');
INSERT INTO [t8216] VALUES (366,'لسحق ردائي هذا أحب إلى مما مضى من الدنيا، ولما بقي منها أشبه بما مضى من الماء','366','0','0');
INSERT INTO [t8216] VALUES (367,'أمسينا مساء وليس في العرب أحد إلا وهو يرغب إلينا، وهو يرهب منا، فأصبحنا صباحا وليس في','367','0','0');
INSERT INTO [t8216] VALUES (368,'سنيات بلاء، وسنيات رخاء، يوم فيوم، وليلة فليلة: يولد مولود، ويهلك هالك، فلولا المولود باد','368','0','0');
INSERT INTO [t8216] VALUES (369,'أرى أشقياء الناس لا يسأمونها ... على أنهم فيها عراة وجوع','369','0','0');
INSERT INTO [t8216] VALUES (370,'عجبت ممن يحزن على نقصان ماله، ولا يحزن على فناء عمره، وعجبت ممن الدنيا مولية عنه والآخرة','370','0','0');
INSERT INTO [t8216] VALUES (371,'الدنيا ليست بدار قراركم، دار كتب الله عليها الفناء، وكتب على أهلها منها الظعن، فكم عامر','371','0','0');
INSERT INTO [t8216] VALUES (372,'فرحت ببلوغ أملك، وإنما بلغته بانقضاء مدة أجلك، ثم سوفت بعملك، كأن منفعته لغيرك أنشدني','372','0','0');
INSERT INTO [t8216] VALUES (373,'الدنيا سبعة آلاف سنة، فقد مضى منها ستة آلاف وست مائة أو خمس مائة ونيف منذ بعث النبي صلى','373','0','0');
INSERT INTO [t8216] VALUES (374,'الدنيا سبعة آلاف سنة، لأعبدن الله تعالى عبادة لعلي أنجو من يوم كان مقداره خمسين ألف سنة،','374','0','0');
INSERT INTO [t8216] VALUES (375,'أفيعجز أحدكم أن يعمل سبع يوم حتى يأمن ذلك اليوم؟','375','0','0');
INSERT INTO [t8216] VALUES (376,'أهبط آدم من الجنة إليها عقوبة، يحسب من لا يدري ما ثواب الله أنها ثواب، ويحسب من لا يدري','376','0','0');
INSERT INTO [t8216] VALUES (377,'ما الدنيا كلها من أولها إلى آخرها، إلا كرجل نام نومة فرأى في منامه ما يحب، ثم انتبه','377','0','0');
INSERT INTO [t8216] VALUES (378,'الدنيا جمعة من جمع الآخرة','378','0','0');
INSERT INTO [t8216] VALUES (379,'أعوذ بك من دنيا تمنع خير الآخرة','379','0','0');
INSERT INTO [t8216] VALUES (380,'آذنت زينة الحياة ببين ... وانقضاء من أهلها وفناء','380','0','0');
INSERT INTO [t8216] VALUES (381,'من أخذ شيئا من الدنيا بمعصية الله فقد أخذ ثمنا قليلا','381','0','0');
INSERT INTO [t8216] VALUES (382,'الدنيا غرارة، أهلكت من كان قبلكم من الأمم السالفة، ألا وهي مهلكة من بقي، ألا فلا تغرنكم','382','0','0');
INSERT INTO [t8216] VALUES (383,'الدنيا حلم، والآخرة يقظة، والمتوسط بينهما الموت، ونحن في أضغاث،','383','0','0');
INSERT INTO [t8216] VALUES (384,'نبكي بالدين للدنيا أنشدنا أبو سعيد المديني لعبد الله بن عروة: [البحر البسيط] يبكون بالدين','384','0','0');
INSERT INTO [t8216] VALUES (385,'الغرة بالله أن يصر العبد في معصية الله، ويتمنى في ذلك على الله المغفرة، والغرة في الحياة','385','0','0');
INSERT INTO [t8216] VALUES (386,'من سأل الله الدنيا، فإنما يسأله طول الوقوف','386','0','0');
INSERT INTO [t8216] VALUES (387,'الدنيا مشغلة، اللهم لا تشغلني بها، ولا تعطني منها شيئا','387','0','0');
INSERT INTO [t8216] VALUES (388,'ما في الدنيا شيء يسرك إلا قد التصق به شيء يسوؤك','388','0','0');
INSERT INTO [t8216] VALUES (389,'لا تشغلوا قلوبكم بذكر الدنيا','389','0','0');
INSERT INTO [t8216] VALUES (390,'العبد يكون من أهل الآخرة ثم يرجع إلى الدنيا','390','0','0');
INSERT INTO [t8216] VALUES (391,'رأس الزهادة؟ قال: جمع الأشياء بحقها، ووضعها في حقها','391','0','0');
INSERT INTO [t8216] VALUES (392,'من علامة المريدين الزهد في الدنيا ترك كل خليط لا يريد ما يريدون','392','0','0');
INSERT INTO [t8216] VALUES (393,'أوصيك بتقوى الله، والعمل بما علمك الله، والمراقبة حيث لا يراك أحد إلا الله، والاستعداد','393','0','0');
INSERT INTO [t8216] VALUES (394,'بيعوا دنياكم بآخرتكم تربحونهما - والله جميعا، ولا تبيعوا آخرتكم بدنياكم فتخسرونهما والله','394','0','0');
INSERT INTO [t8216] VALUES (395,'عظمه في عيني صغر الدنيا في عينه','395','0','0');
INSERT INTO [t8216] VALUES (396,'أهل الدنيا فيها على وجل، لم تمض بهم نية، ولم تطمئن بهم دار، حتى يأتي أمر الله وهم على','396','0','0');
INSERT INTO [t8216] VALUES (397,'الدنيا عدوة أولياء الله، وعدوة أعداء الله، أما أولياء الله فغمتهم، وأما أعداء الله','397','0','0');
INSERT INTO [t8216] VALUES (398,'يعظمه في عيني صغر الدنيا في عينه، كان خارجا من سلطان بطنه، فلا يتشهى ما لا يجد، ولا يكثر','398','0','0');
INSERT INTO [t8216] VALUES (399,'إذا أقبلت على عبدي بوجهي، زويت عنه الدنيا بحذافيرها','399','0','0');
INSERT INTO [t8216] VALUES (400,'لا تخرج نفس ابن آدم من الدنيا إلا بحسرات ثلاث: أنه لم يشبع مما جمع، ولم يدرك ما أمل، ولم','400','0','0');
INSERT INTO [t8216] VALUES (401,'نال الغنى من عتق من رق الدنيا','401','0','0');
INSERT INTO [t8216] VALUES (402,'الدنيا والدة الموت، وناقضة للمبرم، ومرتجعة للعطية، وكل من فيها يجري على ما لا يدري، وكل','402','0','0');
INSERT INTO [t8216] VALUES (403,'من أذاقته الدنيا حلاوتها لميله إليها، جرعته الآخرة مرارتها لتجافيه عنها أنشدني الحسين بن','403','0','0');
INSERT INTO [t8216] VALUES (404,'ما طلعت شمس قط إلا وبجنبتيها ملكان يناديان إنهما ليسمعان من على ظهر الأرض غير الثقلين: يا','404','0','0');
INSERT INTO [t8216] VALUES (405,'ما من صباح يصبح العباد إلا صارخ يصرخ: أيها الخلائق سبحوا القدوس','405','0','0');
INSERT INTO [t8216] VALUES (406,'ما من يوم ولا ليلة ولا ساعة إلا ولله تعالى فيه صدقة يمن بها عمن يشاء من عباده، وما من','406','0','0');
INSERT INTO [t8216] VALUES (407,'عود لسانك: اللهم اغفر لي، فإن لله عز وجل ساعات لا يرد فيهم سائل','407','0','0');
INSERT INTO [t8216] VALUES (408,'والموت يأتي بغتة، فمن زرع خيرا يوشك أن يحصد رغبة، ومن زرع شرا يوشك أن يحصد ندامة، ولكل','408','0','0');
INSERT INTO [t8216] VALUES (409,'ليس من يوم إلا وهو ينادي: أنا يوم جديد، وأنا عليكم شهيد. ابن آدم إني لم أقر بك أبدا، فاتق','409','0','0');
INSERT INTO [t8216] VALUES (410,'ما من ليلة إلا تقول: ابن آدم، أحدث في خيرا فإني لن أعود إليك','410','0','0');
INSERT INTO [t8216] VALUES (411,'ما مضى يوم من الدنيا إلا يقول عند مضيه: أيها الناس أنا الذي قدمت عليكم جديدا، وقد حان مني','411','0','0');
INSERT INTO [t8216] VALUES (412,'ما من يوم إلا يقول: ابن آدم قد دخلت عليك اليوم، ولن أرجع إليك بعد اليوم أبدا، فانظر ماذا','412','0','0');
INSERT INTO [t8216] VALUES (413,'الدهر ثلاثة أيام: أمس لك خلت عظته، واليوم الذي أنت فيه لك، وغد لا تدري ما','413','0','0');
INSERT INTO [t8216] VALUES (414,'أمس أجل، واليوم عمل، وغد أمل','414','0','0');
INSERT INTO [t8216] VALUES (415,'الأيام ثلاثة: معهود، ومشهود، وموعود، فالمعهود أمس، والمشهود اليوم، والموعود','415','0','0');
INSERT INTO [t8216] VALUES (416,'أمس أجل، واليوم عمل، وغد أمل','416','0','0');
INSERT INTO [t8216] VALUES (417,'الدنيا، قال: ميراث. قال: والأيام؟ قال: دول قال: والدهر؟ قال: أطباق، والموت بكل سبيل،','417','0','0');
INSERT INTO [t8216] VALUES (418,'الأيام ثلاثة: فأمس حكيم مؤدب أبقى فيك موعظة، وترك فيك عبرة، واليوم ضيف كان عنك طويل','418','0','0');
INSERT INTO [t8216] VALUES (419,'أمس مذموم، ويومك غير محمود، وغد غير مأمون','419','0','0');
INSERT INTO [t8216] VALUES (420,'ثلاثة أيام، فقد مضى أمس بما فيه، وغدا أمل لعلك لا تدركه، ويومك إن كنت من أهل غد، فإن غدا','420','0','0');
INSERT INTO [t8216] VALUES (421,'الأيام ثلاثة: فأما أمس فقد انقضى عن الملوك نعمته، وذهبت عني شدته، وإني وإياهم من غد لعلى','421','0','0');
INSERT INTO [t8216] VALUES (422,'ليس من يوم يقدم إلا وهو عارية لليوم الذي بعده، فاليوم الجديد يقتضي عاريته، فإن كان حسنا','422','0','0');
INSERT INTO [t8216] VALUES (423,'أمس شاهد فجعك بنفسه وخلف في يديك حكمته، وإن اليوم يوم كان طويل الغيبة، وهو سريع ظعنه، وإن','423','0','0');
INSERT INTO [t8216] VALUES (424,'الليل والنهار خزانتان، فانظروا ما تضعون فيهما، وكان يقول: اعملوا الليل لما خلق له،','424','0','0');
INSERT INTO [t8216] VALUES (425,'ليس يوم يأتي من أيام الدنيا إلا يتكلم يقول: يا أيها الناس إني يوم جديد، وأنا على من يعمل','425','0','0');
INSERT INTO [t8216] VALUES (426,'اختلاف الليل والنهار غنية الأكياس','426','0','0');
INSERT INTO [t8216] VALUES (427,'ابن آدم طأ الأرض بقدمك، فإنها عن قليل تكون قبرك، ابن آدم إنما أنت أيام، فكلما ذهب يوم ذهب','427','0','0');
INSERT INTO [t8216] VALUES (428,'ما من أحد إلا وفي عقله نقص عن حلمه وعلمه، وذلك أنه إذا أتته الدنيا بزيادة في مال ظل فرحا','428','0','0');
INSERT INTO [t8216] VALUES (429,'اليوم ضيفك، فالضيف مرتحل، يحمدك أو يذمك، وكذلك ليلتك','429','0','0');
INSERT INTO [t8216] VALUES (430,'يوضعك الليل إلى النهار، والنهار إلى الليل، حتى يسلمانك إلى الآخرة، فمن أعظم منك يا ابن','430','0','0');
INSERT INTO [t8216] VALUES (431,'الأيام ثلاثة: فأمس حكيم مودع، ترك فيك عظة حكمته، وأبقى فيك عبرته وعظته، ويومك صديق مودع،','431','0','0');
INSERT INTO [t8216] VALUES (432,'لنا من كر الليل والنهار ليوم سوء، أو غير ذلك، ثم بكى','432','0','0');
INSERT INTO [t8216] VALUES (433,'ذهب من عمري يوم كامل، وإذا أصبح قال: ذهبت ليلة كاملة من عمري، فلما احتضر بكى، وقال: قد','433','0','0');
INSERT INTO [t8216] VALUES (434,'مضت الليلة من عمري ولم أكتسب فيها لنفسي شيئا، ومضى اليوم أيضا ولا أراني اكتسبت فيه شيئا،','434','0','0');
INSERT INTO [t8216] VALUES (435,'هذا يميتني','435','0','0');
INSERT INTO [t8216] VALUES (436,'اعتورك الليل والنهار، يدفعك الليل إلى النهار، ويدفعك النهار إلى الليل، حتى يأتيك','436','0','0');
INSERT INTO [t8216] VALUES (437,'لكأنك قد رزقت الموت، وعاينت ما بعده بتصريف الليل والنهار، فإنهما سريعان في طي الأجل ونقص','437','0','0');
INSERT INTO [t8216] VALUES (438,'لن يلبث القرناء أن يتفرقوا ... ليل يكر عليهم ونهار','438','0','0');
INSERT INTO [t8216] VALUES (439,'إن يسلم المرء من قتل ومن هرم ... وملي العيش أبلاه الجديدان','439','0','0');
INSERT INTO [t8216] VALUES (440,'إنا لنفرح بالأيام نقطعها ... وكل يوم مضى يدني من الأجل','440','0','0');
INSERT INTO [t8216] VALUES (441,'الليل والنهار مراحل، ينزلهما الناس مرحلة مرحلة، حتى ينتهي بهم ذلك إلى آخر سفرهم، فإن','441','0','0');
INSERT INTO [t8216] VALUES (442,'أحيط بك من كل جانب، واعلم أنه يسار بك في كل يوم وليلة، فاحذر الله والمقام بين يديه، وأن','442','0','0');
INSERT INTO [t8216] VALUES (443,'ابن آدم إنك بيومك ولست في غدك، فكن في يومك، فإن يكن غد لك كنت فيه كما كنت في هذا اليوم،','443','0','0');
INSERT INTO [t8216] VALUES (444,'إلى كم يا ليل ويا نهار تحطان من أجلي وأنا غافل عما يراد بي؟ إنا لله، إنا لله، قال: وهو','444','0','0');
INSERT INTO [t8216] VALUES (445,'منع البقاء تقلب الشمس ... وطلوعها من حيث لا تمسي وطلوعها حمراء إذ طلعت ... ومغيبها صفراء','445','0','0');
INSERT INTO [t8216] VALUES (446,'أشاب الصغير وأفنى الكبير ... مر النهار وكر العشي إذا ليلة هدمت يومها ... أتى بعد ذلك يوم','446','0','0');
INSERT INTO [t8216] VALUES (447,'طوى أملي طلوع الشمس وغروبها، فما من حركة تسمع، ولا من قدم توضع إلا ظننت أن الموت في','447','0','0');
INSERT INTO [t8216] VALUES (448,'لئن غفلتم إن لله عبادا لا يغفلون عن طاعته في هذا الليل والنهار','448','0','0');
INSERT INTO [t8216] VALUES (449,'من كان الليل والنهار مطيتيه سارا به وإن لم يسر وأنشدني محمود بن الحسن قوله: [البحر','449','0','0');
INSERT INTO [t8216] VALUES (450,'جز دهرك بيومك','450','0','0');
INSERT INTO [t8216] VALUES (451,'اجعل غدا كيومك، واجعل يومك كما غبر من عمرك، وسل الله الخيرة في جميع أمرك، فهو المعطي، وهو','451','0','0');
INSERT INTO [t8216] VALUES (452,'ما من يوم أخرجه الله لأهل الدنيا إلا نادى: ابن آدم اغتنمني، لعله لا يوم لك بعدي، ولا ليلة','452','0','0');
INSERT INTO [t8216] VALUES (453,'كل يوم بعيشة المؤمن غنيمة','453','0','0');
INSERT INTO [t8216] VALUES (454,'اليوم أقرب إلى الموت يوم نعيت، ولم يزل الليل والنهار سريعين في نقص الأعمار، وتقريب','454','0','0');
INSERT INTO [t8216] VALUES (455,'المغبون من غبن خير الليل والنهار، والمحروم من حرم خيرهما، إنما جعلا سبيلا للمؤمنين إلى','455','0','0');
INSERT INTO [t8216] VALUES (456,'نفسا تحب الدعة، وقلبا يألف اللذات، وهمة تستثقل الطاعة، وقد رهبت نفسي الآفات، وحذرت قلبي','456','0','0');
INSERT INTO [t8216] VALUES (457,'فأحسن ضيافة يومك الذي أنت فيه، وزوده منك برا قبل شخوصه عنك، وأشفق من طلوع التنغيص عليك من','457','0','0');
INSERT INTO [t8216] VALUES (458,'أيقظان أنت اليوم أم أنت نائم ... وكيف يطيق النوم حيران هائم فلو كنت يقظان الغداة لحرقت','458','0','0');
INSERT INTO [t8216] VALUES (459,'الدنيا ثلاثة أيام: أما أمس فقد ذهب بما فيه، وأما غدا فلعلك لا تدركه، واليوم فاعمل','459','0','0');
INSERT INTO [t8216] VALUES (460,'الدنيا ثلاثة أيام، مضى أمس بما فيه، وغدا لعلك لا تدركه، فانظر ما أنت عامل في','460','0','0');
INSERT INTO [t8216] VALUES (461,'لا تحمل هم سنة على يوم، كفى يومك بما فيه، فإن تكن السنة من عمرك يأتك الله فيها برزقك،','461','0','0');
INSERT INTO [t8216] VALUES (462,'سئمت لتكرار الليالي والأيام ودورها علي، هل من شيء يدفع عني سآمة ذلك، أو يسل عني بعض ما','462','0','0');
INSERT INTO [t8216] VALUES (463,'ما من يوم يخرج من الدنيا إلا قال: الحمد لله الذي أخرجني منها، ثم لا يردني إليها وقال','463','0','0');
INSERT INTO [t8216] VALUES (464,'إذا أحب العالم الدنيا نزعت لذة مناجاتي من قلبه','464','0','0');
INSERT INTO [t8216] VALUES (465,'حرام على قلوبكم أن تجد طعم الإيمان حتى تزهدوا في الدنيا','465','0','0');
INSERT INTO [t8216] VALUES (466,'أصبحتم جزرا للموت يأخذكم ... كما البهائم في الدنيا لكم جزر وليس يزجركم ما توعظون به ...','466','0','0');
INSERT INTO [t8216] VALUES (467,'ويلكم علماء السوء من أجل دنيا دنية، وشهوة ردية، تفرطون في ملك جنة علية، وتنسون هول يوم','467','0','0');
INSERT INTO [t8216] VALUES (468,'تعلمون لدنيا صغيرة، وتتركون الآخرة الكبيرة، وعلى كلكم يمر الموت','468','0','0');
INSERT INTO [t8216] VALUES (469,'ما أصبح في الدنيا ما يغر ذا قلب، وكلكم ذو قلب، ولكن ما يغر ذا قلب حي','469','0','0');
INSERT INTO [t8216] VALUES (470,'الخاسر من عمر دنياه بخراب آخرته، والخاسر من استصلح معاشه بفساد دينه، والمغبون حظا من رضي','470','0','0');
INSERT INTO [t8216] VALUES (471,'خبر الدنيا أشد من مختبرها، ومختبر الآخرة أشد من خبرها','471','0','0');
INSERT INTO [t8216] VALUES (472,'أنت فيهم ضيف عند أهل لا تزايلهم، ولا يلهينك مساكن، إنما أنت فيها عمرى عن مساكن أنت مخلد','472','0','0');
INSERT INTO [t8216] VALUES (473,'لو جعلت لي الدنيا كلها من أولها إلى آخرها حلالا لقذرتها ولم أردها','473','0','0');
INSERT INTO [t8216] VALUES (474,'ما لقلوب أحبائي وما للغم بالدنيا؟ إن الغم بها يمص حلاوة مناجاتي من قلوبهم مصا، يا داود لا','474','0','0');
INSERT INTO [t8216] VALUES (475,'من دخل قلبه صافي خالص دين الله شغله عما سواه، يا جابر ما الدنيا؟ وما عسى أن تكون؟ هل هو','475','0','0');
INSERT INTO [t8216] VALUES (476,'اعمل للدنيا على قدر مكثك فيها، واعمل للآخرة على قدر مكثك فيها','476','0','0');
INSERT INTO [t8216] VALUES (477,'من كان صغير الدنيا في عينه أعظم من كبير الآخرة كيف يرجو أن يصنع له في دنياه','477','0','0');
INSERT INTO [t8216] VALUES (478,'يسلم من الدنيا من أخذ منها لها، ثم خرج منه، وحوسب عليه، ومن أخذ منها لغيرها قدم عليه،','478','0','0');
INSERT INTO [t8216] VALUES (479,'الدنيا غموم وهموم، فإذا رأى أحدكم منها سرورا فهو ربح أنشدني أحمد بن موسى البصري قوله:','479','0','0');
INSERT INTO [t8216] VALUES (480,'ارزقني الرغبة في الآخرة حتى أعرف صدق ذلك في قلبي بالزهادة مني في دنياي. اللهم ارزقني بصرا','480','0','0');
INSERT INTO [t8216] VALUES (481,'كل شيء فاتك من الدنيا غنيمة قال: وذكر سعيد بن أبي الحسن الدنيا، فقال الحسن: يا سعيد سهوت','481','0','0');
INSERT INTO [t8216] VALUES (482,'أعظم هؤلاء الدنيا','482','0','0');
INSERT INTO [t8216] VALUES (483,'لا تجعل بيني وبينك عالما قد سكن قلبه حب الدنيا، إن أهون ما أعاقبهم به أن أنزع حب مناجاتي','483','0','0');
INSERT INTO [t8216] VALUES (484,'من كانت الدنيا همه جعل الله فقره في قلبه، وشتت عليه أمره، ولم يأته منها إلا ما كتب له،','484','0','0');
INSERT INTO [t8216] VALUES (485,'حزن الدنيا للدنيا يذهب بهم الآخرة، وفرح الدنيا للدنيا يذهب بحلاوة','485','0','0');
INSERT INTO [t8216] VALUES (486,'تعملون للدنيا وأنتم ترزقون فيها بغير العمل، ولا تعملون للآخرة وأنتم لا ترزقون فيها إلا','486','0','0');
INSERT INTO [t8216] VALUES (487,'خير الدنيا لكم ما لم تبتلوا به منها، فإذا ابتليتم بها فخيرها لكم ما خرج عن أيديكم','487','0','0');
INSERT INTO [t8216] VALUES (488,'أصبحتم في دار مذمومة لأهلها، خلقت فتنة، وضرب لها أجل، إذا انتهت إليه تنفذ، فهي دار قلعة،','488','0','0');
INSERT INTO [t8216] VALUES (489,'لا تعلق قلبك بالدنيا فتعلقه بشر معلق، قطع حبالها، وغلق أبوابها، حسبك أيها المرء ما بلغك','489','0','0');
INSERT INTO [t8216] VALUES (490,'المعمور من هذه الدنيا قد ارتحل، وكأن المغفول من الآخرة قد أناخ بأهله، فثم فضع','490','0','0');
INSERT INTO [t8216] VALUES (491,'هذا أركون الدنيا، إليها خرج، وإياها سأل، لا أشركه في شيء منها، ولا حجرا أضعه تحت رأسي،','491','0','0');
INSERT INTO [t8216] VALUES (492,'هذا لك مع الدنيا، لا حاجة لي فيه','492','0','0');
INSERT INTO [t8216] VALUES (493,'ما أنا لكم بحامد، ولا لهم بغادر، أنتم أصحاب دنيا تنافستموها بينكم، تهافتون في النار تهافت','493','0','0');
INSERT INTO [t8216] VALUES (494,'الزهد على ثلاثة وجوه: واحد أن يخلص العمل لله عز وجل والقول، ولا يراد بشيء منه الدنيا،','494','0','0');
INSERT INTO [t8216] VALUES (495,'إذا كان يوم القيامة صارت أمتي ثلاث فرق: فرقة يعبدون الله عز وجل للدنيا، وفرقة يعبدونه','495','0','0');
INSERT INTO [t8216] VALUES (496,'مثلكم ومثلها كمثل سفر سلكوا طريقا، فكأنهم قد قطعوه، أو أفضوا إلى علم فكأنهم قد بلغوه، وكم','496','0','0');
INSERT INTO [t8216] VALUES (497,'أبغضوا الدنيا يحببكم الله','497','0','0');
INSERT INTO [t8216] VALUES (498,'حب الدنيا رأس كل خطيئة، والنساء حبالة الشيطان، والخمر داعية كل شر','498','0','0');
INSERT INTO [t8216] VALUES (499,'من صبر على الأذى، وترك الشهوات، وأكل الخبز من حلاله، فقد أخذ بأصل','499','0','0');
INSERT INTO [t8216] VALUES (500,'من أدنى الزهد أن يقعد أحدكم في منزله، فإن كان قعوده لله، وإلا خرج، ويخرج، فإن كان خروجه','500','0','0');
INSERT INTO [t8216] VALUES (501,'كنا إذا أسلمنا أقبلنا إلى الآخرة، وتركنا الدنيا لأهل الشرك، وإن الناس اليوم أقبلوا على','501','0','0');
INSERT INTO [t8216] VALUES (502,'الناس ثلاثة: زاهد، وصابر، وراغب، فأما الزاهد: فأصبح قد خرجت الأفراح والأحزان من صدره عن','502','0','0');
INSERT INTO [t8216] VALUES (503,'دخولك على أهل السعة مسخطة','503','0','0');
INSERT INTO [t8216] VALUES (504,'ما بسطت الدنيا لأحد إلا اغترارا أنشدني الحسين بن عبد الرحمن: [البحر الوافر] كفلت لطالب','504','0','0');
INSERT INTO [t8216] VALUES (505,'ما هو بالتقشف، ولا بخشونة المطعم، ولكنه طلق النفس عن محبوب الشهوة','505','0','0');
INSERT INTO [t8216] VALUES (506,'لقد رابني من أهل يثرب أنهم ... يهمهم تقويمنا وهم عصل وذموا لنا الدنيا وهم يرضعونها ...','506','0','0');
INSERT INTO [t8216] VALUES (507,'لو تعلمون ما أعلم لبكيتم كثيرا، ولضحكتم قليلا، ولهانت عليكم الدنيا، ولآثرتم الآخرة ثم قال','507','0','0');
INSERT INTO [t8216] VALUES (508,'رأيته عند أبويه، وما فتى من فتيان قريش مثله، يكرمانه وينعمانه، فخرج من ذلك ابتغاء مرضاة','508','0','0');
INSERT INTO [t8216] VALUES (509,'حتى متى تسقى النفوس بكأسها ... ريب المنون وأنت لاه ترتع أحلام نوم أو كظل زائل ... إن','509','0','0');
INSERT INTO [t8216] VALUES (510,'طالبان يطلبان، فطالب الآخرة مدرك بما طلب، لا فوت به عليه، وطالب الدنيا عسى أن يصيب منها','510','0','0');
INSERT INTO [t8216] VALUES (511,'وكيف تقر بالدنيا عين من عرفها؟ قال: ثم بكى، ويقول: خلف الماضين، وبقية المتقدمين، رحلوا','511','0','0');
INSERT INTO [t8216] VALUES (512,'فبينا نسوس الناس والأمر أمرنا ... إذا نحن فيهم سوقة نتنصف','512','0','0');
INSERT INTO [t8216] VALUES (513,'ينبغي للعالم أن يتكرم عما حرم الله عليه، ويرفع نفسه عن الدنيا، فلا تكون منه على بال وسئل','513','0','0');
INSERT INTO [t8216] VALUES (514,'حب الدنيا في القلب، والذنوب قد احتوشته، فمتى يصل الخير إليه؟','514','0','0');
INSERT INTO [t8216] VALUES (515,'أرى الدنيا لمن هي في يديه ... عذابا كلما كثرت لديه تهين المكرمين لها بصغر ... وتكرم كل من','515','0','0');
INSERT INTO [t8216] VALUES (516,'أوحي إلى الدنيا: من خدمك فأتعبيه، ومن خدمني فاخدميه','516','0','0');
INSERT INTO [t8216] VALUES (517,'لو كنت باليوم العظيم تعنى لكانت الدنيا عليك سجنا ولم تكن بالعيش مطمئنا أما علمت يا ضعيف','517','0','0');
INSERT INTO [t8216] VALUES (518,'كل ما أصبت من الدنيا تريد به الدنيا فهو مذموم، وكل ما أصبت فيها تريد به الآخرة فليس','518','0','0');
INSERT INTO [t8216] VALUES (519,'كانوا يطلبون الدنيا، فإذا بلغوا الأربعين طلبوا الآخرة فحدثت به المعافى بن عمران فأعجبه،','519','0','0');
INSERT INTO [t8216] VALUES (520,'إذا وقعت العزيمة رحلت الدنيا من القلب، ودرج القلب في ملكوت السماء، وإذا لم تقع العزيمة','520','0','0');
INSERT INTO [t8216] VALUES (521,'تجاف عن الدنيا ما استطعت','521','0','0');
INSERT INTO [t8216] VALUES (522,'الدنيا دار بلاء، فإذا رأى أحدكم فيها رخاء فلينكره','522','0','0');
INSERT INTO [t8216] VALUES (523,'ترك إعمال الفكر في شيء من الدنيا أنشدني الحسين بن عبد الرحمن قال: أنشدني إبراهيم بن داود:','523','0','0');
INSERT INTO [t8216] VALUES (524,'ارضوا بدنيء الدنيا مع سلامة الدين، كما رضي أهل الدنيا بدنيء الدين مع سلامة الدنيا قال','524','0','0');
INSERT INTO [t8216] VALUES (525,'بلوتم الدنيا؟ فما زالت تؤنبكم عسفا، وتسومكم خسفا، في كل يوم لكم فيها شغل جديد وحزن عتيد،','525','0','0');
INSERT INTO [t8216] VALUES (526,'من فرح من قلبه بشيء من الدنيا فقد أخطأ الحكمة، ومن جعل شهوته تحت قدميه يفرق شيطانه من','526','0','0');
INSERT INTO [t8216] VALUES (527,'اعمل لنفسك وبادر، فقد أوتيت من كل جانب، واعول كعويل الأسير المكبل، ولا تجعل بقية عمرك','527','0','0');
INSERT INTO [t8216] VALUES (528,'العاقبة للمتقين، وإنما يجزى كل قوم بما كانوا يعملون، أما بعد، فإني أكتب إليك يا ابن أخ،','528','0','0');
INSERT INTO [t8216] VALUES (529,'أصبحنا في دهر حيرة، تضطرب علينا أمواجه بغلبة الهوى، العالم منا والجاهل، فالعالم منا مفتون','529','0','0');
INSERT INTO [t8216] VALUES (530,'الزاهدون في الدنيا، والراغبون في الآخرة؟ قال: أولئك أهل بدر','530','0','0');
INSERT INTO [t8216] VALUES (531,'الزاهد عمر بن عبد العزيز، أتته الدنيا فاغرة فاها فتركها','531','0','0');
INSERT INTO [t8216] VALUES (532,'الزهد فيما يشغلك عن الله عز وجل وقال بعضهم: الزهد ترك الشهوات','532','0','0');
INSERT INTO [t8216] VALUES (533,'جمع الدنيا، وذهب إلى الآخرة، ضيع نفسه قيل له: إنه كان يفعل ويفعل، وذكروا أبوابا من أبواب','533','0','0');
INSERT INTO [t8216] VALUES (534,'ثلاث من مناقب الكفر: الغفلة عن الله عز وجل، وحب الدنيا، والطيرة','534','0','0');
INSERT INTO [t8216] VALUES (535,'أنتم اليوم على بينة من ربكم، تأمرون بالمعروف، وتنهون عن المنكر، وتجاهدون في سبيل الله،','535','0','0');
INSERT INTO [t8216] VALUES (536,'أقلوا من ذم الدنيا، فإنه من أحب شيئا أكثر ذكره','536','0','0');
INSERT INTO [t8216] VALUES (537,'إذا رأيت الرجل ينافسك في الدنيا فنافسه في الآخرة','537','0','0');
INSERT INTO [t8216] VALUES (538,'إن زهد رجل فلا يجعلن زهده عذابا على الناس','538','0','0');
INSERT INTO [t8216] VALUES (539,'هم الدنيا ظلمة في القلب، وهم الآخرة نور في القلب','539','0','0');
INSERT INTO [t8216] VALUES (540,'للدنيا أمثال تضربها الأيام للأنام، وعلم الزمان لا يحتاج إلى ترجمان، ويحب الدنيا من صمت','540','0','0');
INSERT INTO [t8216] VALUES (541,'الدنيا تبغض إلينا نفسها ونحن نحبها، فكيف لو تحببت إلينا؟','541','0','0');
INSERT INTO [t8216] VALUES (542,'للدنيا أهون على الله عز وجل من جزرة البقل على الملك','542','0','0');
INSERT INTO [t8216] VALUES (543,'دار لا يسلم منها من فيها، ما أخذ أهلها منها لها خرجوا منه، ثم حوسبوا به، وما أخذ أهلها','543','0','0');
INSERT INTO [t8216] VALUES (544,'ومن يحمد الدنيا بعيش يسره ... فسوف لعمري عن قليل يلومها إذا أقبلت كانت على المرء حسرة ...','544','0','0');
INSERT INTO [t8216] VALUES (545,'صغر فلان في عيني لعظم الدنيا في عينه، كان يرد السائل، ويبخل بالنائل','545','0','0');
INSERT INTO [t8216] VALUES (546,'من عرف الدنيا لم يفرح بها برخاء، ولم يحزن على بلوى أنشدني أبو عبد الله الكناني: فتى قالت','546','0','0');
INSERT INTO [t8216] VALUES (547,'مثل هذا الخلق مثل قوم اتخذوا الدنيا دار إقامة، واتخذوا الآخرة لهوا وغرورا ثم قال: اضرب','547','0','0');
INSERT INTO [t8216] VALUES (548,'ترك الفدى، أرى الناس قد اتخذوا الدنيا رأس مال، وعدوا ما جاءهم من الآخرة ربحا، وقد عزمت','548','0','0');
INSERT INTO [t8216] VALUES (549,'لا أريد من الدنيا شيئا، فلا ترزقني منها شيئا','549','0','0');
INSERT INTO [t8216] VALUES (550,'لا ترزقنا دينارا ولا درهما، فأمنا كلنا ما خلا الرجل المكثر','550','0','0');
INSERT INTO [t8216] VALUES (551,'ينبغي للعبد المعني بنفسه أن يميت العاجلة الفانية الزائلة، المنغصة، بالآفات من قلبه، ويذكر','551','0','0');
INSERT INTO [t8216] VALUES (552,'الدنيا تطلب الهارب منها، وتهرب من الطالب لها، فإن أدركت الهارب منها جرحته، وإن أدركت','552','0','0');
INSERT INTO [t8216] VALUES (553,'خباث، كل عيدانك قد مصصناه فوجدناه مرا','553','0','0');
INSERT INTO [t8216] VALUES (554,'من هوان الدنيا على الله عز وجل أن جعل بيته وعرا','554','0','0');
INSERT INTO [t8216] VALUES (555,'الخلق كلهم يسعى في أقل من جناح ذبابة، فقال له رجل: وما أقل من جناح ذبابة؟ قال:','555','0','0');
INSERT INTO [t8216] VALUES (556,'قوما أكرموا الدنيا فصلبتهم على الخشب، فأهينوها، فأهنأ ما تكونون إذا','556','0','0');
INSERT INTO [t8216] VALUES (557,'أخرجني من جوار إبليس إلى جوارك وأنشدني الحسين بن عبد الرحمن: [البحر الطويل] لعمرك ما','557','0','0');
INSERT INTO [t8216] VALUES (558,'لا يكون الرجل زاهدا في الدنيا حتى لا يجزع من ذلها، ولا ينافس أهلها','558','0','0');
INSERT INTO [t8216] VALUES (559,'إذا أحب العالم الدنيا نزعت مناجاتي من قلبه أنشدني أبو عبد الله قوله: رويدا بني الدنيا ألم','559','0','0');
INSERT INTO [t8216] VALUES (560,'لما توعد الدنيا به من شرورها ... يكون بكاء الطفل ساعة يوضع','560','0','0');
INSERT INTO [t8216] VALUES (561,'الدنيا غرارة ترفل بالمطمئن، وتفجع الآمن','561','0','0');
INSERT INTO [t8216] VALUES (562,'ليست لك بدار، أخرج منها همك، وفارقها بعقلك، فبئست الدار هي، يا موسى إني مرصد للظالم حتى','562','0','0');
INSERT INTO [t8216] VALUES (563,'ثمنا قليلا} [البقرة: ٤١] ما الثمن القليل؟ قال: الدنيا بحذافيرها','563','0','0');
