INSERT INTO [t8220] VALUES (1,'عليكم بالصدق؛ فإنه مع البر , وهما في الجنة , وإياكم والكذب فإنه مع الفجور , وهما في النار','1','0','0');
INSERT INTO [t8220] VALUES (2,'اللهم اقسم لنا من خشيتك ما يحول بيننا وبين معاصيك , ومن طاعتك ما تبلغنا به جنتك , ومن','2','0','0');
INSERT INTO [t8220] VALUES (3,'نجا أول هذه الأمة باليقين والزهد , ويهلك آخر هذه الأمة بالبخل والأمل','3','0','0');
INSERT INTO [t8220] VALUES (4,'اليقين على أربع شعب: على غائص الفهم , وغمرة العلم , وزهرة الحكم , وروضة الحلم , فمن فهم','4','0','0');
INSERT INTO [t8220] VALUES (5,'ما نزل في الأرض شيء أقل من اليقين , ولا قسم بين الناس شيء أقل من الحلم','5','0','0');
INSERT INTO [t8220] VALUES (6,'اللهم هب لي إيمانا ويقينا ومعافاة ونية','6','0','0');
INSERT INTO [t8220] VALUES (7,'تعلموا اليقين كما تعلموا القرآن حتى تعرفوه فإني أتعلمه','7','0','0');
INSERT INTO [t8220] VALUES (8,'صاحب تقوى ويقين أفضل وأرجح وأعظم من أمثال الجبال عبادة من المغتربين','8','0','0');
INSERT INTO [t8220] VALUES (9,'ما أخاف على أمتي إلا ضعف اليقين','9','0','0');
INSERT INTO [t8220] VALUES (10,'اليقين على أربع شعب , على تبصرة الفطنة , وتأويل الحكمة , وموعظة العبرة , وسنة الأولين ,','10','0','0');
INSERT INTO [t8220] VALUES (11,'لو أن لابن آدم من اليقين قدر شعيرة مشى على الماء','11','0','0');
INSERT INTO [t8220] VALUES (12,'واليقين يورث الغنى , والغنى يورث الحب , والحب يورث اللقاء','12','0','0');
INSERT INTO [t8220] VALUES (13,'إن الناس لم يؤتوا في هذه الدنيا خيرا من اليقين والعافية فسلوهما الله عز وجل قال الحسن:','13','0','0');
INSERT INTO [t8220] VALUES (14,'من حسن اليقين , وإن لكل عمل كمالا وغاية , وكمال العبادة الورع واليقين','14','0','0');
INSERT INTO [t8220] VALUES (15,'وإيمانا في يقين , ونشاطا في هدى , وبرا في استقامة , وكسبا من حلال','15','0','0');
INSERT INTO [t8220] VALUES (16,'ما أيقن عبد بالجنة والنار حق يقينهما إلا خشع ووجل , وذل واستقام , واقتصر حتى يأتيه','16','0','0');
INSERT INTO [t8220] VALUES (17,'بحسن اليقين وطول التهجد','17','0','0');
INSERT INTO [t8220] VALUES (18,'رأيت جوامع الغنى في التوكل , ورأيت جوامع الشر من القنوط , والغني حق الغنى , من أسكن الله','18','0','0');
INSERT INTO [t8220] VALUES (19,'واعبد ربك حتى يأتيك اليقين} [الحجر: ٩٩] قال: الموت حدثنا عبد الله، قال: وقال بعض الحكماء:','19','0','0');
INSERT INTO [t8220] VALUES (20,'اللهم هب لنا يقينا بك حتى تهون علينا مصيبات الدنيا , وحتى نعلم أنه لا يصيبنا إلا ما كتب','20','0','0');
INSERT INTO [t8220] VALUES (21,'الكرم التقوى , والشرف التواضع , واليقين الغنى','21','0','0');
INSERT INTO [t8220] VALUES (22,'الروح والفرج في اليقين والرضا , وإن الغم والحزن من الشك والسخط وقال يعلى: الروح','22','0','0');
INSERT INTO [t8220] VALUES (23,'أشهدكم أن يقيني , شبكور','23','0','0');
INSERT INTO [t8220] VALUES (24,'اللهم هب لي يقينا تهون به علي مصيبات الدنيا','24','0','0');
INSERT INTO [t8220] VALUES (25,'اللهم إني أسألك إيمانا تباشر به قلبي , ويقينا حتى أعلم أنه لا يمنعني رزقا قسمته لي، ورضا','25','0','0');
INSERT INTO [t8220] VALUES (26,'إني أعبدك ولك أصلي , فاجعل الشفاء من جسدي , واليقين في قلبي والنور في بصري , والشكر من','26','0','0');
INSERT INTO [t8220] VALUES (27,'فحقيقة اليقين الصبر , وحقيقة المخافة الطاعة , وحقيقة المعرفة الإيمان وحقيقة الهدى البصيرة','27','0','0');
INSERT INTO [t8220] VALUES (28,'اللهم إني أسألك إيمانا تباشر به قلبي , ويقينا صادقا حتى أعلم أنه لن يصيبني إلا ما كتبت لي','28','0','0');
INSERT INTO [t8220] VALUES (29,'العمل لا يستطاع إلا باليقين , ومن يضعف يقينه يضعف عمله , قال: وقال لقمان لابنه: يا بني:','29','0','0');
INSERT INTO [t8220] VALUES (30,'كفى بالموت واعظا , وكفى باليقين غنى , وكفى بالعبادة شغلا','30','0','0');
INSERT INTO [t8220] VALUES (31,'اليقين أن لا ترضي الناس بسخط الله , ولا تحمد أحدا على رزق الله , ولا تلم أحدا على ما لم','31','0','0');
INSERT INTO [t8220] VALUES (32,'من علامات المسلم قوة في دين , وحزم في لين , وإيمان في يقين , وحلم في علم , وكيس في رفق ,','32','0','0');
INSERT INTO [t8220] VALUES (33,'من ضعف يقينك أن تكون بما في يدك أوثق منك بما في يد الله عز وجل','33','0','0');
INSERT INTO [t8220] VALUES (34,'أسألك يقين الصادقين , وصدق الموقنين , وعمل الطائعين , وخوف العاملين , وعبادة الخاشعين ,','34','0','0');
INSERT INTO [t8220] VALUES (35,'تعملون في أيام قصار لأيام طوال , في دار زوال لدار مقام , ودار حزن ونصب لدار نعيم وخلد ,','35','0','0');
INSERT INTO [t8220] VALUES (36,'كأنا قوم لا يعقلون وكأنا قوم لا يوقنون','36','0','0');
INSERT INTO [t8220] VALUES (37,'نعت الله عباده الموقنين , أذووا عقول في طلب الدنيا وبله عما خلقتم له؟ , فكما ترجون رحمة','37','0','0');
INSERT INTO [t8220] VALUES (38,'إن أحببت أن تعلم علم اليقين فاجعل بينك وبين شهوات الدنيا حائطا من حديد','38','0','0');
INSERT INTO [t8220] VALUES (39,'بأي شيء تمشي على الماء؟ قال: بالإيمان واليقين , قالوا: فإن آمنا كما آمنت وأيقنا كما أيقنت','39','0','0');
INSERT INTO [t8220] VALUES (40,'كم بين الإيمان واليقين؟ قال: أربع أصابع. قال: بين , قال: اليقين ما رأته عينك , والإيمان','40','0','0');
INSERT INTO [t8220] VALUES (41,'ما رأيت يقينا لا شك فيه أشبه من شك لا يقين فيه من أمرنا هذا','41','0','0');
INSERT INTO [t8220] VALUES (42,'إني ومن خلق السماوات ... الطباق ومن براني أدعو وما تحرك يداي ... إذا دعوت لا ينساني إلا','42','0','0');
