INSERT INTO [t8223] VALUES (1,'الله عز وجل اصطفى كنانة من ولد إسماعيل، واصطفى قريشا من كنانة، واصطفى هاشما من قريش،','1','0','0');
INSERT INTO [t8223] VALUES (2,'أنا أبو القاسم، تسموا باسمي، ولا تكنوا بكنيتي','2','0','0');
INSERT INTO [t8223] VALUES (3,'لم يكن النبي صلى الله عليه وسلم بالطويل، ولا بالقصير، شثن الكفين والقدمين، ضخم الرأس، ضخم','3','0','0');
INSERT INTO [t8223] VALUES (4,'لبث بمكة عشر سنين ينزل عليه القرآن، وبالمدينة عشرا','4','0','0');
INSERT INTO [t8223] VALUES (5,'قام النبي صلى الله عليه وسلم بمكة ثلاث عشرة سنة يوحى إليه، وبالمدينة عشر سنين، ومات وهو','5','0','0');
INSERT INTO [t8223] VALUES (6,'أنزل على رسول الله صلى الله عليه وسلم وهو ابن أربعين، فأقام بمكة ثلاث عشرة، وبالمدينة','6','0','0');
INSERT INTO [t8223] VALUES (7,'توفي النبي صلى الله عليه وسلم وهو ابن ثلاث وستين','7','0','0');
INSERT INTO [t8223] VALUES (8,'أكان وجه النبي صلى الله عليه وسلم مثل السيف؟ قال: لا، بل مثل القمر','8','0','0');
INSERT INTO [t8223] VALUES (9,'بعث رسول الله صلى الله عليه وسلم لأربعين سنة، ومكث بمكة ثلاث عشرة يوحى إليه، ثم أمر','9','0','0');
INSERT INTO [t8223] VALUES (10,'مكث رسول الله صلى الله عليه وسلم بمكة ثلاث عشرة سنة، وتوفي وهو ابن ثلاث','10','0','0');
INSERT INTO [t8223] VALUES (11,'أفلحت الوجوه','11','0','0');
INSERT INTO [t8223] VALUES (12,'ستكون فرقة وفتنة واختلاف، فاكسر سيفك واقطع واترك واجلس في بيتك، ففعلت الذي أمرني به النبي','12','0','0');
INSERT INTO [t8223] VALUES (13,'غط فخذيك فإن الفخذ عورة','13','0','0');
INSERT INTO [t8223] VALUES (14,'أتى النبي صلى الله عليه وسلم بأرنبين، فقال: كلهما وقال لنا موسى: حدثنا وهيب، عن داود، عن','14','0','0');
INSERT INTO [t8223] VALUES (15,'أذكرت اسم الله؟ ، قال: نعم، فأمره بأكلهما وقال لنا موسى: حدثنا ثابت، قال: حدثنا عاصم،','15','0','0');
INSERT INTO [t8223] VALUES (16,'من كان منكم طعم فليمسك بقية يومه، ومن لا فليصم يوم عاشوراء، وأرسل إلى أهل العروض يأمر','16','0','0');
INSERT INTO [t8223] VALUES (17,'لو أن رجلا يخر على وجهه من يوم ولد إلى أن يموت هرما في مرضاة الله لحقره يوم','17','0','0');
INSERT INTO [t8223] VALUES (18,'ما في الناس من نفس مسلمة يقبضها الله تحب أن تعود لها الدنيا وما فيها غير','18','0','0');
INSERT INTO [t8223] VALUES (19,'سموه باسمي، ولا تكنوه بكنيتي قال يونس: فلقد عمر أبي حتى شاب كل شيء من أبي، وما شاب موضع','19','0','0');
INSERT INTO [t8223] VALUES (20,'سماني النبي صلى الله عليه وسلم محمدا قاله لي الصلت بن محمد، عن أبي عوانة، عن هلال الوزان،','20','0','0');
INSERT INTO [t8223] VALUES (21,'اذهب بالبأس رب الناس اشف أنت الشافي لا شفاء إلا شفاؤك شفاء لا يغادر سقما قالت: فما قمت بك','21','0','0');
INSERT INTO [t8223] VALUES (22,'رأيت أرضا ذات نخل، فخرج حاطب وجعفر في البحر قبل النجاشي فولدت أنا في تلك','22','0','0');
INSERT INTO [t8223] VALUES (23,'ما الذي أثنى عليكم الله عز وجل في {فيه رجال يحبون أن يتطهروا} [التوبة: ١٠٨] ؟ قالوا:','23','0','0');
INSERT INTO [t8223] VALUES (24,'أول من تلقانا الشيماء بنت بقيلة الأزدية كما قال رسول الله صلى الله عليه وسلم فتعلقت بها،','24','0','0');
INSERT INTO [t8223] VALUES (25,'اخرج فانظر، أما أنت يا زيد فمولاي وإلي وأحب القوم مني','25','0','0');
INSERT INTO [t8223] VALUES (26,'ابدوا أسلم واسكنوا الشعاب، فقالوا: نخاف أن نتعرب بعد هجرتنا. قال: أنتم مهاجرون حيث','26','0','0');
INSERT INTO [t8223] VALUES (27,'اليهود لم يحسدونا بشيء ما حسدوا بالسلام والتأمين','27','0','0');
INSERT INTO [t8223] VALUES (28,'حسدونا على الجمعة وآمين قال لنا موسى: حدثنا حماد، عن سهيل، عن أبيه، عن عائشة، عن النبي','28','0','0');
INSERT INTO [t8223] VALUES (29,'نهى رسول الله صلى الله عليه وسلم عن ضربة الغائص قال لي أحمد بن الحجاج: عن حاتم بن','29','0','0');
INSERT INTO [t8223] VALUES (30,'إذا استيقظ أخذ السواك. حدثنا موسى، قال: حدثنا محمد بن إبراهيم بن مسلم بن مهران، عن رجل','30','0','0');
INSERT INTO [t8223] VALUES (31,'لا يحب الله الفاحش المتفحش','31','0','0');
INSERT INTO [t8223] VALUES (32,'كان لجدي - يعني أبيا جرين من تمر','32','0','0');
INSERT INTO [t8223] VALUES (33,'أبيا كان له جرين من تمر فسرقه الجني. وقال لي سليمان: حدثنا الوليد، قال: حدثنا الأوزاعي،','33','0','0');
INSERT INTO [t8223] VALUES (34,'مفاتيح الصدقة كانت مع أبي هريرة بهذا','34','0','0');
INSERT INTO [t8223] VALUES (35,'ضم إلي النبي صلى الله عليه وسلم تمر الصدقة فذكر نحوه','35','0','0');
INSERT INTO [t8223] VALUES (36,'تكون فتنة النائم فيها خير من القاعد قال لي عبد الله بن محمد، عن روح بن عبادة، قال: حدثنا','36','0','0');
INSERT INTO [t8223] VALUES (37,'آدم نبي مكلم قال لنا عبد الله بن صالح، عن معاوية بن صالح، عن محمد بن','37','0','0');
INSERT INTO [t8223] VALUES (38,'اللهم أحسن عاقبتنا في الأمور كلها وأجرنا من خزي الدنيا وعذاب الآخرة','38','0','0');
INSERT INTO [t8223] VALUES (39,'ثلاث من النبوة: تعجيل الإفطار، وتأخير السحور، ووضع الرجل يده اليمنى على اليسرى في','39','0','0');
INSERT INTO [t8223] VALUES (40,'إذا ركع قال: سبحان ربي العظيم ثلاثا وقال لنا أبو نعيم: حدثنا ابن أبي ذئب، عن إسحاق، عن','40','0','0');
INSERT INTO [t8223] VALUES (41,'من نذر أن يعصي الله فلا يعصه وقال لنا مسلم: حدثنا أبان، حدثنا يحيى، عن محمد بن أبان، عن','41','0','0');
INSERT INTO [t8223] VALUES (42,'علمني عملا أدخل الجنة، قال: كن مؤذنا، أو إماما، أو بإزاء الإمام','42','0','0');
INSERT INTO [t8223] VALUES (43,'أمتي أمة مرحومة جعل عذابها بأيديها في الدنيا فكتبه عمر. قال لي محمد بن عبادة: حدثنا يزيد،','43','0','0');
INSERT INTO [t8223] VALUES (44,'إن كان علينا أمراء يعملون بغير طاعة الله؟ فقال: عليهم ما حملوا وعليكم ما حملتم. قال لي','44','0','0');
INSERT INTO [t8223] VALUES (45,'نهى النبي صلى الله عليه وسلم أن تنكح المرأة على عمتها أو على خالتها قال أبو عبد الله:','45','0','0');
INSERT INTO [t8223] VALUES (46,'فداك أبي وأمي قال أبو عبد الله: فحدثني إبراهيم بن منذر، عن معن، سمع','46','0','0');
INSERT INTO [t8223] VALUES (47,'كنت أبيع فقال النبي صلى الله عليه وسلم: لا بأس به','47','0','0');
INSERT INTO [t8223] VALUES (48,'يهل المهل منا فلا ينكر عليه، ويكبر المكبر منا فلا ينكر عليه وقال لي ابن أبي أويس: حدثني','48','0','0');
INSERT INTO [t8223] VALUES (49,'تزوجها وأقام عندها ثلاثا، فقال: ليس بك على أهلك هوان، إن شئت سبعت لك وسبعت لنسائي وقال لي','49','0','0');
INSERT INTO [t8223] VALUES (50,'حي من ها هنا - وأومأ بيده إلى المشرق - مبغي عليهم منصورون','50','0','0');
INSERT INTO [t8223] VALUES (51,'من يلي هذا الأمر بعد النبي صلى الله عليه وسلم؟ فيقال: أبو بكر، ثم عمر، ثم عثمان، ثم نسكت','51','0','0');
INSERT INTO [t8223] VALUES (52,'فبزق في فيه وسماه محمدا وحنكه عجوة قال لنا علي: عن زيد بن الحباب، عن أبي ثابت، عن إسماعيل','52','0','0');
INSERT INTO [t8223] VALUES (53,'من أخاف الأنصار أخاف ما بين هذين وأومأ إلى جنبيه محمد بن جعفر بن الزبير بن العوام القرشي،','53','0','0');
INSERT INTO [t8223] VALUES (54,'وقعت بامرأتي في رمضان، قال: تصدق، قال: ما عندي شيء، فأتاه إنسان بطعام - قال عبد الرحمن:','54','0','0');
INSERT INTO [t8223] VALUES (55,'وقعت بامرأتي في رمضان، قال: أعتق رقبة قال: لا أجده. قال: أطعم ستين مسكينا، قال: ليس عندي.','55','0','0');
INSERT INTO [t8223] VALUES (56,'أفطرت في رمضان، فأتى النبي صلى الله عليه وسلم بمكتل فيه تمر، فقال: تصدق','56','0','0');
INSERT INTO [t8223] VALUES (57,'وطئت امرأتي في رمضان نهارا، قال: تصدق، تصدق، قال: ما عندي شيء، فأمره أن يمكث، فجاءه عرق','57','0','0');
INSERT INTO [t8223] VALUES (58,'لا يعذب بالنار إلا رب النار','58','0','0');
INSERT INTO [t8223] VALUES (59,'لا صلاة بعد الصبح، حتى تطلع الشمس، ولا بعد العصر حتى تغرب الشمس','59','0','0');
INSERT INTO [t8223] VALUES (60,'رآني ابن عمر','60','0','0');
INSERT INTO [t8223] VALUES (61,'رآني ابن عمر محمد بن حصين بن عبد الرحمن الأنصاري الأشهلي الأوسي المديني، سمع منه طلحة','61','0','0');
INSERT INTO [t8223] VALUES (62,'ما عمل ابن آدم شيئا أفضل من الصلاة، وصلاح ذات البين، وخلق حسن وقال لي صدقة: أخبرنا أبو','62','0','0');
INSERT INTO [t8223] VALUES (63,'لعن النائحة والمستمعة','63','0','0');
INSERT INTO [t8223] VALUES (64,'ما على أهلها لو انتفعوا بإهابها','64','0','0');
INSERT INTO [t8223] VALUES (65,'البحر من جهنم {أحاط بهم سرادقها} [الكهف: ٢٩] والله لا أدخلها حتى أعرض على الله عز','65','0','0');
INSERT INTO [t8223] VALUES (66,'كنت أنا وعلي رفيقين في غزوة محمد بن خالد، عن أبيه، عن جده، سمع النبي صلى الله عليه وسلم','66','0','0');
INSERT INTO [t8223] VALUES (67,'الإمام ضامن والمؤذن مؤتمن','67','0','0');
INSERT INTO [t8223] VALUES (68,'فرق ما بيننا وبين المشركين العمائم على القلانس','68','0','0');
INSERT INTO [t8223] VALUES (69,'من كن له ثلاث بنات كان معي في الجنة وقال لي الوليد بن شجاع: حدثنا أبي، قال: حدثنا زياد بن','69','0','0');
INSERT INTO [t8223] VALUES (70,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله وأن محمدا رسول الله، ويقيموا الصلاة ويؤتوا','70','0','0');
INSERT INTO [t8223] VALUES (71,'من شهد له خزيمة أو شهد عليه فحسبه قاله لي علي: عن زيد بن حباب، سمع','71','0','0');
INSERT INTO [t8223] VALUES (72,'كل بناء وبال إلا إواء يعني ما لا بد منه','72','0','0');
INSERT INTO [t8223] VALUES (73,'قتال المسلم كفر وسبابه فسوق قال لي زهير: حدثنا يونس بن محمد، قال: حدثنا صدقة بن هرمز، عن','73','0','0');
INSERT INTO [t8223] VALUES (74,'قرأ {جنتان} [سبأ: ١٥]','74','0','0');
INSERT INTO [t8223] VALUES (75,'قرأ {جنتان} [سبأ: ١٥]','75','0','0');
INSERT INTO [t8223] VALUES (76,'إذا ذكر داود عليه السلام قال: كان أعبد البشر حدثني ابن سلام، حدثنا ابن فضيل، عن محمد بن','76','0','0');
INSERT INTO [t8223] VALUES (77,'من نسي فأكل، أو شرب، فليتم صومه وقال لنا موسى: حدثنا أبان، قال: ثنا قتادة، عن أبي رافع،','77','0','0');
INSERT INTO [t8223] VALUES (78,'من استقاء فعليه القضاء','78','0','0');
INSERT INTO [t8223] VALUES (79,'أمر النبي صلى الله عليه وسلم عثمان بن أبي العاص على قومه وقال لي يحيى بن موسى: حدثنا عمر','79','0','0');
INSERT INTO [t8223] VALUES (80,'من خرج قاصدا إلى المسجد - يعني مسجد قباء - كان كعدل عمرة محمد بن سليمان بن أبي حثمة، عن','80','0','0');
INSERT INTO [t8223] VALUES (81,'هل يضر الغبط؟ قال: نعم كما يضر الشجر الخبط قال لي هشام بن عمار: سمع','81','0','0');
INSERT INTO [t8223] VALUES (82,'لا تقوم الساعة حتى يظهر البخل والفحش','82','0','0');
INSERT INTO [t8223] VALUES (83,'من صلى ثنتي عشرة ركعة في يوم سوى المكتوبة بني له بيت في الجنة وقال لنا أبو النعمان: حدثنا','83','0','0');
INSERT INTO [t8223] VALUES (84,'لا حول ولا قوة إلا بالله كنز من كنوز الجنة','84','0','0');
INSERT INTO [t8223] VALUES (85,'صلى في ثوب علي وعليه وفيه كان ما كان','85','0','0');
INSERT INTO [t8223] VALUES (86,'من يرد هوان قريش أهانه الله','86','0','0');
INSERT INTO [t8223] VALUES (87,'اتقوا النار ولو بشق تمرة حدثني محمد بن بشار، قال: حدثنا أبو عاصم، حدثنا محمد بن سليم','87','0','0');
INSERT INTO [t8223] VALUES (88,'من دعا للبعير الأحمر؟ فقال النبي صلى الله عليه وسلم: لا وجدت','88','0','0');
INSERT INTO [t8223] VALUES (89,'أنت مني بمنزلة هارون من موسى إلا النبوة','89','0','0');
INSERT INTO [t8223] VALUES (90,'هذا تشهد النبي صلى الله عليه وسلم: التحيات لله','90','0','0');
INSERT INTO [t8223] VALUES (91,'من أخاف أهل المدينة فعليه لعنة الله','91','0','0');
INSERT INTO [t8223] VALUES (92,'أيا كعب ما نسي ربك {وما كان ربك نسيا} [مريم: ٦٤] بيتا قلته، قال: وما هو يا رسول الله؟','92','0','0');
INSERT INTO [t8223] VALUES (93,'الود والعداوة تتوارث وقال لنا بشر بن محمد: عن ابن المبارك، عن محمد بن عبد الرحمن بن فلان','93','0','0');
INSERT INTO [t8223] VALUES (94,'ألك أم؟ ففيها فجاهد وقال لي يوسف بن بهلول: حدثنا عبدة، عن ابن إسحاق، عن الزهري، عن أبي','94','0','0');
INSERT INTO [t8223] VALUES (95,'أسماء نفست بالشجرة','95','0','0');
INSERT INTO [t8223] VALUES (96,'خرج حجاجا مع النبي صلى الله عليه وسلم فولدت أسماء بالشجرة، محمد بن أبي','96','0','0');
INSERT INTO [t8223] VALUES (97,'أرسل إلى نبيه ملكا معه جبرئيل فما أكل بعده طعاما متكئا، حتى لقي الله عز وجل وقال لي عمرو','97','0','0');
INSERT INTO [t8223] VALUES (98,'أين صلى النبي صلى الله عليه وسلم في وجه الكعبة أو في صفحة الكعبة؟ فقال: عند الشقة الثالثة','98','0','0');
INSERT INTO [t8223] VALUES (99,'لا يفتي بالتمتع بالعمرة إلى الحج إلا من جهل، فقال سعد: ليس ما قلت يا ابن أخي، فوالله لقد','99','0','0');
INSERT INTO [t8223] VALUES (100,'قولي اللهم أصلح لي ديني قاله لي عبد الله بن عثمان بن عطاء: سمع عطافا، سمع','100','0','0');
INSERT INTO [t8223] VALUES (101,'ما هذه الكسرة؟ أما إنها أول طعام دخل بطن أبيك منذ ثلاثة أيام قال لي هشام بن عبد الملك: عن','101','0','0');
INSERT INTO [t8223] VALUES (102,'صليت مع النبي صلى الله عليه وسلم بمنى ركعتين قاله لي عبد الله بن يوسف: عن الليث، عن بكير','102','0','0');
INSERT INTO [t8223] VALUES (103,'من سره أن ينسأ في أجله، ويوسع عليه في رزقه، فليصل رحمه','103','0','0');
INSERT INTO [t8223] VALUES (104,'مدمن خمر كعابد وثن وقال لي فروة: حدثنا محمد بن سليمان، عن سهيل، عن أبيه، عن أبي هريرة،','104','0','0');
INSERT INTO [t8223] VALUES (105,'ما هممت بشيء مما كان أهل الجاهلية يعملون به غير مرتين كل ذلك يحول الله بيني وبينه، ما','105','0','0');
INSERT INTO [t8223] VALUES (106,'الحج المبرور ليس له جزاء إلا الجنة','106','0','0');
INSERT INTO [t8223] VALUES (107,'ما صلى النبي صلى الله عليه وسلم على سهيل بن بيضاء إلا في المسجد','107','0','0');
INSERT INTO [t8223] VALUES (108,'كان له شعر جعد، فسماه رسول الله صلى الله عليه وسلم جعدة قاله لي خليفة بن خياط: سمع أبا','108','0','0');
INSERT INTO [t8223] VALUES (109,'ضم رداءك، فضممته إلى صدري، فما نسيت حديثا سمعته من النبي صلى الله عليه وسلم بعد مجلسي ذاك','109','0','0');
INSERT INTO [t8223] VALUES (110,'أتصدق بعرضي قاله لي فضل بن سهل: سمع أبا النضر، سمع محمدا','110','0','0');
INSERT INTO [t8223] VALUES (111,'في أولاد الزنا','111','0','0');
INSERT INTO [t8223] VALUES (112,'لا تديموا النظر إلى المجذمين وقال لي أحمد بن إشكاب: حدثنا حميد الرؤاسي، عن عبد الله بن','112','0','0');
INSERT INTO [t8223] VALUES (113,'إذا سجد فليضع يديه قبل ركبتيه','113','0','0');
INSERT INTO [t8223] VALUES (114,'المجوس تعفي شواربها وتحفي لحاها، فخالفوهم فجزوا شواربكم وأعفوا لحاكم','114','0','0');
INSERT INTO [t8223] VALUES (115,'أعفوا اللحى، وخذوا من الشوارب','115','0','0');
INSERT INTO [t8223] VALUES (116,'ليس فيما دون خمسة أوسق من التمر صدقة','116','0','0');
INSERT INTO [t8223] VALUES (117,'ليس فيما دون خمسة أوسق من التمر صدقة','117','0','0');
INSERT INTO [t8223] VALUES (118,'إذا خرجت إلى العشاء فلا تمسي طيبا وقال لنا موسى: حدثنا إبراهيم، قال: حدثنا محمد بن عبد','118','0','0');
