INSERT INTO [t8223] VALUES (509,'الله عز وجل على عرشه، وعرشه فوق سماواته وقال لي محمد بن بشار، غيره: حدثنا وهب، عن أبيه،','509','0','0');
INSERT INTO [t8223] VALUES (510,'كنت أصلي فدخل رجل فسرني، فقال: لك أجران السر والعلانية، وقال لي إسحاق: حدثنا أبو داود، عن','510','0','0');
INSERT INTO [t8223] VALUES (511,'أبيعها وأشتري بثمنها بدنا فأنحره؟ قال: لا، ولكن انحرها، قال لي محمد بن سلام: أخبرني محمد','511','0','0');
INSERT INTO [t8223] VALUES (512,'لا تغبطن فاجرا بنعمة، إن له عند الله قاتلا لا يموت فبلغ ذلك وهب بن منبه، فأرسل إليه أبا','512','0','0');
