INSERT INTO [t8223] VALUES (515,'من صام يوما في سبيل الله، بعده الله من النار سبعين خريفا قال لي عمرو بن محمد: سمع العلاء','515','0','0');
INSERT INTO [t8223] VALUES (516,'لجهنم سبعة أبواب قال لنا موسى: ثنا وهيب، عن هشام، عن أبيه، عن الأحنف بن قيس، عن بعض','516','0','0');
