INSERT INTO [t8223] VALUES (623,'لا تمنعوا إماء الله مساجد الله، وليخرجن تفلات وقال لي محمد أبو يحيى: نا داود بن رشيد، نا','623','0','0');
INSERT INTO [t8223] VALUES (624,'الله يحب الرفق في الأمر كله قاله لنا عبد الله بن يوسف، عنه','624','0','0');
INSERT INTO [t8223] VALUES (625,'كبرت خيانة أن تحدث رجلا حديثا هو لك مصدق وأنت له به كاذب','625','0','0');
INSERT INTO [t8223] VALUES (626,'ساقي القوم آخرهم وحدثني عمرو بن خالد، نا عيسى، نا عبادة أبو مالك النخعي، عن موسى بن أبي','626','0','0');
INSERT INTO [t8223] VALUES (627,'لا تشددوا على أنفسكم، فإنما هلك من قبلكم بتشديدهم على أنفسهم، وستجدون بقاياهم في الصوامع','627','0','0');
INSERT INTO [t8223] VALUES (628,'ابن عمك يزعم أنك قد زدت عليه؟ فقال: يا رسول الله، لقد تركت له قدر عرية أهله وما يصيب','628','0','0');
INSERT INTO [t8223] VALUES (629,'لا يبغي على الناس إلا ولد بغي، أو فيه عرق منه','629','0','0');
INSERT INTO [t8223] VALUES (630,'صلى عليه رسول الله صلى الله عليه وسلم','630','0','0');
INSERT INTO [t8223] VALUES (631,'من جلس فقال: سبحانك ربنا وبحمدك، فهو كفارة','631','0','0');
INSERT INTO [t8223] VALUES (632,'إن من البيان لسحرا قال لنا علي: نا جرير، عن منصور، عن هلال بن يساف، قال: كنا مع سالم بن','632','0','0');
INSERT INTO [t8223] VALUES (633,'يقرأ في ركعتي الفجر قل يا أيها الكافرون وقل هو الله أحد، قال البخاري قال حجاج بن موسى:','633','0','0');
INSERT INTO [t8223] VALUES (634,'إنما أنا بشر أغضب كما يغضب البشر، وإني اتخذت عندك عهدا لن تخلفنيه، فأيما مؤمن آذيته أو','634','0','0');
INSERT INTO [t8223] VALUES (635,'تستعجب بأمانته في الوضوء','635','0','0');
INSERT INTO [t8223] VALUES (636,'من أسعد الناس بشفاعتك؟ قال: لقد ظننت أنك أول من يسأل عنها لما رأيت من حرصك، من قال: لا','636','0','0');
INSERT INTO [t8223] VALUES (637,'أعطيت خمسا لم يعطها أحد من قبلي من الأنبياء: جعلت لي الأرض طهورا ومسجدا ولم يكن أحد من','637','0','0');
INSERT INTO [t8223] VALUES (638,'شهرا عيد لا ينقصان رمضان وذو الحجة','638','0','0');
INSERT INTO [t8223] VALUES (639,'لا تستطيع صلاتي. فقام يغتسل فسترته بثوب وأنا محول عنه، فاغتسل ثم فعلت مثله فقال: هكذا','639','0','0');
INSERT INTO [t8223] VALUES (640,'توضأ ثلاثا ثلاثا حدثنا أبو كريب، نا خالد بن حيان، عن سالم','640','0','0');
INSERT INTO [t8223] VALUES (641,'أعطى الفرس سهمين وصاحبه سهما','641','0','0');
INSERT INTO [t8223] VALUES (642,'حسن السبلة وكانت العرب تسمي اللحية السبلة','642','0','0');
INSERT INTO [t8223] VALUES (643,'فجمع بين المغرب والعشاء وقال: هكذا كان النبي صلى الله عليه وسلم يفعل إذا جاءه أمر بشدة','643','0','0');
INSERT INTO [t8223] VALUES (644,'يجيء أحدكم يسأل عن خبر السماء، ويدع أظفاره كأظافير الطير تجتمع فيها الخباثة حدثني ابن','644','0','0');
INSERT INTO [t8223] VALUES (645,'كأنك كنت صاحب بادية، قال: أجل قاله لنا عياش: عن عبد الأعلى، نا محمد بن إسحاق قال: حدثني','645','0','0');
INSERT INTO [t8223] VALUES (646,'يوضع الصراط بين ظهراني جهنم','646','0','0');
INSERT INTO [t8223] VALUES (647,'العلم والإيمان مكانهما، من ابتغاهما وجدهما، فالتمسوا العلم عند أربعة رهط: عند عويمر أبي','647','0','0');
INSERT INTO [t8223] VALUES (648,'انظر إلى ما أمرت به فرأيت الخاتم بين كتفيه مثل بيضة الحمامة','648','0','0');
INSERT INTO [t8223] VALUES (649,'أبي كان يصل الرحم ويقري الضيف، قال: إنه مات قبل الإسلام','649','0','0');
INSERT INTO [t8223] VALUES (650,'صدقت المسلم أخو المسلم','650','0','0');
INSERT INTO [t8223] VALUES (651,'ما لنا إلا خادم فلطمه أحدنا، فأمرنا النبي صلى الله عليه وسلم أن نعتقه','651','0','0');
INSERT INTO [t8223] VALUES (652,'أكل سويقا، ثم مضمض ولم يتوضأ','652','0','0');
INSERT INTO [t8223] VALUES (653,'الله أكبر جبل يحبنا ونحبه وقال يونس: غزوة حنين','653','0','0');
INSERT INTO [t8223] VALUES (654,'فابتاع منا سراويل، وثم وزان يزن بالأجر فقال: يا وزان زن، فأرجح حدثني عبدة، نا عبد الصمد،','654','0','0');
INSERT INTO [t8223] VALUES (655,'أعفو لحاكم ولا تشبهوا باليهود حدثناه يحيى، نا ابن وهب، نا عمرو أن سويدا','655','0','0');
INSERT INTO [t8223] VALUES (656,'إذا سألتم الله فاسألوه الفردوس','656','0','0');
INSERT INTO [t8223] VALUES (657,'عليكم بالصدق فإنه مع البر. . . . إلي وكونوا عباد الله إخوانا','657','0','0');
INSERT INTO [t8223] VALUES (658,'الله عز وجل يأمرك أن تأمر أصحابك أن يرفعوا أصواتهم بالتلبية، فإنها من شعار','658','0','0');
INSERT INTO [t8223] VALUES (659,'حج بي مع النبي صلى الله عليه وسلم وأنا ابن سبع سنين','659','0','0');
INSERT INTO [t8223] VALUES (660,'الاستنجاء بثلاثة أحجار','660','0','0');
INSERT INTO [t8223] VALUES (661,'أقرب العمل إلى الجهاد في سبيل الله لا يقارنه شيء إلا ما كان على مثل هذا وأشار النبي صلى','661','0','0');
INSERT INTO [t8223] VALUES (662,'يصبح جنبا فيصوم','662','0','0');
INSERT INTO [t8223] VALUES (663,'قام فينا فأمر بالصلاة، وتقوى الله، وصلاح ذات البين، ومن ساءته سيئته وسرته حسنته فهي أمارة','663','0','0');
INSERT INTO [t8223] VALUES (664,'ما الرعب الذي ألقي في قلوب المشركين يوم حنين؟ ما وجدتم؟ قال: وكان أبو حاجر مع المشركين','664','0','0');
INSERT INTO [t8223] VALUES (665,'ارجعوا شاهت الوجوه فانصرفنا ما تلقى منا أحدا إلا وهو يمسح القذى عن','665','0','0');
INSERT INTO [t8223] VALUES (666,'يكره الشكال من الخيل','666','0','0');
INSERT INTO [t8223] VALUES (667,'يكره الشكال في الخيل','667','0','0');
INSERT INTO [t8223] VALUES (668,'وسموا باسمي ولا تكنوا بكنيتي','668','0','0');
INSERT INTO [t8223] VALUES (669,'من سمي باسمي فلا يكتني بكنيتي، ومن اكتنى بكنيتي فلا يتسمى باسمي','669','0','0');
INSERT INTO [t8223] VALUES (670,'إذا رأيتم آية فاسجدوا وأي آية أشد من ذهاب أزواج النبي صلى الله عليه','670','0','0');
INSERT INTO [t8223] VALUES (671,'الأمراء من قريش','671','0','0');
INSERT INTO [t8223] VALUES (672,'توفيت أمي وعليها مشي إلى الكعبة نذرا؟ فقال: هل تستطعين تمشين عنها؟ قالت: نعم، فقال: امشي','672','0','0');
INSERT INTO [t8223] VALUES (673,'لتركبن سنن من قبلكم','673','0','0');
INSERT INTO [t8223] VALUES (674,'خرجنا مع النبي صلى الله عليه وسلم إلى حنين','674','0','0');
INSERT INTO [t8223] VALUES (675,'يوجز الصلاة','675','0','0');
INSERT INTO [t8223] VALUES (676,'مروا صبيانكم بالصلاة إذا بلغوا سبع سنين','676','0','0');
INSERT INTO [t8223] VALUES (677,'هب لي أذان قومي، فوهب لي','677','0','0');
INSERT INTO [t8223] VALUES (678,'المسلم من سلم المسلمون من لسانه ويده','678','0','0');
INSERT INTO [t8223] VALUES (679,'أمر بلال أن يشفع الأذان ويوتر الإقامة','679','0','0');
INSERT INTO [t8223] VALUES (680,'نعم الرجل من أهل الجنة عويم بن ساعدة','680','0','0');
INSERT INTO [t8223] VALUES (681,'من ترك جمعة من غير عذر فليتصدق بدينار، فإن لم يجد فنصف دينار','681','0','0');
INSERT INTO [t8223] VALUES (682,'نعم الفتى سمرة لو أخذ من لمته وشمر من مئزره وفعل ذلك سمرة','682','0','0');
INSERT INTO [t8223] VALUES (683,'مسح عليها بيده فلست أحلقها حتى أموت فلم يحلقها حتى مات حدثني عبدة، نا زيد بن حباب، نا سكن','683','0','0');
INSERT INTO [t8223] VALUES (684,'اطلعت في الجنة فرأيت أكثر أهلها الفقراء، واطلعت في النار فرأيت أكثر أهلها النساء حدثني','684','0','0');
INSERT INTO [t8223] VALUES (685,'عامة أهل النار النساء وقال لي محمد: عن جعفر بن عون، سمع ابن أبي عروبة، سمع أبا رجاء، سمع','685','0','0');
INSERT INTO [t8223] VALUES (686,'أقل ساكن أهل الجنة النساء','686','0','0');
INSERT INTO [t8223] VALUES (687,'مر بنيك أن يقصوا أظفارهم عن ضروع إبلهم ومواشيهم، وقل لهم: فليحتلبوا عليها سخالها لا','687','0','0');
INSERT INTO [t8223] VALUES (688,'عليك بالخيل فارتبطها، الخيل معقود في نواصيها الخير','688','0','0');
INSERT INTO [t8223] VALUES (689,'نهى النبي صلى الله عليه وسلم أن يتوضأ بفضل وضوء المرأة','689','0','0');
INSERT INTO [t8223] VALUES (690,'نهى النبي صلى الله عليه وسلم عن الدباء والحنتم حدثنا مسدد، نا يحيى، عن التيمي، عن أبي','690','0','0');
INSERT INTO [t8223] VALUES (691,'ليستتر أحدكم في صلاته ولو بسهم وقال لي علي بن إبراهيم: حدثنا يعقوب بن محمد، نا سبرة بن','691','0','0');
INSERT INTO [t8223] VALUES (692,'الشيطان قعد لابن آدم بطرقه فقعد له بطريق الإسلام، فقال: تسلم وتدع دينك ودين آبائك؟ ثم قعد','692','0','0');
INSERT INTO [t8223] VALUES (693,'غسل النبي صلى الله عليه وسلم كفيه ثلاثا، ومضمض ثلاثا، واستنشق ثلاثا، وغسل وجهه ثلاثا،','693','0','0');
INSERT INTO [t8223] VALUES (694,'يغزو هذا البيت جيش يخسف به بالبيداء','694','0','0');
INSERT INTO [t8223] VALUES (695,'من كنت مولاه فعلي مولاه سمعته أذناي، قال ابن شريك: فقدم عبد الله بن علقمة، وسهم فلما','695','0','0');
INSERT INTO [t8223] VALUES (696,'نحن رسول رسول الله صلى الله عليه وسلم في الصدقة، قلت: وما الصدقة؟ قالا: شاة من غنمك فقمت','696','0','0');
INSERT INTO [t8223] VALUES (697,'أنا رسول الله صلى الله عليه وسلم','697','0','0');
INSERT INTO [t8223] VALUES (698,'إني رسول رسول الله صلى الله عليه وسلم','698','0','0');
INSERT INTO [t8223] VALUES (699,'إذا حدث حديثا أعاد ثلاثا','699','0','0');
INSERT INTO [t8223] VALUES (700,'صاحبكم محبوس بدين على باب الجنة وقال لي محمود: نا أبو داود، نا سلام بن سليم، عن سعيد بن','700','0','0');
INSERT INTO [t8223] VALUES (701,'من السائل عن السكر؟ لا يشربه ولا يسقه أحد قط ابتغاء لذة سكره فيسقيه الله خمرا يوم','701','0','0');
INSERT INTO [t8223] VALUES (702,'لم يلحقنا من الطلب غير سراقة بن مالك بن جعشم','702','0','0');
INSERT INTO [t8223] VALUES (703,'خذ هذا الدم فادفنه من الطير والدواب والناس فتغيبت فشربته، ثم سألني أو أخبر أني شربته','703','0','0');
INSERT INTO [t8223] VALUES (704,'كان النبي صلى الله عليه وسلم رحيما','704','0','0');
INSERT INTO [t8223] VALUES (705,'فناء أمتي بالطعن والطاعون','705','0','0');
INSERT INTO [t8223] VALUES (706,'بني الإسلام على خمس قاله لنا الحميدي: عن ابن عيينة، عن سعير، عن حبيب','706','0','0');
INSERT INTO [t8223] VALUES (707,'من استغفر للمؤمنين رد الله عليه من آدم فما دونه','707','0','0');
INSERT INTO [t8223] VALUES (708,'توضأ النبي صلى الله عليه وسلم ثلاثا ثلاثا حدثنيه أحمد بن صباح، أنا عمر بن يونس، نا جهضم','708','0','0');
INSERT INTO [t8223] VALUES (709,'قائما يصلي ويده اليمنى على اليسرى','709','0','0');
INSERT INTO [t8223] VALUES (710,'لو شربت من ماء بطحان لبرأت قال: فما يمنعك؟ قلت: الهجرة، قال: فأنت مهاجر حيث كنت شداد بن','710','0','0');
INSERT INTO [t8223] VALUES (711,'من حفظ الله له فرجه فله الجنة','711','0','0');
INSERT INTO [t8223] VALUES (712,'كلها بقي إلا كتفها يعني الشاة','712','0','0');
INSERT INTO [t8223] VALUES (713,'عينان لا تريان النار: عين باتت تكلأ في سبيل الله، وعين بكت من خشية الله حدثني ابن حميد،','713','0','0');
INSERT INTO [t8223] VALUES (714,'ما أنزل الله عز وجل داء إلا أنزل معه دواء إلا السام يعني الموت','714','0','0');
INSERT INTO [t8223] VALUES (715,'حرم النبي صلى الله عليه وسلم كل السكر، وكل مسكر حرام','715','0','0');
INSERT INTO [t8223] VALUES (716,'ننبذ للنبي صلى الله عليه وسلم في سقاء شهاب: أنه أتى المدينة فلقي أبا هريرة. قاله لنا','716','0','0');
INSERT INTO [t8223] VALUES (717,'خير الناس رجل تنحى عن شرور الناس','717','0','0');
INSERT INTO [t8223] VALUES (718,'من شرب الخمر وهو غير مكره، ولا مضطر، خرج منه الإيمان','718','0','0');
INSERT INTO [t8223] VALUES (719,'لا يدخل الجنة أحدكم بعمل ولا أنا إلا أن يتغمدني برحمته، وما منكم من أحد إلا له شيطان وإلا','719','0','0');
INSERT INTO [t8223] VALUES (720,'الحية والعقرب والفأرة فاسقة، والكلب الأسود البهيم شيطان حدثنا موسى، نا أبو عوانة، عن عبد','720','0','0');
INSERT INTO [t8223] VALUES (721,'قرأت سورة الأحزاب على النبي صلى الله عليه وسلم فنسيت منها سبعين آية ما','721','0','0');
INSERT INTO [t8223] VALUES (722,'أصلى النبي صلى الله عليه وسلم في الكعبة؟ قال: صلى بين العمودين، ثم ألزق بها بطنه','722','0','0');
INSERT INTO [t8223] VALUES (723,'توضأ ثلاثا قال: رأيت أباك يفعله يعني النبي صلى الله عليه وسلم','723','0','0');
INSERT INTO [t8223] VALUES (724,'أنتظرك منذ ثلاث قاله لي محمد بن يوسف: نا محمد بن سنان، نا إبراهيم بن طهمان، عن عبد الكريم','724','0','0');
INSERT INTO [t8223] VALUES (725,'فأسبغوا الوضوء، وويل للأعقاب من النار، أتموا الركوع والسجود فقلت لأبي عبد الله: من حدثك','725','0','0');
INSERT INTO [t8223] VALUES (726,'لا تزال عصابة قوامة وقال النبي صلى الله عليه وسلم: هم أهل الشام','726','0','0');
INSERT INTO [t8223] VALUES (727,'حضرموت خير من بني الحارث؟ قال: نعم، قال السمط: آمنت بالله ورسوله','727','0','0');
INSERT INTO [t8223] VALUES (728,'فصلى ركعتين فسألته فقال: أصنع كما رأيت النبي صلى الله عليه وسلم يصنع حدثنا عبد الله بن','728','0','0');
INSERT INTO [t8223] VALUES (729,'ادن مني، فدنوت منه، فقال: افتحها، ففتحتها، فنفث في كفي ووضع كفه على السلعة فما زال يصلحها','729','0','0');
INSERT INTO [t8223] VALUES (730,'أبو يحيى؟ ادنه هلم الغداء، قلت: إني أريد الصوم، قال: وأنا أريد الصوم ولكن مؤذننا هذا في','730','0','0');
INSERT INTO [t8223] VALUES (731,'هلم إلى الغداء قلت: أريد الصوم، قال: وأنا أريد الصوم ولكن مؤذننا هذا سيئ البصر أذن قبل أن','731','0','0');
INSERT INTO [t8223] VALUES (732,'إذا أراد أحدكم أمرا فليقل: اللهم إني أستخيرك بعلمك، وأستقدرك بقدرتك، وأسألك من فضلك، فإنك','732','0','0');
INSERT INTO [t8223] VALUES (733,'لي الواجد يحل عرضه وعقوبته حدثنا عبد الله بن عثمان، عن ابن المبارك، أنا وبر بن أبي دليلة،','733','0','0');
INSERT INTO [t8223] VALUES (734,'من يحرسنا الليلة فأدعو له بدعاء يصيب به فضلا؟ قال رجل: أنا، قال: ممن أنت؟ قال: فلان بن','734','0','0');
INSERT INTO [t8223] VALUES (735,'قل: أعوذ بك من شر سمعي وبصري، ومن شر قلبي ولساني، وشر منيي حتى حفظتها','735','0','0');
INSERT INTO [t8223] VALUES (736,'للغازي أجره وللجاعل أجره وأجر الغازي','736','0','0');
INSERT INTO [t8223] VALUES (737,'من يحول بيني وبين هذه النار؟ فقام رجل فحال بيني وبينه، قلت: ما أصنع بهما؟ قال: أحرقهما','737','0','0');
INSERT INTO [t8223] VALUES (738,'وبايعه بيعة الإسلام وصدق إليه ماله، فأقطعه النبي صلى الله عليه وسلم مياه عدة قاله لي أحمد','738','0','0');
INSERT INTO [t8223] VALUES (739,'الرجل ليتكلم بالكلمة وما يلقي لها بالا يهوي في جهنم، وإن الرجل ليدرك بحسن خلقه درجة','739','0','0');
INSERT INTO [t8223] VALUES (740,'البيع عن تراض','740','0','0');
INSERT INTO [t8223] VALUES (741,'يؤتى بالقاضي العادل يوم القيامة فيلقى من شدة الحساب حتى يود أنه لم يكن قضى بين اثنين في','741','0','0');
INSERT INTO [t8223] VALUES (742,'من صبر على لأواء المدينة كنت له شهيدا أو شفيعا قاله لنا موسى: عن وهيب، سمع هشام بن عروة،','742','0','0');
INSERT INTO [t8223] VALUES (743,'ما كان النبي صلى الله عليه وسلم يمتنع من وجهي وهو صائم','743','0','0');
INSERT INTO [t8223] VALUES (744,'أنا قائد المسلمين ولا فخر، وأنا خاتم النبيين ولا فخر، وأنا أول شافع ومشفع ولا فخر قاله','744','0','0');
INSERT INTO [t8223] VALUES (745,'فكتب لنا اثني عشر قلوصا، فكنا في استخراجها حتى جاءنا وفاة النبي صلى الله عليه وسلم','745','0','0');
INSERT INTO [t8223] VALUES (746,'لا يحل أكل البغال والحمير','746','0','0');
