INSERT INTO [t8227] VALUES (1,'أول زمرة من أمتي تدخل الجنة على مثل صورة القمر ليلة البدر، ثم الثانية على أشد نجم في','1','0','0');
INSERT INTO [t8227] VALUES (2,'الجنة سجسج لا حر فيها ولا قر','2','0','0');
INSERT INTO [t8227] VALUES (3,'يدخل الجنة من أمتي سبعين ألفا بغير حساب. فقال يزيد بن الأخنس: والله ما أولئك في أمتك يا','3','0','0');
INSERT INTO [t8227] VALUES (4,'أدنى وقت الحيض يوم','4','0','0');
INSERT INTO [t8227] VALUES (5,'ضع القلم على أذنك فإنه أذكر للمالي','5','0','0');
INSERT INTO [t8227] VALUES (6,'يدني رأسه مني، وهو مجاور في المسجد، وأنا حائض في حجرتي فأرجله','6','0','0');
INSERT INTO [t8227] VALUES (7,'خذي بالمعروف ما يكفيك ويكفي بنيك','7','0','0');
INSERT INTO [t8227] VALUES (8,'خذي ما يكفيك وولدك بالمعروف أخبركم أبو الفضل الزهري، نا جعفر، نا عمرو بن علي، نا يحيى بن','8','0','0');
INSERT INTO [t8227] VALUES (9,'لولا حداثة عهد قومك بالكفر لنقضت الكعبة، ثم بنيتها على أس إبراهيم عليه السلام، وإن قريشا','9','0','0');
INSERT INTO [t8227] VALUES (10,'إن قومك لما بنوا الكعبة استقصروا، ولولا أنهم حديثو عهد بجاهلية لهدمتها وجعلتها على أساس','10','0','0');
INSERT INTO [t8227] VALUES (11,'ما ضرب رسول الله صلى الله عليه وسلم أحدا من نسائه قط، ولا ضرب خادما له قط، ولا ضرب بيده','11','0','0');
INSERT INTO [t8227] VALUES (12,'فأذني له إنه عمك قلت: يا رسول الله، إنما أرضعتني المرأة، ولم يرضعني الرجل , فقال: ائذني','12','0','0');
INSERT INTO [t8227] VALUES (13,'فليلج عليك عمك. فقالت: إنما أرضعتني المرأة , ولم يرضعني الرجل. فقال رسول الله صلى الله','13','0','0');
INSERT INTO [t8227] VALUES (14,'أدرج رسول الله صلوات الله عليه وسلم في يمنة كانت لعبد الله بن أبي بكر , ثم نزعت عنه، وكفن','14','0','0');
INSERT INTO [t8227] VALUES (15,'كفن رسول الله صلى الله عليه وسلم في ثوبين حبرة , كانا لعبد الرحمن بن أبي بكر، ثم نزعا عنه','15','0','0');
INSERT INTO [t8227] VALUES (16,'أي يوم مات النبي صلى الله عليه وسلم؟ فقالت: يوم الاثنين قال أي يوم هذا؟ قالت: يوم الاثنين','16','0','0');
INSERT INTO [t8227] VALUES (17,'إن شئت فصم، وإن شئت فأفطر','17','0','0');
INSERT INTO [t8227] VALUES (18,'يوم عاشوراء يوم كانت قريش تصومه في الجاهلية، وكان رسول الله صلى الله عليه وسلم يصومه،','18','0','0');
INSERT INTO [t8227] VALUES (19,'كان يوم عاشوراء يوما تصومه قريش في الجاهلية، وكان رسول الله صلى الله عليه وسلم يصومه،','19','0','0');
INSERT INTO [t8227] VALUES (20,'فما أملك إن كان الله تعالى نزع منك الرحمة أخبركم أبو الفضل الزهري، نا جعفر بن محمد','20','0','0');
INSERT INTO [t8227] VALUES (21,'ما ذنبي إن كان الله تعالى نزع الرحمة من قلبك أخبركم أبو الفضل الزهري، نا جعفر، نا عبد','21','0','0');
INSERT INTO [t8227] VALUES (22,'أفتجد فلا جناح عليه ألا يطوف بهما؟ قلت: لا قالت: ليس كما قلت , لو كان كما قلت لكان فلا','22','0','0');
INSERT INTO [t8227] VALUES (23,'يا ابن أختي، إنما قال الله تعالى: {فلا جناح عليه أن يطوف بهما} [البقرة: ١٥٨] ولم يقل: فلا','23','0','0');
INSERT INTO [t8227] VALUES (24,'ما أرى علي جناحا أن لا أطوف بين الصفا والمروة , فذكر الحديث نحو حديث علي بن','24','0','0');
INSERT INTO [t8227] VALUES (25,'لا والله، ما أتم الله حج رجل , ولا عمرته لم يطف بين الصفا والمروة فذكر','25','0','0');
INSERT INTO [t8227] VALUES (26,'إن أمي افتلتت نفسها , وأظن أنها لو تكلمت لتصدقت، فهل لها من أجر إن تصدقت عنها؟ قال: نعم.','26','0','0');
INSERT INTO [t8227] VALUES (27,'نهى رسول الله صلى الله عليه وسلم أن ينتبذ في الدباء والمزفت','27','0','0');
INSERT INTO [t8227] VALUES (28,'نهى عن الدباء، والمزفت','28','0','0');
INSERT INTO [t8227] VALUES (29,'لا تشرب نبيذ الجر , وإن كان أحلى من العسل','29','0','0');
INSERT INTO [t8227] VALUES (30,'نهى رسول الله صلى الله عليه وسلم عن الدباء، والحنتم، والنقير، وحلق الذهب، وعن لبس الحرير','30','0','0');
INSERT INTO [t8227] VALUES (31,'نهانا أن ينتبذ في الدباء والمزفت','31','0','0');
INSERT INTO [t8227] VALUES (32,'أول زمرة من أمتي تدخل الجنة على صورة القمر ليلة البدر، ثم الذين يلونهم على أشد نجم في','32','0','0');
INSERT INTO [t8227] VALUES (33,'ألا هل مشمر للجنة، فإن الجنة لا خطر لها، هي ورب الكعبة نور تلألأ، وريحانة تهتز، ونهر','33','0','0');
INSERT INTO [t8227] VALUES (34,'أهل الجنة يأكلون فيها ويشربون , لا يمتخطون , ولا يتغوطون ولا يبولون، إنما يكون طعامهم ذلك','34','0','0');
INSERT INTO [t8227] VALUES (35,'هو نهر أعطانيه ربي عز وجل في الجنة أشد بياضا من اللبن وأحلا من العسل , فيه طيور أعناقها','35','0','0');
INSERT INTO [t8227] VALUES (36,'يبعث أهل الجنة على صورة آدم عليه السلام في ميلاد ثلاث وثلاثين جرد مرد، مكحلين، ثم يذهب','36','0','0');
INSERT INTO [t8227] VALUES (37,'كان شعر رسول الله صلى الله عليه وسلم فوق الوفرة ودون الجمة','37','0','0');
INSERT INTO [t8227] VALUES (38,'كان أحب العمل إلى رسول الله صلى الله عليه وسلم الذي يدوم عليه صاحبه','38','0','0');
INSERT INTO [t8227] VALUES (39,'أهل الجنة ليتراءون الغرف من فوقهم كما تتراءون الكوكب الدري في الأفق من المشرق أو المغرب','39','0','0');
INSERT INTO [t8227] VALUES (40,'من فطر صائما كان له مثل أجره','40','0','0');
INSERT INTO [t8227] VALUES (41,'كنت أرجل رأس رسول الله صلى الله عليه وسلم وأنا حائض','41','0','0');
INSERT INTO [t8227] VALUES (42,'نهى رسول الله صلى الله عليه وسلم عن الدباء والمزفت','42','0','0');
INSERT INTO [t8227] VALUES (43,'نهى رسول الله صلى الله عليه وسلم أن ينبذ في الدباء والحنتم والمزفت','43','0','0');
INSERT INTO [t8227] VALUES (44,'نهى النبي صلى الله عليه وسلم عن الدباء والحنتم والمزفت','44','0','0');
INSERT INTO [t8227] VALUES (45,'نهى رسول الله صلى الله عليه وسلم عن الدباء، والحنتم والمزفت','45','0','0');
INSERT INTO [t8227] VALUES (46,'يا نساء المؤمنين , إنكن لتسألن عن ظروف ما كان كثير منها على عهد رسول الله صلى الله عليه','46','0','0');
INSERT INTO [t8227] VALUES (47,'أكثرتن علينا يا أهل العراق في نبيذ الجر , وما على إحداكن أن تطبخ تمرها , ثم تصفيه فتجعله','47','0','0');
INSERT INTO [t8227] VALUES (48,'لأن أشرب أبوال الحمر أحب إلي من أن أشرب، في الحنتم قال: والحنتم: جرار حمر كان يؤتى بها من','48','0','0');
INSERT INTO [t8227] VALUES (49,'نبيذ الجر حرام','49','0','0');
INSERT INTO [t8227] VALUES (50,'أقبل يسير على أتان، ورسول الله صلى الله عليه وسلم، يصلي بالناس بمنى في حجة الوداع , فسارت','50','0','0');
INSERT INTO [t8227] VALUES (51,'أقبلت راكبا على أتان , وأنا يومئذ قد ناهزت الاحتلام , ورسول الله صلى الله عليه وسلم يصلي','51','0','0');
INSERT INTO [t8227] VALUES (52,'أقبلت أسير على أتان , وأنا يومئذ، قد ناهزت الاحتلام , ورسول الله صلى الله عليه وسلم يصلي','52','0','0');
INSERT INTO [t8227] VALUES (53,'شرب لبنا , ثم دعا بماء , فتمضمض , وقال: إن له دسما','53','0','0');
INSERT INTO [t8227] VALUES (54,'شرب لبنا فتمضمض , وقال: إن له دسما','54','0','0');
INSERT INTO [t8227] VALUES (55,'شرب لبنا فتمضمض، وقال: إن له دسما أخبركم أبو الفضل الزهري، نا جعفر، نا وهب بن بقية، أنا','55','0','0');
INSERT INTO [t8227] VALUES (56,'أجود الناس بالخير , وكان أجود ما يكون في رمضان حين يلقاه جبريل، وكان جبريل يلقاه في كل','56','0','0');
INSERT INTO [t8227] VALUES (57,'أجود الناس، وكان أجود ما يكون في رمضان حين يلقاه جبريل، وكان جبريل يلقاه في رمضان فيدارسه','57','0','0');
INSERT INTO [t8227] VALUES (58,'كان رسول الله صلى الله عليه وسلم أجود الناس، وكان أجود ما يكون في رمضان حين يلقاه جبريل،','58','0','0');
INSERT INTO [t8227] VALUES (59,'أقرأني جبريل على حرف , فراجعته فلم أزل أستزيده فيزيدني حتى انتهى إلى سبعة','59','0','0');
INSERT INTO [t8227] VALUES (60,'أقرأني جبريل على حرف فراجعته فلم أزل أستزيده , فيزيدني حتى انتهى إلى سبعة أحرف قال ابن','60','0','0');
INSERT INTO [t8227] VALUES (61,'أقرأني جبريل على حرف , فراجعته , فلم أزل أستزيده , فيزيدني حتى انتهى إلى سبعة أحرف. قال','61','0','0');
INSERT INTO [t8227] VALUES (62,'ألا استمعتم بجلدها قالوا: يا رسول الله، وهي ميتة؟ قال: إن دباغه ذكاته','62','0','0');
INSERT INTO [t8227] VALUES (63,'هلا استمتعتم بجلدها قالوا: يا رسول الله، إنها ميتة، قال: إنما حرم أكلها أخبركم أبو الفضل','63','0','0');
INSERT INTO [t8227] VALUES (64,'يحب ويعجبه موافقة أهل الكتاب في بعض ما لم يؤمر فيه , فسدل رسول الله صلى الله عليه وسلم','64','0','0');
INSERT INTO [t8227] VALUES (65,'كان يسدل شعره , وكان المشركون يفرقون رءوسهم، وكان أهل الكتاب يسدلون رءوسهم، فكان رسول','65','0','0');
INSERT INTO [t8227] VALUES (66,'كيف تسألون أهل الكتاب عن شيء، وكتابكم الذي أنزل على نبيه صلوات الله عليه وسلم أحدث','66','0','0');
INSERT INTO [t8227] VALUES (67,'كنا فيما نقرأ , ولا ترغبوا عن آبائكم فإنه كفر بكم، أو إن كفرا بكم أن ترغبوا عن','67','0','0');
INSERT INTO [t8227] VALUES (68,'بعث بكتابه إلى كسرى، وأمر أن يدفع إلى عظيم البحرين، فدفعه عظيم البحرين إلى كسرى , فلما','68','0','0');
INSERT INTO [t8227] VALUES (69,'مروا من يصلي بالناس','69','0','0');
INSERT INTO [t8227] VALUES (70,'بينا أنا نائم أتيت بقدح من لبن، فشربت منه، ثم أعطيت فضلي عمر بن الخطاب رضي الله عنه','70','0','0');
INSERT INTO [t8227] VALUES (71,'قد كان يكون في الأمم محدثون , فإن يك في أمتي أحد، فعمر بن الخطاب رضي الله','71','0','0');
INSERT INTO [t8227] VALUES (72,'اتخذ خاتما فصه حبشي','72','0','0');
INSERT INTO [t8227] VALUES (73,'سمنتني أمي لدخولي على رسول الله صلى الله عليه وسلم. قالت: فلم أقبل عليها بشيء حتى أطعمتني','73','0','0');
INSERT INTO [t8227] VALUES (74,'دخلت على عمر بن الخطاب رضي الله عنه بالهاجرة فوجدته يسبح، فقمت وراءه، فقربني، فجعلني عن','74','0','0');
INSERT INTO [t8227] VALUES (75,'أتيت عمر بن الخطاب وهو يصلي في الظهيرة , فقمت عن يساره، فأخذ بيدي , فأخلفني عن يمينه، ثم','75','0','0');
INSERT INTO [t8227] VALUES (76,'من شرب الخمر في الدنيا حرمها في الآخرة','76','0','0');
INSERT INTO [t8227] VALUES (77,'كنت أرجل رأس رسول الله صلى الله عليه وسلم وأنا حائض','77','0','0');
INSERT INTO [t8227] VALUES (78,'كان مجاورا في المسجد , فيدني إليها رأسه , فترجله وهي حائض','78','0','0');
INSERT INTO [t8227] VALUES (79,'حلق رأسه في حجة الوداع','79','0','0');
INSERT INTO [t8227] VALUES (80,'أوصيت؟ قلت: نعم، قال: بكم؟ قلت: بمالي كله في سبيل الله والفقراء، فقال لي: أوص بالعشر قال','80','0','0');
INSERT INTO [t8227] VALUES (81,'ما من مسلم يعزي أخاه المسلم بمصيبته إلا كساه الله من حلل الكرامة يوم','81','0','0');
INSERT INTO [t8227] VALUES (82,'من صلى في يوم ثنتا عشرة سجدة، تطوعا بنى الله له بيتا في الجنة. قالت أم حبيبة: ما تركتهن','82','0','0');
INSERT INTO [t8227] VALUES (83,'عاد أبا أحيحة في مرضه الذي مات فيه مرتين وهو مشرك','83','0','0');
INSERT INTO [t8227] VALUES (84,'ارفع إزارك، فكشف الرجل عن ركبتيه، فقال: يا رسول الله، إني أحنف فتصطك ركبتاي، فقال رسول','84','0','0');
INSERT INTO [t8227] VALUES (85,'خياركم عند الله خيركم أخلاقا، وخيركم لنسائه','85','0','0');
INSERT INTO [t8227] VALUES (86,'مازال الشيطان يأكل معه حتى سمى , فلما سمى قاء , فما بقي في بطنه شيء إلا','86','0','0');
INSERT INTO [t8227] VALUES (87,'لا تسبوا الريح، فإذا رأيتم منها شيئا مما تكرهون، فقولوا: اللهم , إنا نسألك من خير هذه','87','0','0');
INSERT INTO [t8227] VALUES (88,'المرء على دين خليله، فلينظر أحدكم من يخال','88','0','0');
INSERT INTO [t8227] VALUES (89,'وذكرهم بأيام الله} [إبراهيم: ٥] قال: بأنعم الله عز وجل','89','0','0');
INSERT INTO [t8227] VALUES (90,'من أغاث ملهوفا كتب الله له ثلاثا وسبعين حسنة، واحدة منها يصلح الله بها أمر دنياه وآخرته ,','90','0','0');
INSERT INTO [t8227] VALUES (91,'دفن عمرو بن الجموح وغلامين من الأنصار في قبر واحد، وكفنهما بقميصه، وقدمهما أمام عمرو بن','91','0','0');
INSERT INTO [t8227] VALUES (92,'إذا تذكرت شجوا من أخي ثقة ... فاذكر أخاك أبا بكر بما فعلا خير البرية أتقاها وأعدلها ...','92','0','0');
INSERT INTO [t8227] VALUES (93,'يا أبا بكر، ما ظنك باثنين الله ثالثهما','93','0','0');
INSERT INTO [t8227] VALUES (94,'يا أبا بكر ما ظنك باثنين الله ثالثهما','94','0','0');
INSERT INTO [t8227] VALUES (95,'ما نفعني مال، ما نفعني مال أبي بكر، قال: فبكى أبو بكر، وقال: هل أنا ومالي إلا لك يا رسول','95','0','0');
INSERT INTO [t8227] VALUES (96,'إن عبدا من عباد الله خير ما بين الدنيا وبين ما عند ربه، فاختار ما عند ربه فبكى أبو بكر','96','0','0');
INSERT INTO [t8227] VALUES (97,'إن الله تعالى خير عبدا بين الدنيا وبين ما عنده، فاختار ذلك العبد ما عند الله فبكى أبو','97','0','0');
INSERT INTO [t8227] VALUES (98,'أعظم الناس أجرا في المصاحف أبو بكر الصديق رضي الله عنه، كان أول من جمع القرآن بين','98','0','0');
INSERT INTO [t8227] VALUES (99,'إن الله رفيق يحب الرفق، ويعطي على الرفق ما لا يعطي على العنف قال وكان يقول: خذوا بالناس','99','0','0');
INSERT INTO [t8227] VALUES (100,'يا خديجة , إن جبريل أتاني، فقال: يا محمد , ائت خديجة، فأخبرها أن ربها يقرئها السلام،','100','0','0');
INSERT INTO [t8227] VALUES (101,'كبرنا مع رسول الله صلى الله عليه وسلم على الجنائز أربعا وخمسا','101','0','0');
INSERT INTO [t8227] VALUES (102,'لله عبادا يعرفون الناس بالتوسم','102','0','0');
INSERT INTO [t8227] VALUES (103,'يوضع للأنبياء منابر من ذهب يجلسون عليها، ويبقى منبري لا أجلس عليه، أو قال: لا أقعد عليه،','103','0','0');
INSERT INTO [t8227] VALUES (104,'من كانت له سريرة صالحة أو سيئة أظهر الله عليه منها رداء يعرف به','104','0','0');
INSERT INTO [t8227] VALUES (105,'يرفع يديه في أول تكبيرة في الصلاة ثم لا يعود','105','0','0');
INSERT INTO [t8227] VALUES (106,'ألا قبلتيها منها وكافأتيها، فلا ترى أنك حقرتيها، تواضعي يا عائشة، فإن الله يحب','106','0','0');
INSERT INTO [t8227] VALUES (107,'مه يا بني، كل مما يليك','107','0','0');
INSERT INTO [t8227] VALUES (108,'من يشتريه مني؟ فاشتراه نعيم بن عبد الله بثمان مائة درهم , فدفعها إليه، قال جابر: عبدا','108','0','0');
INSERT INTO [t8227] VALUES (109,'من قرأ بالكهف يوم الجمعة فهو معصوم إلى ثمانية أيام من كل فتنة تكون، فإن خرج الدجال عصم','109','0','0');
INSERT INTO [t8227] VALUES (110,'على كل باب من المسجد سبعون ملكا يكتبون الناس بأسمائهم وأنسابهم حتى يكون آخر من يكتب رجل','110','0','0');
INSERT INTO [t8227] VALUES (111,'ألا أخذت بيد رجل فأقمته إلى جنبك أو دخلت في الصف، قم فأعد صلاتك','111','0','0');
INSERT INTO [t8227] VALUES (112,'الليلة الزهراء , واليوم الأزهر يوم الجمعة، ما من دابة إلا وهي على حذر من ليلة الجمعة إلى','112','0','0');
INSERT INTO [t8227] VALUES (113,'من قال الله أكبر لا إله إلا الله، والحمد لله، ولا حول ولا قوة إلا بالله، كفر الله عنه','113','0','0');
INSERT INTO [t8227] VALUES (114,'نعم الإدام الخل إنه هلاك بالرجل أن يدخل الرجل إلى بيته من إخوانه , ثم يحتقر ما في بيته أن','114','0','0');
INSERT INTO [t8227] VALUES (115,'صلي على سهيل ابن بيضاء في المسجد','115','0','0');
INSERT INTO [t8227] VALUES (116,'من قال: أشهد أن الله هو الحق المبين، وأن الله على كل شيء قدير، وأشهد أن الساعة آتية لا','116','0','0');
INSERT INTO [t8227] VALUES (117,'الكمأة من المن، وماؤها شفاء للعين، والعجوة من الجنة، وهي شفاء من السم','117','0','0');
INSERT INTO [t8227] VALUES (118,'اللهم , إني أحبه فأحبه','118','0','0');
INSERT INTO [t8227] VALUES (119,'ما لها لعنها الله، لو كانت تاركة أحدا تركت النبي صلى الله عليه وسلم','119','0','0');
INSERT INTO [t8227] VALUES (120,'فإن كانت عيناك لما بهما قال: إذا أصبر وأحتسب، قال: إذا تلقى الله ولا حساب','120','0','0');
INSERT INTO [t8227] VALUES (121,'بلى، أسلمت إذا كفروا، وأقبلت إذ أدبروا، ووفيت إذ غدروا، وعرفت إذ','121','0','0');
INSERT INTO [t8227] VALUES (122,'ما أنا بشيء من عملي أوثق مني بحب أصحاب محمد صلى الله عليه وسلم','122','0','0');
INSERT INTO [t8227] VALUES (123,'لقي حكيم حكيما فقال له: لا يراك الله عندما نهاك، ولا يفقدك عندما أمرك','123','0','0');
INSERT INTO [t8227] VALUES (124,'يا رب , أرني أهل صفوتك فقيل له: انطلق إلى خربة كذا وكذا، فانطلق، فإذا هو برجل ميت، قد','124','0','0');
INSERT INTO [t8227] VALUES (125,'عز المؤمن استغناؤه عن الناس، وشرفه قيامه بالليل','125','0','0');
INSERT INTO [t8227] VALUES (126,'أتؤدون زكاة الحديث؟ قال: قلنا: يا أبا نصر، وللحديث زكاة، قال: نعم، إذا سمعتم عملا أو صلاة','126','0','0');
INSERT INTO [t8227] VALUES (127,'لا يسعى بالناس إلا ولد زنى','127','0','0');
INSERT INTO [t8227] VALUES (128,'من أعرض بوجه عن صاحب بدعة بغضا له، ملأ الله قلبه يمنا وإيمانا , ومن انتهر صاحب بدعة أمنه','128','0','0');
INSERT INTO [t8227] VALUES (129,'فذكروا عثمان بن عفان، فقال علي: {إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون}','129','0','0');
INSERT INTO [t8227] VALUES (130,'المجالس بالأمانة','130','0','0');
INSERT INTO [t8227] VALUES (131,'ما هذا؟ قالوا: فلان نكح، فقال علي رضي الله عنه: ولا نكاح السر إلا نكاح العلانية، ولا نكاح','131','0','0');
INSERT INTO [t8227] VALUES (132,'أصدق الحديث كتاب الله، وأوثق العرى كلمة التقوى، وخير الملل ملة إبراهيم، وأحسن السنن سنة','132','0','0');
INSERT INTO [t8227] VALUES (133,'أربع قد فرغ الله منهن: الخلق، والخلق , والرزق، والأجل، فليس أحد بأكسب من','133','0','0');
INSERT INTO [t8227] VALUES (134,'اتقوا الله وصلوا أرحامكم','134','0','0');
INSERT INTO [t8227] VALUES (135,'لقد رأيتني سادس ستة وما على الأرض مسلم غيرنا','135','0','0');
INSERT INTO [t8227] VALUES (136,'من مات وليس له إمام، مات ميتة جاهلية','136','0','0');
INSERT INTO [t8227] VALUES (137,'من شرب الخمر فاجلدوه، فإن عاد فاجلدوه، فإن عاد فاجلدوه، فإن عاد','137','0','0');
INSERT INTO [t8227] VALUES (138,'أمر الله تعالى - يعني شجرة - فخرجت في وجه النبي صلى الله عليه وسلم لتستره، وإن الله تعالى','138','0','0');
INSERT INTO [t8227] VALUES (139,'فأمرنا رسول الله صلى الله عليه وسلم فأعتقناها','139','0','0');
INSERT INTO [t8227] VALUES (140,'نهى رسول الله صلى الله عليه وسلم عن خلط التمر والبسر , والتمر والزبيب','140','0','0');
INSERT INTO [t8227] VALUES (141,'ينهاهم عن خلط التمر والزبيب والبسر','141','0','0');
INSERT INTO [t8227] VALUES (142,'إن أعظم خطيئة عند الله تعالى بعد الكبائر التي نهى الله عنها أن يموت الرجل وعليه دين في','142','0','0');
INSERT INTO [t8227] VALUES (143,'أيها الناس، إني لا علم لي بهذا حتى سمعتموه، ألا وإنه يجير على الناس','143','0','0');
INSERT INTO [t8227] VALUES (144,'لا إله إلا الله، وحده لا شريك له، له الملك وله الحمد، اللهم لا مانع لما أعطيت، ولا معطي','144','0','0');
INSERT INTO [t8227] VALUES (145,'صلاة الليل مثنى مثنى، فإذا خفت الفجر فأوتر بواحدة','145','0','0');
INSERT INTO [t8227] VALUES (146,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله، فمن قالها فقد عصم مني ماله ونفسه إلا بحقه','146','0','0');
INSERT INTO [t8227] VALUES (147,'أشاهد فلان؟ قالوا: نعم، قال: إن هاتين الصلاتين أثقل الصلوات على المنافقين , ولو يعلمون ما','147','0','0');
INSERT INTO [t8227] VALUES (148,'لكل أمة مجوس وإن هؤلاء القدرية مجوس أمتي، فإن مرضوا فلا تعودوهم، وإن ماتوا فلا','148','0','0');
INSERT INTO [t8227] VALUES (149,'توضأ , ومسح على خفيه , وصلى','149','0','0');
INSERT INTO [t8227] VALUES (150,'من توضأ , فأحسن الوضوء خرجت خطاياه من مسامعه وبصره ويديه ورجليه فقال أبو ظبية الحمصي','150','0','0');
INSERT INTO [t8227] VALUES (151,'ما من عبد يتوضأ , فيغسل يديه إلا خرت خطاياه من يديه، ثم يغسل وجهه إلا خرت خطاياه من وجهه،','151','0','0');
INSERT INTO [t8227] VALUES (152,'إن أهل الجنة ليتراءون أهل الغرف من فوقهم كما تراءون الكوكب الدري الغابر، أو الطالع في','152','0','0');
INSERT INTO [t8227] VALUES (153,'إن هذا المال خضرة حلوة، فمن أخذه بحقه فنعم المعون هو','153','0','0');
INSERT INTO [t8227] VALUES (154,'خطب ميمونة بنت الحارث، فجعلت أمرها بيد العباس، فزوجها النبي صلى الله عليه','154','0','0');
INSERT INTO [t8227] VALUES (155,'ستأتي عليكم ليلة، مثل ثلاث ليال من لياليكم هذه، فإذا كانت عرفها المتهجدون، يقوم الرجل','155','0','0');
INSERT INTO [t8227] VALUES (156,'من دخل السوق , فقال: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، وهو على كل شيء','156','0','0');
INSERT INTO [t8227] VALUES (157,'لا يخطب الرجل على خطبة أخيه , ولا يبع على بيعه قال ابن صاعد , وعمران قد سمع من الحسن ,','157','0','0');
INSERT INTO [t8227] VALUES (158,'البيعان بالخيار ما لم يتفرقا','158','0','0');
INSERT INTO [t8227] VALUES (159,'قد كنا نفعل هذا , ثم أمرنا الله تعالى أن نرفع إلى الركب','159','0','0');
INSERT INTO [t8227] VALUES (160,'يركع ويضع يديه على ركبتيه، ويتجافى بعضديه من غير أن يزيل كفيه عن','160','0','0');
INSERT INTO [t8227] VALUES (161,'اعربوا القرآن , والتمسوا غرائبه','161','0','0');
INSERT INTO [t8227] VALUES (162,'من جاء منكم الجمعة فليغتسل','162','0','0');
INSERT INTO [t8227] VALUES (163,'كفن رسول الله صلى الله عليه وسلم في ثلاثة أثواب بيض سحولية من ثياب','163','0','0');
INSERT INTO [t8227] VALUES (164,'قراءتك على العالم , وقراءة العالم عليك واحد، أو قال: سواء','164','0','0');
INSERT INTO [t8227] VALUES (165,'قراءتك على العالم، وقراءته عليك سواء','165','0','0');
INSERT INTO [t8227] VALUES (166,'الرجل الذي ليس في جوفه شيء من القرآن كالبيت الخرب','166','0','0');
INSERT INTO [t8227] VALUES (167,'فدخل , ثم خرج، فأمر بلالا , فأذن وأقام، فصلى، ثم خطب، فقال: {يا أيها الناس اتقوا ربكم','167','0','0');
INSERT INTO [t8227] VALUES (168,'لا يسن عبد سنة صالحة يعمل بها بعده إلا كان له مثل أجرها، ولا ينقص من أجورهم شيء، ومن سن','168','0','0');
INSERT INTO [t8227] VALUES (169,'لا آكل متكئا','169','0','0');
INSERT INTO [t8227] VALUES (170,'لا آكل متكئا','170','0','0');
INSERT INTO [t8227] VALUES (171,'أحدا يحبنا ونحبه','171','0','0');
INSERT INTO [t8227] VALUES (172,'كتب إلى كسرى وقيصر، وإلى كل جبار يدعوهم إلى الله عز وجل','172','0','0');
INSERT INTO [t8227] VALUES (173,'كتب إلى بكر بن وائل: من محمد رسول الله إلى بكر بن وائل، أسلموا تسلموا قال: فما وجدوا من','173','0','0');
INSERT INTO [t8227] VALUES (174,'سبحان الله، لن تستطيعه - أو لن تطيقه - فهلا قلت: اللهم , آتنا في الدنيا حسنة , وفي الآخرة','174','0','0');
INSERT INTO [t8227] VALUES (175,'إذا أراد الله بعبد خيرا عجل له عقوبة ذنبه، وإذا أراد بعبد شرا أمسك عقوبة ذنبه حتى يلقاه','175','0','0');
INSERT INTO [t8227] VALUES (176,'أما إنه لا يجني عليك , ولا تجني عليه، ثم قرأ رسول الله صلى الله عليه وسلم: {ولا تزر وازرة','176','0','0');
INSERT INTO [t8227] VALUES (177,'نهى عن بيع حبل الحبلة','177','0','0');
INSERT INTO [t8227] VALUES (178,'ما رأيت قط مثل أيوب ويونس وابن عون','178','0','0');
INSERT INTO [t8227] VALUES (179,'ما رأيت بالبصرة مثل أيوب , ولا بالكوفة مثل مسعر','179','0','0');
INSERT INTO [t8227] VALUES (180,'كان اسم زينب برة، فقالوا: تزكي نفسها، فسماها رسول الله صلى الله عليه وسلم','180','0','0');
INSERT INTO [t8227] VALUES (181,'كنت إذا رأيت سليمان التيمي كأنه غلام حدث دخل في العبادة، وكانوا يرون أنه أخذ عبادته، عن','181','0','0');
INSERT INTO [t8227] VALUES (182,'ما أتينا سليمان التيمي في ساعة يطاع الله فيه إلا وجدناه مطيعا، وكنا نرى أنه لا يحسن يعصي','182','0','0');
INSERT INTO [t8227] VALUES (183,'يا معتمر , حدثني بالرخص لعلي ألقى الله تعالى وأنا حسن الظن به','183','0','0');
INSERT INTO [t8227] VALUES (184,'حرمت الخمرة بعينها , والمسكر من كل شراب قال أبو عبد الله: حدث به شريك، عن أبي عون، فقال','184','0','0');
INSERT INTO [t8227] VALUES (185,'كانت خمرهم يومئذ - يعني الفضيخ قال أبو عبد الله: وقال يحيى مرة أخرى: قد حرمت يوم حرمت ,','185','0','0');
INSERT INTO [t8227] VALUES (186,'من أنفق زوجا مما يملك في سبيل الله عز وجل، كل خزنة الجنة يناديه، يا عبد الله، يا مسلم هذا','186','0','0');
INSERT INTO [t8227] VALUES (187,'من كذب بالقدر أو خاصمهم فقد كفر بما جئت به','187','0','0');
INSERT INTO [t8227] VALUES (188,'الخير كثير وقليل فاعله','188','0','0');
INSERT INTO [t8227] VALUES (189,'ثلاث من كن فيه فهو منافق , وإن صام وصلى وزعم أنه مسلم، من إذا حدث كذب , وإذا وعد أخلف،','189','0','0');
INSERT INTO [t8227] VALUES (190,'فلما رفع رأسه من الركعة الثانية قام هنية','190','0','0');
INSERT INTO [t8227] VALUES (191,'ما من مسلم دعا الله تعالى بدعوة ليس فيها قطيعة رحم. ولا إثم إلا أعطاه الله عز وجل بها','191','0','0');
INSERT INTO [t8227] VALUES (192,'من تفرد بدم رجل فقتله فله سلبه قال: فجاء أبو طلحة بسلب أحد وعشرين رجلا','192','0','0');
INSERT INTO [t8227] VALUES (193,'من قتل فله السلب','193','0','0');
INSERT INTO [t8227] VALUES (194,'ليس منا من لم يأخذ شاربه','194','0','0');
INSERT INTO [t8227] VALUES (195,'يحفي شاربه','195','0','0');
INSERT INTO [t8227] VALUES (196,'يحفي شاربه حتى كأنه قد حلقه','196','0','0');
INSERT INTO [t8227] VALUES (197,'انهكوا الشوارب , واعفوا اللحى','197','0','0');
INSERT INTO [t8227] VALUES (198,'يحفي شاربه','198','0','0');
INSERT INTO [t8227] VALUES (199,'أخذ الشارب من الدين','199','0','0');
INSERT INTO [t8227] VALUES (200,'كنا نؤمر أن نوفر، السبال , ونأخذ من الشارب','200','0','0');
INSERT INTO [t8227] VALUES (201,'إنه ليغان على قلبي، فأستغفر الله عز وجل في اليوم مائة مرة','201','0','0');
INSERT INTO [t8227] VALUES (202,'كنت أنا والنبي صلى الله عليه وسلم نغتسل من إناء واحد','202','0','0');
INSERT INTO [t8227] VALUES (203,'إني لأعلم أنك حجر، وإن الله تعالى ربي، ولولا أني رأيت رسول الله صلى الله عليه وسلم يقبلك','203','0','0');
INSERT INTO [t8227] VALUES (204,'لا أحلف على يمين وأرى غيرها خيرا منها إلا أتيت الذي هو خير، وكفرت عن','204','0','0');
INSERT INTO [t8227] VALUES (205,'اللهم , اغفر لعبد القيس - مرتين - أتوا إلى الإسلام غير خزايا , ولا موتورين لم يراموا في','205','0','0');
INSERT INTO [t8227] VALUES (206,'يا مغيرة، أقر الخفين قرارهما، إني أدخلت الخفين , وهما طاهرتان , فتوضأ رسول الله صلى الله','206','0','0');
INSERT INTO [t8227] VALUES (207,'سيأتيكم بعدي ولاة يليكم البر ببره، ويليكم الفاجر بفجوره، فاسمعوا له وأطيعوا في كل ما وافق','207','0','0');
INSERT INTO [t8227] VALUES (208,'نهى عن المتعة يوم الفتح','208','0','0');
INSERT INTO [t8227] VALUES (209,'فأمره أن يعتكفها','209','0','0');
INSERT INTO [t8227] VALUES (210,'اذهب فواره قال: ما أنا بمواره قال: فمن يواره؟ اذهب فواره، ولا تحدثن شيئا حتى تأتي، قال','210','0','0');
INSERT INTO [t8227] VALUES (211,'أدرج رسول الله صلى الله عليه وسلم في ثوب حبرة، ثم أخرج عنه. قالت: ثم , بقي ذلك الثوب','211','0','0');
INSERT INTO [t8227] VALUES (212,'عن قول لا إله إلا الله','212','0','0');
INSERT INTO [t8227] VALUES (213,'إذا تقرب عبدي مني شبرا تقربت منه ذراعا، وإذا تقرب مني ذراعا، تقربت منه باعا، وإن تقدم مني','213','0','0');
INSERT INTO [t8227] VALUES (214,'وشاهد ومشهود} [البروج: ٣]: يوم عرفة، ويوم الجمعة، ويوم الموعود يوم','214','0','0');
INSERT INTO [t8227] VALUES (215,'القبلة حسنة والحسنة عشرة','215','0','0');
INSERT INTO [t8227] VALUES (216,'لا تأتي المائة وعلى ظهرها أحد حي فحدثت بها ابن حجيرة، فقام عبد الرحمن بن حجيرة، ودخل إلى','216','0','0');
INSERT INTO [t8227] VALUES (217,'يرمي الجمرة وعليه إزار مرقوع بقطعة جراب','217','0','0');
INSERT INTO [t8227] VALUES (218,'كيف أنت إذا رأيت الدم يجري في حجارة الزيت كما يجري الماء في النهر، وصار القبر، وكان من','218','0','0');
INSERT INTO [t8227] VALUES (219,'من نجى من ثلاث فقد نجى من نجى من ثلاث فقد نجى، قالوا: يا رسول الله، وما ذاك؟ قال: موتي،','219','0','0');
INSERT INTO [t8227] VALUES (220,'كان عامة ما ينصرف من الصلاة عن يساره إلى الحجرات','220','0','0');
INSERT INTO [t8227] VALUES (221,'ألا أستحي من رجل تستحي منه الملائكة، أخبركم أبو الفضل الزهري، نا محمد، نا أحمد بن','221','0','0');
INSERT INTO [t8227] VALUES (222,'اللهم , أسلمت نفسي إليك، وفوضت أمري إليك، وألجأت ظهري إليك، رهبة ورغبة إليك، لا ملجأ ولا','222','0','0');
INSERT INTO [t8227] VALUES (223,'أحب الناس إلى رسول الله صلى الله عليه وسلم؟ قالت: أبو بكر - قلت: ثم من؟ قالت: ثم عمر -','223','0','0');
INSERT INTO [t8227] VALUES (224,'إنا لا نستعمل على عملنا من أراده ولكن اذهب أنت يا أبا موسى - أو يا عبد الله بن قيس -','224','0','0');
INSERT INTO [t8227] VALUES (225,'لن أو لا نستعمل على عملنا هذا من أراده، ولكن اذهب أنت يا أبا موسى - أو يا عبد الله بن','225','0','0');
INSERT INTO [t8227] VALUES (226,'الرؤيا جزء من ستة وأربعين جزءا من النبوة، وأصدقهم رؤيا أصدقهم حديثا، والرؤيا ثلاثة منها','226','0','0');
INSERT INTO [t8227] VALUES (227,'طهور إناء أحدكم الكلب إذا ولغ في الإناء سبع مرات','227','0','0');
INSERT INTO [t8227] VALUES (228,'سجد أبو بكر وعمر رضي الله عنهما في {إذا السماء انشقت} [الانشقاق: ١] ومن هو خير','228','0','0');
INSERT INTO [t8227] VALUES (229,'سجد في إذا السماء انشقت واقرأ باسم ربك الذي خلق أبو بكر وعمر، ومن هو خير من أبي بكر وعمر','229','0','0');
INSERT INTO [t8227] VALUES (230,'لا حليم إلا ذو عثرة، ولا حكيم إلا ذو تجربة','230','0','0');
INSERT INTO [t8227] VALUES (231,'هذه طيبة، أسكنيها ربي عز وجل , تنفي الخبث كما ينفي الكير خبث الحديد، فلا يكلمن أحد منكم','231','0','0');
INSERT INTO [t8227] VALUES (232,'رأيت على النبي صلى الله عليه وسلم حلة حمراء في ليلة إضحيان، فجعلت أنظر إليه وإلى القمر،','232','0','0');
INSERT INTO [t8227] VALUES (233,'شيبتني هود وأخواتها','233','0','0');
INSERT INTO [t8227] VALUES (234,'فأمر لنا باثنا عشر قلوصا، فذهبنا لنأخذها، فأتينا وفاته، قلت: صفه لي: قال: كان أبيض','234','0','0');
INSERT INTO [t8227] VALUES (235,'معقبات لا يخيب قائلهن: يسبح في دبر كل صلاة ثلاثا وثلاثين، ويحمده ثلاثا وثلاثين، ويكبر','235','0','0');
INSERT INTO [t8227] VALUES (236,'خرج رجل من بني سهم مع تميم الداري، وعدي بن بداء، فمات السهمي بأرض ليس بها مسلم، فلما قدما','236','0','0');
INSERT INTO [t8227] VALUES (237,'من كانت الآخرة همه كف الله عليه ضيعته، وجعل غناه في قلبه، ومن كانت الدنيا همه ونيته','237','0','0');
INSERT INTO [t8227] VALUES (238,'ارجع , فقل: السلام عليكم، أأدخل. وذلك بعد ما أسلم صفوان، ثم قال عمرو: أخبرني هذا الحديث','238','0','0');
INSERT INTO [t8227] VALUES (239,'من سره أن ينظر إلى امرأة من الحور العين فلينظر إلى هذه','239','0','0');
INSERT INTO [t8227] VALUES (240,'ما فتح الله على عاد من الريح التي أهلكوا فيها إلا مثل موضع الخاتم، فمرت على أهل البادية ,','240','0','0');
INSERT INTO [t8227] VALUES (241,'يدعى إلى خبز الشعير والإهالة السنخة فيجيب، ولقد كانت له درع رهن عند يهودي , فما وجد ما','241','0','0');
INSERT INTO [t8227] VALUES (242,'لا تذهب الدنيا حتى يأتي على الناس يوم لا يدري القاتل فيم قتل، ولا المقتول فيم قتل، قيل','242','0','0');
INSERT INTO [t8227] VALUES (243,'الفتنة تجيء من ها هنا، وأومأ بيده نحو المشرق من حيث يطلع قرن الشيطان، وأنتم يضرب بعضكم','243','0','0');
INSERT INTO [t8227] VALUES (244,'أولا تدرين يا عائشة أن الله تعالى خلق الجنة , فخلق لها أهلا، وخلق النار فخلق لها','244','0','0');
INSERT INTO [t8227] VALUES (245,'أغمي على رجل من جهينة , فظنوا أنه مات، فهم جلوس حوله , فحفروا له، إذ أفاق، فقال: ما فعل','245','0','0');
INSERT INTO [t8227] VALUES (246,'اشترى رسول الله صلى الله عليه وسلم من يهودي طعاما , وأرهنه درعه','246','0','0');
INSERT INTO [t8227] VALUES (247,'بينما الناس يسيرون في البحر , فنفد طعامهم، فرفعت لهم جزيرة , فخرجوا يريدون الخبز، فلقيتهم','247','0','0');
INSERT INTO [t8227] VALUES (248,'عهد إلي النبي صلى الله عليه وسلم أنه لا يحبني إلا مؤمن، ولا يبغضني إلا منافق. أخبركم أبو','248','0','0');
INSERT INTO [t8227] VALUES (249,'هذه لعبد الله، لأمين الله , وخليفته ليس فيها سوية، والله، لو أمرت رجلا يخرج من باب المسجد','249','0','0');
INSERT INTO [t8227] VALUES (250,'تسألوني عن الشيخ الكافر؟','250','0','0');
INSERT INTO [t8227] VALUES (251,'شمرت ثيابك , وحللت إزارك , وقلت: إن الحجاج مؤمن ضال، وكيف يجتمع في رجل إيمان وضلال،','251','0','0');
INSERT INTO [t8227] VALUES (252,'لقد فررت حتى استحييت من الله تعالى. قال: فقلت: والله , إني لأراك كما سمتك أمك. قال أبو','252','0','0');
INSERT INTO [t8227] VALUES (253,'لماشي الحج سبع مائة حسنة من حسنات الحرم، كل حسنة مائة ألف حسنة، وللراكب في الحج سبعون','253','0','0');
INSERT INTO [t8227] VALUES (254,'من عاد مريضا فلا يزال في الرحمة، حتى إذا قعد عنده استنقع فيها، ثم إذا قام من عنده فلا','254','0','0');
INSERT INTO [t8227] VALUES (255,'يذبح الشاة فيتيمم بأعضائها صدائق خديجة','255','0','0');
INSERT INTO [t8227] VALUES (256,'إذا قام أحدكم من مبيته فليفرغ على يده الماء يغسلها , فإنه لا يدري أين باتت','256','0','0');
INSERT INTO [t8227] VALUES (257,'ما يصيب المسلم من نصب ولا وصب ولاهم، ولا حزن ولا غم، ولا أذى، حتى الشوكة يشاكها، إلا كفر','257','0','0');
INSERT INTO [t8227] VALUES (258,'ما رأيت أحدا قطع في طير، وما أرى عليه في ذلك قطعا','258','0','0');
INSERT INTO [t8227] VALUES (259,'إني والله ما اغتسلت من أجله , ولكني وجدت حرا','259','0','0');
INSERT INTO [t8227] VALUES (260,'فما رأيته صلى بعد الفجر، ولا بعد العصر','260','0','0');
INSERT INTO [t8227] VALUES (261,'سأل زيد بن ثابت عن النجم فيها سجدة؟ قال زيد: قرأتها عند رسول الله , فلم يسجد وقال عطاء','261','0','0');
INSERT INTO [t8227] VALUES (262,'أردت الجمعة في زمن الحجاج، فتهيئت للذهاب. فقلت: أين أذهب؟ أصلي خلف هذا، فقلت مرة: أذهب،','262','0','0');
INSERT INTO [t8227] VALUES (263,'ما رأيت رسول الله صلى الله عليه وسلم صلى المغرب قط حتى يفطر، ولو على شربة','263','0','0');
INSERT INTO [t8227] VALUES (264,'إنه إن اقتطع أرضك بيمينه كان ممن لا ينظر الله إليه يوم القيامة، ولا يزكيه , وله عذاب','264','0','0');
INSERT INTO [t8227] VALUES (265,'إني بعثت إلى أمة فيهم الغلام , وفيهم الجارية، وفيهم العجوز، وفيهم الشيخ الفان قال: فمرهم','265','0','0');
INSERT INTO [t8227] VALUES (266,'إذا جاء من سفر، تلقي بصبيان أهل بيته، وأنه قدم مرة فتناولني إليه فحملني بين يديه، ثم','266','0','0');
INSERT INTO [t8227] VALUES (267,'دعوا لي أصحابي، أو أصيحابي، فإن أحدكم لو أنفق مثل أحد ذهبا، لم يدرك مد أحدهم ولا','267','0','0');
INSERT INTO [t8227] VALUES (268,'أما ليلة القدر، فالتمسوها في العشر الأواخر وترا','268','0','0');
INSERT INTO [t8227] VALUES (269,'صلى الله عليك يا عمر، فما أجد من هذه الأمة أحب إلي ألقى الله بمثل صحيفته','269','0','0');
INSERT INTO [t8227] VALUES (270,'اللهم اجعل في قلبي نورا، واجعل في لساني نورا، واجعل في سمعي نورا، واجعل في بصري نورا،','270','0','0');
INSERT INTO [t8227] VALUES (271,'من باع نخلا قد أبرها , فالثمر للبائع إلا أن يشترط المشتري','271','0','0');
INSERT INTO [t8227] VALUES (272,'نهى عن المزابنة والمزابنة: بيع الثمرة بخرصها','272','0','0');
INSERT INTO [t8227] VALUES (273,'فأمر بهما رسول الله صلى الله عليه وسلم , فرجما قال عبد الله بن عمر: كنت فيمن','273','0','0');
INSERT INTO [t8227] VALUES (274,'لعن الله اليهود، لعن الله اليهود، لعن الله اليهود، حرمت عليهم الشحوم، فباعوها وأكلوا','274','0','0');
INSERT INTO [t8227] VALUES (275,'اخنث الإداوة، ثم شرب منها','275','0','0');
INSERT INTO [t8227] VALUES (276,'أشبههم وجها برسول الله صلى الله عليه وسلم','276','0','0');
INSERT INTO [t8227] VALUES (277,'كان يترك العمل وهو يحب أن يعمله كراهية أن يستن الناس به، فيفرض عليهم، وكان يحب ما خف عنهم','277','0','0');
INSERT INTO [t8227] VALUES (278,'إذا توضأت , فأكمل الوضوء، ثم استقبل القبلة، ثم كبر، ثم اقرأ بما معك من القرآن، أو بما','278','0','0');
INSERT INTO [t8227] VALUES (279,'لولا أن أشق على أمتي لأمرتهم بالسواك مع الوضوء، ولأمرتهم أن يؤخروا العشاء إلى ثلث الليل','279','0','0');
INSERT INTO [t8227] VALUES (280,'من صلى على جنازة فله قيراط، ومن انتظر دفنها فله قيراطان','280','0','0');
INSERT INTO [t8227] VALUES (281,'كان عامة ما ينصرف من الصلاة عن يساره إلى الحجرات','281','0','0');
INSERT INTO [t8227] VALUES (282,'من توضأ يوم الجمعة فأحسن الوضوء، ثم أتى الجمعة فدنا، وأنصت واستمع، غفر له ما بينه وبين','282','0','0');
INSERT INTO [t8227] VALUES (283,'قافية رأس أحدكم بالليل حبل فيه ثلاث عقد، فإذا استيقظ، فذكر الله تعالى، انحلت عقدة، وإذا','283','0','0');
INSERT INTO [t8227] VALUES (284,'انظروا إلى من هو أسفل منكم , ولا تنظروا إلى من هو فوقكم، فإنه أجدر أن لا تزدروا نعم الله','284','0','0');
INSERT INTO [t8227] VALUES (285,'لقد هممت أن لا أتخلف عن سرية تخرج في سبيل الله، ولكن ليس عندي ما يحملهم، ولوددت أني أقتل','285','0','0');
INSERT INTO [t8227] VALUES (286,'كنت أفتل قلائد هدي رسول الله صلى الله عليه وسلم , فيبعث بها , ثم لا يدع شيئا مما كان يصنع','286','0','0');
INSERT INTO [t8227] VALUES (287,'مره فليراجعها حتى تطهر، ثم تحيض، ثم تطهر','287','0','0');
INSERT INTO [t8227] VALUES (288,'لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة لك والملك، لا شريك لك قال: وكان','288','0','0');
INSERT INTO [t8227] VALUES (289,'لا تبايعوا الثمر حتى يبدو صلاحه','289','0','0');
INSERT INTO [t8227] VALUES (290,'من أوسط ما نطعم أهلنا: الخبز والتمر، وخير ما نطعم أهلنا الخبز واللحم','290','0','0');
INSERT INTO [t8227] VALUES (291,'يقرأ في الصبح يوم الجمعة بتنزيل السجدة، وهل أتى على الإنسان حين من','291','0','0');
INSERT INTO [t8227] VALUES (292,'الشهر تسع وعشرون','292','0','0');
INSERT INTO [t8227] VALUES (293,'كفى لامرئ من الإثم أن يضيع من يعول قال: ثم سألوا عبد الله بن عمرو عن يأجوج ومأجوج , من','293','0','0');
INSERT INTO [t8227] VALUES (294,'ما جعل الله تعالى في شيء منها مثقال حبة من خردل من خير، وما هي إلا زينة من الشيطان، وما','294','0','0');
INSERT INTO [t8227] VALUES (295,'أمرها بقتل الأوزاغ','295','0','0');
INSERT INTO [t8227] VALUES (296,'يسجد في أعلى جبهته على قصاص الشعر','296','0','0');
INSERT INTO [t8227] VALUES (297,'أتريدين أن ترجعي إلى رفاعة، لا حتى تذوقي من عسيلته , ويذوق من عسيلتك','297','0','0');
INSERT INTO [t8227] VALUES (298,'قل: اللهم لك الحمد كله، ولك الخلق كله، وإليك يرجع الأمر كله، أسألك من الخير كله، وأعوذ بك','298','0','0');
INSERT INTO [t8227] VALUES (299,'المسلم من سلم المسلمون من لسانه ويده','299','0','0');
INSERT INTO [t8227] VALUES (300,'قضى باليمين مع الشاهد','300','0','0');
INSERT INTO [t8227] VALUES (301,'زر غبا تزدد حبا','301','0','0');
INSERT INTO [t8227] VALUES (302,'ما يؤمن أحدكم إذا رفع رأسه قبل الإمام أن يحول الله رأسه رأس كبش','302','0','0');
INSERT INTO [t8227] VALUES (303,'يصلي في ثوب واحد متوشحا به','303','0','0');
INSERT INTO [t8227] VALUES (304,'اضرب بهذا الحائط، فإن هذا شراب من لا يؤمن بالله , ولا باليوم الآخر','304','0','0');
INSERT INTO [t8227] VALUES (305,'إن من الشعر حكمة','305','0','0');
INSERT INTO [t8227] VALUES (306,'نهى عن كسب الإماء','306','0','0');
INSERT INTO [t8227] VALUES (307,'الأئمة من قريش، ما استرحموا رحموا، وما عاهدوا وفوا','307','0','0');
INSERT INTO [t8227] VALUES (308,'ما أحسن الإسلام، ويزينه الإيمان، وما أحسن الإيمان، ويزينه التقوى، وما أحسن التقوى، ويزينه','308','0','0');
INSERT INTO [t8227] VALUES (309,'ليس على مختلس، ولا منتهب، ولا خائن، قطع','309','0','0');
INSERT INTO [t8227] VALUES (310,'سيليكم أمراء يفسدون، وما يصلح الله تعالى بهم أكثر، فمن عمل منهم بطاعة الله، فلهم الأجر،','310','0','0');
INSERT INTO [t8227] VALUES (311,'يقبض الله تعالى الأرضين يوم القيامة، ويطوي السماء بيمينه، ثم يقول: أنا الملك أين ملوك','311','0','0');
INSERT INTO [t8227] VALUES (312,'يحب الرفق في الأمور كلها','312','0','0');
INSERT INTO [t8227] VALUES (313,'ألا أخبركم على من تحرم النار غدا، على كل هين لين قريب سهل','313','0','0');
INSERT INTO [t8227] VALUES (314,'لا تتمنوا الموت، فإن هول المطلع شديد، وإن من السعادة أن يطيل الله سبحانه وتعالى عمر','314','0','0');
INSERT INTO [t8227] VALUES (315,'يهلل بهن دبر كل صلاة','315','0','0');
INSERT INTO [t8227] VALUES (316,'أمسينا وأمسى الملك لله، والحمد لله، لا إله إلا الله وحده لا شريك له، اللهم إني أسألك من','316','0','0');
INSERT INTO [t8227] VALUES (317,'أصبحنا على فطرة الإسلام , وكلمة الإخلاص , ودين نبينا محمد صلى الله عليه وسلم، وملة أبينا','317','0','0');
INSERT INTO [t8227] VALUES (318,'اللهم , بك أصبحنا، وبك نحيا، وبك نموت، وإليك النشور، وإذا أمسى قال: اللهم , بك أمسينا،','318','0','0');
INSERT INTO [t8227] VALUES (319,'ما من مسلم، أو إنسان، أو عبد يقول حين يمسي وحين يصبح ثلاث مرات: رضيت بالله ربا، وبالإسلام','319','0','0');
INSERT INTO [t8227] VALUES (320,'من قال: رضيت بالله ربا، وبالإسلام دينا، وبمحمد رسولا، وجبت له الجنة','320','0','0');
INSERT INTO [t8227] VALUES (321,'شأنكم بها , ولم يصل عليها','321','0','0');
INSERT INTO [t8227] VALUES (322,'اللهم , اغفر لحينا وميتنا , وشاهدنا وغائبنا , وصغيرنا وكبيرنا , وذكرنا وأنثانا قال: وحدث','322','0','0');
INSERT INTO [t8227] VALUES (323,'نعم، فلما ولى قال: فقال: هذا جبريل عليه السلام يقول: إلا أن يكون عليه','323','0','0');
INSERT INTO [t8227] VALUES (324,'نعم، فلما أدبر قال: فقال جبريل عليه السلام يقول: إلا أن يكون عليه دين','324','0','0');
INSERT INTO [t8227] VALUES (325,'اللهم , في الرفيق الأعلى، في الرفيق الأعلى. قالت: ثم قبض صلى الله عليه','325','0','0');
INSERT INTO [t8227] VALUES (326,'كل شراب أسكر فهو حرام','326','0','0');
INSERT INTO [t8227] VALUES (327,'دخل يوم الفتح وعليه عمامة سوداء','327','0','0');
INSERT INTO [t8227] VALUES (328,'من دخل على مريض لم تحضر وفاته , فقال: أسأل الله العظيم رب العرش العظيم أن يشفيك سبع مرات،','328','0','0');
INSERT INTO [t8227] VALUES (329,'اللهم , اغفر لي ما عملت , وما لم أعمل','329','0','0');
INSERT INTO [t8227] VALUES (330,'تحلي بها يا بنية','330','0','0');
INSERT INTO [t8227] VALUES (331,'رخص رسول الله صلى الله عليه وسلم في الرقية من كل ذي حمة','331','0','0');
INSERT INTO [t8227] VALUES (332,'يوم تبدل الأرض غير الأرض والسموات} , فأين الناس يومئذ؟ قال: على الصراط','332','0','0');
INSERT INTO [t8227] VALUES (333,'إذا وضع رجله في الغرز، وانبعثت به راحلته، أهل من ذي الحليفة','333','0','0');
INSERT INTO [t8227] VALUES (334,'كان إذا قدم مكة في حج أو عمرة رمل بالبيت ثلاثة أشواط، ومشى أربعا، ويقول: كذا كان يفعل','334','0','0');
INSERT INTO [t8227] VALUES (335,'إنك لا تدري في أي طعامك تكون البركة','335','0','0');
INSERT INTO [t8227] VALUES (336,'أقطع الزبير نخلا','336','0','0');
INSERT INTO [t8227] VALUES (337,'أقطع الزبير أرضا يقال لها ثرير','337','0','0');
INSERT INTO [t8227] VALUES (338,'لن يحن عليكم بعدي إلا الصالحين , سقى الله ابن عوف من سلسبيل الجنة','338','0','0');
INSERT INTO [t8227] VALUES (339,'آمين، إذا قال: {ولا الضالين} [الفاتحة: ٧]','339','0','0');
INSERT INTO [t8227] VALUES (340,'يحبسون حتى يبلغ الرشح أنصاف آذانهم','340','0','0');
INSERT INTO [t8227] VALUES (341,'عمرة رمضان تعدل حجة','341','0','0');
INSERT INTO [t8227] VALUES (342,'قص من شعره بمشقص , فقلنا لابن عباس: ما بلغنا هذا إلا عن معاوية , فقال ابن عباس رضي الله','342','0','0');
INSERT INTO [t8227] VALUES (343,'أوتر بركعة فقال: إنه قد صحب النبي صلى الله عليه وسلم','343','0','0');
INSERT INTO [t8227] VALUES (344,'عجب لأمر المؤمن، إن أمره كله له خير، وليس ذاك لأحد إلا للمؤمن، إن أصابته سراء شكر، فكان','344','0','0');
INSERT INTO [t8227] VALUES (345,'من لقي الله تعالى لا يشرك به دخل الجنة، ومن لقيه مشركا دخل النار. أخبركم أبو الفضل','345','0','0');
INSERT INTO [t8227] VALUES (346,'اسمح يسمح لك','346','0','0');
INSERT INTO [t8227] VALUES (347,'نهيتكم عن النبيذ فانتبذوا، ولا أحل مسكرا','347','0','0');
INSERT INTO [t8227] VALUES (348,'لإبليس - لعنه الله - مردة من الشياطين، يقول لهم: عليكم بالحاج والمجاهدين , فأضلوهم عن','348','0','0');
INSERT INTO [t8227] VALUES (349,'ما من مسلم يموت إلا جعل الله تعالى مكانه رجلا من اليهود والنصارى في','349','0','0');
INSERT INTO [t8227] VALUES (350,'لا تصوموا قبل رمضان يوما، صوموا لرؤيته، وأفطروا لرؤيته، فإن غم عليكم فأكملوا','350','0','0');
INSERT INTO [t8227] VALUES (351,'نهى رسول الله صلى الله عليه وسلم عن الدباء والحنتم والنقير والمزفت , وأن يخلط الزهو','351','0','0');
INSERT INTO [t8227] VALUES (352,'أتبع السيئة الحسنة تمحها، وخالق الناس خلقا حسنا','352','0','0');
INSERT INTO [t8227] VALUES (353,'قل: اللهم , إنك عالم الغيب والشهادة، فاطر السماوات والأرض، رب كل شيء ومليكه، أشهد أن لا','353','0','0');
INSERT INTO [t8227] VALUES (354,'رأيت سفيان الثوري، وقد أردف ابن أخته خلفه على حمار','354','0','0');
INSERT INTO [t8227] VALUES (355,'أكثر من أن تقول: سبحان الملك القدوس، رب الملائكة والروح، جللت السماوات والأرض بالعزة','355','0','0');
INSERT INTO [t8227] VALUES (356,'من شهد أن لا إله إلا الله وحده لا شريك له , وأن محمدا عبده ورسوله، وأن الجنة حق، وأن','356','0','0');
INSERT INTO [t8227] VALUES (357,'هذان سيدا كهول أهل الجنة من الأولين والآخرين، إلا النبيين والمرسلين','357','0','0');
INSERT INTO [t8227] VALUES (358,'أحب النساء إلى رسول الله صلى الله عليه وسلم فاطمة، ومن الرجال علي رضي الله','358','0','0');
INSERT INTO [t8227] VALUES (359,'كل نسب وصهر ينقطع يوم القيامة إلا نسبي وصهري','359','0','0');
INSERT INTO [t8227] VALUES (360,'ربما أهديت لنا الطرفة، فنقول: لولا صومك قربناها إليك، فيدعوا بها فيفطر','360','0','0');
INSERT INTO [t8227] VALUES (361,'أما إن الله تعالى قد وصلك بجناحين تطير بهما في الجنة كما وصلت جناح ابن','361','0','0');
INSERT INTO [t8227] VALUES (362,'رأيت جعفر له جناحين في الجنة يطير بهما','362','0','0');
INSERT INTO [t8227] VALUES (363,'وقبل ما بين عينيه','363','0','0');
INSERT INTO [t8227] VALUES (364,'عانقه النبي صلى الله عليه وسلم','364','0','0');
INSERT INTO [t8227] VALUES (365,'ما احتذا النعال، ولا انتعل , ولا ركب المطايا , ولا ركب الكور، بعد النبي صلى الله عليه','365','0','0');
INSERT INTO [t8227] VALUES (366,'اللهم , علمه التأويل','366','0','0');
INSERT INTO [t8227] VALUES (367,'إن شئت فاقض رمضان متتابعا، وإن شئت متفرقا','367','0','0');
INSERT INTO [t8227] VALUES (368,'لا بأس بقضاء رمضان متفرقا','368','0','0');
INSERT INTO [t8227] VALUES (369,'إن الله وملائكته يصلون على الصف المقدم، والمؤذن يغفر له مدى صوته، ويصدقه من سمعه من رطب','369','0','0');
INSERT INTO [t8227] VALUES (370,'إن الله تعالى وملائكته يصلون على الصف الأول','370','0','0');
INSERT INTO [t8227] VALUES (371,'وقت رسول الله صلى الله عليه وسلم لأهل المدينة ذا الحليفة، ولأهل الشام ومصر الجحفة، ولأهل','371','0','0');
INSERT INTO [t8227] VALUES (372,'لا رضاع إلا ما فتق الأمعاء','372','0','0');
INSERT INTO [t8227] VALUES (373,'لا تكن ذا وجهين وذا لسانين، فتظهر للناس أنك تخشى الله عز وجل , فيحمدونك، وقلبك','373','0','0');
INSERT INTO [t8227] VALUES (374,'لا تكن وليا لله في العلانية، وعدوا في السر','374','0','0');
INSERT INTO [t8227] VALUES (375,'لا تنظر إلى صغر الخطيئة , ولكن انظر من عصيت','375','0','0');
INSERT INTO [t8227] VALUES (376,'نعم الرب ربنا، لو أطعناه ما عصانا','376','0','0');
INSERT INTO [t8227] VALUES (377,'يا أهل الخلود، ويا أهل البقاء، إنكم لم تخلقوا للفناء، وإنما خلقتم للبقاء , وإنما تنقلون','377','0','0');
INSERT INTO [t8227] VALUES (378,'لا يجربن عليك كذبة، ولا تفشين له سرا، ولا تغتابن عنده أحدا قال عامر: فقلت لابن عباس: يا','378','0','0');
INSERT INTO [t8227] VALUES (379,'كان يجمع بين الظهر والعصر , والمغرب والعشاء في السفر في غزوة تبوك','379','0','0');
INSERT INTO [t8227] VALUES (380,'للمسافر ثلاثا , وللمقيم يوما يمسح على الخفين','380','0','0');
INSERT INTO [t8227] VALUES (381,'قطع في مجن ثمنه خمسة دراهم، وإني لأقطع في مجن ثمنه خمسة دراهم','381','0','0');
INSERT INTO [t8227] VALUES (382,'جعله الله حجابا لك من النار، فلما خرج عثمان قال للغلام: إن شئت فخذ كذا، وإن شئت فخذ كذا،','382','0','0');
INSERT INTO [t8227] VALUES (383,'اطعموا، فأكلوا حتى شبعوا، ثم خرجوا، فدعا عشرة آخرين، حتى أكل منها ثمانون رجلا، وفضل ما','383','0','0');
INSERT INTO [t8227] VALUES (384,'الدال على الخير كفاعله','384','0','0');
INSERT INTO [t8227] VALUES (385,'لكن الله تعالى يعلم غير ما علمتم قالوا: يا رسول الله، فما حاله؟ قال: قبل شهادتكم , وغفر','385','0','0');
INSERT INTO [t8227] VALUES (386,'للصائم في الجنة بابا يقال له الريان، لا يدخله أحد غيرهم، فإذا دخل آخرهم أغلق، فمن دخل منه','386','0','0');
INSERT INTO [t8227] VALUES (387,'يا معشر الشباب من استطاع منكم الباءة فليتزوج، ومن لا فليصم، فإن الصوم له وجاء، أخبركم أبو','387','0','0');
INSERT INTO [t8227] VALUES (388,'أخذ سهما من كنانته، فناوله معاوية رضي الله عنه وقال: ائتني به في الجنة قال المدائني: هكذا','388','0','0');
INSERT INTO [t8227] VALUES (389,'من نعم الله على الشاب أن يرافق صاحب سنة يحمله عليها','389','0','0');
INSERT INTO [t8227] VALUES (390,'سيد الحلواء الفالوذج وسيد الرطب السكر','390','0','0');
INSERT INTO [t8227] VALUES (391,'لقد قرأت من في رسول الله صلى الله عليه وسلم بضعا وسبعين سورة، وإن لزيد','391','0','0');
INSERT INTO [t8227] VALUES (392,'من رأى أحدا به بلاء فقال: الحمد لله الذي عافاني مما ابتلاه به وفضلني عليه وعلى كثير ممن','392','0','0');
INSERT INTO [t8227] VALUES (393,'كان أحب الأعمال إلى النبي صلى الله عليه وسلم إذا قدم مكة الطواف بالبيت','393','0','0');
INSERT INTO [t8227] VALUES (394,'إن قتلك لقربة إلى الله تعالى، فقال له الرجل: إنك تمزح، فقال: والله ما هذا بمزاح، ولكنه','394','0','0');
INSERT INTO [t8227] VALUES (395,'أما ترضى أن تكون مني بمنزلة هارون من موسى، إلا أنه لا نبي بعدي.','395','0','0');
INSERT INTO [t8227] VALUES (396,'نهى رسول الله صلى الله عليه وسلم، عن بيع الثمرة حتى يبدو صلاحها','396','0','0');
INSERT INTO [t8227] VALUES (397,'من أعمر عمرى فهي له ولعقبه','397','0','0');
INSERT INTO [t8227] VALUES (398,'ونزعنا ما في صدورهم من غل} [الأعراف: ٤٣]، قال: نزلت في عشرة: في أبي بكر، وعمر، وعثمان،','398','0','0');
INSERT INTO [t8227] VALUES (399,'على المرء المسلم الطاعة فيما حب وكره، ما لم يؤمر بمعصية، فإذا أمر بمعصية، فلا سمع عليه','399','0','0');
INSERT INTO [t8227] VALUES (400,'اللهم أدخل علي أحب أهل الأرض إليك، يأكل معي قال أنس: فجاء علي رضي الله عنه فحجبته، ثم جاء','400','0','0');
INSERT INTO [t8227] VALUES (401,'اللهم أدخل من تحبه يأكل معي من هذا الطير فجاء رجل، فاستأذن وأنا على الباب، فقلت: إنه على','401','0','0');
INSERT INTO [t8227] VALUES (402,'هل تسمعون أطيط السماء، وحق لها أن تئط، ما فيها موضع قدم إلا وعليه ملك قائم أو ساجد، وإن','402','0','0');
INSERT INTO [t8227] VALUES (403,'إذا قال الرجل للرجل: يا مخنث، فاجلدوه أربعين، وإذا قال: يا يهودي، فاجلدوه أربعين، وإذا','403','0','0');
INSERT INTO [t8227] VALUES (404,'أبو بكر وعمر خير أهل السماء، وخير أهل الأرض، وخير الأولين والآخرين، إلا النبيين','404','0','0');
INSERT INTO [t8227] VALUES (405,'لقد هممت أن أنهى عن الغيلة، حتى ذكرت أن فارس والروم يفعلون ذلك فلا تضر أولادهم قال مالك','405','0','0');
INSERT INTO [t8227] VALUES (406,'من يدخل الجنة يحيا لا يموت وينعم لا يبؤس، لا تبلى ثيابه ولا يبلى شبابه، كذا قال، قيل: يا','406','0','0');
INSERT INTO [t8227] VALUES (407,'عاتب الله تعالى المسلمين جميعا في نبيه صلى الله عليه وسلم، غير أبي بكر، رضي الله عنه،','407','0','0');
INSERT INTO [t8227] VALUES (408,'أمسك علينا الباب، فجاء أبو بكر رضي الله عنه يستأذن، ورسول الله صلى الله عليه وسلم جالس','408','0','0');
INSERT INTO [t8227] VALUES (409,'كان أبو بكر، رضوان الله عليه، أحبنا إلى رسول الله صلى الله عليه وسلم، وكان سيدنا وكان','409','0','0');
INSERT INTO [t8227] VALUES (410,'من مات لا يشرك بالله شيئا دخل الجنة','410','0','0');
INSERT INTO [t8227] VALUES (411,'نهى رسول الله صلى الله عليه وسلم أن تباشر المرأة المرأة في ثوب واحد أجل أن تصفها لزوجها','411','0','0');
INSERT INTO [t8227] VALUES (412,'نهانا النبي صلى الله عليه وسلم، إذا كنا ثلاثة أن ينتجي اثنان دون واحد، فإن ذلك','412','0','0');
INSERT INTO [t8227] VALUES (413,'الكذب فجور، وإن الفجور يهدي إلى النار، وإن العبد ليتحرى الكذب حتى يكتب عند الله','413','0','0');
INSERT INTO [t8227] VALUES (414,'نهى عن آطام المدينة أن تهدم','414','0','0');
INSERT INTO [t8227] VALUES (415,'ما ذهب عقل رجل قط إذا حفظ القرآن، وإن بلغ عمرا','415','0','0');
INSERT INTO [t8227] VALUES (416,'أفضل أمة رسول الله صلى الله عليه وسلم بعده أبو بكر، ثم عمر، ثم عثمان رضي الله عنهم.','416','0','0');
INSERT INTO [t8227] VALUES (417,'أبو بكر بعد رسول الله صلى الله عليه وسلم، وعمر بعد أبي بكر، وعثمان بعد عمر، رضي الله عنهم','417','0','0');
INSERT INTO [t8227] VALUES (418,'خير الناس بعد رسول الله صلى الله عليه وسلم: أبو بكر، ثم عمر، ثم عثمان، رضي الله','418','0','0');
INSERT INTO [t8227] VALUES (419,'أفضل أمة رسول الله صلى الله عليه وسلم بعده: أبو بكر، ثم عمر، ثم عثمان، رضوان الله عليهم','419','0','0');
INSERT INTO [t8227] VALUES (420,'أفضل أمة رسول الله صلى الله عليه وسلم، بعده أبو بكر وعمر وعثمان، رضي الله','420','0','0');
INSERT INTO [t8227] VALUES (421,'أفضل أمة رسول الله صلى الله عليه وسلم، أبو بكر وعمر وعثمان رضي الله','421','0','0');
INSERT INTO [t8227] VALUES (422,'لا يزال الرجل يذهب بنفسه حتى يكتب مع الجبارين، فيصيبه ما أصابهم من','422','0','0');
INSERT INTO [t8227] VALUES (423,'لا يتم من حلم','423','0','0');
INSERT INTO [t8227] VALUES (424,'هؤلاء لهذه، وهؤلاء لهذه قال: فتفرقت الناس وهم لا يختلفون في القدر','424','0','0');
INSERT INTO [t8227] VALUES (425,'لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير، اللهم لا مانع','425','0','0');
INSERT INTO [t8227] VALUES (426,'إذا سلم عن يمينه يرى بياض خده الأيمن، وإذا سلم عن يساره يرى بياض خده الأيسر، وكان تسليمه','426','0','0');
INSERT INTO [t8227] VALUES (427,'كان شيب رسول الله صلى الله عليه وسلم نحوا من عشرين شعرة','427','0','0');
INSERT INTO [t8227] VALUES (428,'ما من عبد يخرج من عينيه من الدموع مثل الذباب أو رأس الذباب من خشية الله، عز وجل، فيصيب حر','428','0','0');
INSERT INTO [t8227] VALUES (429,'أمر رجلا فنادى أيام منى: إن هذه أيام أكل وشرب','429','0','0');
INSERT INTO [t8227] VALUES (430,'استقوا من بئر صالح','430','0','0');
INSERT INTO [t8227] VALUES (431,'تسحروا فإن في السحور بركة','431','0','0');
INSERT INTO [t8227] VALUES (432,'إن أفواهكم طرق للقرآن فطهروها بالسواك','432','0','0');
INSERT INTO [t8227] VALUES (433,'حبذا المتخللون، قالوا: يا رسول الله، ما المتخللون؟ من الوضوء أو تخلل بين أصابعك وأظافرك،','433','0','0');
INSERT INTO [t8227] VALUES (434,'لا تشموا الطعام كما تشمه السباع','434','0','0');
INSERT INTO [t8227] VALUES (435,'الفقيه أشد على الشيطان من ألف ورع وألف مجتهد وألف متعبد فإن طير الهواء ونينان البحار','435','0','0');
INSERT INTO [t8227] VALUES (436,'صدقة الفطر على الصغير والكبير والحاضر والبادي','436','0','0');
INSERT INTO [t8227] VALUES (437,'من لقي رجلا يريد أن يقتله، فليقل: أعوذ بالله والأمانة منك، فإن قتل فهو شهيد، وإن قتل','437','0','0');
INSERT INTO [t8227] VALUES (438,'ويلك، تدري عن من أحدثك؟ عن من وقف بالموقف ثمانين وقفة، ويلك، تدري عن من أحدثك؟ ثم قال','438','0','0');
INSERT INTO [t8227] VALUES (439,'أعتق جارية له عن دبر، فكان يطؤها.','439','0','0');
INSERT INTO [t8227] VALUES (440,'لم ير قصر الصلاة في أقل من خمسة عشر أو ستة عشر فرسخا','440','0','0');
INSERT INTO [t8227] VALUES (441,'اشتاقت الجنة إلى أربعة: علي وسلمان وأبي ذر وعمار بن ياسر رضي الله عنهم','441','0','0');
INSERT INTO [t8227] VALUES (442,'إذا ترك الرجل الصلاة متعمدا كتب اسمه على باب النار فيمن يدخلها','442','0','0');
INSERT INTO [t8227] VALUES (443,'عينان لا تمسهما النار، عين بكت من خشية الله، وعين نامت تحرس الحرس في سبيل','443','0','0');
INSERT INTO [t8227] VALUES (444,'فظن بالله ما شئت، فإن الله عند ظن المؤمن به','444','0','0');
INSERT INTO [t8227] VALUES (445,'لا تدفنوا موتاكم بالليل','445','0','0');
INSERT INTO [t8227] VALUES (446,'كان يعجبه الصلاة في الحيطان','446','0','0');
INSERT INTO [t8227] VALUES (447,'ليس الغنى عن كثرة العرض، ولكن الغنى غنى النفس','447','0','0');
INSERT INTO [t8227] VALUES (448,'كان إذا توضأ مسح ظاهر أذنيه وباطنهما، ويقول: رأيت رسول الله صلى الله عليه وسلم يفعل','448','0','0');
INSERT INTO [t8227] VALUES (449,'ليس لامرئ شيء، فاتقوا النار ولو بشق تمرة','449','0','0');
INSERT INTO [t8227] VALUES (450,'ما حاجتك؟ قال: مولاي، فلان مات وخلف شيئا جئتك به، قال: فبسط إبراهيم كساءه، وقال له: هات،','450','0','0');
INSERT INTO [t8227] VALUES (451,'هو في المسبحات، ثم أمسكت عنه أياما، فرأيته طيب النفس، فقلت له: يا أبا إسحاق، إن لي مودة','451','0','0');
INSERT INTO [t8227] VALUES (452,'يصلي على حمار وهو متوجه إلى خيبر والقبلة خلفه','452','0','0');
INSERT INTO [t8227] VALUES (453,'الملك لله، والصلوات صلاة كل من صلى لله، والطيبات من الأعمال التي تعمل لله عز وجل، السلام','453','0','0');
INSERT INTO [t8227] VALUES (454,'على الرجل السمع والطاعة فيما أحب وكره، إلا أن يؤمر بمعصية، فإذا أمر بمعصية، فلا طاعة لأحد','454','0','0');
INSERT INTO [t8227] VALUES (455,'هذان سيدا كهول أهل الجنة من الأولين والآخرين ما خلا النبيين والمرسلين، لا تخبرهما، قال','455','0','0');
INSERT INTO [t8227] VALUES (456,'المدينة محفوظة بالملائكة كالرماح المركوزة','456','0','0');
INSERT INTO [t8227] VALUES (457,'لقد رأيتني وإني لأربط الحجر على بطني من الجوع، وإن صدقتي لتبلغ اليوم أربعة ألف','457','0','0');
INSERT INTO [t8227] VALUES (458,'يا ويح هذا كان أمس مسلما واليوم كافرا، فقال علي: مهلا، كان أمس مؤمنا واليوم','458','0','0');
INSERT INTO [t8227] VALUES (459,'حتى ننظر لم نفير عائشة أم المؤمنين؟ قال له عمار: أما إنك لو أردت غير هذا ما','459','0','0');
INSERT INTO [t8227] VALUES (460,'الموت شريك النوم، وليس في الجنة موت، قالوا: يا رسول الله، فما راحتهم؟ قال رسول صلى الله','460','0','0');
INSERT INTO [t8227] VALUES (461,'لو أن الثقلين اجتمعوا على قتل مؤمن لأكبهم الله يوم القيامة على وجوههم في النار، وما من','461','0','0');
INSERT INTO [t8227] VALUES (462,'أوحى الله تعالى إلى الجنة أن تزيني، فتزينت، فقال لها: تكلمي، فقالت: طوبى لمن رضيت','462','0','0');
INSERT INTO [t8227] VALUES (463,'ليس منا من انتهب ولا سلب ولا أشار بالسلب','463','0','0');
INSERT INTO [t8227] VALUES (464,'لا يقلب كعباتها أحد ينتظر ما تأتي به إلا عصى الله ورسوله','464','0','0');
INSERT INTO [t8227] VALUES (465,'مروا بالمعروف وإن لم تفعلوه كله، وانهوا عن المنكر وإن لم تنتهوا عنه','465','0','0');
INSERT INTO [t8227] VALUES (466,'سل الله تعالى الفردوس، فإنه سرة الجنة، وأهل الفردوس يسمعون أطيط العرش','466','0','0');
INSERT INTO [t8227] VALUES (467,'أما إنها ستقال لك يا أبا بكر','467','0','0');
INSERT INTO [t8227] VALUES (468,'هذه أصوات يهود تعذب في قبورها','468','0','0');
INSERT INTO [t8227] VALUES (469,'ثلاث من لم تكن فيه، فإن الله تعالى يغفر له ما سوى ذلك، إن شاء: من مات لا يشرك بالله شيئا،','469','0','0');
INSERT INTO [t8227] VALUES (470,'المهجر يوم الجمعة كمقرب القربان، يقرب جزورا ويقرب بقرة، ويقرب شاة، وويقرب دجاجة، ويقرب','470','0','0');
INSERT INTO [t8227] VALUES (471,'من عكف نفسه ما بين المغرب والعشاء في مسجد جماعة لم يتكلم إلا بصلاة أو قرآن، كان حقا على','471','0','0');
INSERT INTO [t8227] VALUES (472,'أيما عامل استعملناه، وفرضنا له رزقا، فما أصاب سوى رزقه، فهو غلول','472','0','0');
INSERT INTO [t8227] VALUES (473,'كذبتك الهواجر، قال عمرو بن عون: يعني أنك لو مشيت في الرمضاء لم يصبك','473','0','0');
INSERT INTO [t8227] VALUES (474,'لا يقولن أحدكم خبثت نفسي، وليقل: لقست نفسي','474','0','0');
INSERT INTO [t8227] VALUES (475,'إذا مد يديه في الدعاء لم يردهما حتى يمسح بهما وجهه','475','0','0');
INSERT INTO [t8227] VALUES (476,'ما سقطت أمة من عين الله تعالى، إلا ضرب أكبادها الجوع','476','0','0');
INSERT INTO [t8227] VALUES (477,'صلى بنا صلاة الصبح، فقرأ سورة البقرة حتى رأيت أصحاب رسول الله صلى الله عليه وسلم','477','0','0');
INSERT INTO [t8227] VALUES (478,'فإنك تسر بما يصير إليك، مما لم يكن ليفوتك، ويسرك ما لم تكن تدركه، فما نلت من الدنيا، يا','478','0','0');
