INSERT INTO [t8227] VALUES (619,'لا تسبوا الديك؛ فإنه يوقظ للصلاة','619','0','0');
INSERT INTO [t8227] VALUES (620,'الشفق: الحمرة. وقال مالك: الشفق: الحمرة.','620','0','0');
