INSERT INTO [t8227] VALUES (624,'لا يلبس القميص، ولا العمائم، ولا السراويلات، ولا البرانس، ولا الخفاف إلا أحد لا يجد','624','0','0');
INSERT INTO [t8227] VALUES (625,'كنت أطيب رسول الله صلى الله عليه وسلم لإحرامه قبل أن يحرم، ولحله قبل أن يطوف','625','0','0');
INSERT INTO [t8227] VALUES (626,'لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة لك والملك، لا شريك لك قال: فكان','626','0','0');
