INSERT INTO [t8236] VALUES (4,'خمس صلوات كتبهن الله على العباد , فمن جاء بهن لم يضيع منهن شيئا استخفافا بحقهن كان له عند','4','0','0');
INSERT INTO [t8236] VALUES (5,'من فاتته صلاة العصر فكأنما وتر أهله وماله','5','0','0');
INSERT INTO [t8236] VALUES (6,'من فاتته صلاة العصر فكأنما وتر أهله وماله قال أبو الحسين: ورواه عبد الرحمن بن إسحاق فزاد','6','0','0');
INSERT INTO [t8236] VALUES (7,'إن من الصلوات صلاة؛ من فاتته فكأنما وتر أهله وماله','7','0','0');
