INSERT INTO [t8236] VALUES (9,'الذي تفوته صلاة العصر، فكأنما وتر أهله وماله قال الشيخ: وهكذا هو في','9','0','0');
INSERT INTO [t8236] VALUES (10,'ما صلي على ابن بيضاء في المسجد فقال: إن مالكا - والله - أعلم بالحديث مني منذ نشأ، والله','10','0','0');
