INSERT INTO [t8238] VALUES (1,'عمر كان رشيد الأمر، ولا أرد قضاء قضاه عمر','1','0','0');
INSERT INTO [t8238] VALUES (2,'عمر كان رشيد الأمر ولن نرد قضاء قضى به عمر رضي الله عنه','2','0','0');
INSERT INTO [t8238] VALUES (3,'لم أكن لأحل عقدة عقدها عمر عليه السلام','3','0','0');
INSERT INTO [t8238] VALUES (4,'انزل عن منبر أبي، فقال: منبر أبيك والله لا منبر أبي، قال علي: والله ما أمرت بذلك، فقال','4','0','0');
INSERT INTO [t8238] VALUES (5,'عمر ناصح لله، فناصحه الله قال: ورئي على علي عليه السلام برد فقال: كسانيه خليلي عمر رضي','5','0','0');
INSERT INTO [t8238] VALUES (6,'عمر ناصح الله، فنصحه الله، قال: ثم بكى حدثنا جعفر بن محمد الواسطي، قال: نا موسى بن إسحاق،','6','0','0');
INSERT INTO [t8238] VALUES (7,'إن عمر نصح الله، فنصحه','7','0','0');
INSERT INTO [t8238] VALUES (8,'إن عمر ناصح فناصحه الله','8','0','0');
INSERT INTO [t8238] VALUES (9,'كسانيه عمر رضي الله عنه','9','0','0');
INSERT INTO [t8238] VALUES (10,'كسانيه خليلي عمر بن الخطاب عليه السلام، ثم رفع البرد، فقنع به رأسه، ثم بكى حتى ظننا أن','10','0','0');
INSERT INTO [t8238] VALUES (11,'إن عمر نصح الله فنصحه الله','11','0','0');
INSERT INTO [t8238] VALUES (12,'إن عمر ناصح الله فناصحه الله','12','0','0');
INSERT INTO [t8238] VALUES (13,'كسانيه عمر رضي الله عنه','13','0','0');
INSERT INTO [t8238] VALUES (14,'هذا برد كسانيه خليلي عمر بن الخطاب عليه السلام، ثم رفع البرد فقنع به رأسه، ثم بكى حتى','14','0','0');
INSERT INTO [t8238] VALUES (15,'إن عمر ناصح الله عز وجل فنصحه الله عز وجل','15','0','0');
INSERT INTO [t8238] VALUES (16,'إن سرك أن تلحق بصاحبيك، فاقصر الأمل، وكل دون الشبع، وانكس الإزار، وارقع القميص، واخصف','16','0','0');
INSERT INTO [t8238] VALUES (17,'وددنا أنا زدنا في عمرك من أعمارنا','17','0','0');
INSERT INTO [t8238] VALUES (18,'لا إلا أن يكون عمر بن الخطاب رضي الله عنه','18','0','0');
INSERT INTO [t8238] VALUES (19,'إنك عففت فعفت الرعية','19','0','0');
INSERT INTO [t8238] VALUES (20,'رحم الله أبا بكر، كان لنا واليا، فنعم الوالي كان لنا، ما رأينا حاضنا قط كان خيرا منه، إنا','21','0','0');
INSERT INTO [t8238] VALUES (21,'ولينا أبو بكر رضي الله عنه، فخير خليفة، أرحمه بنا، وأحناه علينا','22','0','0');
INSERT INTO [t8238] VALUES (22,'ولينا أبو بكر الصديق رضي الله عنه خير خليفة الله، وأرحمه بنا وأحناه','23','0','0');
INSERT INTO [t8238] VALUES (23,'ولينا أبو بكر الصديق، فما ولينا أحد من الناس مثله','24','0','0');
INSERT INTO [t8238] VALUES (24,'وابرأ من عدوهما، فإنهما كانا إمامي هدى','25','0','0');
INSERT INTO [t8238] VALUES (25,'تولهما وابرأ من عدوهما، وإنهما كانا إمامي هدى','26','0','0');
INSERT INTO [t8238] VALUES (26,'تولوا أبا بكر وعمر رضوان الله عليهما، فما أصابكم من ذلك فهو في عنقي','27','0','0');
INSERT INTO [t8238] VALUES (27,'ما ظلما من حقنا ما يزن حبة خردل قال: قلت: أفأتولاهما جعلني الله فداك؟ قال: نعم يا كثير','28','0','0');
INSERT INTO [t8238] VALUES (28,'اللهم إني أحب أبا بكر وعمر وأتولاهما، اللهم إن كان في نفسي غير هذا فلا تنالني شفاعة محمد','29','0','0');
INSERT INTO [t8238] VALUES (29,'أبو بكر رضي الله عنه جدي لا نالتني شفاعة محمد صلى الله عليه وسلم يوم القيامة إن لم أكن','30','0','0');
INSERT INTO [t8238] VALUES (30,'وأنا أرجو من شفاعة أبي بكر عليه السلام مثله، ولقد ولدني مرتين','31','0','0');
INSERT INTO [t8238] VALUES (31,'إن أمكن الله منكم لنقطعن أيديكم وأرجلكم من خلاف، ولا نقبل منكم توبة','32','0','0');
INSERT INTO [t8238] VALUES (32,'مرقت علينا الرافضة كما مرقت الحرورية على علي بن أبي طالب','33','0','0');
INSERT INTO [t8238] VALUES (33,'من لم يعرف فضل أبي بكر وعمر رضي الله عنهما فقد جهل السنة','34','0','0');
INSERT INTO [t8238] VALUES (34,'يزعم أنك تتبرأ من أبي بكر الصديق وعمر فما تقول؟ فقال: برئ الله من جارك، إني أرجو أن','35','0','0');
INSERT INTO [t8238] VALUES (35,'كمنزلتهما اليوم، هما ضجيعاه','36','0','0');
INSERT INTO [t8238] VALUES (36,'فقالوا في أبي بكر وعمر، ثم ابتركوا في عثمان، فلم يتزكوا، فلما فرغوا، قال لهم علي بن','37','0','0');
INSERT INTO [t8238] VALUES (37,'لأتولاهما وأستغفر لهما، وما أدركنا أحدا من أهل بيتي إلا وهو يتولاهما','38','0','0');
INSERT INTO [t8238] VALUES (38,'سألت أبا جعفر عن أبى بكر وعمر؟ فقال: مسلمين رحمهما الله؟ فقلت له: أتولاهما وأستغفر لهما؟','39','0','0');
INSERT INTO [t8238] VALUES (39,'من جعل عمر بن الخطاب رضي الله عنه وأرضاه بينه وبين الله، فقد استوثق','40','0','0');
INSERT INTO [t8238] VALUES (40,'أخبرك عن عمر، وتسألني عن رأيي، فعمر رضي الله عنه كان خيرا مني وملء الأرض مثلي، قلت: يا','41','0','0');
INSERT INTO [t8238] VALUES (41,'بغض أبي بكر وعمر نفاق، وبغض الأنصار نفاق. يا كثير من شك فيهما، فقد شك في السنة، تولهما','42','0','0');
INSERT INTO [t8238] VALUES (42,'يخاف الأحياء ولا يخاف الأموات، فعل الله بهشام بن عبد الملك كذا وكذا','43','0','0');
INSERT INTO [t8238] VALUES (43,'ينتقص أبا بكر وعمر؟ فقال: أولئك المراق','44','0','0');
INSERT INTO [t8238] VALUES (44,'البراءة من أبي بكر وعمر البراءة من علي رضي الله عنهم','45','0','0');
INSERT INTO [t8238] VALUES (45,'كنت أصنع كما صنع علي عليه السلام. قال: فارض بما صنع علي عليه السلام','46','0','0');
INSERT INTO [t8238] VALUES (46,'لحكمت بمثل ما حكم به أبو بكر عليه السلام في فدك','47','0','0');
INSERT INTO [t8238] VALUES (47,'انطلق الخوارج فبرئت ممن دون أبي بكر وعمر، ولم يستطيعوا أن يقولوا فيهما شيئا، وانطلقتم','48','0','0');
INSERT INTO [t8238] VALUES (48,'البراءة من أبي بكر وعمر البراءة من علي، فإن شئت فتقدم، وإن شئت فتأخر','49','0','0');
INSERT INTO [t8238] VALUES (49,'أتسمي أبا بكر رضي الله عنه الصديق؟ قال: سماه رسول الله صلى الله عليه وسلم الصديق، فمن لم','50','0','0');
INSERT INTO [t8238] VALUES (50,'أبو بكر الصديق رضي الله عنه إمام الشاكرين ثم قرأ {وسيجزي الله الشاكرين} [آل عمران:','51','0','0');
INSERT INTO [t8238] VALUES (51,'ولا تقولوا في أبي بكر وعمر ما ليسا له بأهل إن أبا بكر الصديق رضي الله عنه كان مع رسول','52','0','0');
INSERT INTO [t8238] VALUES (52,'لهما عندي أفضل من علي رضي الله عنه','53','0','0');
INSERT INTO [t8238] VALUES (53,'ولا صلى على من لا يصلي عليهما','54','0','0');
INSERT INTO [t8238] VALUES (54,'والله إن قبلك أقر به إلى الله عز وجل','55','0','0');
INSERT INTO [t8238] VALUES (55,'ولا صلى على من لا يصلي عليهما','56','0','0');
INSERT INTO [t8238] VALUES (56,'ما أرى رجلا يسب أبا بكر وعمر ثبتت له توبة أبدا','57','0','0');
INSERT INTO [t8238] VALUES (57,'لا يقبل الله توبة عبد تبرأ من أبي بكر وعمر، وأنهما ليعرضان على قلب، فأدعو الله لهما،','58','0','0');
INSERT INTO [t8238] VALUES (58,'يقولوا في أبي بكر وعمر أحسن ما يكون من القول','59','0','0');
INSERT INTO [t8238] VALUES (59,'أفيكم إمام تفترض طاعته تعرفون ذلك له من لم يعرف ذلك له فمات ميتة جاهلية؟ . فقال عمر بن','60','0','0');
INSERT INTO [t8238] VALUES (60,'عن الصديق، تسأل؟ قال: قلت: نعم يرحمك الله، وتسميه الصديق؟ قال: ثكلتك أمك، قد سماه صديقا','61','0','0');
INSERT INTO [t8238] VALUES (61,'فمن لم يقل له الصديق فلا صدق الله قوله في الدنيا ولا في الآخرة','62','0','0');
INSERT INTO [t8238] VALUES (62,'هل كان أحد من أهل البيت يسب أبا بكر وعمر؟ قال: معاذ الله بل يتولونهما، ويستغفرون لهما،','63','0','0');
INSERT INTO [t8238] VALUES (63,'برئ الله ممن تبرأ من أبي بكر وعمر','64','0','0');
INSERT INTO [t8238] VALUES (64,'تبرأ ممن ذكرهما إلا بخير يعني: أبا بكر وعمر رضي الله عنهما','65','0','0');
INSERT INTO [t8238] VALUES (65,'ومن زعم أني أبرأ من أبي بكر وعمر فأنا منه بريء','66','0','0');
INSERT INTO [t8238] VALUES (66,'أتولى أبا بكر وعمر وأحبهما، اللهم إن كان في نفسي غير هذا فلا نالتني شفاعة محمد يوم','67','0','0');
INSERT INTO [t8238] VALUES (67,'أبرأ ممن ذكرهما إلا بخير قلت: لعلك تقول هذا تقية قال: أنا إذا من المشركين، ولا نالتني','68','0','0');
INSERT INTO [t8238] VALUES (68,'كان آل أبي بكر رضي الله عنه يدعون على عهد رسول الله صلى الله عليه وسلم آل محمد صلى الله','69','0','0');
INSERT INTO [t8238] VALUES (69,'آل أبي بكر رضي الله عنهم يدعون على عهد رسول الله صلى الله عليه وسلم آل محمد صلى الله عليه','70','0','0');
INSERT INTO [t8238] VALUES (70,'آل أبي بكر كانوا يدعون على عهد رسول الله صلى الله عليه وسلم آل محمد صلى الله عليه','71','0','0');
INSERT INTO [t8238] VALUES (71,'أعطى عليا أم محمد ابن الحنفية','72','0','0');
INSERT INTO [t8238] VALUES (72,'أسلم فكان أفضلهم إسلاما حتى قبضه الله على ذلك','73','0','0');
INSERT INTO [t8238] VALUES (73,'كان خيرهم إسلاما، يوم أسلم، ثم لم يزل كذلك حتى قبضه الله على ذلك. قال الدقيقي: قال لنا','74','0','0');
INSERT INTO [t8238] VALUES (74,'يزعمون أنا نقع في أبي بكر وعمر وهما والداي','75','0','0');
INSERT INTO [t8238] VALUES (75,'تسألني عن رجلين، قد أكلا من ثمار الجنة','76','0','0');
