INSERT INTO [t8240] VALUES (1,'ائتني بلوح أكتب لأبي بكر كتابا لا يختلف عليه، قالت: فلما ذهب ليقوم قال: أبى الله','1','0','0');
INSERT INTO [t8240] VALUES (2,'أفضلكم من تعلم القرآن وعلمه هذا غلط فاحش جدا من حديث عبد الرحمن عن سفيان، وإنما روى عبد','2','0','0');
INSERT INTO [t8240] VALUES (3,'لا تنكر ولدا لك في الدنيا فيفضحك الله على رءوس الأشهاد يوم القيامة، ولا تلق الله بأمانة','3','0','0');
INSERT INTO [t8240] VALUES (4,'جامع أحدكم أهله فلا ينظر إلى الفرج؛ فإنه يورث العمى، وإذا جامع أحدكم فلا يكثر الكلام فإنه','4','0','0');
INSERT INTO [t8240] VALUES (5,'حرملة سمع من الشافعي، غير أنه كان يمر إلى مالك، فمر يوما بالربيع، فقال الربيع: ثنا محمد','5','0','0');
INSERT INTO [t8240] VALUES (6,'أعوذ بكلمات الله التامات من شر ما خلق لم تضرك غريب من حديث شعبة عن صالح، لم يروه غير سلم،','6','0','0');
INSERT INTO [t8240] VALUES (7,'لا نورث ما تركنا فهو صدقة صحيح مخرج، رواه الخلق عن الزهري، وهو حسن من حديث عمرو بن دينار','7','0','0');
INSERT INTO [t8240] VALUES (8,'النبطي، رأسه قتب، وعنقه عصب، وكلامه صخب، ومشيته خبب، فالهرب الهرب إذا تهجى','8','0','0');
INSERT INTO [t8240] VALUES (9,'أخ لك كلما لقيك ذكرك بحظك من الله خير لك من أخ كلما لقيك وضع في كفك','9','0','0');
INSERT INTO [t8240] VALUES (10,'لا عز لأحد أدخله عزه النار، ولا ذل لأحد أدخله ذله الجنة، الموت الأحمر الحاجة بعد الغنى','10','0','0');
INSERT INTO [t8240] VALUES (11,'الملائكة تضع أجنحتها لطالب العلم رضاء بما يفعله، فعن أي شيء تسأل؟ قلت: عن المسح على','11','0','0');
INSERT INTO [t8240] VALUES (12,'أصبت شارفا من مغنم من بدر، وأعطاني النبي صلى الله عليه وسلم شارفا، فأنختهما على باب رجل','12','0','0');
INSERT INTO [t8240] VALUES (13,'أحبب حبيبك هونا ما عسى أن يكون بغيضك يوما ما، وابغض بغيضك هونا ما عسى أن يكون حبيبك يوما','13','0','0');
INSERT INTO [t8240] VALUES (14,'يشفع الأذان، ويوتر الإقامة ولم يروه من حديث شعبة عن قتادة إلا الجدي، وإنما المحفوظ من','14','0','0');
INSERT INTO [t8240] VALUES (15,'علي أول من أسلم. قال عمرو: فذكرته لإبراهيم فقال: أول من أسلم أبو بكر رضي الله','15','0','0');
INSERT INTO [t8240] VALUES (16,'هل رأى محمد صلى الله عليه وسلم ربه؟ قال: نعم. قال عكرمة: فقلت لابن عباس: أليس الله يقول','16','0','0');
INSERT INTO [t8240] VALUES (17,'مكتوب في الإنجيل: الرشوة تعمي عيني الحكيم، فكيف مبلغها من الجاهل؟','17','0','0');
INSERT INTO [t8240] VALUES (18,'لله تعالى ملائكة سياحين في الأرض يبلغوني عن أمتي السلام لم يروه عن الأعمش إلا أبو إسحاق،','18','0','0');
INSERT INTO [t8240] VALUES (19,'لولا بنيات كزغب القطا ... رددن من بعض إلى بعض لكان لي مضطرب واسع ... أقوم بالطول','19','0','0');
INSERT INTO [t8240] VALUES (20,'جلس عند رأسه يدعو له ويستغفر له، فكشف عن وجهه فقال: السلام عليك، إني قدمت على ربي جل وعز','20','0','0');
INSERT INTO [t8240] VALUES (21,'لا يتقدمن أحدكم بصيام يوم أو يومين قبل رمضان إلا أن يكون صياما كان يصومه حدثنا عبد الله','21','0','0');
INSERT INTO [t8240] VALUES (22,'يأتي قباء راكبا وماشيا صحيح من حديث عبد الله بن دينار، غريب من حديث مسعر عنه لم أكتبه إلا','22','0','0');
INSERT INTO [t8240] VALUES (23,'عرى الإسلام أربعة: إقام الصلاة لميقاتها، وأداء الزكاة طيبة بها نفسه، وصلة الرحم، وإيفاء','23','0','0');
INSERT INTO [t8240] VALUES (24,'اكتب عن زياد بن مخراق، فإنه رجل موسر لا يكذب في الحديث','24','0','0');
INSERT INTO [t8240] VALUES (25,'عمر سراج أهل الجنة لم نكتبه من حديث مالك إلا بهذا الإسناد، والمحفوظ من هذا حديث الغفاري،','25','0','0');
INSERT INTO [t8240] VALUES (26,'علام يضحك أحدكم مما يصنع مثله حدثناه عمر بن إبراهيم المقدسي ببغداد فذكر مثله سواء، هذا','26','0','0');
INSERT INTO [t8240] VALUES (27,'أول ما يحاسب الرجل يوم القيامة بصلاته الحديث غريب عن سفيان، عن داود، لم يروه عنه إلا عبد','27','0','0');
INSERT INTO [t8240] VALUES (28,'حذر وأنذر أصحابك كل الشهوات؛ فإن القلوب المعلقة بشهوات الدنيا عقولها عني','28','0','0');
INSERT INTO [t8240] VALUES (29,'لا يؤمن أحدكم حتى يؤمن بأربع: يشهد أن لا إله إلا الله وحده لا شريك له، وأني رسول الله','29','0','0');
INSERT INTO [t8240] VALUES (30,'أحدثكم عني وعن رسول الله صلى الله عليه وسلم؟ قلنا: بلى، قالت: لما كانت ليلتي. . . وذكر','30','0','0');
INSERT INTO [t8240] VALUES (31,'ساجدا بمكة حول البيت، فطفت سبعة أسابيع قبل أن يرفع رأسه','31','0','0');
