INSERT INTO [t8242] VALUES (1,'ما طلعت شمس قط إلا بجنبتيها ملكان يناديان، إنهما ليسمعان من على الأرض غير الثقلين: يا','1','0','0');
INSERT INTO [t8242] VALUES (2,'ما من صباح يصبح العباد إلا صارخ يصرخ: أيها الخلائق، سبحوا القدوس','2','0','0');
INSERT INTO [t8242] VALUES (3,'ما من يوم ولا ليلة إلا ولله فيه صدقة يمن بها على من يشاء من عباده، وما من الله على عبد','3','0','0');
INSERT INTO [t8242] VALUES (4,'عود لسانك: اللهم اغفر لي، فإن لله ساعات لا يرد فيها سائلا','4','0','0');
INSERT INTO [t8242] VALUES (5,'إنكم من الليل والنهار في آجال منقوصة، وأعمال محفوظة، من زرع خيرا يوشك أن يحصد رغبة، ومن','5','0','0');
INSERT INTO [t8242] VALUES (6,'ليس من يوم إلا وهو ينادي: أنا يوم جديد، وأنا عليكم شهيد، ابن آدم إني لن أمر بك أبدا،','6','0','0');
INSERT INTO [t8242] VALUES (7,'ما من ليلة إلا تقول: ابن آدم، أحدث في خيرا فإني لن أعود إليك أبدا','7','0','0');
INSERT INTO [t8242] VALUES (8,'أنا الذي قدمت عليكم جديدا، وقد حان مني تصرم، فلا يستطيع محسن أن يزداد في إحسانا، ولا','8','0','0');
INSERT INTO [t8242] VALUES (9,'ما من يوم إلا يقول: ابن آدم، قد دخلت عليك اليوم، ولن أرجع إليك بعد اليوم، فانظر ماذا تعمل','9','0','0');
INSERT INTO [t8242] VALUES (10,'الدهر ثلاثة أيام: أمس خلت عظته، واليوم الذي أنت فيه لك، وغدا لا تدري ما','10','0','0');
INSERT INTO [t8242] VALUES (11,'أمس أجل، واليوم عمل، وغدا أمل','11','0','0');
INSERT INTO [t8242] VALUES (12,'الأيام ثلاثة: معهود، ومشهود، وموعود، فالمعهود أمس، والمشهود اليوم، والموعود','12','0','0');
INSERT INTO [t8242] VALUES (13,'أمس أجل، واليوم عمل، وغدا أمل','13','0','0');
INSERT INTO [t8242] VALUES (14,'الدنيا، فقال: ميراث قال: فالأيام؟، قال: دول، قال: فالدهر؟، قال: أطباق، والموت بكل سبيل،','14','0','0');
INSERT INTO [t8242] VALUES (15,'الأيام ثلاثة: فأمس حكيم مؤدب، أبقى فيك موعظة، وترك فيك عبرة، واليوم ضيف عندك، طويل','15','0','0');
INSERT INTO [t8242] VALUES (16,'أمس مذموم، ويومك غير محمود، وغدا غير مأمون','16','0','0');
INSERT INTO [t8242] VALUES (17,'المؤمن يقول لنفسه: إنما هي ثلاثة أيام: فقد مضى أمس بما فيه، وغدا أمل لعلك لا تدركه. إنك','17','0','0');
INSERT INTO [t8242] VALUES (18,'الأيام ثلاثة: فأما أمس فقد انقضى عن الملوك نعمته، وذهبت عني شدته، وإني وإياهم من غد لعلى','18','0','0');
INSERT INTO [t8242] VALUES (19,'ليس من يوم يقدم إلا وهو عارية لليوم الذي بعده فاليوم الجديد يقتضي عاريته، فإن كان حسنا','19','0','0');
INSERT INTO [t8242] VALUES (20,'وإن غدا لا تدري ما مهله، فأين اجتماع شهادتهم عليك. . . .','20','0','0');
INSERT INTO [t8242] VALUES (21,'الليل والنهار خزانتان، فانظروا ما تضعون فيهما، وكان يقول: اعملوا لليل لما خلق له، واعملوا','21','0','0');
INSERT INTO [t8242] VALUES (22,'إني يوم جديد، وإني على ما يعمل في شهيد، فإني لو قد غربت الشمس لم أرجع إليكم إلى يوم','22','0','0');
INSERT INTO [t8242] VALUES (23,'اختلاف الليل والنهار غنيمة الأكياس','23','0','0');
INSERT INTO [t8242] VALUES (24,'ابن آدم، طأ الأرض بقدمك، فإنها عن قليل تكون قبرك، ابن آدم، إنما أنت أيام، فكلما ذهب يوم','24','0','0');
INSERT INTO [t8242] VALUES (25,'ما من أحد إلا وفي عقله نقص عن حلمه وعلمه، وذلك أنه إذا أتته الدنيا بزيادة في مال ظل فرحا','25','0','0');
INSERT INTO [t8242] VALUES (26,'اليوم ضيفك، والضيف مرتحل بحمدك أو بذمك، وكذلك ليلتك','26','0','0');
INSERT INTO [t8242] VALUES (27,'إنك بين مطيتين يوضعانك يوضعك الليل إلى النهار، والنهار إلى الليل، حتى يسلماك إلى الآخرة،','27','0','0');
INSERT INTO [t8242] VALUES (28,'الأيام ثلاثة: فأمس حكيم مؤدب ترك فيك عظة حكمته، وأبقى فيك عبرته وعظته، ويومك صديق مودع،','28','0','0');
INSERT INTO [t8242] VALUES (29,'الليل والنهار ليوم سوء، أو غير ذلك. ثم بكى','29','0','0');
INSERT INTO [t8242] VALUES (30,'ذهب من عمري يوم كامل، فإذا أصبح قال: ذهبت ليلة كاملة من عمري، فلما احتضر بكى وقال: قد كنت','30','0','0');
INSERT INTO [t8242] VALUES (31,'مضت الليلة من عمري ولم اكتسب فيها لنفسي شيئا، ويمضي اليوم أيضا ولا أراني أكتسب فيه شيئا،','31','0','0');
INSERT INTO [t8242] VALUES (32,'كان رجل إذا رأى الليل مقبلا بكى وقال: هذا يميتني','32','0','0');
INSERT INTO [t8242] VALUES (33,'قد اعتورك الليل والنهار، يدفعك الليل إلى النهار، ويدفعك النهار إلى الليل، حتى يأتيك','33','0','0');
INSERT INTO [t8242] VALUES (34,'أوصيك بتقوى الله، والانشمار بما استطعت من مالك، وما رزقك الله إلى دار قرارك، فكأنك والله','34','0','0');
INSERT INTO [t8242] VALUES (35,'لن يلبث القرناء أن يتفرقوا ... ليل يكر عليهم ونهار','35','0','0');
INSERT INTO [t8242] VALUES (36,'إن يسلم المرء من قتل ومن هرم ... وملي العيش أبلاه الجديدان','36','0','0');
INSERT INTO [t8242] VALUES (37,'إنا لنفرح بالأيام نقعطها ... وكل يوم مضى يدني من الأجل','37','0','0');
INSERT INTO [t8242] VALUES (38,'الليل والنهار مراحل، ينزلها الناس مرحلة مرحلة، حتى ينتهي بهم ذلك إلى آخر سفرهم، فإن','38','0','0');
INSERT INTO [t8242] VALUES (39,'قد أحيط بك من كل جانب، واعلم أنه يسار بك في كل يوم وليلة، فاحذر الله والمقام بين يديه،','39','0','0');
INSERT INTO [t8242] VALUES (40,'إنك بيومك ولست بغد، فكس في يومك، فإن يكن غد لك فكن كما كنت في هذا اليوم، وإلا يك غد لك لم','40','0','0');
INSERT INTO [t8242] VALUES (41,'إلى كم يا ليل ويا نهار تحطان من أجلي وأنا غافل عما يراد بي؟ إنا لله، إنا لله. فهو كذلك','41','0','0');
INSERT INTO [t8242] VALUES (42,'منع البقاء تقلب الشمس ... وطلوعها من حيث لا تمسي وطلوعها حمراء إذ طلعت ... وتغيب في صفراء','42','0','0');
INSERT INTO [t8242] VALUES (43,'أشاب الصغير وأفنى الكبير ... مر النهار وكر العشي إذا ليلة هدمت يومها ... أتى بعد ذلك يوم','43','0','0');
INSERT INTO [t8242] VALUES (44,'إن لله عبادا لا يغفلون عن طاعته في هذا الليل والنهار','44','0','0');
INSERT INTO [t8242] VALUES (45,'كانت. . أما طلوع الشمس وغروبها، فما من. . . . . يسمع، ولا ٠٠ أثرها','45','0','0');
INSERT INTO [t8242] VALUES (46,'لا يخدعنك من ترى عن نفسكا ... وصل التفكر في المعاد بحسكا لا تغبنن بمر يومك ذا الذي ...','46','0','0');
INSERT INTO [t8242] VALUES (47,'من كان الليل والنهار مطيته سارا وإن لم يسر','47','0','0');
INSERT INTO [t8242] VALUES (48,'يا أيها الشيخ المعلل ... نفسه والشيب شامل اعلم بأنك نائم ... فوق الفراش وأنت راحل والليل','48','0','0');
INSERT INTO [t8242] VALUES (49,'اجعل غدا كيومك، واجعل يومك كما غبر من عمرك، وسل الله الخيرة في جميع أمرك، فهو المعطي، وهو','49','0','0');
INSERT INTO [t8242] VALUES (50,'جزئ دهرك بيومك','50','0','0');
INSERT INTO [t8242] VALUES (51,'ما من يوم أخرجه الله إلى أهل الدنيا إلا ينادي: ابن آدم اغتنمني لعله لا يوم لك بعدي، ولا','51','0','0');
INSERT INTO [t8242] VALUES (52,'وجربت ماذا العيش إلا تعلة ... وما الدهر إلا منجنون يقلب','52','0','0');
INSERT INTO [t8242] VALUES (53,'يا للمنايا ويا للبين والحين ... كل اجتماع من الدنيا إلى بين حتى متى نحن في الأيام نحسبها','53','0','0');
INSERT INTO [t8242] VALUES (54,'كل يوم يعيشه المؤمن غنيمة','54','0','0');
INSERT INTO [t8242] VALUES (55,'واعلم رحمنا الله وإياك أبا عبد الله أنك اليوم أقرب إلى الموت يوم نعيت له، ولم يزل الليل','55','0','0');
INSERT INTO [t8242] VALUES (56,'اعملوا لأنفسكم رحمكم الله في هذا الليل وسواده، فإن المغبون من غبن خير الليل والنهار،','56','0','0');
INSERT INTO [t8242] VALUES (57,'لي نفسا تحب الدعة، وقلبا يألف اللذات، وهمة تستثقل الطاعة وقد رهبت نفسي الآفات، وحذرت قلبي','57','0','0');
INSERT INTO [t8242] VALUES (58,'فأحسن ضيافة يومك الذي أنت فيه، وزوده منك. . مشخوصة عنك، وأشفق من طلوع. . . عليك من بعض','58','0','0');
INSERT INTO [t8242] VALUES (59,'يطارحني يوم جديد وليلة ... هما أفنيا عمري وكل فتى بالي','59','0','0');
INSERT INTO [t8242] VALUES (60,'أيقظان أنت اليوم أم أنت نائم ... كيف يطيق النوم حيران هائم فلو كنت يقظان الغداة لخرمت ...','60','0','0');
INSERT INTO [t8242] VALUES (61,'الدنيا ثلاثة أيام: أما أمس فقد ذهب بما فيه، أما غد فلعلك لا تدركه، واليوم لك فاعمل','61','0','0');
INSERT INTO [t8242] VALUES (62,'ابن آدم لا تحمل هم سنة على يوم، كفى يومك بما فيه، فإن تكن السنة من عمرك يأتك الله فيها','62','0','0');
INSERT INTO [t8242] VALUES (63,'الدنيا ثلاثة أيام: مضى أمس بما فيه، وغدا لعلك لا تدركه، فانظر ما أنت عامل في','63','0','0');
INSERT INTO [t8242] VALUES (64,'سئمت لتكرار الليالي والأيام ودورها علي، فهل من شيء يدفع عني سآمة ذلك أو يسلي عني بعض ما','64','0','0');
INSERT INTO [t8242] VALUES (65,'ما من يوم يخرج من الدنيا إلا قال: الحمد لله الذي أخرجني منها، ثم لا يردني إليها. قال','65','0','0');
