INSERT INTO [t8249] VALUES (1,'ذكر من نزل داريا من أصحاب رسول الله صلى الله عليه وسلم والتابعين، وتابعي التابعين، وأهل العلم على طبقاتهم وأزمانهم، وذكر وفاتهم، ومن أعقب منهم، ومن لم يعقب، إلى وقتنا هذا، وبالله التوفيق','3','0','0');
INSERT INTO [t8249] VALUES (2,'ذكر بلال مؤذن رسول الله صلى الله عليه وسلم كان مولدا، يعني: من مولدي جمح، فاشتراه أبو بكر رضي الله عنه وأعتقه سكن داريا وتزوج امرأة من أهلها يقال لها: هند الخولانية','4','0','0');
INSERT INTO [t8249] VALUES (3,'ذكر من روى عن بلال من أهل داريا أبو مسلم الخولاني، وأبو إدريس الخولاني، وأبو قلابة الجرمي، وهند الخولانية زوجة بلال ولو ذهبنا إلى ذكر أحاديثهم وما رووا عنه لاتسع الكتاب وطال به الشرح، ولكنا اختصرنا هذا الكلام لشهرة ذلك، وصحة الراوية عنه عند أهل','12','0','0');
INSERT INTO [t8249] VALUES (4,'ذكر أبي راشد الخولاني سماه النبي صلى الله عليه وسلم وكناه، ومن ولده جماعة بداريا إلى اليوم','13','0','0');
INSERT INTO [t8249] VALUES (5,'ذكر أسود بن أصرم المحاربي والدليل على نزوله داريا قطائع له بها، تعرف به إلى اليوم روى عنه سليمان بن حبيب المحاربي قاضي الخلفاء، وهو ممن نزل داريا وله بها أوقاف تجري على مساكينها إلى وقتنا هذا','15','0','0');
INSERT INTO [t8249] VALUES (6,'ذكر قيس بن عباية بن عبيد بن الحارث بن عبيد الخولاني من خولان قضاعة، حليف بني حارثة بن الحارث من الأوس: شهد بدرا وهو حدث السن، وشهد فتوح الشام مع أبي عبيدة بن الجراح وهو كهل يستشيره أبو عبيدة في أموره قال عبد الرحمن بن إبراهيم: هو قيس بن عباية أبو','17','0','0');
INSERT INTO [t8249] VALUES (7,'ذكر أبي ثعلبة الخشني واسمه جرثوم بن ناشر والدليل على نزوله داريا ومقامه بها','19','0','0');
INSERT INTO [t8249] VALUES (8,'ذكر بكر بن زرعة الخولاني من أهل داريا','23','0','0');
