INSERT INTO [t8249] VALUES (16,'ذكر عمرو بن الأسود العنسي ويكنى أبا عياض، ونزوله داريا، وبها جماعة من ولده إلى هذا اليوم','60','0','0');
INSERT INTO [t8249] VALUES (17,'ذكر عمرو بن عبد الخولاني قال أبو علي: وكان عمرو بن عبد الخولاني تزوج بزوجة أبي مسلم الخولاني بعد وفاته، فسمعت من أدركت من شيوخنا يذكر أن أم مسلم سئلت، أو قيل لها: أي الرجلين كان أفضل؟ فقالت: أما أبو مسلم فإنه لم يكن يسأل الله شيئا إلا أعطاه إياه،','65','0','0');
INSERT INTO [t8249] VALUES (18,'ذكر أبي قلابة الجرمي وهو عبد الله بن زيد بن عامر بن ناتل بن مالك بن عبيد بن علقمة بن سعد بن كثير بن غالب بن عدي بن بيهس بن طرود بن قدامة بن جرم بن زياد بن خولان بن عمران بن الحاف بن قضاعة مولده بالبصرة، وقدم الشام ونزل داريا وسكن بها عند ابن عمه بيهس بن','66','0','0');
INSERT INTO [t8249] VALUES (19,'ذكر عمير بن هانئ العنسي ومن لقي من أصحاب رسول الله صلى الله عليه وسلم، ومن روى عنه من أهل داريا، وكيف كان سبيل قتله، وذكر عقبه','75','0','0');
INSERT INTO [t8249] VALUES (20,'ذكر سليمان بن حبيب المحاربي قاضي الخلفاء قال عبد الرحمن بن إبراهيم: كنيته أبو ثابت','83','0','0');
INSERT INTO [t8249] VALUES (21,'ذكر أبي كبير المحاربي من ساكني داريا','88','0','0');
INSERT INTO [t8249] VALUES (22,'ذكر عثمان بن عبد الأعلى بن سراقة الأزدي قال أبو زرعة عبد الرحمن بن عمرو: هو في الطبقة الثانية من التابعين، ولم يزل من ولده جماعة إلى هذا الوقت قال أبو زرعة: إن عثمان بن عبد الأعلى بن سراقة من قضاة التابعين وعداده فيهم','92','0','0');
INSERT INTO [t8249] VALUES (23,'ذكر عبد الرحمن بن أبي كبيرة العنسي قال عبد الرحمن بن إبراهيم: هو من داريا','93','0','0');
INSERT INTO [t8249] VALUES (24,'ذكر معاوية بن طويع وعمر بن طويع اليزنيين من ساكني داريا وأولادهم بها إلى اليوم','95','0','0');
