INSERT INTO [t8249] VALUES (39,'ذكر سالم بن عبد الله بن عصمة المحاربي من ساكني داريا، ذكره عبد الرحمن بن إبراهيم في كتاب الطبقات','153','0','0');
INSERT INTO [t8249] VALUES (40,'ومن النساء بداريا: هند الخولانية امرأة بلال رحمه الله','156','0','0');
INSERT INTO [t8249] VALUES (41,'وأم مسلم الخولانية زوجة أبي مسلم رحمها الله، ومات عنها وتزوجت بعده عمرو بن عبد الخولاني، قال أبو علي: فسمعت من أرضى من شيوخنا يقولون: إن أم مسلم سئلت فقيل لها: أي الرجلين أفضل؟ قالت: أما أبو مسلم فإنه لم يكن يسأل الله شيئا إلا أعطاه إياه، وأما','158','0','0');
INSERT INTO [t8249] VALUES (42,'ذكر التابعين الأكابر ممن أدرك مولده حياة رسول الله صلى الله عليه وسلم ولم يهاجر إليه','159','0','0');
INSERT INTO [t8249] VALUES (43,'أبو مسلم الخولاني اسمه عبد الله بن ثوب وقد قيل: عبد الله بن ثواب بن عبد الله بن رحب بن عمرو بن خولان أدرك الجاهلية وكان من الأفاضل الأخيار، روى عن جماعة من أصحاب رسول الله صلى الله عليه وسلم، وكان فاضلا دينا ورعا','160','0','0');
