INSERT INTO [t8249] VALUES (53,'أحمد بن عمرو بن معاذ العنسي وابنه عبد الله بن أحمد بن عمرو بن معاذ العنسي أبو الحسين يروي عن أبي الميمون بن راشد وأبي الحسن بن حذلم، وأبي القاسم بن أبي العقب، وأبي يعقوب الأذرعي، وغيرهم. توفي بداريا في شوال سنة أربع عشرة وأربعمئة.','214','0','0');
INSERT INTO [t8249] VALUES (54,'علي بن داود بن عبد الله المقري إمام المسجد الجامع بدمشق، وإليه انتهت الرياسة في القراءة بدمشق، توفى لست خلون من جمادى الأولى سنة اثنتين وأربعمئة. روى عن خيثمة بن سليمان والحسن بن حبيب وغيرهما','215','0','0');
INSERT INTO [t8249] VALUES (55,'علي بن بجيلة أبو الحسن المقريء، يعرف بصهر الأطروش. توفى سنة خمس عشرة وأربعمئة. ذكره الحداد في الوفيات.','216','0','0');
INSERT INTO [t8249] VALUES (56,'علي بن محمد بن طوق يعرف بابن الطبراني. حدثنا عنه عبد العزيز بن أحمد','217','0','0');
INSERT INTO [t8249] VALUES (57,'أحمد بن منصور بن الفقيه أبو العباس المالكي','218','0','0');
INSERT INTO [t8249] VALUES (58,'عمرو بن عذرة بن محمد السلمي المالكي أبو البركات، توفى في شوال سنة ستين وأربعمائة. قال: ذكر أبو الحسن محمد بن عبد الله بن جعفر بن عبد الله بن الجنيد الرازي في تسمية من كتب عنه بدمشق، فذكر: " وممن كتبت عنه في قرى دمشق','219','0','0');
INSERT INTO [t8249] VALUES (59,'أبو عبد الله محمد بن هارون بن عبد الرحمن بن عبيد بن زكريا العنسي من أهل داريا، مات سنة أربع وعشرين وثلاثمئة','220','0','0');
