INSERT INTO [t8249] VALUES (62,'ابناه: أبو القاسم عبيد الله، وأبو الفضل عبد الواحد ابنا عبد الله بن هشام بن سوار كتب عنهما عن أبي محمد عبد الرحمن بن عثمان بن أبي نصر رحمه الله','223','0','0');
