INSERT INTO [t8251] VALUES (1,'من أحب لقاء الله أحب الله لقاءه، ومن كره لقاء الله كره الله لقاءه قال: قيل: يا رسول الله،','1','0','0');
INSERT INTO [t8251] VALUES (2,'من أحب لقاء الله أحب الله لقاءه، ومن كره لقاء الله كره الله لقاءه. قلت: يا نبي الله،','2','0','0');
INSERT INTO [t8251] VALUES (3,'أنا النذير، والموت المغير، والساعة الموعد','3','0','0');
INSERT INTO [t8251] VALUES (4,'الموت من ذلك','4','0','0');
INSERT INTO [t8251] VALUES (5,'أن نفرا من بني إسرائيل خرجوا يمشون في الأرض، ويفكرون فيها حتى انتهوا إلى مقبرة، فسألوا','5','0','0');
INSERT INTO [t8251] VALUES (6,'الميت ليسمع خفق نعالهم إذا ولوا مدبرين','6','0','0');
INSERT INTO [t8251] VALUES (7,'كيف أنت إذا كنت في أربعة أذرع في ذراعين , ورأيت منكرا ونكيرا؟ قال: قلت: يا رسول الله، وما','7','0','0');
INSERT INTO [t8251] VALUES (8,'كانت امرأة مسقاما، فذكرت شدة الموت، وضغطة القبر، فدعوت الله، فخفف عنها','8','0','0');
INSERT INTO [t8251] VALUES (9,'يتعوذ من عذاب القبر قال أبو بكر بن أبي داود: هذه أم خالد بن سعيد بن العاص روت عن النبي','9','0','0');
INSERT INTO [t8251] VALUES (10,'أبي أول من كتب بسم الله الرحمن الرحيم','10','0','0');
INSERT INTO [t8251] VALUES (11,'أوحي إلي أنكم تفتنون في قبوركم قريبا من فتنة الدجال','11','0','0');
INSERT INTO [t8251] VALUES (12,'قد أوحي إلي أنكم تفتنون في قبوركم قريبا من فتنة الدجال قال أبو بكر بن أبي داود: وعند','12','0','0');
INSERT INTO [t8251] VALUES (13,'استعيذوا بالله من عذاب القبر','13','0','0');
INSERT INTO [t8251] VALUES (14,'لولا أن لا تدافنوا، لسألت الله أن يسمعكم ما أسمعني من عذاب القبر','14','0','0');
INSERT INTO [t8251] VALUES (15,'تعوذوا بالله من عذاب القبر، وعذاب النار، وفتنة الدجال. قال: قالوا: وما ذاك يا رسول الله؟','15','0','0');
INSERT INTO [t8251] VALUES (16,'ذراري المسلمين يكفلهم إبراهيم','16','0','0');
INSERT INTO [t8251] VALUES (17,'يأكل التراب كل شيء في الإنسان إلا عجب ذنبه قيل: وما هو يا رسول الله؟ قال: مثل حبة خردل،','17','0','0');
INSERT INTO [t8251] VALUES (18,'كيف أنعم وصاحب الصور قد التقم الصور ينتظر متى يؤمر أن ينفخ، فينفخ، قالوا: وماذا نقول يا','18','0','0');
INSERT INTO [t8251] VALUES (19,'ينادي مناد بين يدي الصيحة: يا أيها الناس، أتتكم الساعة - ومد بها التيمي صوته - قال:','19','0','0');
INSERT INTO [t8251] VALUES (20,'الشاهد محمد صلى الله عليه وآله وسلم، والمشهود: يوم القيامة فذلك قوله تعالى: {فكيف إذا','20','0','0');
INSERT INTO [t8251] VALUES (21,'أرض بيضاء، عفراء، كالخبزة من النقي','21','0','0');
INSERT INTO [t8251] VALUES (22,'يحشر الناس يوم القيامة على ثلاثة أثلاث: ثلث على الدواب، وثلث ينسلون على أقدامهم نسلا،','22','0','0');
INSERT INTO [t8251] VALUES (23,'يبعث الناس يوم القيامة حفاة عراة غرلا، فقالت له عائشة: يا رسول الله، فكيف بالعورات؟ فقال:','23','0','0');
INSERT INTO [t8251] VALUES (24,'محشورون عراة، غرلا، فأول الخلائق يكسى إبراهيم، ثم يجاء برجال منكم، فيؤخذ بهم ذات الشمال،','24','0','0');
INSERT INTO [t8251] VALUES (25,'يجيئون يوم القيامة على أفواههم الفدام، فأول ما يتكلم من العبد فخذه','25','0','0');
INSERT INTO [t8251] VALUES (26,'يجيء النبي ومعه رجل , ويجيء النبي ومعه الرجلان، وأنا أكثر الأنبياء تبعا يوم','26','0','0');
INSERT INTO [t8251] VALUES (27,'يحشر الناس يوم القيامة، فأكون أنا وأمتي على تل، فيكسوني ربي عز وجل حلة خضراء، ثم يؤذن لي،','27','0','0');
INSERT INTO [t8251] VALUES (28,'يجمع الله الناس , فيقوم المؤمنون حين تزلف الجنة , فيأتون آدم، فيقولون: يا أبانا استفتح','28','0','0');
INSERT INTO [t8251] VALUES (29,'حتى يؤدى إلى كل ذي حق حقه قال الزبير: إن الأمر إذا لشديد','29','0','0');
INSERT INTO [t8251] VALUES (30,'يؤتى بحسنات العبد وسيئاته يوم القيامة، فيقضى بعضها من بعض، فإن بقيت له حسنة، وسع له في','30','0','0');
INSERT INTO [t8251] VALUES (31,'يكفر بالحسنة الواحدة ألف ألف خطيئة قال: نعم، وألفي ألف خطيئة سمعت ذلك من رسول الله صلى','31','0','0');
INSERT INTO [t8251] VALUES (32,'رجلان جثيا بين يدي رب العزة عز وجل، فقال أحدهما: خذ لي بمظلمتي من أخي. قال الله: أعط أخاك','32','0','0');
INSERT INTO [t8251] VALUES (33,'إذا كان يوم القيامة دعا الله بعبده، فيضع كنفه عليه، فيقول: ألم تعمل يوم كذا وكذا ذنب كذا','33','0','0');
INSERT INTO [t8251] VALUES (34,'يؤتى بالعبد يوم القيامة، فيقال: ألم أجعل لك سمعا، وبصرا، ومالا، وولدا، وسخرت لك الأنام','34','0','0');
INSERT INTO [t8251] VALUES (35,'يقضى للجماء من القرناء قال أبو بكر بن أبي داود: لم يروه عن شعبة إلا حجاج بن','35','0','0');
INSERT INTO [t8251] VALUES (36,'الله يدري، وسيقضي بينهما قال أبو بكر بن أبي داود: أخطأ فيه أبو داود، والصواب: شمر بن','36','0','0');
INSERT INTO [t8251] VALUES (37,'تصدقوا، فيوشك الرجل أن يخرج بماله، فلا يجد من يتصدق عليه، ثم ذكر حوضه، فقال: هو ما بين','37','0','0');
INSERT INTO [t8251] VALUES (38,'حوض محمد صلى الله عليه وآله وسلم أشد بياضا من اللبن , وأحلى من العسل , وأبرد من الثلج،','38','0','0');
INSERT INTO [t8251] VALUES (39,'عدد آنية الحوض كعدد نجوم السماء','39','0','0');
INSERT INTO [t8251] VALUES (40,'الكوثر: نهر في الجنة، وعدنيه ربي، ترده أمتي، فيختلج الرجل دوني فأقول: يا رب إنه من أمتي ,','40','0','0');
INSERT INTO [t8251] VALUES (41,'إني فرطكم، وأنا شهيد عليكم، وإني والله لأنظر إلى حوضي الآن , وإني قد أعطيت مفاتيح خزائن','41','0','0');
INSERT INTO [t8251] VALUES (42,'ينفخ في الصور، والصور كهيئة القرن، فصعق من في السموات ومن في الأرض، وبين النفختين أربعون','42','0','0');
INSERT INTO [t8251] VALUES (43,'وإذا أصحابه كأن على رءوسهم الطير، وإذا الإبل قد وضعت جرانها، فإذا أنا بخيال، فإذا معاذ بن','43','0','0');
INSERT INTO [t8251] VALUES (44,'أتاني آت من ربي آنفا، فخيرني بين أن يدخل نصف أمتي الجنة، وبين الشفاعة، فاخترت','44','0','0');
INSERT INTO [t8251] VALUES (45,'خيرت بين الشفاعة، وبين أن يدخل شطر أمتي الجنة، فاخترت الشفاعة، لأنها أعم وأكفى، أفترونها','45','0','0');
INSERT INTO [t8251] VALUES (46,'خيرت بين الشفاعة، وبين أن يدخل نصف أمتي الجنة، فاخترت الشفاعة، لأنها أعم وأكفى، أترونها','46','0','0');
INSERT INTO [t8251] VALUES (47,'ربي خيرني بين خصلتين: بين أن يدخل نصف أمتي الجنة، وبين الشفاعة لأمتي، فاخترت','47','0','0');
INSERT INTO [t8251] VALUES (48,'فاخترت لهم الشفاعة إلى يوم القيامة','48','0','0');
INSERT INTO [t8251] VALUES (49,'لكل نبي دعوة يدعو بها لأمته، وإني اختبأت دعوتي شفاعة لأمتي يوم القيامة','49','0','0');
INSERT INTO [t8251] VALUES (50,'اغفر لكل مسلم يؤمن بك، لا يشرك بك شيئا قال أبو بكر بن أبي داود: قوله: يقول: يعني النبي','50','0','0');
INSERT INTO [t8251] VALUES (51,'الله وعدني أن يدخل من أمتي الجنة أربع مائة ألف، فقال أبو بكر: زدنا يا رسول الله. قال:','51','0','0');
INSERT INTO [t8251] VALUES (52,'ناسا من أهل لا إله إلا الله يدخلون النار بذنوبهم، فيقول لهم أهل اللات والعزى: ما أغنى','52','0','0');
INSERT INTO [t8251] VALUES (53,'رحمتي سبقت غضبي، وأنا أرحم الراحمين، فيخرج من النار مثل أهل الجنة - أو - مثلي أهل الجنة','53','0','0');
INSERT INTO [t8251] VALUES (54,'يدخل قوم جهنم، ثم يخرجون منها، فيدخلون الجنة، فيعرفون فيها بأسمائهم يقال لهم:','54','0','0');
INSERT INTO [t8251] VALUES (55,'يؤتى بالموت يوم القيامة في صورة كبش أملح، فيذبح بين الجنة والنار، ثم يقال: يا أهل الجنة ,','55','0','0');
INSERT INTO [t8251] VALUES (56,'ما يحشرون على أقدامهم، ولا يساقون سوقا، ولكنهم يؤتون بنوق من الجنة، لم ينظر الخلائق إلى','56','0','0');
INSERT INTO [t8251] VALUES (57,'احتجت الجنة والنار، فقالت النار: يدخلني الجبارون والمتكبرون، وقالت الجنة: يدخلني الفقراء','57','0','0');
INSERT INTO [t8251] VALUES (58,'من اتقى الله دخل الجنة، ينعم فيها، ولا ييأس، ويحيا فيها فلا يموت، ولا تبلى ثيابه، ولا','58','0','0');
INSERT INTO [t8251] VALUES (59,'جنتان من ذهب , آنيتهما وما فيهما، وجنتان من فضة، آنيتهما وما فيهما، وما بين القوم، وبين','59','0','0');
INSERT INTO [t8251] VALUES (60,'للنار سبعة أبواب، وللجنة ثمانية أبواب','60','0','0');
INSERT INTO [t8251] VALUES (61,'ما بين كل مصراعين من مصاريع الجنة مسيرة سبع سنين','61','0','0');
INSERT INTO [t8251] VALUES (62,'الجنة مائة درجة، ما بين كل درجتين مسيرة خمسمائة عام','62','0','0');
INSERT INTO [t8251] VALUES (63,'لو أن ما أقل ظفر من الجنة برز من الدنيا لتزخرفت له ما بين السماء','63','0','0');
INSERT INTO [t8251] VALUES (64,'يدخل أهل الجنة الجنة جردا، مردا، بيضا، جعادا، مكحلين، أبناء ثلاثة وثلاثين، على خلق آدم:','64','0','0');
INSERT INTO [t8251] VALUES (65,'يبعث أهل الجنة على صورة آدم، في ميلاد ثلاث وثلاثين سنة، جردا، مردا، مكحلين، ثم يذهب بهم','65','0','0');
INSERT INTO [t8251] VALUES (66,'ما في الجنة شجرة، إلا ساقها من ذهب','66','0','0');
INSERT INTO [t8251] VALUES (67,'في الجنة شجرة، يسير الراكب في ظلها، مائة سنة','67','0','0');
INSERT INTO [t8251] VALUES (68,'شجرة في الجنة، مسيرة مائة سنة , ثياب أهل الجنة تخرج من أكمامها','68','0','0');
INSERT INTO [t8251] VALUES (69,'في الجنة قصر من لؤلؤة، ليس فيها صدع، ولا وهي، أعده الله لخليله إبراهيم عليه السلام','69','0','0');
INSERT INTO [t8251] VALUES (70,'الله يجعل مكان كل شوكة منها ثمرة مثل خصوة التيس الملبود - يعني الخصى - فيها سبعون لونا من','70','0','0');
INSERT INTO [t8251] VALUES (71,'في الجنة بحر الماء، وبحر اللبن، وبحر الخمر، وبحر العسل، ثم تنفجر الأنهار','71','0','0');
INSERT INTO [t8251] VALUES (72,'الجنة لا خطر لها هي ورب الكعبة نور يتلألأ، وريحانة تهتز، وقصر مشيد، ونهر مطرد، وثمرة','72','0','0');
INSERT INTO [t8251] VALUES (73,'أهل الجنة ليتراءون الغرفة في الجنة، كما يتراءون الكوكب الدري الغابر في الأفق الشرقي، أو','73','0','0');
INSERT INTO [t8251] VALUES (74,'أهل الجنة ليتراءون أهل الغرف فوقهم كما يتراءون الكوكب الدري الغابر في الأفق من المشرق','74','0','0');
INSERT INTO [t8251] VALUES (75,'في الجنة لغرفا يرى ظهورها من بطونها، وبطونها من ظهورها، فقام أعرابي، فقال: يا رسول الله ,','75','0','0');
INSERT INTO [t8251] VALUES (76,'الحور في الجنة يتغنين، يقلن: نحن الحور الحسان، خبين لأزواج كرام','76','0','0');
INSERT INTO [t8251] VALUES (77,'لا تؤذي امرأة زوجها في الدنيا، إلا قالت زوجته من الحور العين: لا تؤذيه قاتلك الله، فإنما','77','0','0');
INSERT INTO [t8251] VALUES (78,'أدنى أهل الجنة منزلة الذي له ثمانون ألف خادم، واثنتان وسبعون زوجة، وتنصب له قبة من لؤلؤ،','78','0','0');
INSERT INTO [t8251] VALUES (79,'من مات من أهل الجنة من صغير، أو كبير يردون بني ثلاثة وثلاثين سنة في الجنة، لا يزيدون','79','0','0');
INSERT INTO [t8251] VALUES (80,'لو أن امرأة من نساء أهل الجنة أشرفت إلى أهل الأرض لملأت الأرض ريح مسك، ولأذهبت ضوء الشمس','80','0','0');
INSERT INTO [t8251] VALUES (81,'الرجل في الجنة ليتكئ سبعين سنة قبل أن يتحول، ثم تأتيه امرأة , فتضرب على منكبه، فينظر وجهه','81','0','0');
