INSERT INTO [t8254] VALUES (1,'لو أنكم توكلتم على الله حق توكله لرزقكم كما يرزق الطير تغدو خماصا وتروح','1','0','0');
INSERT INTO [t8254] VALUES (2,'اللهم لك أسلمت، وبك آمنت، وعليك توكلت، وإليك أنبت، وبك خاصمت، أعوذ بعزتك، لا إله إلا أنت','2','0','0');
INSERT INTO [t8254] VALUES (3,'اللهم إني أسألك التوفيق لمحابك من الأعمال، وصدق التوكل عليك، وحسن الظن','3','0','0');
INSERT INTO [t8254] VALUES (4,'اللهم اجعلني ممن توكل عليك فكفيته، واستهداك فهديته، واستنصرك فنصرته','4','0','0');
INSERT INTO [t8254] VALUES (5,'التوكل على الله جماع الإيمان','5','0','0');
INSERT INTO [t8254] VALUES (6,'أربع لا يعطيهن الله إلا من أحب. قال ابن شبرمة: ما هن؟ قال: الصمت، وهو أول العبادة،','6','0','0');
INSERT INTO [t8254] VALUES (7,'توكلوا على الله، وثقوا به؛ فإنه يكفي ممن سواه','7','0','0');
INSERT INTO [t8254] VALUES (8,'يا بني، الدنيا بحر غرق فيه أناس كثير، فإن استطعت أن تكون سفينتك فيها الإيمان بالله،','8','0','0');
INSERT INTO [t8254] VALUES (9,'من سره أن يكون أقوى الناس، فليتوكل على الله','9','0','0');
INSERT INTO [t8254] VALUES (10,'المتوكل الذي يلقي حبه في الأرض، ويتوكل على الله','10','0','0');
INSERT INTO [t8254] VALUES (11,'اعقلها وتوكل','11','0','0');
INSERT INTO [t8254] VALUES (12,'توكل وأبشر، فلم أر مثل التوكل قط، توكل وأبشر، فلم أر مثل التوكل قط','12','0','0');
INSERT INTO [t8254] VALUES (13,'ما من عبد ألجأته حاجة، فأخذ بأمانته توكلا على ربه، ثم أنفقه على أهله في غير إسراف، فأدركه','13','0','0');
INSERT INTO [t8254] VALUES (14,'العز والغنى يجولان في طلب التوكل، فإذا ظفرا أوطنا','14','0','0');
INSERT INTO [t8254] VALUES (15,'تحد لي التوكل؟ قال: آه، كيف تتوكل عليه، وأنت يختار لك، فتسخط قضاءه، أرأيت لو دخلت بيتك،','15','0','0');
INSERT INTO [t8254] VALUES (16,'الدنيا عرض حاضر، يأكل منها البر والفاجر، والآخرة أجل صادق، يحكم فيها ملك قادر، يفصل بين','16','0','0');
INSERT INTO [t8254] VALUES (17,'الرضا عن الله','17','0','0');
INSERT INTO [t8254] VALUES (18,'من توكل العبد أن يكون الله هو ثقته','18','0','0');
INSERT INTO [t8254] VALUES (19,'من المتوكل؟ قال: من لم يسخط حكم الله عز وجل على كره أو محبة','19','0','0');
INSERT INTO [t8254] VALUES (20,'باسم الله، توكلت على الله، لا حول ولا قوة إلا بالله، فيقال له حينئذ: كفيت، ووقيت، وتنحى','20','0','0');
INSERT INTO [t8254] VALUES (21,'إذا خرج الرجل من بيته، فقال: باسم الله، توكلت على الله، لا حول ولا قوة إلا بالله، قال','21','0','0');
INSERT INTO [t8254] VALUES (22,'باسم الله، توكلت على الله، اللهم إني أعوذ بك من شر ما خرجت إليه','22','0','0');
INSERT INTO [t8254] VALUES (23,'باسم الله، ولا قوة إلا بالله، التكلان على الله','23','0','0');
INSERT INTO [t8254] VALUES (24,'إنه ليس له سلطان على الذين آمنوا وعلى ربهم يتوكلون} [النحل: ٩٩] قال: أن يحملهم على ذنب لا','24','0','0');
INSERT INTO [t8254] VALUES (25,'يدخل الجنة من أمتي سبعون ألفا بغير حساب، لا يكتوون، ولا يسترقون، ولا يتطيرون، وعلى ربهم','25','0','0');
INSERT INTO [t8254] VALUES (26,'أنزلني من نفسك كهمك، واجعلني ذخرا لك في معادك، وتقرب إلي بالنوافل أدنك، وتوكل علي أكفك،','26','0','0');
INSERT INTO [t8254] VALUES (27,'إذا بلغ غاية من الزهد أخرجه ذلك إلى التوكل','27','0','0');
INSERT INTO [t8254] VALUES (28,'وما على أحدكم إذا أمسى وأصبح أن يقول: {وما لنا ألا نتوكل على الله} [إبراهيم: ١٢] قال','28','0','0');
INSERT INTO [t8254] VALUES (29,'لما ألقي يوسف في الجب قال: حسبي الله ونعم الوكيل. وكان الماء آجنا فصفا، وكان مالحا','29','0','0');
INSERT INTO [t8254] VALUES (30,'أرى التوكل حسن الظن','30','0','0');
INSERT INTO [t8254] VALUES (31,'لما ألقي إبراهيم في النار قال: حسبنا الله ونعم الوكيل. وقال محمد صلى الله عليه وسلم','31','0','0');
INSERT INTO [t8254] VALUES (32,'حسبي الله ونعم الوكيل، فساخ في الأرض وأنا أنظر إليه، وسمعت صوتا من ورائي يقرأ هذه الآية:','32','0','0');
INSERT INTO [t8254] VALUES (33,'حسبي الله وكفى، سمع الله لمن دعا، ليس وراء الله منتهى','33','0','0');
INSERT INTO [t8254] VALUES (34,'أسألك خوف العالمين بك، وعلم الخائفين لك، وتوكل المؤمنين بك، ويقين المتوكلين عليك، وإنابة','34','0','0');
INSERT INTO [t8254] VALUES (35,'بحسبك من التوكل عليه أن يعلم من قلبك حسن توكلك عليه، وكم من عبد من عباده قد فوض إليه','35','0','0');
INSERT INTO [t8254] VALUES (36,'لو عامل عبد الله بحسن التوكل، وصدق النية له بطاعته؛ لاحتاجت إليه الأمراء فمن دونهم، فكيف','36','0','0');
INSERT INTO [t8254] VALUES (37,'توكل على الله حتى يكون جليسك وأنيسك، وموضع شكواك، وأكثر ذكر الموت حتى لا يكون لك جليس','37','0','0');
INSERT INTO [t8254] VALUES (38,'لا تعمل عملا تريد به غير الله؛ فيجعل الله ثوابك على ما أردت. قال: واجتمع إلي أصحاب محمد،','38','0','0');
INSERT INTO [t8254] VALUES (39,'لا رقية إلا من عين أو حمة فقال سعيد بن جبير: قد أحسن من انتهى إلى ما','39','0','0');
INSERT INTO [t8254] VALUES (40,'هم الذين لا يسترقون، ولا يكتوون، وعلى ربهم يتوكلون. فقام عكاشة بن محصن، فقال: أنا منهم يا','40','0','0');
INSERT INTO [t8254] VALUES (41,'إن أهل الجنة يتراءون في الغرف كما تراءون الكوكب الشرقي، أو الكوكب الغربي الغارب في الأفق،','41','0','0');
INSERT INTO [t8254] VALUES (42,'الطيرة من الشرك، ولكن الله يذهبها بالتوكل','42','0','0');
INSERT INTO [t8254] VALUES (43,'الطيرة شرك - ثلاثا - وما منا إلا، ولكن الله يذهبه بالتوكل','43','0','0');
INSERT INTO [t8254] VALUES (44,'من استرقى واكتوى فقد برئ من التوكل','44','0','0');
INSERT INTO [t8254] VALUES (45,'اللهم إني سمعتك في كتابك تندب عبادك إلى كفايتك، وتشترط عليهم التوكل عليك، اللهم وأجد سبيل','45','0','0');
INSERT INTO [t8254] VALUES (46,'من خرج من بيته يريد سفرا، فقال حين خرج: باسم الله، آمنت بالله، واعتصمت بالله، وتوكلت على','46','0','0');
INSERT INTO [t8254] VALUES (47,'التوكل على ثلاث درجات: أولاها ترك الشكاية، والثانية الرضا، والثالثة المحبة، فترك الشكاية','47','0','0');
INSERT INTO [t8254] VALUES (48,'قلت: حسبي الله ونعم الوكيل. قالت لها زينب: قلت كلمة المؤمنين','48','0','0');
INSERT INTO [t8254] VALUES (49,'لو توكلنا على الله حق توكله ما بنينا حائطا على لبنتين، ولا جعلنا على بابنا','49','0','0');
INSERT INTO [t8254] VALUES (50,'ما أقدر أن أقول: توكلت على الله','50','0','0');
INSERT INTO [t8254] VALUES (51,'إن أشد آية في القرآن تفويضا: {ومن يتوكل على الله فهو حسبه} [الطلاق: ٣] فقال مسروق:','51','0','0');
INSERT INTO [t8254] VALUES (52,'صدق الكذوب ولم يكن بصدوق ... ما الحرص إلا من طريق الموق قد قدر الله الأمور بعلمه ... فيها','52','0','0');
INSERT INTO [t8254] VALUES (53,'إن فوضت أمرك إلى الله، اجتمع لك في ذلك أمران، قلت: ما هما؟ قال: قلة الاكتراث بما قد ضمن','53','0','0');
INSERT INTO [t8254] VALUES (54,'توكل تسق إليك الأرزاق بلا تعب ولا تكلف','54','0','0');
INSERT INTO [t8254] VALUES (55,'من أجمع التوكل عليه كفاه ما همه، وساق الرزق والخير له، وقد قال الله عز وجل: {ومن يتوكل','55','0','0');
INSERT INTO [t8254] VALUES (56,'توكل على من توكل عليه المتوكلون قبلك؛ فإنه جل ثناؤه لا يكل متوكلا عليه إلى','56','0','0');
INSERT INTO [t8254] VALUES (57,'أدلك على أمر إن أخذت به وتركت ما سواه كفيت أمر السلطان وغيره؟ قال: بلى، قلت: ارجع إلى','57','0','0');
INSERT INTO [t8254] VALUES (58,'اقصد إلى الله في أمرك تجده سريعا قريبا، فإني ما ظاهرت أحدا في أمر أريده إلا الله عز وجل،','58','0','0');
INSERT INTO [t8254] VALUES (59,'أكثر من ذكر الموت، وأقصر أملك، وخصلة ثالثة، إن أنت أصبتها بلغت الغاية القصوى، وظفرت','59','0','0');
INSERT INTO [t8254] VALUES (60,'إني أريد أن أخرج إلى مكة، أفأخرج وأتوكل؟ قال: لو أردت أن تتوكل لخرجت ولم','60','0','0');
INSERT INTO [t8254] VALUES (61,'لا توكل على ابن آدم؛ فإن ابن آدم ليس له قوام، ولكن توكل على الله الحي الذي لا','61','0','0');
INSERT INTO [t8254] VALUES (62,'ملعون من كان ثقته بإنسان مثله','62','0','0');
