INSERT INTO [t8257] VALUES (1,'يوشك أن لا يبقى من الإسلام إلا اسمه , ومن القرآن إلا رسمه , مساجدهم يومئذ عامرة وهي خراب','5','0','0');
INSERT INTO [t8257] VALUES (2,'اللهم لا مانع لما أعطيت ولا معطي لما منعت , من يرد الله به خيرا يفقهه في الدين سمعت هؤلاء','6','0','0');
INSERT INTO [t8257] VALUES (3,'من يرد الله به خيرا يفقهه في الدين','7','0','0');
INSERT INTO [t8257] VALUES (4,'من يرد الله به خيرا يفقهه في الدين قال عبيد الله بن محمد شيخنا رضي الله عنه: ولهذا الفقيه','8','0','0');
INSERT INTO [t8257] VALUES (5,'إنما الفقيه من يخاف الله عز وجل','9','0','0');
INSERT INTO [t8257] VALUES (6,'الفقيه من يخاف الله عز وجل.','10','0','0');
INSERT INTO [t8257] VALUES (7,'ألا أخبركم بالفقيه , كل الفقيه؟ من لم يقنط الناس من رحمة الله , ولم يؤمنهم من مكر الله ,','11','0','0');
INSERT INTO [t8257] VALUES (8,'كفى بخشية الله علما وكفى بالاغترار بالله جهلا','12','0','0');
INSERT INTO [t8257] VALUES (9,'إن الفقه ليس بكثرة السرد وسعة الهذر وكثرة الرواية , وإنما الفقه خشية الله عز','13','0','0');
INSERT INTO [t8257] VALUES (10,'قلت لسعد بن إبراهيم: من أفقه أهل المدينة؟ قال: أتقاهم','14','0','0');
INSERT INTO [t8257] VALUES (11,'إن كمال علم العالم ثلاثة: ترك طلب الدنيا بعلمه , ومحبة الانتفاع لمن يجلس إليه , ورأفته','15','0','0');
INSERT INTO [t8257] VALUES (12,'لا يكون العالم عالما حتى تكون فيه ثلاث خصال: لا يحقر من دونه في العلم , ولا يحسد من فوقه','16','0','0');
INSERT INTO [t8257] VALUES (13,'سألت الحسن عن مسألة , فقال فيها , فقلت: يا أبا سعيد , يأبى عليك الفقهاء. فقال الحسن:','17','0','0');
INSERT INTO [t8257] VALUES (14,'الفقيه: المجتهد في العبادة , الزاهد في الدنيا , المقيم على سنة رسول الله صلى الله عليه','18','0','0');
INSERT INTO [t8257] VALUES (15,'من الفقيه؟ قال: الزاهد في الدنيا , الراغب في الآخرة , البصير في دينه , المجتهد في العبادة','19','0','0');
INSERT INTO [t8257] VALUES (16,'ما رأيت فقيها قط يداري ولا يماري , إنما يفشي حكمته , فإن قبلت حمد الله , وإن ردت حمد','20','0','0');
INSERT INTO [t8257] VALUES (17,'الفقيه , العفيف , المتمسك بالسنة , أولئك أتباع الأنبياء في كل زمان','21','0','0');
INSERT INTO [t8257] VALUES (18,'الفقيه الذي يعد البلاء نعمة , والرخاء مصيبة , وأفقه منه من لم يجترئ على الله عز وجل في','22','0','0');
INSERT INTO [t8257] VALUES (19,'يقال إن الفقيه كل الفقه من فقه في القرآن وعرف مكيدة الشيطان','23','0','0');
INSERT INTO [t8257] VALUES (20,'لا يفقه الرجل كل الفقه حتى يمقت الناس في ذات الله , ثم يرجع إلى نفسه فيكون لها أشد','24','0','0');
INSERT INTO [t8257] VALUES (21,'إن من فقه المرء ممشاه ومدخله ومجلسه','25','0','0');
INSERT INTO [t8257] VALUES (22,'إنك لا تفقه كل الفقه حتى ترى للقرآن وجوها , وإنك لا تفقه كل الفقه حتى تمقت الناس في جنب','26','0','0');
INSERT INTO [t8257] VALUES (23,'إنما الفقيه الذي أنطقته الخشية وأسكتته الخشية , إن قال قال بالكتاب والسنة , وإن سكت سكت','27','0','0');
INSERT INTO [t8257] VALUES (24,'إنا لنجالس الرجل فنرى أن به عيا وما به عي , وإنه لفقيه مسلم. قال وكيع: أسكتته','28','0','0');
INSERT INTO [t8257] VALUES (25,'كنت أسأل الشعبي فيعرض عني ويجبهني بالمسألة. قال: فقلت: يا معشر العلماء. تزوون عنا','29','0','0');
INSERT INTO [t8257] VALUES (26,'استفتى رجل الشعبي فقال: أيها العالم أفتني. فقال: إنما العالم من يخاف','30','0','0');
INSERT INTO [t8257] VALUES (27,'وما يعقلها إلا العالمون} [العنكبوت: ٤٣] فقال: العالم الذي عقل عن الله أمره فعمل بطاعة','31','0','0');
INSERT INTO [t8257] VALUES (28,'ليس العلم للمرء بكثرة الرواية ولكن العلم الخشية','32','0','0');
INSERT INTO [t8257] VALUES (29,'خرجنا مع سفيان بن عيينة إلى منى في جماعة فيهم أبو مسلم المستملي , فقال سفيان في بعض ما','33','0','0');
INSERT INTO [t8257] VALUES (30,'سئل عبد الله بن المبارك: هل للعلماء علامة يعرفون بها؟ قال: علامة العالم من عمل بعلمه ,','34','0','0');
INSERT INTO [t8257] VALUES (31,'كيف يعرف العالم الصادق؟ فقال: الذي يزهد الدنيا , ويعقل أمر آخرته. فقال: نعم كذا نريد أن','35','0','0');
INSERT INTO [t8257] VALUES (32,'لا نثق للناس بعمل عامل لا يعلم , ولا نرضى لهم بعلم عالم لا يعمل','36','0','0');
INSERT INTO [t8257] VALUES (33,'من حسن إسلام المرء تركه ما لا يعنيه وحب الدنيا رأس كل خطيئة وأنا أستغفر الله من اعتذاري','37','0','0');
INSERT INTO [t8257] VALUES (34,'كان الرجل إذا طلب بابا من العلم لم يلبث أن يرى ذلك في تخشعه وبصره ولسانه ويده وزهده','38','0','0');
INSERT INTO [t8257] VALUES (35,'ينبغي للعالم أن يضع التراب على رأسه تواضعا لله عز وجل','39','0','0');
INSERT INTO [t8257] VALUES (36,'العلم إذا لم ينفع ضر','40','0','0');
INSERT INTO [t8257] VALUES (37,'لا ينبغي للرجل أن ينصب نفسه - يعني للفتوى - حتى يكون فيه خمس خصال: أما أولاها: فأن يكون','41','0','0');
INSERT INTO [t8257] VALUES (38,'لا يجوز أن ينصب نفسه للفتوى , ولا يجوز أن يستفتى إلا الموثوق في عفافه وعقله وصلاحه ودينه','42','0','0');
INSERT INTO [t8257] VALUES (39,'إذا كنت في زمان يرضى فيه بالقول دون الفعل , والعلم دون العمل , فاعلم بأنك في شر زمان بين','43','0','0');
INSERT INTO [t8257] VALUES (40,'لا يحل الخلع إلا أن تقول المرأة لزوجها: إني أكرهك وما أحبك. وقد خشيت أن آثم في جنبك ولا','44','0','0');
INSERT INTO [t8257] VALUES (41,'لا تحل الفدية ولا يتم الخلع حتى يكون الفساد من قبلها , ولم يكن يقول: لا تحل له حتى تقول:','45','0','0');
INSERT INTO [t8257] VALUES (42,'إذا قالت: لا أبر لك قسما , ولا أغتسل لك من جنابة , فحينئذ حل الخلع','46','0','0');
INSERT INTO [t8257] VALUES (43,'لا يصلح الخلع إلا أن يكون الفساد من قبل المرأة','47','0','0');
INSERT INTO [t8257] VALUES (44,'لا يصلح الخلع إلا أن يكون الفساد من قبل المرأة','48','0','0');
INSERT INTO [t8257] VALUES (45,'الخلع لا يكون إلا من قبل المرأة لأنها هي المطالبة','49','0','0');
INSERT INTO [t8257] VALUES (46,'متى يجوز الخلع بين الرجل والمرأة ومتى يطيب له أخذ الفدية منها؟ قال: إذا كرهته وعصت الله','50','0','0');
INSERT INTO [t8257] VALUES (47,'ما أقام الزوجان على إقامة حدود الله بينهما فالخلع غير جائز , والفدية لا','51','0','0');
INSERT INTO [t8257] VALUES (48,'لا يجوز الخلع إلا من الناشز','52','0','0');
INSERT INTO [t8257] VALUES (49,'إذا كان من قبلها فلا بأس , وإذا كان من قبله فلا، ولا نعمى عين','53','0','0');
INSERT INTO [t8257] VALUES (50,'إذا كان من قبلها فلا بأس , وإذا كان من قبله فلا','54','0','0');
INSERT INTO [t8257] VALUES (51,'لا يجوز الخلع حتى يكون من قبل المرأة , وإذا كان من قبل الرجل لم يتم','55','0','0');
INSERT INTO [t8257] VALUES (52,'فإن خفتم ألا يقيما حدود الله فلا جناح عليهما فيما افتدت به} [البقرة: ٢٢٩] قال: ذلك في','56','0','0');
INSERT INTO [t8257] VALUES (53,'تردين إليه حديقته؟ قالت: نعم. فأمره رسول الله صلى الله عليه وسلم أن يأخذ ما ساق ولا','57','0','0');
INSERT INTO [t8257] VALUES (54,'ما بال أقوام يلعبون بحدود الله ويستهزئون بآياته، خلعتك، راجعتك، طلقتك،','58','0','0');
INSERT INTO [t8257] VALUES (55,'ما بال أقوام يلعبون بحدود الله. طلقتك , راجعتك , طلقتك , راجعتك قال أبو عبد الله رحمه','59','0','0');
INSERT INTO [t8257] VALUES (56,'لا ترتكبوا ما ارتكبت اليهود , فتستحلوا محارم الله بأدنى الحيل','60','0','0');
INSERT INTO [t8257] VALUES (57,'لعن الله اليهود , يحرمون شحم الغنم ويأكلون أثمانها قال أبو عبد الله: فرسول الله صلى الله','61','0','0');
INSERT INTO [t8257] VALUES (58,'إن عمك عصى الله فأندمه , وأطاع الشيطان , فلم يجعل الله له مخرجا. قال: فإني أتزوجها بغير','62','0','0');
INSERT INTO [t8257] VALUES (59,'أيما رجل ابتاع من رجل بيعا , فإن كل واحد منهما بالخيار حتى يتفرقا من مكانهما , ولا يحل','63','0','0');
INSERT INTO [t8257] VALUES (60,'لا يحل لواحد منهما أن يفارق صاحبه خشية أن يستقيله. يرويه ابن عجلان؟ قال أبو عبد الله: وفي','64','0','0');
INSERT INTO [t8257] VALUES (61,'كانت بنو إسرائيل تأتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون لا تأتيهم , فلما رأت ذلك','66','0','0');
INSERT INTO [t8257] VALUES (62,'هذه الحيل التي وضعها هؤلاء - أبو حنيفة وأصحابه - عمدوا إلى السنن فاحتالوا في نقضها , أتوا','67','0','0');
INSERT INTO [t8257] VALUES (63,'من حلف على يمين ثم احتال لإبطالها , هل تجوز تلك الحيلة؟ قال: لا. نحن لا نرى','68','0','0');
INSERT INTO [t8257] VALUES (64,'إذا حلف على شيء ثم احتال بحيلة فصار إليها , فقد صار إلى ذلك الذي حلف عليه بعينه قال أبو','69','0','0');
INSERT INTO [t8257] VALUES (65,'ولا تنقضوا الأيمان بعد توكيدها} [النحل: ٩١] قال: قال صالح: قال أبي: والحيل لا','70','0','0');
INSERT INTO [t8257] VALUES (66,'يحتالون لنقض سنن رسول الله صلى الله عليه وسلم','71','0','0');
INSERT INTO [t8257] VALUES (67,'لا يجوز شيء من الحيل','72','0','0');
INSERT INTO [t8257] VALUES (68,'وأنا أحتال لك حتى تفعل ولا تحنث. فقال له الفضيل: تعرف الرجل؟ قال: نعم. قال: ارجع واستثبته','73','0','0');
INSERT INTO [t8257] VALUES (69,'من أفتى الناس بالحيلة فيما لا يجوز , يتأول الرأي والهوى بلا كتاب ولا سنة , فهذا من','74','0','0');
INSERT INTO [t8257] VALUES (70,'أيما امرأة سألت زوجها طلاقها من غير ما بأس , فحرام عليها رائحة الجنة. حدثنا القاضي','78','0','0');
INSERT INTO [t8257] VALUES (71,'للمختلعة طلاق ما كانت في العدة. قال بذلك عبد الله بن مسعود , وسعيد بن المسيب , وشريح ,','79','0','0');
INSERT INTO [t8257] VALUES (72,'في الرجل يقول لامرأته: إن دخلت دار فلان فأنت طالق , فطلقها قبل أن تدخل فبانت , ثم خطبها','81','0','0');
INSERT INTO [t8257] VALUES (73,'إن أحدهم ليفتي في المسألة لو وردت على عمر لجمع لها أهل بدر','83','0','0');
INSERT INTO [t8257] VALUES (74,'رأيت أعلم الناس بالقضاء والفتوى أشدهم منه فرارا وأشدهم منه فرقا , وأعماهم عنه أشدهم','84','0','0');
INSERT INTO [t8257] VALUES (75,'يا ربيعة إني أرى الناس قد أحاطوا بك , فإذا سألك الرجل عن مسألة فلا تكن همتك أن تخلصه ,','85','0','0');
INSERT INTO [t8257] VALUES (76,'سألت إبراهيم عن مسألة , فقال: ما وجدت من بلدك من تسأله غيري','86','0','0');
INSERT INTO [t8257] VALUES (77,'ما سألت إبراهيم عن شيء , قط إلا عرفت الكراهية في وجهه','87','0','0');
INSERT INTO [t8257] VALUES (78,'لأن يعيش الرجل جاهلا خير له من أن يقول على الله ما لا يعلم. فقال مالك: هذا كلام شديد. ثم','88','0','0');
INSERT INTO [t8257] VALUES (79,'زباء ذات وبر , لا تنقاد ولا تنساق , لو سئل عنها أصحاب محمد صلى الله عليه وسلم وعليهم','89','0','0');
INSERT INTO [t8257] VALUES (80,'زباء ذات وبر , أعيت السائق والقائد. لو ألقيت على أصحاب رسول الله صلى الله عليه وسلم','90','0','0');
INSERT INTO [t8257] VALUES (81,'من أفتى الناس في كل ما يستفتونه فهو مجنون','91','0','0');
INSERT INTO [t8257] VALUES (82,'والله إن الذي يفتي للناس في كل ما يسألونه لمجنون. قال الأعمش: قال لي الحكم: لو سمعت هذا','92','0','0');
INSERT INTO [t8257] VALUES (83,'من أفتى فتوى يعمي فيها فإثمها عليه. قال أبو عبد الله: فهذا عبد الله بن مسعود يحلف بالله:','93','0','0');
INSERT INTO [t8257] VALUES (84,'اليمين حنث أو مندمة. ولقد روي عن النبي صلى الله عليه وسلم. مما يدل على صحة توحيد من آمن','94','0','0');
INSERT INTO [t8257] VALUES (85,'إن البلاء موكل بالقول , ما قال عبد قط لشيء: والله لا أفعله , إلا ترك الشيطان كل شيء من','95','0','0');
INSERT INTO [t8257] VALUES (86,'عن رجل حلف بالطلاق أنه لا بد أن يطأ امرأته الليلة , فوجدها حائضا؟ فقال: تطلق منه امرأته','96','0','0');
