INSERT INTO [t8259] VALUES (1,'من يصبر يصبره الله، ولم يعطوا عطاء خيرا وأوسع من الصبر','1','0','0');
INSERT INTO [t8259] VALUES (2,'من ورائكم أيام الصبر، صبر فيهن مثل قبض على الجمر للعامل فيهن مثل أجر خمسين يعملون مثل','2','0','0');
INSERT INTO [t8259] VALUES (3,'ستجدون أثرة شديدة فاصبروا حتى تلقوا الله ورسوله فإني على الحوض قالوا:','3','0','0');
INSERT INTO [t8259] VALUES (4,'اتقوا الله واصبروا، فإنه ليس من عام إلا والذي بعده أشد منه، حتى تقوم الساعة. قال عثمان:','4','0','0');
INSERT INTO [t8259] VALUES (5,'نصبر على طاعة الله، وكنا نصبر عن معاصي الله، فيقال لهم: ادخلوا الجنة فنعم أجر','5','0','0');
INSERT INTO [t8259] VALUES (6,'أفضل عيش أدركناه بالصبر، ولو أن الصبر كان من الرجال كان كريما','6','0','0');
INSERT INTO [t8259] VALUES (7,'لو كان الصبر والشكر بعيرين، ما باليت أيهما ركبت','7','0','0');
INSERT INTO [t8259] VALUES (8,'الصبر من الإيمان بمنزلة الرأس من الجسد، فإذا قطع الرأس باد الجسد، ثم رفع صوته فقال: ألا','8','0','0');
INSERT INTO [t8259] VALUES (9,'الصبر على أربع شعب: على الشوق، والشفق، والزهادة، والترقب. فمن اشتاق إلى الجنة سلا عن','9','0','0');
INSERT INTO [t8259] VALUES (10,'عليك بالجماعة، فإن الله لن يجمع أمة محمد صلى الله عليه وسلم على ضلالة، واصبر حتى يستريح','10','0','0');
INSERT INTO [t8259] VALUES (11,'لا تؤذ وإن أوذيت فاصبر','11','0','0');
INSERT INTO [t8259] VALUES (12,'يا دنيا أمري على المؤمن يصبر عليك، لا تحولي له فتفتنيه','12','0','0');
INSERT INTO [t8259] VALUES (13,'طوبى لمن غلب بتقواه هواه، وبصبره الشهوات','13','0','0');
INSERT INTO [t8259] VALUES (14,'الكرام الكاتبين ربما شكوا إلى الله من صاحبهم الذي يكونون معه أن من أمره إن إن، فيؤمرون','14','0','0');
INSERT INTO [t8259] VALUES (15,'لو كان الصبر من الرجال لكان كريما','15','0','0');
INSERT INTO [t8259] VALUES (16,'الصبر كنز من كنوز الخير، لا يعطيه الله إلا لعبد كريم عليه','16','0','0');
INSERT INTO [t8259] VALUES (17,'ما من عبد وهب الله له صبرا على الأذى، وصبرا على البلاء، وصبرا على المصائب، إلا وقد أوتي','17','0','0');
INSERT INTO [t8259] VALUES (18,'الصبر صبران، الصبر على المصيبة حسن، وأفضل من ذلك الصبر عن المعاصي','18','0','0');
INSERT INTO [t8259] VALUES (19,'إلا بالصبر','19','0','0');
INSERT INTO [t8259] VALUES (20,'كل عمل يعرف ثوابه إلا الصبر. قال الله عز وجل: {إنما يوفى الصابرون أجرهم بغير حساب}','20','0','0');
INSERT INTO [t8259] VALUES (21,'إنما يوفى الصابرون أجرهم بغير حساب} [الزمر: ١٠] قال: فقال بيديه هكذا - وبسطهما -: غرفا','21','0','0');
INSERT INTO [t8259] VALUES (22,'ما أنعم الله على عبد نعمة فانتزعها منه، فعاضه مكان ما انتزع منه الصبر، إلا كان ما عوضه','22','0','0');
INSERT INTO [t8259] VALUES (23,'سلام عليكم بما صبرتم} [الرعد: ٢٤] قال: على دينكم، فنعم ما أعقبتكم من الدنيا','23','0','0');
INSERT INTO [t8259] VALUES (24,'الصبر ثلاث: فصبر على المصيبة، وصبر على الطاعة، وصبر عن المعصية، فمن صبر على المصيبة حتى','24','0','0');
INSERT INTO [t8259] VALUES (25,'اللهم ارزقنا صبرا على طاعتك، وارزقنا صبرا عن معصيتك، وارزقنا صبرا على ما تحب، وارزقنا','25','0','0');
INSERT INTO [t8259] VALUES (26,'الشكر أفضل أم الصبر؟ قال: الصبر، والعافية أحب إلي','26','0','0');
INSERT INTO [t8259] VALUES (27,'الحلم زين، والتقى كرم، والصبر خير مراكب الصعب','27','0','0');
INSERT INTO [t8259] VALUES (28,'الغرفة} [الفرقان: ٧٥] : الجنة، {بما صبروا} [الإنسان: ١٢] : على الفقر','28','0','0');
INSERT INTO [t8259] VALUES (29,'صبروا أنفسهم على ما أمرهم به من طاعته، وصبروا أنفسهم عما نهاهم عنه من معصيته، فقالت لهم','29','0','0');
INSERT INTO [t8259] VALUES (30,'الذين صبروا وعملوا الصالحات} [هود: ١١] قال: {صبروا} [الإنسان: ١٢] في البأساء والضراء','30','0','0');
INSERT INTO [t8259] VALUES (31,'الجنة حظرت بالصبر والمكاره، فلا تؤتى إلا من باب صبر أو مكروه، وإن جهنم شعبت بالشهوات','31','0','0');
INSERT INTO [t8259] VALUES (32,'اصبري، حتى عد أصابعي الخمس، ثم أخذ بيساري فقال: يا بنية اصبري، حتى عد أصابعي','32','0','0');
INSERT INTO [t8259] VALUES (33,'من ابتلي فصبر، وأعطي فشكر وظلم فغفر، وظلم فاستغفر ثم سكت قالوا: ما له يا رسول الله؟ قال:','33','0','0');
INSERT INTO [t8259] VALUES (34,'قلب شاكر، ولسان ذاكر، وبدن على البلاء صابر، وزوجة لا تبغيه خونا في نفسه ولا','34','0','0');
INSERT INTO [t8259] VALUES (35,'الإيمان قال: الصبر والسماح','35','0','0');
INSERT INTO [t8259] VALUES (36,'السماح بفرائض الله، والصبر عن محارم الله','36','0','0');
INSERT INTO [t8259] VALUES (37,'واستعينوا بالصبر والصلاة} [البقرة: ٤٥] قال: الصبر: الصيام','37','0','0');
INSERT INTO [t8259] VALUES (38,'ثلاث من كن فيه أصاب البر: سخاوة النفس، والصبر على الأذى، وطيب الكلام','38','0','0');
INSERT INTO [t8259] VALUES (39,'إن الحسنات يذهبن السيئات} [هود: ١١٤] فقالت: بأبي أنت وأمي، ما تلك الحسنات؟ قال: الصلوات','39','0','0');
INSERT INTO [t8259] VALUES (40,'الصبر رضا','40','0','0');
INSERT INTO [t8259] VALUES (41,'حقيقة اليقين الصبر، وحقيقة العمل النية','41','0','0');
INSERT INTO [t8259] VALUES (42,'خشية الله، وحب الفردوس يباعدان من زهرة الدنيا، ويورثان الصبر على','42','0','0');
INSERT INTO [t8259] VALUES (43,'فإن صبر صاحبها أفضت به إلى روح وإن جزع رجع','43','0','0');
INSERT INTO [t8259] VALUES (44,'الدنيا خوانة لا يدوم نعيمها، ولا يؤمن فجائعها، ومن يعش يبتل، ومن يتفقد يفقد، ومن لا يعد','44','0','0');
INSERT INTO [t8259] VALUES (45,'اجعلوا عزمكم في الأمور كلها بين يدي هواكم، ثم اتخذوا كتاب الله إماما لكم في','45','0','0');
INSERT INTO [t8259] VALUES (46,'صبرا يا أبا ياسر وآل ياسر، فإن موعدكم الجنة','46','0','0');
INSERT INTO [t8259] VALUES (47,'لو كان الصبر من الرجال كان كريما','47','0','0');
INSERT INTO [t8259] VALUES (48,'وهل وجدنا عيشنا إلا في الصبر','48','0','0');
INSERT INTO [t8259] VALUES (49,'بالصبر، لم نلق قوما إلا صبرنا لهم ما صبروا لنا','49','0','0');
INSERT INTO [t8259] VALUES (50,'كلنا نكره الموت وألم الجراح، ولكنا نتفاضل بالصبر','50','0','0');
INSERT INTO [t8259] VALUES (51,'صبر ساعة','51','0','0');
INSERT INTO [t8259] VALUES (52,'إذا لم تسامح في الأمور تعقدت ... عليك فسامح واخرج العسر باليسر','52','0','0');
INSERT INTO [t8259] VALUES (53,'الصبر عند أول صدمة','53','0','0');
INSERT INTO [t8259] VALUES (54,'إن أصابه خير حمد الله وشكره، وإن أصابته مصيبة احتسب وصبر. المؤمن يؤجر في كل شيء، حتى','54','0','0');
INSERT INTO [t8259] VALUES (55,'وللصبر اليوم عن معاصي الله خير من الصبر على الأغلال في نار جهنم','55','0','0');
INSERT INTO [t8259] VALUES (56,'فإن أعطي شكر وإن ابتلي صبر','56','0','0');
INSERT INTO [t8259] VALUES (57,'رفع عن هذه الأمة الخطأ والنسيان، وما استكرهوا عليه، وما لا يطيقون، وأحل لهم في حال','57','0','0');
INSERT INTO [t8259] VALUES (58,'والصابرين في البأساء والضراء وحين البأس} [البقرة: ١٧٧] قال: أما {البأساء} [البقرة: ١٧٧] :','58','0','0');
INSERT INTO [t8259] VALUES (59,'كل عمل له ثواب يعرف إلا الصبر قال الله: {إنما يوفى الصابرون أجرهم بغير حساب} [الزمر:','59','0','0');
INSERT INTO [t8259] VALUES (60,'إن صبرت فلك الجنة، وإن شئت دعوت الله أن يعافيك قالت: إني أتكشف، فادع الله أن لا أنكشف.','60','0','0');
INSERT INTO [t8259] VALUES (61,'لم يعط العباد أفضل من الصبر، به دخلوا الجنة','61','0','0');
INSERT INTO [t8259] VALUES (62,'إذا جاء الموت وسكراته لم يغن عن الفتى ما كان فيه من النعيم واللذة ثم مال مغشيا','62','0','0');
INSERT INTO [t8259] VALUES (63,'ما ازددت لك - بتوفيقك - إلا صبرا، وعنك - بمنك ونعمتك - إلا رضا. قال خلف: وكان الجذام قد','63','0','0');
INSERT INTO [t8259] VALUES (64,'فأفرغ علي صبرا يبلغني رضاك عني، وإن كنت إنما ابتليتني لتثيبني وتأجرني وتجعل بلاءك لي سببا','64','0','0');
INSERT INTO [t8259] VALUES (65,'الصبر على منن الرجال أشد من الصبر على ما بي من البلاء','65','0','0');
INSERT INTO [t8259] VALUES (66,'اغتنموا الصبر فكأنكم قد بلغتم مدته','66','0','0');
INSERT INTO [t8259] VALUES (67,'من شروط الصبر أن تعرف كيف تصبر؟ ولمن تصبر؟ وما تريد بصبرك؟ وتحتسب في ذلك وتحسن النية فيه،','67','0','0');
INSERT INTO [t8259] VALUES (68,'لا تعجبن بصبرك، فإني قد علمت ما يمتص كل شعرة من لحمك ودمك، ولولا أني أعطيت موضع كل شعرة','68','0','0');
INSERT INTO [t8259] VALUES (69,'لم يكن الذي خرج بأيوب أكلة، كان الذي يخرج به أمثال ثدي النساء ثم يتفطر','69','0','0');
INSERT INTO [t8259] VALUES (70,'مكث أيوب عليه السلام ملقى على زبالة سبع سنين يمر به الرجل فيمسك على أنفه، حتى مر به رجلان','70','0','0');
INSERT INTO [t8259] VALUES (71,'ما أصبت من أيوب شيئا فرحت به، إلا أني كنت إذا سمعت أنينه علمت أني قد أبلغت','71','0','0');
INSERT INTO [t8259] VALUES (72,'كانت الدودة لتقع من جسد أيوب فيأخذها فيعيدها إلى مكانها ويقول: كلي من رزق','72','0','0');
INSERT INTO [t8259] VALUES (73,'يأمرك أن تدعو بهؤلاء الكلمات، فإن الله معطيك إحداهن: اللهم إني أسألك تعجيل عافيتك، أو','73','0','0');
INSERT INTO [t8259] VALUES (74,'أفلح من أسلم وجعل رزقه كفافا، فصبر على ذلك','74','0','0');
INSERT INTO [t8259] VALUES (75,'أدخل نفسك في هموم الدنيا، واخرج منها بالصبر','75','0','0');
INSERT INTO [t8259] VALUES (76,'إذا شئت رأيت بصيرا لا صبر له، فإذا رأيت بصيرا ذا صبر فهناك','76','0','0');
INSERT INTO [t8259] VALUES (77,'ما أحسن عاقبة الصبر','77','0','0');
INSERT INTO [t8259] VALUES (78,'مفتاح باب الفرج الصبر ... كل عسر معه يسر والدهر لا يبقى على حاله ... والأمر يأتي بعده','78','0','0');
INSERT INTO [t8259] VALUES (79,'يوشك أن يفضي بالصابر البلاء إلى الرخاء، وبالفاجر الرخاء إلىالبلاء','79','0','0');
INSERT INTO [t8259] VALUES (80,'إذا رأيتم أمرا لا تستطيعون أن تغيروا، فاصبروا حتى يكون الله هو الذي','80','0','0');
INSERT INTO [t8259] VALUES (81,'ذكرت آخر أمتي وما يلقون من البلاء، فالصابر منهم يجيء يوم القيامة وله أجر','81','0','0');
INSERT INTO [t8259] VALUES (82,'إذ لم تكن من أهل العزائم هلا استخرتني في عافية؟','82','0','0');
INSERT INTO [t8259] VALUES (83,'أعطاكم الدنيا قرضا، وسألكموها قرضا، فإن أعطيتموها طيبة بها أنفسكم ضاعف لكم ما بين الحسنة','83','0','0');
INSERT INTO [t8259] VALUES (84,'في الصبر جوامع التقوى وإليه موئل المؤمنين','84','0','0');
INSERT INTO [t8259] VALUES (85,'الصبر معقل','85','0','0');
INSERT INTO [t8259] VALUES (86,'يحتاج المؤمن إلى الصبر كما يحتاج إلى الطعام والشراب','86','0','0');
INSERT INTO [t8259] VALUES (87,'اشرب واسق بما صبرت وكنت من الكاظمين','87','0','0');
INSERT INTO [t8259] VALUES (88,'رآكم أهلا أن يختبركم، فأروه أهلا أن تصبروا له. فقالوا: من أنت رحمك الله؟ قال: من ينتظر من','88','0','0');
INSERT INTO [t8259] VALUES (89,'يؤخذ الرجل، فيحفر له في الأرض، ثم يجاء بالمنشار فيوضع فوق رأسه، ما يصرفه عن دينه أو يمشط','89','0','0');
INSERT INTO [t8259] VALUES (90,'اتقوا واصبروا، فوالله إن كان الرجل من المؤمنين قبلكم ليوضع المنشار على رأسه فيشق باثنين،','90','0','0');
INSERT INTO [t8259] VALUES (91,'جعل بعضكم لبعض فتنة فاصبروا','91','0','0');
INSERT INTO [t8259] VALUES (92,'إذا رأيت أمرا لا تستطيع غيره، فاصبر وانتظر فرج الله','92','0','0');
INSERT INTO [t8259] VALUES (93,'رأس أمور العباد العقل، ودليلهم العلم، وسائقهم العمل، ومقويهم على ذلك','93','0','0');
INSERT INTO [t8259] VALUES (94,'لأصبر على الكلمة لهي أشد علي من القبض على الجمر، ما يحملني على الصبر عليها إلا التخوف من','94','0','0');
INSERT INTO [t8259] VALUES (95,'ثلاث يدرك بهن العبد رغائب الدنيا والآخرة: الصبر عند البلاء، والرضا بالقضاء، والدعاء في','95','0','0');
INSERT INTO [t8259] VALUES (96,'باعث من بعدك أمة إن أصابهم ما يحبون حمدوا وشكروا، وإن أصابهم ما يكرهون احتسبوا وصبروا،','96','0','0');
INSERT INTO [t8259] VALUES (97,'غفر لي بصبري على الفقر في الدنيا','97','0','0');
INSERT INTO [t8259] VALUES (98,'فأي الخيل وجدتم أصبر؟ قالوا: الكمت الخمر قال: فأي الإبل وجدتم أصبر؟ قالوا: الحمر الجعاد','98','0','0');
INSERT INTO [t8259] VALUES (99,'وردت من الصابرين على درجة لم ينالوها إلا بالصبر عند البلاء، والشكر عند الرخاء. قال','99','0','0');
INSERT INTO [t8259] VALUES (100,'مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين وحسن أولئك رفيقا}','100','0','0');
INSERT INTO [t8259] VALUES (101,'ألم حل هونه ثواب الله عليه، أفأردفه بألم الجزع الذي لا جدوى فيه، ولا دريكة لفائت معه؟ وفي','101','0','0');
INSERT INTO [t8259] VALUES (102,'ضم إلي منه لعلي أدنو قيد رمح أو رمحين في سبيل الله','102','0','0');
INSERT INTO [t8259] VALUES (103,'تسألني عن رجل أنت خطيئة من خطاياه، وقد ملأت الأرض فسادا؟ قال: فهل خلوت لك؟ قال: مرة','103','0','0');
INSERT INTO [t8259] VALUES (104,'تطيعه في معصية الله وتبيع آخرتك بدنياه، أنت ممن خسر الدنيا والآخرة، فتبا لك آخر الدهر.','104','0','0');
INSERT INTO [t8259] VALUES (105,'اصبر أخرجك من دار الحزن إلى دار الفرح، ومن دار الضيق إلى دار السعة، فلما بلغ السلخ إلى','105','0','0');
INSERT INTO [t8259] VALUES (106,'صبرت كما صبر الإسرائيلي فنعم. قيل له: وكيف كان الإسرائيلي؟ قال: كان ثلاثة نفر، فاجتمعوا','106','0','0');
INSERT INTO [t8259] VALUES (107,'أعوذ بك أن أفر من بأس الناس إلى بأسك، وأعوذ بك أن أجعل فتنة الناس كعذابك، وأعوذ بك أن يرى','107','0','0');
INSERT INTO [t8259] VALUES (108,'ما كنت لآكل شيئا حرمه الله علي أبدا، فأمر به فقطعت يداه ورجلاه، وقطعه عضوا عضوا حتى قتله،','108','0','0');
INSERT INTO [t8259] VALUES (109,'أجد عافيته أكثر مما ابتلاني به، وأجد نعمه علي أكثر من أحصيها. فقلت: أتجد لما أنت فيه ألما','109','0','0');
INSERT INTO [t8259] VALUES (110,'عليك بتقوى الله واقنع برزقه ... فخير عباد الله من هو قانع ولا تلهك الدنيا ولا طمع بها ...','110','0','0');
INSERT INTO [t8259] VALUES (111,'الخلق للخالق والشكر للـ ... ـمنعم والتسليم للقادر','111','0','0');
INSERT INTO [t8259] VALUES (112,'يصيب الإنسان الخير في صبر ساعة','112','0','0');
INSERT INTO [t8259] VALUES (113,'ما يزال البلاء بالمؤمن والمؤمنة في جسده وفي ولده حتى يلقى الله يوم القيامة وما عليه من','113','0','0');
INSERT INTO [t8259] VALUES (114,'أول من يدعى إلى الجنة الذين يحمدون الله على السراء والضراء','114','0','0');
INSERT INTO [t8259] VALUES (115,'صبر لا شكوى فيه','115','0','0');
INSERT INTO [t8259] VALUES (116,'الصبر يأتي من الله العبد على قدر المصيبة','116','0','0');
INSERT INTO [t8259] VALUES (117,'ذكرت آخر أمتي وما يلقون من البلاء، فالصابر منهم يجيء وله أجر شهيدين','117','0','0');
INSERT INTO [t8259] VALUES (118,'الصبر اعتراف العبد لله بما أصابه منه، واحتسابه عند الله رجاء ثوابه، وقد يجزع الرجل وهو','118','0','0');
INSERT INTO [t8259] VALUES (119,'ما منتهى الصبر؟ قال: أن يكون يوم تصيبه المصيبة مثله قبلها','119','0','0');
INSERT INTO [t8259] VALUES (120,'أن يكون صاحب المصيبة في القوم لا يعرف من هو؟','120','0','0');
INSERT INTO [t8259] VALUES (121,'فصبر جميل} [يوسف: ٨٣] قال: الرضا بالمصيبة، والتسليم','121','0','0');
INSERT INTO [t8259] VALUES (122,'الكظيم: الصبور','122','0','0');
INSERT INTO [t8259] VALUES (123,'لو كان الصبر حلوا ما قال الله عز وجل لنبيه صلى الله عليه وسلم: اصبر ولكن الصبر','123','0','0');
INSERT INTO [t8259] VALUES (124,'ما وعد الله من ثوابه الصابرين','124','0','0');
INSERT INTO [t8259] VALUES (125,'ولمن صبر وغفر إن ذلك لمن عزم الأمور} [الشورى: ٤٣] .','125','0','0');
INSERT INTO [t8259] VALUES (126,'انتظار الفرج بالصبر عبادة','126','0','0');
INSERT INTO [t8259] VALUES (127,'تكذب وأنت أمير، تزعم أنه لا يحل ترك عاص، وهؤلاء بنو عمك حولك كلهم عصاة؟ أليس كذلك؟ يقول','127','0','0');
INSERT INTO [t8259] VALUES (128,'عاهدت الله أن أجاهدك بيدي وبلساني وبقلبي، فأما يدي فقد فتها، وأما لساني فهذا تسمع ما','128','0','0');
INSERT INTO [t8259] VALUES (129,'فضرب بطنه مائة، وظهره مائة، ثم أدرجه في عباءة وألقاه في الدار فقلت: أعطشان أنت يا حطيط؟','129','0','0');
INSERT INTO [t8259] VALUES (130,'بلغ العذاب منه وما يتكلم، جاء ذباب فوقع على جراحته. فقال: حس. فقيل له: صبرت على العذاب،','130','0','0');
INSERT INTO [t8259] VALUES (131,'كان يدخل في يده المسال، ثم تسل','131','0','0');
INSERT INTO [t8259] VALUES (132,'عاهدت ربي على ثلاث عند الكعبة: لئن سئلت لأصدقن، ولئن ابتليت لأصبرن، ولئن عوفيت لأشكرن.','132','0','0');
INSERT INTO [t8259] VALUES (133,'ملك الدنيا منقطع إلى الله تبارك وتعالى ما لي إليه من حاجة إلا أن يتوفاني على','133','0','0');
INSERT INTO [t8259] VALUES (134,'الخلق كلهم عبيد الله وعياله فإذا رأيت من العباد عيلة فالشكوى إلى الله، ليس الله يشتكى إلى','134','0','0');
INSERT INTO [t8259] VALUES (135,'لي من لا يغفل عني. قلت: إني أنسيتك. قال: إن لي من لا ينساني. قلت: إني لم أذكرك. قال: إن','135','0','0');
INSERT INTO [t8259] VALUES (136,'نحل أهل الصبر نحلا ما نحله غيرهم من عباده قيل: ما هو رحمك الله؟ قال: سمعته يقول تبارك','136','0','0');
INSERT INTO [t8259] VALUES (137,'واصبر وما صبرك إلا بالله} [النحل: ١٢٧] ، {ولئن صبرتم لهو خير للصابرين} [النحل: ١٢٦] ثم','137','0','0');
INSERT INTO [t8259] VALUES (138,'سلا بنفسي عن الدنيا القدوم على الله عز وجل، والله لله أحب إلي من خلقه ثم','138','0','0');
INSERT INTO [t8259] VALUES (139,'ليس من ناركم صرخت، ولا على دنياكم أسفت، ذكرت بها النار الكبرى، فكان الذي رأيتم من ذلك','139','0','0');
INSERT INTO [t8259] VALUES (140,'هذا آخر يومي من الدنيا، وهو غير مأسوف عليه، وأرجو أن يكون أول أيامى من الآخرة، وهو اليوم','140','0','0');
INSERT INTO [t8259] VALUES (141,'الحمد لله على السراء والضراء، وعلى العافية والبلاء، قد كنت أؤمل في الله ما هو أكثر من','141','0','0');
INSERT INTO [t8259] VALUES (142,'ألا إن كل ميتة على الفراش فهي ظنون، ثم قال: هل تدرون ما كان حال أختكم الشجاء؟ قالوا: وما','142','0','0');
INSERT INTO [t8259] VALUES (143,'اصبروا على طاعته، فإنما هو صبر قليل وغنم طويل، والأمر أعجل من ذلك','143','0','0');
INSERT INTO [t8259] VALUES (144,'من صبر فما أقل ما يصبر، ومن جزع فما أقل ما يتمتع','144','0','0');
INSERT INTO [t8259] VALUES (145,'أعطي الصابرون الصلاة من الله عليهم، والرحمة منه لهم، فمن ذا الذي يدرك فضلهم إلا من كان','145','0','0');
INSERT INTO [t8259] VALUES (146,'كأنكم بعاقبة الصبر محمودة، ليت شعري ما يصنع في القيامة من غبن أيامه الحالية، ثم يبكي حتى','146','0','0');
INSERT INTO [t8259] VALUES (147,'أي عبادك أصبر؟ قال: أكظمهم للغيظ','147','0','0');
INSERT INTO [t8259] VALUES (148,'ما المروءة؟ قال: الصبر والصمت: الصبر لمن غاظك وإن بلغ منك، والصمت حتى','148','0','0');
INSERT INTO [t8259] VALUES (149,'من لقي في الله فصبر حتى يقتل أو يغلب لم يفتن في قبره','149','0','0');
INSERT INTO [t8259] VALUES (150,'من نوى الصبر على طاعة الله صبره الله عليها وقواه لها، ومن عزم على الصبر عن معاصي الله','150','0','0');
INSERT INTO [t8259] VALUES (151,'موئل الصبر موئل كريم هنيء غير مردي وليعلمن أهل الاستخفاف بمعاصي الله أن ذلك كائن عليهم','151','0','0');
INSERT INTO [t8259] VALUES (152,'من امتطى الصبر قوي على العبادة، ومن أجمع اليأس استغنى عن الناس، ومن أهمته نفسه لم يول','152','0','0');
INSERT INTO [t8259] VALUES (153,'عليك بالصبر، والتصبر، والاصطبار. قال: قلت: ما الصبر؟، وما التصبر؟، وما الاصطبار؟ قال: أما','153','0','0');
INSERT INTO [t8259] VALUES (154,'لا يكون الصبر إلا في رجل له عند الله عظيم من الذخر، ولرب صابر برز به صبره أمام المتقين','154','0','0');
INSERT INTO [t8259] VALUES (155,'ستكون أمور تنكرونها، فعليكم فيها بالصبر، صبر كقبض على الجمر، ولا تقولوا: تغير، حتى يكون','155','0','0');
INSERT INTO [t8259] VALUES (156,'الحمد لله الذي أعطانيهما فأمتعني بهما شبابي وصحتي، حتى إذا فنيت أيامي وحضر أجلي أخذهما','156','0','0');
INSERT INTO [t8259] VALUES (157,'فقطعت رجله وهو جالس عند الوليد، فما تضور وجهه','157','0','0');
INSERT INTO [t8259] VALUES (158,'لو كان الصبر رجلا كان أكمل الرجال، وإن الجزع والجهل والشره والحسد لفروع أصلها','158','0','0');
INSERT INTO [t8259] VALUES (159,'الصبر قوة من قوى العقل، وبقدر مولد العقل ينمى الصبر','159','0','0');
INSERT INTO [t8259] VALUES (160,'صبرت ولم أبد اكتئابا ولن ترى ... أخا جزع إلا يصير إلى الصبر وإني وإن أبديت صبرا لمنطو ...','160','0','0');
INSERT INTO [t8259] VALUES (161,'تعز إذا أصبت بكل أمر ... من التقوى أمرت به مصابا','161','0','0');
INSERT INTO [t8259] VALUES (162,'ما التمست اتباع رضوانه بشيء أبلغ فيما تريد من اجتناب سخطه قال: ثم بكى وقال: وكيف وغرور','162','0','0');
INSERT INTO [t8259] VALUES (163,'أسألك اللهم خيرا يبلغنا ثواب الصابرين لديك، وأسألك اللهم شكرا يبلغنا مزيد الشاكرين لك،','163','0','0');
INSERT INTO [t8259] VALUES (164,'الصبر أعلى خلال الكرم','164','0','0');
INSERT INTO [t8259] VALUES (165,'ما أعطي عبد - بعد الإيمان - أفضل من الصبر إلا الشكر، فإنه أفضلهما وأسرعهما','165','0','0');
INSERT INTO [t8259] VALUES (166,'الصبر من الإيمان بمنزلة اليدين من الجسد، من لم يكن صابرا على البلاء لم يكن شاكرا على','166','0','0');
INSERT INTO [t8259] VALUES (167,'من أجمع على الصبر في الأمور فقد حوى الخير، والتمس معاقل البر وكمال','167','0','0');
INSERT INTO [t8259] VALUES (168,'فضل ثواب الصبر على جميع أعمال البر، فانظر إلى أهل البلاء مع أهل العافية، ثم ميز ما بينهم،','168','0','0');
INSERT INTO [t8259] VALUES (169,'كف أذاك عنه واصبر لأذاه، فكفى بالموت مفرقا','169','0','0');
INSERT INTO [t8259] VALUES (170,'ما أرى أن لثواب الصبر في القيمة مثلا. أي أخي، عليك بالصبر تدرك به ذخر أهله، واعلم أن','170','0','0');
INSERT INTO [t8259] VALUES (171,'إن كان خيرا رضينا، وإن كان بلاء صبرنا','171','0','0');
INSERT INTO [t8259] VALUES (172,'لئن كان بدء الصبر مرا فعقبه ... لقد يجتنى من غبه الثمر الحلو','172','0','0');
INSERT INTO [t8259] VALUES (173,'ليس أحد فيها يعرف الله غيري؟','173','0','0');
INSERT INTO [t8259] VALUES (174,'إنكم ستجدون أثرة شديدة، فاصبروا حتى تلقوا الله ورسوله، فإني على الحوض قالوا:','174','0','0');
INSERT INTO [t8259] VALUES (175,'القول بالحق والصبر عليه يعدل بأعمال الشهداء','175','0','0');
INSERT INTO [t8259] VALUES (176,'ومن أعلم مني وقد آتيتني التوراة وفيها علم كل شيء؟ فأوحى الله إليه: أعلم منك عبد من عبادي','176','0','0');
INSERT INTO [t8259] VALUES (177,'دلني على أعبد أهل الأرض، قال: فأتى على رجل قد قطع الجذام يديه ورجليه وهو يقول: متعتني','177','0','0');
INSERT INTO [t8259] VALUES (178,'أعاهدك لئن أعطيتني لأشكرن، ولئن ابتليتني لأصبرن، فسأله فصدقه، فلم يكن يسأله عن شيء إلا','178','0','0');
INSERT INTO [t8259] VALUES (179,'تفرغ الصبر إفراغا، فأفرغ الصبر على عبدك حطيط','179','0','0');
INSERT INTO [t8259] VALUES (180,'أفسدت علي دنياي، وأفسد عليك آخرتك. فأرسل إلى امرأة كانت عنده يسألها عنه. قالت: لا أدري،','180','0','0');
INSERT INTO [t8259] VALUES (181,'بليت الحراقف، وطالت الضجعة، والله ما يسرني أن الله نقصني منه قلامة ظفر','181','0','0');
INSERT INTO [t8259] VALUES (182,'ما أحب أن هذا الذي بي بأعتى الديلم على الله تعالى','182','0','0');
INSERT INTO [t8259] VALUES (183,'من وعك ليلة فصبر ورضي بها عن الله، خرج من ذنوبه كهيئة يوم ولدته أمه','183','0','0');
INSERT INTO [t8259] VALUES (184,'لا خير في جسد لا يبلى، ولا خير في مال لا يرزأ منه إن الله إذا أحب عبدا ابتلاه، وإذا','184','0','0');
INSERT INTO [t8259] VALUES (185,'الصبر على محبة الله وإرادته رأس كل بر، أو قال: كل خير','185','0','0');
INSERT INTO [t8259] VALUES (186,'الصبر من الرضا بمنزلة الرأس من الجسد، لا يصلح أحدهما إلا بالآخر','186','0','0');
INSERT INTO [t8259] VALUES (187,'أقعدت من رجليها، فجزعت ابنة لها، فقالت: اللهم إن كان خيرا فزد','187','0','0');
INSERT INTO [t8259] VALUES (188,'ماذا لله علي في هذه القرحة من النعمة؟ فسكت، فقال: حين لم يجعلها على حدقتي، ولا على طرف','188','0','0');
INSERT INTO [t8259] VALUES (189,'دبرت الحراقف والصلب، فما من ضجعة غير ما ترى، والله ما أحب أني نقصت منه قلامة','189','0','0');
INSERT INTO [t8259] VALUES (190,'يكره الأنين، فما سمع له أنين في مرضه حتى مات','190','0','0');
INSERT INTO [t8259] VALUES (191,'أعافى فأشكر، أحب إلي من أن ابتلى فأصبر. وزعم أن أبا العلاء كان يقول: اللهم أي ذاك كان أحب','191','0','0');
INSERT INTO [t8259] VALUES (192,'الصبر على نحوين: أما أحدهما فالصبر عما حرم الله، والصبر لما افترض الله من عبادته، وذلك','192','0','0');
INSERT INTO [t8259] VALUES (193,'الحق كتاب الله {وتواصوا بالصبر} [العصر: ٣] قال: الصبر على طاعة الله','193','0','0');
INSERT INTO [t8259] VALUES (194,'لم يتركوا الذنوب حتى تركتهم، فيا ليتهم إذ ضعفوا عنها لا يتمنون أن تعود لهم القوة عليها','194','0','0');
INSERT INTO [t8259] VALUES (195,'الجزع شر الحالين، يباعد المطلوب، ويورث الحسرة، ويبقي على صاحبه عارا','195','0','0');
INSERT INTO [t8259] VALUES (196,'فأفرغ الصبر على عبدك حطيط','196','0','0');
INSERT INTO [t8259] VALUES (197,'لو قطعتني بالبلاء قطعا ما ازددت لك إلا حبا','197','0','0');
INSERT INTO [t8259] VALUES (198,'صبر لا يتبعه أذى','198','0','0');
INSERT INTO [t8259] VALUES (199,'لك الكرامة والعتبى بأن لا أخالفك أبدا','199','0','0');
INSERT INTO [t8259] VALUES (200,'أمتك مقتتلة من بعدك بقليل من الدهر غير كثير، فقلت: من أين وأنا تارك فيهم كتاب الله؟ فقال:','200','0','0');
