INSERT INTO [t8275] VALUES (1,'يؤخذ الرجل فيحفر له في الأرض فيجعل فيها، ويجاء بالمنشار فيوضع على رأسه، فينشر باثنين، فما','1','0','0');
INSERT INTO [t8275] VALUES (2,'كلكم راع، وكلكم مسؤول عن رعيته؛ الرجل عن أهل بيته، والمرأة عن أهل بيتها، والعبد عن مال','2','0','0');
INSERT INTO [t8275] VALUES (3,'خير الدعاء يوم عرفة، وخير ما جاء به النبيون قبلي لا إله إلا الله','3','0','0');
INSERT INTO [t8275] VALUES (4,'لا يحقرن أحدكم يرى أمرا لله فيه مقال أن يقول فيه، فيبعثه الله عز وجل يوم القيامة، فيقول:','4','0','0');
INSERT INTO [t8275] VALUES (5,'قسم بينكم أخلاقكم كما قسم بينكم أرزاقكم، وإن الله تعالى يعطي الدنيا من يحب ومن لا يحب،','5','0','0');
INSERT INTO [t8275] VALUES (6,'قسم بينكم أخلاقكم كما قسم بينكم أرزاقكم، وإن الله عز وجل يعطي المال من يحب ومن لا يحب ولا','6','0','0');
INSERT INTO [t8275] VALUES (7,'أولى الناس بالله وبرسوله الذي بدأهم بالسلام','7','0','0');
INSERT INTO [t8275] VALUES (8,'من اطلع في بيت قوم قبل أن يأذنوا له فقد حل لهم أن يفقؤوا عينه','8','0','0');
INSERT INTO [t8275] VALUES (9,'أصحاب هذه الصور يعذبون يوم القيامة، ويقال لهم: أحيوا ما خلقتم','9','0','0');
INSERT INTO [t8275] VALUES (10,'أذهب البأس رب الناس، واشف أنت الشافي، لا شافي إلا أنت، اللهم إني أسألك شفاء لا يغادر','10','0','0');
INSERT INTO [t8275] VALUES (11,'إن كنا لنطرد على عهد رسول الله صلى الله عليه وسلم أن نصلي بين الساريتين إلا أن نلجأ','11','0','0');
INSERT INTO [t8275] VALUES (12,'يا أهل الجنة، تخلدون فلا تموتون أبدا، وتصحون فلا تمرضون أبدا، وتشبون فلا تهرمون أبدا،','12','0','0');
INSERT INTO [t8275] VALUES (13,'يكتحل كل ليلة، ويحتجم كل شهر، ويشرب الدواء في كل سمة','13','0','0');
INSERT INTO [t8275] VALUES (14,'هلاك هذه الأمة إذا بغت نساؤها ورجالها، واستخف بالقرآن، وكان النكاح زنى: يطلق الرجل','14','0','0');
INSERT INTO [t8275] VALUES (15,'لك الحمد لما بسطت رزقنا، وأظهرت أمننا، وأحسنت معافاتنا، ومن كل ما سألناك أعطيتنا، فلك','15','0','0');
INSERT INTO [t8275] VALUES (16,'نودي بصوت أيما صوت ... ما أقرب الحي من الميت كأن أهل الغي في غيهم ... قد أخذوا أمنا من','16','0','0');
INSERT INTO [t8275] VALUES (17,'من لقي الله عز وجل بهما غير شاك لم يحجب عن الجنة','17','0','0');
INSERT INTO [t8275] VALUES (18,'فما رآه المسلمون حسنا فهو عند الله حسن، وما رآه المسلمون سيئا فهو عند الله','18','0','0');
INSERT INTO [t8275] VALUES (19,'خلق أحدكم يجمع في بطن أمه خمسة وأربعين يوما، ثم يكون علقة مثل ذلك، ثم يكون مضغة مثل ذلك،','19','0','0');
INSERT INTO [t8275] VALUES (20,'اطردهم لا يجترؤون علينا. قال: وكنت أنا وابن مسعود ورجل من هذيل، ورجلان نسيت أسماءهما،','20','0','0');
INSERT INTO [t8275] VALUES (21,'منزلنا غدا إن شاء الله لخيف بني كنانة بحيث تقاسموا على الكفر','21','0','0');
INSERT INTO [t8275] VALUES (22,'عندك ما يكفيك فلم تطلب ما يطغيك؟ لا بقليل تقنع، ولا بكثير تشبع، إذا أصبحت آمنا في سربك،','22','0','0');
INSERT INTO [t8275] VALUES (23,'الهين اللين السهل القريب','23','0','0');
INSERT INTO [t8275] VALUES (24,'ليس شيء يقربكم من النار ويباعدكم عن الجنة إلا وقد نهيتكم عنه، وإنه ليس من شيء يدنيكم من','24','0','0');
INSERT INTO [t8275] VALUES (25,'رأيت رسول الله صلى الله عليه وسلم والحسن بن علي عليهما السلام يشبهه','25','0','0');
INSERT INTO [t8275] VALUES (26,'إذا أتي أحدكم بالطيب فليصب منه','26','0','0');
INSERT INTO [t8275] VALUES (27,'أحب بيوتكم إلى الله عز وجل بيت فيه يتيم مكرم','27','0','0');
INSERT INTO [t8275] VALUES (28,'لا تجوز شهادة خائن ولا خائنة، ولا ذي غمر على أخيه، ولا موقوفا على حد','28','0','0');
INSERT INTO [t8275] VALUES (29,'ستة لعنتهم؛ فلعنهم الله، وكل نبي مجاب الدعوة: المكذب بقدر الله، والزائد في كتاب الله،','29','0','0');
INSERT INTO [t8275] VALUES (30,'أذن له بالدعاء','30','0','0');
INSERT INTO [t8275] VALUES (31,'لا يكن حبك كلفا، ولا يكن بغضك تلفا. قلت: كيف ذلك يا أمير المؤمنين؟ قال: إذا أحببت كلفت','31','0','0');
INSERT INTO [t8275] VALUES (32,'فلا تجزع وإن أعسرت يوما ... فقد أيسرت في الدهر الطويل فإن العسر يتبعه يسار ... وقول الله','32','0','0');
INSERT INTO [t8275] VALUES (33,'لأقضين بينكم بالحق: أما ما أعطيته فيرده إليك، وأما ابنك فيجلد مائة جلدة، ويغرب سنة، وأما','34','0','0');
INSERT INTO [t8275] VALUES (34,'إذا اطمأن الرجل إلى الرجل، ثم قتله بعدما اطمأن إليه نصب له لواء غدر','35','0','0');
INSERT INTO [t8275] VALUES (35,'الخيل معقود بنواصيها الخير إلى يوم القيامة قيل: وما ذاك؟ قال: الأجر، والمغنم إلى يوم','36','0','0');
INSERT INTO [t8275] VALUES (36,'في الجنة طيرا له سبعون ألف ريشة، فإذا وضع الخوان قدام ولي من الأولياء جاء الطير فسقط','37','0','0');
INSERT INTO [t8275] VALUES (37,'أيما مؤمن سقى مؤمنا شربة على ظمأ سقاه الله عز وجل يوم القيامة من الرحيق المختوم، وأيما','38','0','0');
INSERT INTO [t8275] VALUES (38,'يخرج من النار من قال: لا إله إلا الله وكان في قلبه من الخير ما يزن شعيرة، ثم يخرج من','39','0','0');
INSERT INTO [t8275] VALUES (39,'خلق السماوات وكان الإسلام قديما، وكان فيه زيادة، فأما في آخر الزمان فإنه ينقص، قال: فقال','40','0','0');
INSERT INTO [t8275] VALUES (40,'على كل ذي ميسم من الإنسان صلاة كل يوم، فقال رجل من القوم: هذا من أشد ما أتينا به قال','41','0','0');
INSERT INTO [t8275] VALUES (41,'لا حسد إلا في اثنين: رجل أتاه الله الكتاب فهو يقوم به آناء الليل وآناء النهار، ورجل أتاه','42','0','0');
INSERT INTO [t8275] VALUES (42,'الله أمرني بمداراة الناس كما أمرني بإقامة الفرائض','43','0','0');
INSERT INTO [t8275] VALUES (43,'ما من عبد يقول حين يمسي أو حين يصبح ثلاث مرات: رضيت بالله ربا، وبالإسلام دينا، ومحمد نبيا','44','0','0');
INSERT INTO [t8275] VALUES (44,'من حج بمال حرام فقال: لبيك اللهم لبيك، قال الله عز وجل له: لا لبيك ولا سعديك، وحجك مردود','45','0','0');
INSERT INTO [t8275] VALUES (45,'شر الطعام طعام الوليمة؛ يدعى الشبعان ويترك الغرثان','46','0','0');
INSERT INTO [t8275] VALUES (46,'أيما مؤمن لقي مؤمنا فصافحه وتبسم في وجهه؛ لم يفترقا إلا عن مغفرة','47','0','0');
INSERT INTO [t8275] VALUES (47,'لو تكونون إذا خرجتم من عندي كما تكونون عندي؛ لزاركم الملائكة؛ ولصافحتكم في الطريق، ولو لم','48','0','0');
INSERT INTO [t8275] VALUES (48,'المؤمن في الدنيا كالغريب، لا ينافس في عزها، ولا يجزع من ذلها، للناس حال وله حال، وجهوا','49','0','0');
INSERT INTO [t8275] VALUES (49,'بكائي طويل والدموع غزيرة ... وأنت بعيد والمزار قريب نسيبك من أمسى يناجيك طرفه ... وليس','50','0','0');
