INSERT INTO [t8376] VALUES (1053,'ثم دخلت سنة ثمان وثلاثين ومائة فيها دخل قسطنطين ملك الروم ملطية عنوة فهدم سورها وعفا عمن قدر عليه من مقاتليها.','3701','1003','0');
