INSERT INTO [t8376] VALUES (1055,'وفيها قتل الملبد الخارجي','3701','1053','0');
INSERT INTO [t8376] VALUES (1056,'ثم دخلت سنة تسع وثلاثين ومائة فيها أكمل صالح بن علي بناء ملطية ثم غزا الصائفة على طريق الحديث، فوغل في بلاد الروم، وغزا معه أختاه أم عيسى ولبابة ابنتا علي، وكانتا نذرتا إن زال ملك بني أمية أن يجاهدا في سبيل الله عز وجل.','3702','1003','0');
INSERT INTO [t8376] VALUES (1057,'وفيها توفي','3702','1056','0');
