INSERT INTO [t8376] VALUES (1060,'ثم دخلت سنة إحدى وأربعين ومائة فيها خرجت طائفة يقال لها الراوندية على المنصور.','3703','1003','0');
INSERT INTO [t8376] VALUES (1061,'ثم دخلت سنة ثنتين وأربعين ومائة فيها خلع عيينة بن موسى بن كعب نائب السند الخليفة، فجهز إليه العساكر صحبة عمر بن حفص بن أبي صفرة، وولاه السند والهند، فحاربه عمر بن حفص وقهره على الارض وتسلمها منه.','3705','1003','0');
INSERT INTO [t8376] VALUES (1062,'وفيها توفي','3705','1061','0');
INSERT INTO [t8376] VALUES (1063,'وفيها توفي','3706','1061','0');
INSERT INTO [t8376] VALUES (1064,'وممن توفي فيها','3706','1061','0');
INSERT INTO [t8376] VALUES (1065,'ثم دخلت سنة ثلاث وأربعين ومائة فيها ندب المنصور الناس إلى غزو الديلم، لانهم قتلوا من المسلمين خلقا، وأمر أهل الكوفة والبصرة من كان منهم يقدر على عشرة آلاف فصاعدا فليذهب مع الجيش إلى الديلم، فانتدب خلق كثير وجم غفير لذلك.','3708','1003','0');
INSERT INTO [t8376] VALUES (1066,'ثم دخلت سنة أربع وأربعين ومائة فيها سار محمد بن أبي العباس السفاح عن أمر عمه المنصور إلى بلاد الديلم ومعه الجيوش من','3708','1003','0');
INSERT INTO [t8376] VALUES (1067,'وفيها توفي','3708','1066','0');
INSERT INTO [t8376] VALUES (1068,'ثم دخلت سنة خمس وأربعين ومائة فمما كان فيها من الاحداث مخرج محمد بن عبد الله بن حسن بالمدينة وأخيه إبراهيم بالبصرة، على ما سنبينه إن شاء الله.','3711','1003','0');
INSERT INTO [t8376] VALUES (1069,'فصل مقتل محمد بن عبد الله بن حسن','3715','1068','0');
INSERT INTO [t8376] VALUES (1070,'ذكر خروج إبراهيم بن عبد الله بن حسن بالبصرة','3719','1068','0');
INSERT INTO [t8376] VALUES (1071,'ذكر من توفي فيها','3723','1068','0');
INSERT INTO [t8376] VALUES (1072,'وفيها توفي','3724','1068','0');
INSERT INTO [t8376] VALUES (1073,'ثم دخلت سنة ست وأربعين ومائة فيها تكامل بناء مدينة السلام بغداد، وسكنها المنصور في صفر من هذه السنة، وكان مقيما قبل ذلك بالهاشمية المتاخمة للكوفة، وكان قد شرع في بنائها في السنة الخارجة، وقيل في سنة أربع وأربعين ومائة فالله أعلم.','3725','1003','0');
INSERT INTO [t8376] VALUES (1074,'ما ورد في مدينة بغداد من الآثار وما فيها من الاخبار','3730','1073','0');
INSERT INTO [t8376] VALUES (1075,'فصل محاسن بغداد ومساويها وما روي في ذلك عن الائمة','3731','1073','0');
INSERT INTO [t8376] VALUES (1076,'سنة سبع وأربعين، ولم يزل المنصور يزيد فيها ويتأنق في بنائها حتى كان آخر ما بنى فيها قصر الخلد، فظن أنه يخلد فيها، أو أنها تخلد فلا تخرب، فعند كماله مات.','3732','1003','0');
INSERT INTO [t8376] VALUES (1077,'ثم دخلت سنة سبع وأربعين ومائة فيها أغار اشتر خان الخوارزمي في جيش من الاتراك على ناحية أرمينية فدخلوا تفليس وقتلوا خلقا كثيرا وأسروا كثيرا من المسلمين وأهل الذمة، وممن قتل يومئذ حرب بن عبد الله الراوندي الذي تنسب إليه الحربية (١) ببغداد، وكان مقيما بالموصل في ألفين لمقابلة الخوارج، فأرسله المنصور لمساعدة المسلمين ببلاد أرمينية، وكان في جيش جبريل بن يحيى، فهزم جبريل وقتل حرب رحمه الله.','3732','1003','0');
INSERT INTO [t8376] VALUES (1078,'وفيها توفي','3732','1077','0');
INSERT INTO [t8376] VALUES (1079,'وفيها توفي','3733','1077','0');
INSERT INTO [t8376] VALUES (1080,'ثم دخلت سنة ثمان وأربعين ومائة فيها بعث المنصور حميد بن قحطبة لغزو الترك الذين عاثوا في السنة الماضية ببلاد تفليس، فلم يجد منهم أحدا فإنهم انشمروا إلى بلادهم.','3734','1003','0');
INSERT INTO [t8376] VALUES (1081,'ثم دخلت سنة تسع وأربعين ومائة فيها فرغ من بناء سور بغداد وخندقها.','3734','1003','0');
INSERT INTO [t8376] VALUES (1082,'وفيها توفي','3734','1081','0');
