INSERT INTO [t8376] VALUES (1085,'وفيها توفي الامام أبو حنيفة.','3735','1083','0');
INSERT INTO [t8376] VALUES (1086,'ذكر ترجمته','3736','1083','0');
INSERT INTO [t8376] VALUES (1087,'ثم دخلت سنة إحدى وخمسين ومائة فيها عزل المنصور عمر بن حفص عن السند وولى عليها هشام بن عمرو التغلبي، وكان سبب عزله عنها أن محمد بن عبد الله بن حسن لما ظهر بعث ابنه عبد الله الملقب بالاشتر ومعه جماعة بهدية وخيول عتاق إلى عمر بن حفص هذا إلى السند فقبلها، فدعوه إلى؟ وة أبيه محمد بن عبد الله بن حسن في السر فأجابهم إلى ذلك ولبسوا البياض.','3737','1003','0');
INSERT INTO [t8376] VALUES (1088,'ثم دخلت سنة ثنتين وخمسين ومائة فيها عزل المنصور عن إمرة مصر يزيد بن حاتم وولاها محمد بن سعيد، وبعث إلى نائب إفريقية وكان قد بلغه أنه عصى وخالف، فلما جئ به أمر بضرب عنقه (١) .','3738','1003','0');
INSERT INTO [t8376] VALUES (1089,'ثم دخلت سنة ثلاث وخمسون ومائة وفيها غضب المنصور على كاتبه أبي أيوب المورياني (٢) وسجنه وسجن أخاه خالدا وبني أخيه','3738','1003','0');
INSERT INTO [t8376] VALUES (1090,'وفيها توفي','3738','1089','0');
INSERT INTO [t8376] VALUES (1091,'بناء الرصافة','3738','1089','0');
INSERT INTO [t8376] VALUES (1092,'ثم دخلت سنة أبع وخمسين ومائة فيها دخل المنصور بلاد الشام وزار بيت المقدس وجهز يزيد بن حاتم في خمسين ألفا وولاه بلاد إفريقية، وأمره بقتال الخوارج، وأنفق على هذا الجيش نحوا من ثلاث وستين ألف (٤) درهم، وغزا الصائفة زفر بن عاصم الهلالي.','3740','1003','0');
INSERT INTO [t8376] VALUES (1093,'وفيها توفي','3740','1092','0');
INSERT INTO [t8376] VALUES (1094,'وفيها توفي','3741','1092','0');
INSERT INTO [t8376] VALUES (1095,'ثم دخلت سنة خمس وخمسين ومائة فيها دخل يزيد بن حاتم بلاد إفريقية فافتتحها عودا على بدء، وقتل من كان فيها ممن تغلب عليها من الخوارج، وقتل أمراءهم وأسر كبراءهم وأذل أشرافهم واستبدل أهل تلك البلاد بالخوف أمنا وسلامة، وبالاهانة كرامة، وكان من جملة من قتل من أمرائهم أبو حاتم وأبو عباد (١) الخارجيان.','3742','1003','0');
INSERT INTO [t8376] VALUES (1096,'وفيها توفي','3743','1095','0');
INSERT INTO [t8376] VALUES (1097,'بناء الرافقة','3743','1095','0');
INSERT INTO [t8376] VALUES (1098,'ثم دخلت سنة ست وخمسين ومائة فيها ظفر الهيثم بن معاوية نائب المنصور على البصرة، بعمرو بن شداد الذي كان عاملا لابراهيم بن محمد (٥) على فارس، فقيل: أمر فقطعت يداه ورجلاه وضربت عنقه ثم صلب.','3744','1003','0');
INSERT INTO [t8376] VALUES (1099,'وفيها قتل حماد عجرد على الزندقة','3744','1098','0');
INSERT INTO [t8376] VALUES (1100,'ثم دخلت سنة سبع وخمسين ومائة فيها بنى المنصور قصره المسمى بالخلد في بغداد، تفاؤلا بالتخليد في الدنيا، فعند كماله مات وخرب القصر من بعده، وكان المستحث في عمارته أبان بن صدقة، والربيع مولى المنصور وهو حاجبه.','3745','1003','0');
INSERT INTO [t8376] VALUES (1101,'وفيها توفي','3745','1100','0');
INSERT INTO [t8376] VALUES (1102,'شئ من ترجمة الاوزاعي','3745','1100','0');
INSERT INTO [t8376] VALUES (1103,'ثم دخلت سنة ثمان وخمسين ومائة فيها تكامل بناء قصر المنصور المسمى بالخلد وسكنه أياما يسيرة ثم مات وتركه، وفيها مات طاغية الروم.','3750','1003','0');
INSERT INTO [t8376] VALUES (1104,'ترجمة المنصور','3751','1103','0');
INSERT INTO [t8376] VALUES (1105,'خلافة المهدي بن منصور','3759','1103','0');
INSERT INTO [t8376] VALUES (1106,'ثم دخلت سنة تسع وخمسين ومائة استهلت هذه السنة وخليفة الناس أبو عبد الله محمد بن المنصور المهدي، فبعث في أولها العباس بن محمد إلى بلاد الروم في جيش كثيف، وركب معهم مشيعا لهم، فساروا إليها فافتتحوا مدينة عظيمة للروم، وغنمو غنائم كثيرة ورجعوا سالمين لم يفقد منهم أحد.','3760','1003','0');
INSERT INTO [t8376] VALUES (1107,'وفيها توفي','3760','1106','0');
INSERT INTO [t8376] VALUES (1108,'وفيها توفي','3761','1106','0');
INSERT INTO [t8376] VALUES (1109,'ثم دخلت سنة ستين ومائة','3761','1003','0');
INSERT INTO [t8376] VALUES (1110,'فيها خرج رجل بخراسان على المهدي','3761','1109','0');
INSERT INTO [t8376] VALUES (1111,'البيعة لموسى الهادي','3761','1109','0');
INSERT INTO [t8376] VALUES (1112,'وفيها توفي','3763','1109','0');
INSERT INTO [t8376] VALUES (1113,'ثم دخلت سنة إحدى وستين ومائة فيها غزا الصائفة ثمامة بن الوليد فنزل دابق، وجاشت الروم عليه فلم يتمكن المسلمون من الدخول إليها بسبب ذلك.','3764','1003','0');
INSERT INTO [t8376] VALUES (1114,'وفيها توفي','3764','1113','0');
INSERT INTO [t8376] VALUES (1115,'وممن توفي فيها','3765','1113','0');
INSERT INTO [t8376] VALUES (1116,'ثم دخلت سنة ثنتين وستين ومائة فيها خرج عبد السلام بن هاشم اليشكري بأرض قنسرين واتبعه خلق كثير، وقويت شوكته فقاتله جماعة من الامراء فلم يقدروا عليه، وجهز إليه المهدي جيوشا وأنفق فيهم أموالا فهزمهم مرات ثم آل الامر به أن قتل بعد ذلك.','3766','1003','0');
INSERT INTO [t8376] VALUES (1117,'وفيها توفي','3766','1116','0');
INSERT INTO [t8376] VALUES (1118,'وفيها توفي','3776','1116','0');
INSERT INTO [t8376] VALUES (1119,'ثم دخلت سنة ثلاث وستين ومائة فيها حصر المقنع الزنديق الذي كان قد نبغ بخراسان وقال بالتناسخ، واتبعه على جهالته','3776','1003','0');
INSERT INTO [t8376] VALUES (1120,'وفيها توفي','3778','1119','0');
INSERT INTO [t8376] VALUES (1121,'ثم دخلت سنة أربع وستين ومائة فيها غزا عبد الكبير بن عبد الحميد بن عبد الرحمن بن زيد بن الخطاب بلاد الروم، فأقبل إليه ميخائيل البطريق في نحو من تسعين ألفا، فيهم طازاذ الارمني البطريق ففشل عنه عبد الكبير ومنع المسلمين من القتال وانصرف راجعا - فأراد المهدي ضرب عنقه فكلم فيه فحبسه في المطبق.','3778','1003','0');
INSERT INTO [t8376] VALUES (1122,'ثم دخلت سنة خمس وستين ومائة فيها جهز المهدي ولده الرشيد لغزو الصائفة، وأنفذ معه من الجيوش خمسة وتسعين ألفا وسبعمائة وثلاثة وتسعين رجلا، وكان معه من النفقة مائة ألف دينار، وأربعة (٢) وتسعون ألف دينار، وأربعمائة وخمسون دينارا، ومن الفضة (٣) إحدى وعشرون ألف ألف وأربعمائة ألف، وأربعة عشر ألفا وثمانمائة درهم.','3778','1003','0');
INSERT INTO [t8376] VALUES (1123,'وفيها توفي','3779','1122','0');
INSERT INTO [t8376] VALUES (1124,'ثم دخلت سنة ست وستين ومائة في المحرم منها قدم الرشيد من بلاد الروم فدخل بغداد في أبهة عظيمة ومعه الروم يحملون الجزية من الذهب وغيره.','3779','1003','0');
INSERT INTO [t8376] VALUES (1125,'وفيها توفي','3781','1124','0');
INSERT INTO [t8376] VALUES (1126,'ثم دخلت سنة سبع وستين ومائة فيها وجه المهدي ابنه موسى الهادي إلى جرجان في جيش كثيف لم ير مثله، وجعل على رسائله أبان بن صدقة.','3781','1003','0');
INSERT INTO [t8376] VALUES (1127,'وممن توفي فيها','3781','1126','0');
INSERT INTO [t8376] VALUES (1128,'وفيها توفي','3782','1126','0');
INSERT INTO [t8376] VALUES (1129,'ثم دخلت سنة ثمان وستين ومائة فيها في رمضان منها نقضت الروم ما بينهم وبين المسلمين من الصلح الذي عقده هارون الرشيد عن أمر أبيه المهدي، ولم يستمروا على الصلح إلا ثنتين وثلاثين شهرا، فبعث نائب الجزيرة خيلا إلى','3782','1003','0');
INSERT INTO [t8376] VALUES (1130,'وفيها توفي','3783','1129','0');
INSERT INTO [t8376] VALUES (1131,'ثم دخلت سنة تسع وستين ومائة فيها في المحرم منها توفي المهدي بن المنصور بمكان يقال له ما سبذان، بالحمى، وقيل مسموما وقيل عضه فرس فمات.','3783','1003','0');
INSERT INTO [t8376] VALUES (1132,'وفيها توفي','3789','1131','0');
INSERT INTO [t8376] VALUES (1133,'خلافة موسى الهادي بن المهدي','3789','1131','0');
INSERT INTO [t8376] VALUES (1134,'ثم دخلت سنة سبعين ومائة من الهجرة النبوية وفيها عزم الهادي على خلع أخيه هارون الرشيد من الخلافة وولاية العهد لابنه جعفر بن الهادي فانقاد هارون لذلك ولم يظهر منازعة بل أجاب، واستدعى الهادي جماعة من الامراء فأجابوه إلى ذلك، وأبت ذلك أمهما الخيزران، وكانت تميل إلى ابنها هارون أكثر من موسى، وكان الهادي قد منعها من التصرف في شئ من المملكة لذلك، بعد ما كانت قد استحوذت عليه في أول ولايته، وانقلبت الدول','3790','1003','0');
INSERT INTO [t8376] VALUES (1135,'وفيها توفي','3790','1134','0');
INSERT INTO [t8376] VALUES (1136,'خلافة هارون الرشيد بن المهدي','3793','1134','0');
INSERT INTO [t8376] VALUES (1137,'ذكر من توفي فيها','3794','1134','0');
INSERT INTO [t8376] VALUES (1138,'ثم دخلت سنة إحدى وسبعين ومائة فيها أضاف الرشيد الخاتم إلى يحيى بن خالد مع الوزارة.','3795','1003','0');
INSERT INTO [t8376] VALUES (1139,'ثم دخلت سنة ثنتين وسبعين ومائة فيها وضع الرشيد عن أهل العراق العشر الذي كان يؤخذ منهم بعد النصف.','3795','1003','0');
INSERT INTO [t8376] VALUES (1140,'ثم دخلت سنة ثلاث وسبعين ومائة فيها توفي بالبصرة محمد بن سليمان فأمر الرشيد بالاحتياط على حواصله التي تصلح للخلفاء،','3795','1003','0');
INSERT INTO [t8376] VALUES (1141,'وفيها قتل الرشيد أبا هريرة محمد بن فروخ','3795','1003','0');
INSERT INTO [t8376] VALUES (1142,'وفيها توفي','3796','1141','0');
INSERT INTO [t8376] VALUES (1143,'وفيها توفي','3797','1141','0');
INSERT INTO [t8376] VALUES (1144,'ثم دخلت سنة أربع وسبعين ومائة من الهجرة فيها وقعت عصبية بالشام وتخبيط من أهلها.','3798','1003','0');
INSERT INTO [t8376] VALUES (1145,'ثم دخلت سنة خمس وسبعين ومائة','3799','1003','0');
INSERT INTO [t8376] VALUES (1146,'فيها أخذ الرشيد بولاية العهد من بعده لولده محمد بن زبيدة وسماه الامين','3799','1145','0');
