INSERT INTO [t8376] VALUES (1148,'ثم دخلت سنة ست وسبعين ومائة فيها كان ظهور يحيى بن عبد الله بن حسن بن حسن بن علي بن أبي طالب ببلاد الديلم، واتبعه خلق كثير وجم غفير، وقويت شوكته، وارتحل إليه الناس من الكور والامصار، فانزعج لذلك الرشيد وقلق من أمره، فندب إليه الفضل بن يحيى بن خالد بن برمك في خمسين ألفا، وولاه كور الجبل والري وجرجان وطبرستان وقومس وغير ذلك.','3800','1003','0');
INSERT INTO [t8376] VALUES (1149,'وفيها توفي','3800','1148','0');
INSERT INTO [t8376] VALUES (1150,'وفيها توفي','3803','1148','0');
INSERT INTO [t8376] VALUES (1151,'وفيها توفي','3804','1148','0');
INSERT INTO [t8376] VALUES (1152,'ثم دخلت سنة سبع وسبعين ومائة فيها عزل الرشيد جعفر البرمكي عن مصر وولى عليها إسحاق بن سليمان، وعزل حمزة بن','3805','1003','0');
INSERT INTO [t8376] VALUES (1153,'وفيها توفي','3805','1152','0');
INSERT INTO [t8376] VALUES (1154,'ثم دخلت سنة ثمان وسبعين ومائة فيها وثبت طائفة من الحوفية (١) من قيس وقضاعة على عامل مصر إسحاق بن سليمان فقاتلوه وجرت فتنة عظيمة فبعث الرشيد هرثمة بن أعين نائب فلسطين في خلق من الامراء مددا لاسحاق، فقاتلوهم حتى أذعنوا بالطاعة وأدوا ما عليهم من الخراج والوظائف، واستمر هرثمة نائبا على مصر نحوا من شهر عوضا عن إسحاق بن سليمان، ثم عزله الرشيد عنها وولى عليها عبد الملك بن صالح.','3806','1003','0');
INSERT INTO [t8376] VALUES (1155,'وفيها توفي','3807','1154','0');
INSERT INTO [t8376] VALUES (1156,'ثم دخلت سنة تسع وسبعين ومائة فيها كان قدوم الفضل بن يحيى من خراسان وقد استخلف عليها عمر بن جميل (١) ، فولى الرشيد عليها منصور بن يزيد بن منصور الحميري.','3808','1003','0');
INSERT INTO [t8376] VALUES (1157,'وفيها توفي','3808','1156','0');
INSERT INTO [t8376] VALUES (1158,'ثم دخلت سنة ثمانين ومائة فيها هاجت الفتنة بالشام بين النزارية واليمنية، فانزعج الرشيد لذلك فندب جعفر البرمكي إلى الشام في جماعة من الامراء والجنود، فدخل الشام فانقاد الناس له ولم يدع جعفر بالشام فرسا ولا سيفا ولا رمحا إلا استلبه من الناس، وأطفأ الله به نار تلك الفتنة.','3810','1003','0');
INSERT INTO [t8376] VALUES (1159,'فيها كانت وفاة','3810','1158','0');
INSERT INTO [t8376] VALUES (1160,'وفيها توفي','3811','1158','0');
INSERT INTO [t8376] VALUES (1161,'ثم دخلت سنة إحدى وثمانين ومائة فيها غزا الرشيد بلاد الروم فافتتح حصنا يقال له الصفصاف، فقال في ذلك مروان بن أبي حفصة: إن أمير المؤمنين المنصفا (٢) * قد ترك الصفصاف قاعا صفصفا وفيها غزا عبد الملك بن صالح بلاد الروم فبلغ أنقرة وافتتح مطمورة.','3812','1003','0');
INSERT INTO [t8376] VALUES (1162,'وفيها توفي','3812','1161','0');
INSERT INTO [t8376] VALUES (1163,'ثم دخلت سنة ثنتين وثمانين ومائة فيها أخذ الرشيد لولده عبد الله المأمون ولاية العهد من بعد أخيه محمد الامين بن زبيدة، وذلك','3815','1003','0');
INSERT INTO [t8376] VALUES (1164,'وفيها توفي','3815','1163','0');
INSERT INTO [t8376] VALUES (1165,'وفيها توفي','3818','1163','0');
INSERT INTO [t8376] VALUES (1166,'ثم دخلت سنة ثلاث وثمانين ومائة فيها خرجت الخزر على الناس من ثلمة أرمينية فعاثوا في تلك البلاد فسادا، وسبوا من المسلمين وأهل الذمة نحوا من مائة ألف، وقتلوا بشرا كثيرا وانهزم نائب أرمينية سعيد بن مسلم (١) ،','3819','1003','0');
INSERT INTO [t8376] VALUES (1167,'وفيها توفي','3819','1166','0');
INSERT INTO [t8376] VALUES (1168,'ثم دخلت سنة أربع وثمانين ومائة فيها رجع الرشيد من الرقة إلى بغداد فأخذ الناس بأداء بقايا الخراج الذي عليهم، وولى رجلا (٥) يضرب الناس على ذلك ويحبسهم، وولى على أطراف البلاد.','3820','1003','0');
