INSERT INTO [t8376] VALUES (1173,'ثم دخلت سنة ست وثمانين ومائة فيها خرج علي بن عيسى بن ماهان من مرو لحرب أبي الخصيب إلى نسا فقاتله بها، وسبى نساءه وذراريه.','3824','1003','0');
