INSERT INTO [t8376] VALUES (1175,'ثم دخلت سنة سبع وثمانين ومائة فيها كان مهلك البرامكة على يدي الرشيد، قتل جعفر بن يحيى بن خالد البرمكي، ودمر ديارهم واندرست آثارهم، وذهب صغارهم وكبارهم.','3826','1003','0');
