INSERT INTO [t8376] VALUES (1178,'ثم دخلت سنة ثمان وثمانين ومائة فيها غزا إبراهيم بن إسرائيل (٢) الصائفة فدخل بلاد الروم من درب الصفصاف فخرج النقفور','3837','1003','0');
