INSERT INTO [t8376] VALUES (1181,'ثم دخلت سنة تسع وثمانين ومائة فيها رجع الرشيد من الحج وسار إلى الري فولى وعزل.','3839','1003','0');
INSERT INTO [t8376] VALUES (1182,'ذكر من توفي فيها','3840','1181','0');
INSERT INTO [t8376] VALUES (1183,'ثم دخلت سنة تسعين ومائة من الهجرة فيها خلع رافع بن ليث بن نصر بن سيار نائب سمرقند الطاعة ودعا إلى نفسه (٢) ، وتابعه أهل بلده وطائفة كثيرة من تلك الناحية، واستفحل أمره، فسار إليه نائب خراسان علي بن عيسى فهزمه','3841','1003','0');
INSERT INTO [t8376] VALUES (1184,'وفيها توفي','3841','1183','0');
INSERT INTO [t8376] VALUES (1185,'وفيها توفي','3843','1183','0');
INSERT INTO [t8376] VALUES (1186,'ثم دخلت سنة إحدى وتسعين ومائة فيها خرج رجل بسواد العراق يقال له ثروان بن سيف، وجعل يتنقل فيها من بلد إلى بلد، فوجه إليه الرشيد طوق بن مالك فهزمه وجرح ثروان وقتل عامة أصحابه، وكتب بالفتح إلى الرشيد.','3845','1003','0');
INSERT INTO [t8376] VALUES (1187,'وفيها توفي','3845','1186','0');
INSERT INTO [t8376] VALUES (1188,'ثم دخلت سنة ثنتين وتسعين ومائة فيها دخل هرثمة بن أعين إلى خراسان نائبا عليها، وقبض على علي بن عيسى فأخذ أمواله وحواصله وأركبه على بعير وجهه لذنبه ونادى عليه ببلاد خراسان، وكتب إلى الرشيد بذلك فشكره على ذلك، ثم أرسله إلى الرشيد بعد ذلك فحبس بداره ببغداد.','3846','1003','0');
