INSERT INTO [t8376] VALUES (1201,'اختلاف الامين والمأمون','3864','1194','0');
INSERT INTO [t8376] VALUES (1202,'ثم دخلت سنة أربع وتسعين ومائة فيها خلع أهل حمص نائبهم فعزله عنهم الامين وولى عليهم عبد الله بن سعيد الحرشي فقتل طائفة من وجوه أهلها وحرق نواحيها، فسألوه الامان فأمنهم ثم هاجوا فضرب أعناق كثير منهم أيضا.','3865','1003','0');
