INSERT INTO [t8376] VALUES (1204,'وفيها توفي','3866','1202','0');
INSERT INTO [t8376] VALUES (1205,'ثم دخلت سنة خمس وتسعين ومائة فيها في صفر منها أمر الامين الناس أن لا يتعاملوا بالدراهم والدنانير التي عليها اسم أخيه المأمون ونهى أن يدعى له على المنابر، وأن يدعى له ولولده من بعده: وفيها تسمى المأمون بإمام المؤمنين.','3867','1003','0');
