INSERT INTO [t8376] VALUES (1214,'خلافة عبد الله المأمون بن الرشيد هارون','3887','1209','0');
INSERT INTO [t8376] VALUES (1215,'ثم دخلت سنة تسع وتسعين ومائة فيها قدم الحسن بن سهل بغداد نائبا عليها من جهة المأمون، ووجه نوابه إلى بقية أعماله، وتوجه طاهر إلى نيابة الجزيرة والشام ومصر وبلاد المغرب.','3888','1003','0');
INSERT INTO [t8376] VALUES (1216,'ثم دخلت سنة مائتين من الهجرة في أول يوم منها جلس حسين بن حسن الافطس على طنفسة مثلثة خلف المقام وأمر بتجريد الكعبة مما عليها من كساوي بني العباس، وقال: نطهرها من كساويهم.','3889','1003','0');
INSERT INTO [t8376] VALUES (1217,'وفيها توفي','3889','1216','0');
INSERT INTO [t8376] VALUES (1218,'وفيها توفي','3890','1216','0');
INSERT INTO [t8376] VALUES (1219,'وفيها قتل المأمون يحيى بن عامر بن إسماعيل','3890','1216','0');
INSERT INTO [t8376] VALUES (1220,'ثم دخلت سنة إحدى ومائتين فيها راود أهل بغداد منصور بن المهدي على الخلافة فامتنع من ذلك، فراودوه على أن يكون نائبا للمأمون يدعو له في الخطبة فأجابهم إلى ذلك، وقد أخرجوا علي بن هشام نائب الحسن بن سهل من بين أظهرهم بعد أن جرت حروب كثيرة بسبب ذلك.','3891','1003','0');
INSERT INTO [t8376] VALUES (1221,'ثم دخلت سنة ثنتين ومائتين في أول يوم منها بويع لابراهيم بن المهدي بالخلافة ببغداد وخلع المأمون، فلما كان يوم الجمعة خامس المحرم صعد إبراهيم بن المهدي المنبر فبايعه الناس ولقب بالمبارك، وغلب على الكوفة وأرض السواد، وطلب منه الجند أرزاقهم فماطلهم ثم أعطاهم مائتي درهم لكل واحد، وكتب لهم بتعويض من أرض السواد، فخرجوا لا يمرون بشئ إلا انتهبوه، وأخذوا حاصل الفلاح والسلطان، واستناب على الجانب الشرقي العباس بن موسى الهادي، وعلى الجانب الغربي إسحاق بن موسى الهادي.','3892','1003','0');
INSERT INTO [t8376] VALUES (1222,'وفيها توفي','3892','1221','0');
INSERT INTO [t8376] VALUES (1223,'بيعة أهل بغداد لابراهيم بن المهدي','3892','1221','0');
INSERT INTO [t8376] VALUES (1224,'ثم دخلت سنة ثلاث ومائتين فيها وصل المأمون العراق ومر بطوس فنزل بها وأقام عند قبر أبيه أياما من شهر صفر، فلما كان في آخر الشهر أكل علي بن موسى الرضى عنبا فمات فجأة فصلى عليه المأمون ودفنه إلى جانب أبيه الرشيد، وأسف عليه أسفا كثيرا فيما ظهر، وكتب إلى الحسن بن سهل يعزيه فيه ويخبره بما حصل له من الحزن عليه، وكتب إلى بني العباس يقول لهم: إنكم إنما نقمتم علي بسبب توليتي العهد من بعدي لعلي بن موسى الرضى، وها هو قد مات فارجعوا إلى السمع والطاعة.','3894','1003','0');
INSERT INTO [t8376] VALUES (1225,'وفيها توفي','3894','1224','0');
INSERT INTO [t8376] VALUES (1226,'ثم دخلت سنة أربع ومائتين','3895','1003','0');
INSERT INTO [t8376] VALUES (1227,'فيها كان قدوم المأمون أرض العراق','3895','1226','0');
INSERT INTO [t8376] VALUES (1228,'وفيها توفي','3896','1226','0');
INSERT INTO [t8376] VALUES (1229,'محمد بن إدريس الشافعي','3896','1226','0');
INSERT INTO [t8376] VALUES (1230,'وفيها توفي','3899','1226','0');
INSERT INTO [t8376] VALUES (1231,'ثم دخلت سنة خمس ومائتين فيها ولى المأمون طاهر بن الحسين بن مصعب نيابة بغداد والعراق وخراسان إلى أقصى عمل المشرق، ورضي عنه ورفع منزلته جدا، وذلك لاجل مرض الحسن بن سهل بالسواد.','3900','1003','0');
INSERT INTO [t8376] VALUES (1232,'وفيها توفي','3900','1231','0');
INSERT INTO [t8376] VALUES (1233,'ثم دخلت سنة ست ومائتين فيها ولى المأمون داود بن ماسجور بلاد البصرة وكور دجلة واليمامة والبحرين، وأمره بمحاربة','3904','1003','0');
INSERT INTO [t8376] VALUES (1234,'ثم دخلت سنة سبع ومائتين فيها خرج عبد الرحمن بن أحمد بن عبد الله بن محمد بن عمر بن علي بن أبي طالب ببلاد عك في','3904','1003','0');
INSERT INTO [t8376] VALUES (1235,'وفيها توفي','3904','1234','0');
INSERT INTO [t8376] VALUES (1236,'وفيها توفي','3906','1234','0');
INSERT INTO [t8376] VALUES (1237,'ثم دخلت سنة ثمان ومائتين فيها ذهب الحسن بن الحسين بن مصعب أخو طاهر فارا من خراسان إلى كرمان فعصي بها، فسار إليه أحمد بن أبي خالد فحاصره حتى نزل قهرا، فذهب به إلى المأمون فعفا عنه فاستحسن ذلك منه.','3907','1003','0');
INSERT INTO [t8376] VALUES (1238,'وفيها توفي','3907','1237','0');
INSERT INTO [t8376] VALUES (1239,'وفاة السيدة نفيسة','3908','1237','0');
INSERT INTO [t8376] VALUES (1240,'ثم دخلت سنة تسع ومائتين فيها حصر عبد الله بن طاهر نصر بن شبث بعد ما حاربه خمس سنين وضيق عليه جدا حتى ألجأه إلى أن طلب منه الامان، فكتب ابن طاهر إلى المأمون يعلمه بذلك، فأرسل إليه أن يكتب له أمانا عن أمير المؤمنين.','3909','1003','0');
INSERT INTO [t8376] VALUES (1241,'وفيها توفي','3909','1240','0');
INSERT INTO [t8376] VALUES (1242,'ثم دخلت سنة عشر ومائتين في صفر منها دخل نصر بن شبث بغداد، بعثه عبد الله بن طاهر فدخلها ولم يتلقاه أحد من الجند بل دخلها وحده، فأنزل في المدينة أبي جعفر ثم حول إلى موضع آخر.','3910','1003','0');
INSERT INTO [t8376] VALUES (1243,'وفيها توفي','3911','1242','0');
INSERT INTO [t8376] VALUES (1244,'ثم دخلت سنة إحدى عشرة ومائتين فيها توفي أبو الجواب (١) .','3912','1003','0');
INSERT INTO [t8376] VALUES (1245,'أبو العتاهية الشاعر المشهور','3912','1244','0');
INSERT INTO [t8376] VALUES (1246,'ثم دخلت سنة ثلاث عشرة ومائتين فيها ثار رجلان عبد السلام وابن جليس فخلعا المأمون واستحوذا على الديار المصرية، وتابعهما طائفة من القيسية واليمانية، فولى المأمون أخا أبا إسحاق نيابة الشام، وولى ابنه العباس نيابة الجزيرة والثغور والعواصم، وأطلق لكل منهما ولعبد الله بن طاهر ألف ألف دينار وخمسمائة ألف دينار (٥) .','3913','1003','0');
INSERT INTO [t8376] VALUES (1247,'وفيها توفي','3913','1246','0');
INSERT INTO [t8376] VALUES (1248,'وفيها توفي','3914','1246','0');
INSERT INTO [t8376] VALUES (1249,'العكوك الشاعر','3914','1246','0');
INSERT INTO [t8376] VALUES (1250,'ثم دخلت سنة أربع عشرة ومائتين في يوم السبت لخمس بقين من ربيع الاول منها التقى محمد بن حميد وبابك الخرمي لعنه الله، فقتل الخرمي خلقا كثيرا من جيشه، وقتله أيضا وانهزم بقية أصحاب ابن حميد، فبعث المأمون إسحاق بن إبراهيم ويحيى بن أكثم إلى عبد الله بن طاهر يخيرانه بين خراسان، ونيابة الجبال وأذربيجان وأرمينية ومحاربة بابك، فاختار المقام بخراسان لكثرة احتياجها إلى الضبط، وللخوف من ظهور الخوارج.','3915','1003','0');
INSERT INTO [t8376] VALUES (1251,'وفيها توفي','3915','1250','0');
INSERT INTO [t8376] VALUES (1252,'ثم دخلت سنة خمس عشرة ومائتين','3917','1003','0');
INSERT INTO [t8376] VALUES (1253,'ثم دخلت سنة ست عشرة ومائتين فيها عدا ملك الروم وهو توفيل بن ميخائيل على جماعة من المسلمين فقتلهم في أرض طرسوس نحوا من ألف وستمائة إنسان، وكتب إلى المأمون فبدأ بنفسه، فلما قرأ المأمون كتابه نهض من فوره إلى بلاد الروم عودا على بدء وصحبته أخوه أبو إسحاق بن الرشيد نائب الشام ومصر، فافتتح بلدانا كثيرة','3917','1003','0');
INSERT INTO [t8376] VALUES (1254,'وفيها توفي','3917','1253','0');
INSERT INTO [t8376] VALUES (1255,'ثم دخلت سنة سبع عشرة ومائتين في المحرم منها دخل المأمون مصر وظفر بعبدوس الفهري فأمر فضربت عنقه، ثم كر راجعا إلى الشام.','3919','1003','0');
INSERT INTO [t8376] VALUES (1256,'وفيها توفي','3919','1255','0');
INSERT INTO [t8376] VALUES (1257,'ثم دخلت سنة ثمان عشرة ومائتين في أول يوم من جمادى الاولى وجه المأمون ابنه العباس إلى بلاد الروم لبناء الطوانة وتجديد عمارتها.','3920','1003','0');
INSERT INTO [t8376] VALUES (1258,'ذكر أول المحنة والفتنة','3920','1257','0');
INSERT INTO [t8376] VALUES (1259,'فصل','3922','1257','0');
INSERT INTO [t8376] VALUES (1260,'خلافة المعتصم بالله أبي إسحاق بن هارون','3929','1257','0');
INSERT INTO [t8376] VALUES (1261,'وفيها توفي','3930','1257','0');
INSERT INTO [t8376] VALUES (1262,'ثم دخلت سنة تسع عشرة ومائتين فيها ظهر محمد بن القاسم بن عمر بن علي بن الحسين بن علي بن أبي طالب بالطالقان من خراسان يدعو إلى الرضى من آل محمد، واجتمع عليه خلق كثير وقاتله قواد عبد الله بن طاهر مرات متعددة، ثم ظهروا عليه وهرب فأخذ ثم بعث به إلى عبد الله بن طاهر فبعث به إلى المعتصم فدخل عليه للنصف من ربيع الآخر (١) فأمر به فحبس في مكان ضيق طوله ثلاثة أذرع في ذراعين، فمكث فيه ثلاثا، ثم حول لاوسع منه وأجرى عليه رزق ومن يخدمه، فلم يزل محبوسا هناك إلى ليلة عيد الفطر فاشتغل الناس بالعيد فدلي له حبل من كوة كان يأتيه الضوء منها، فذهب فلم يدر كيف ذهب وإلى أين صار من الارض (٢) .','3931','1003','0');
INSERT INTO [t8376] VALUES (1263,'ثم دخلت سنة عشرين ومائتين من الهجرة في يوم عاشوراء منها دخل عجيف في السفن إلى بغداد ومعه من الزط سبعة وعشرون ألفا قد جاؤوا بالامان إلى الخليفة، فأنزلوا في الجانب الشرقي ثم نفاهم إلى عين زربة (٤) ، فأغارت الروم عليهم','3931','1003','0');
INSERT INTO [t8376] VALUES (1264,'وفيها توفي','3931','1263','0');
INSERT INTO [t8376] VALUES (1265,'ثم دخلت سنة إحدى وعشرين ومائتين فيها كانت وقعة هائلة بين بغا الكبير وبابك فهزم بابك بغا وقتل خلقا من أصحابه.','3932','1003','0');
INSERT INTO [t8376] VALUES (1266,'وفيها توفي','3932','1265','0');
INSERT INTO [t8376] VALUES (1267,'ثم دخلت سنة ثنتين وعشرين ومائتين فيها جهز المعتصم جيشا كثيرا مددا للافشين على محاربة بابك وبعث إليه ثلاثين ألف ألف درهم نفقة للجند، فاقتتلوا قتالا عظيما، وافتتح الافشين البذ مدينة بابك واستباح ما فيها، وذلك يوم الجمعة لعشر بقين من رمضان.','3933','1003','0');
INSERT INTO [t8376] VALUES (1268,'وفيها توفي','3933','1267','0');
INSERT INTO [t8376] VALUES (1269,'ثم دخلت سنة ثلاث وعشرين ومائتين في يوم الخميس ثالث صفر منها دخل الافشين وصحبته بابك على المعتصم سامرا، ومعه أيضا أخو بابك في تجمل عظيم، وقد أمر المعتصم ابنه هارون الواثق أن يتلقى الافشين وكانت أخباره تفد إلى المعتصم في كل يوم من شدة اعتناء المعتصم بأمر بابك، وقد ركب المعتصم قبل وصول بابك بيومين على البريد حتى دخل إلى بابك وهو لا يعرفه، فنظر إليه ثم رجع، فلما كان يوم دخوله عليه تأهب المعتصم واصطف الناس سماطين وأمر بابك أن يركب على فيل ليشهر أمره ويعرفوه، وعليه قباء ديباج وقلنسوة سمور مدورة، وقد هيأوا الفيل وخضبوا أطرافه ولبسوه من الحرير والامتعة التي تليق به شيئا كثيرا، وقد قال فيه بعضهم (٥) : قد خضب الفيل كعاداته * يحمل شيطان خراسان والفيل لا تخضب أعضاؤه * إلا لذي شأن من الشان ولما أحضر بين يدي المعتصم أمر بقطع يديه ورجليه وجز رأسه وشق بطنه، ثم أمر بحمل رأسه','3934','1003','0');
INSERT INTO [t8376] VALUES (1270,'وفيها توفي','3934','1269','0');
INSERT INTO [t8376] VALUES (1271,'فتح عمورية','3936','1269','0');
INSERT INTO [t8376] VALUES (1272,'مقتل العباس بن المأمون','3938','1269','0');
INSERT INTO [t8376] VALUES (1273,'ثم دخلت سنة أربع وعشرين ومائتين فيها خرج رجل بآمل طبرستان يقال له مازيار بن قارن بن يزداهرمز (٤) ، وكان لا يرضى أن يدفع الخراج إلى نائب خراسان عبد الله بن طاهر بن الحسين، بل يبعثه إلى الخليفة ليقبضه منه، فيبعث الخليفة من يتلقى الحمل إلى بعض البلاد ليقبضه منه ثم يدفعه إلى ابن طاهر، ثم آل أمره إلى أن وثب على تلك البلاد وأظهر المخالفة للمعتصم.','3939','1003','0');
INSERT INTO [t8376] VALUES (1274,'وفيها توفي','3939','1273','0');
INSERT INTO [t8376] VALUES (1275,'وفيها توفي','3941','1273','0');
INSERT INTO [t8376] VALUES (1276,'ثم دخلت سنة خمس وعشرين ومائتين فيها دخل بغا الكبير ومعه منكجور قد أعطى الطاعة بالامان.','3943','1003','0');
INSERT INTO [t8376] VALUES (1277,'وفيها توفي','3943','1276','0');
INSERT INTO [t8376] VALUES (1278,'ثم دخلت سنة ست وعشرين ومائتين في شعبان منها توفي الافشين في الحبس فأمر به المعتصم فصلب ثم أحرق وذري رماده في دجلة واحتيط على أمواله وحواصله فوجدوا فيها أصناما مكللة بذهب وجواهر، وكتبا في فضل دين المجوس وأشياء كثيرة كان يتهم بها، تدل على كفره وزندقته، وتحقق بسببها ما ذكر عنه من الانتماء إلى دين آبائه المجوس.','3944','1003','0');
INSERT INTO [t8376] VALUES (1279,'وفيها توفي','3944','1278','0');
INSERT INTO [t8376] VALUES (1280,'ثم دخلت سنة سبع وعشرين ومائتين فيها خرج رجل من أهل الثغور بالشام (١) يقال له أبو حرب المبرقع اليماني، فخلع الطاعة ودعا إلى نفسه.','3946','1003','0');
INSERT INTO [t8376] VALUES (1281,'وفيها توفي','3948','1280','0');
INSERT INTO [t8376] VALUES (1282,'ثم دخلت سنة ثمان وعشرين ومائتين في رمضان منها خلع الواثق على أشناس الامير، وتوجه وألبسه وشاحين من جوهر وحج بالناس فيها محمد بن داود الامير.','3950','1003','0');
INSERT INTO [t8376] VALUES (1283,'وممن توفي فيها','3950','1282','0');
INSERT INTO [t8376] VALUES (1284,'وفيها توفي','3952','1282','0');
INSERT INTO [t8376] VALUES (1285,'ثم دخلت سنة تسع وعشرين ومائتين فيها أمر الواثق بعقوبة الدواوين وضربهم واستخلاص الاموال منهم، لظهور خياناتهم وإسرافهم في أمورهم، فمنهم من ضرب ألف سوط وأكثر من ذلك وأقل، ومنهم من أخذ منه ألف ألف دينار، ودون ذلك، وجاهر الوزير محمد بن عبد الملك لسائر ولاة الشرط بالعداوة فعسفوا وحبسوا ولقوا شرا عظيما، وجهدا جهيدا، وجلس إسحاق بن إبراهيم للنظر في أمرهم، وأقيموا للناس وافتضحوهم والدواوين فضحية بليغة.','3953','1003','0');
INSERT INTO [t8376] VALUES (1286,'ثم دخلت سنة ثلاثين ومائتين في جمادى منها خرجت بن سليم حول المدينة النبوية فعاثوا في الارض فسادا، وأخافوا السبيل، وقاتلهم أهل المدينة فهزموا أهلها واستحوذوا على ما بين المدينة ومكة من المناهل والقرى، فبعث إليهم الواثق بغا الكبير أبا موسى التركي في جيش فقاتلهم في شعبان فقتل منهم خمسين فارسا وأسر منهم وانهزم بقيتهم، فدعاهم إلى الامان وأن يكونوا على حكم أمير المؤمنين، فاجتمع إليه منهم خلق كثير، فدخل بهم المدينة وسجن رؤوسهم في دار يزيد بن معاوية وخرج إلى الحج في هذه السنة، وشهد معه الموسم إسحاق بن إبراهيم بن مصعب نائب العراق.','3954','1003','0');
INSERT INTO [t8376] VALUES (1287,'وفيها توفي','3954','1286','0');
INSERT INTO [t8376] VALUES (1288,'وفيها توفي','3955','1286','0');
INSERT INTO [t8376] VALUES (1289,'ثم دخلت سنة إحدى وثلاثين ومائتين فيها وقعت مفاداة الاسارى المسلمين الذين كانوا في أيدي الروم على يدي الامير خاقان الخادم وذلك في المحرم من هذه السنة، وكان عدة الاسارى أربعة آلاف وثلثمائة واثنين وستين أسيرا (١) .','3956','1003','0');
INSERT INTO [t8376] VALUES (1290,'ثم دخلت سنة ثنتين وثلاثين ومائتين فيها عاثت قبيلة يقال لها بنو نمير باليمامة فسادا فكتب الواثق إلى بغا الكبير وهو مقيم بأرض الحجاز فحاربهم فقتل منهم جماعة وأسر منهم آخرين، وهزم بقيتهم، ثم التقى مع بني تميم وهو في','3960','1003','0');
INSERT INTO [t8376] VALUES (1291,'وممن توفي فيها','3960','1290','0');
INSERT INTO [t8376] VALUES (1292,'وفاة الخليفة الواثق بن محمد المعتصم بن هارون الرشيد أبي جعفر هارون الواثق.','3961','1290','0');
INSERT INTO [t8376] VALUES (1293,'فيها كانت وفاة','3961','1290','0');
INSERT INTO [t8376] VALUES (1294,'خلافة المتوكل على الله جعفر بن المعتصم','3963','1290','0');
INSERT INTO [t8376] VALUES (1295,'ثم دخلت سنة ثلاث وثلاثين ومائتين في يوم الاربعاء سابع صفر منها أمر الخليفة المتوكل على الله بالقبض على محمد بن عبد الملك بن','3964','1003','0');
INSERT INTO [t8376] VALUES (1296,'وفيها توفي','3964','1295','0');
INSERT INTO [t8376] VALUES (1297,'ثم دخلت سنة أربع وثلاثين ومائتين فيها خرج محمد بن البعيث بن حلبس عن الطاعة في بلاده أذربيجان، وأظهر أن المتوكل قد مات والتف عليه جماعة من أهل تلك الرساتيق، ولجأ إلى مدينة مرند فحصنها، وجاءته البعوث من كل جانب، وأرسل إليه المتوكل جيوشا يتبع بعضها بعضا، فنصبوا على بلده المجانيق من كل جانب، وحاصروه محاصرة عظيمة جدا، وقاتلهم مقاتلة هائلة، وصبر هو وأصحابه صبر بليغا، وقدم بغا الشرابي لمحاصرته، فلم يزل به حتى أسره واستباح أمواله وحريمه وقتل خلقا من رؤوس أصحابه، وأسر سائرهم وانحسمت مادة ابن البعيث.','3965','1003','0');
INSERT INTO [t8376] VALUES (1298,'وفيها توفي','3965','1297','0');
INSERT INTO [t8376] VALUES (1299,'ثم دخلت سنة خمس وثلاثين ومائتين في جمادى الآخرة منها كان هلاك إيتاخ في السجن، وذلك أنه رجع من الحج فتلقته هدايا الخليفة، فلما اقترب يريد دخول سامرا التي فيها المتوكل بعث إليه إسحاق بن إبراهيم نائب بغداد عن أمر الخليفة يستدعيه إليها ليتلقاه وجوه الناس وبني هاشم، فدخلها في أبهة عظيمة، فقبض عليه إسحاق بن إبراهيم وعلى ابنيه مظفر ومنصور وكاتبيه سليمان بن وهب وقدامة بن زياد النصراني فأسلم تحت العقوبة، وكان هلاك إيتاخ بالعطش، وذلك أنه أكل أكلا كثيرا بعد جوع شديد ثم استسقى الماء فلم يسق حتى مات ليلة الاربعاء لخمس خلون من جمادى الآخرة منها.','3966','1003','0');
INSERT INTO [t8376] VALUES (1300,'وفيها توفي','3966','1299','0');
INSERT INTO [t8376] VALUES (1301,'وفيها توفي','3968','1299','0');
INSERT INTO [t8376] VALUES (1302,'ثم دخلت سنة ست وثلاثين ومائتين فيها أمر المتوكل بهدم قبر الحسين بن علي بن أبي طالب وما حوله من المنازل والدور، ونودي في الناس من وجد هنا بعد ثلاثة أيام ذهبت به إلى المطبق.','3969','1003','0');
INSERT INTO [t8376] VALUES (1303,'ثم دخلت سنة سبع وثلاثين ومائتين فيها قبض يوسف بن محمد بن يوسف نائب أرمينية على البطريق الكبير بها وبعثه إلى نائب الخليفة، واتفق بعد بعثه إياه أن سقط ثلج عظيم على تلك البلاد، فتحزب أهل تلك الطريق وجاؤوا فحاصروا البلد التي بها يوسف فخرج إليهم ليقاتلهم فقتلوه وطائفة كبيرة من المسلمين الذين معه وهلك كثير من الناس من شدة البرد، ولما بلغ المتوكل ما وقع من هذا الامر الفظيع أرسل إلى أهل تلك الناحية بغا الكبير في جيش كثيف جدا فقتل من أهل تلك الناحية ممن حاصر المدينة نحوا من ثلاثين ألفا وأسر منهم طائفة كبيرة، ثم سار إلى بلاد الباق من كور البسفرجان وسلك إلى مدن كثيرة كبار ومهد المماليك ووطد البلاد والنواحي.','3969','1003','0');
INSERT INTO [t8376] VALUES (1304,'وفيها توفي','3969','1303','0');
INSERT INTO [t8376] VALUES (1305,'ثم دخلت سنة ثمان وثلاثين ومائتين في ربيع الاول منها حاصر بغا مدينة تفليس وعلى مقدمته زيرك التركي، فخرج إليه صاحب تفليس إسحاق بن إسماعيل فقاتله فأسر بغا إسحاق فأمر بغا بضرب عنقه وصلبه، وأمر بإلقاء النار في النفط إلى نحو المدينة، وكان أكثر بنائها من خشب الصنوبر، فأحرق أكثرها وأحرق من أهلها نحوا من خمسين ألفا، وطفئت النار بعد يومين، لان نار الصنوبر لا بقاء لها.','3971','1003','0');
INSERT INTO [t8376] VALUES (1306,'وفيها توفي','3971','1305','0');
INSERT INTO [t8376] VALUES (1307,'وممن توفي فيها','3971','1305','0');
INSERT INTO [t8376] VALUES (1308,'ثم دخلت سنة تسع وثلاثين ومائتين في المحرم منها زاد المتوكل في التغليظ على أهل الذمة في التميز في اللباس وأكد الامر بتخريب الكنائس المحدثة في الاسلام.','3972','1003','0');
INSERT INTO [t8376] VALUES (1309,'وفيها توفي','3972','1308','0');
INSERT INTO [t8376] VALUES (1310,'وممن توفي فيها','3972','1308','0');
INSERT INTO [t8376] VALUES (1311,'ثم دخلت سنة أربعين ومائتين فيها عدا أهل حمص على عاملهم أبي الغيث (١) موسى بن إبراهيم الرافقي (٢) لانه قتل رجلا من أشرافهم فقتلوا جماعة من أصحابه وأخرجوه من بين أظهرهم، فبعث إليهم المتوكل أميرا عليهم وقال للسفير معه: إن قبلوه وإلا فأعلمني.','3973','1003','0');
INSERT INTO [t8376] VALUES (1312,'وممن توفي فيها','3977','1311','0');
INSERT INTO [t8376] VALUES (1313,'سحنون المالكي صاحب المدونة','3978','1311','0');
