INSERT INTO [t8376] VALUES (1317,'ورعه وتقشفه وزهده رحمه الله','3983','1314','0');
INSERT INTO [t8376] VALUES (1318,'ذكر ضربه رضي الله عنه بين يدي المعتصم','3988','1314','0');
INSERT INTO [t8376] VALUES (1319,'ثناء الائمة على الامام أحمد بن حنبل','3991','1314','0');
INSERT INTO [t8376] VALUES (1320,'ما كان من أمر الامام أحمد بعد المحنة','3993','1314','0');
INSERT INTO [t8376] VALUES (1321,'وفاة الامام أحمد بن حنبل','3996','1314','0');
INSERT INTO [t8376] VALUES (1322,'ذكر ما رئي له من المنامات','3998','1314','0');
INSERT INTO [t8376] VALUES (1323,'ثم دخلت سنة ثنتين وأربعين ومائتين فيها كانت زلازل هائلة في البلاد، فمنها ما كان بمدينة قومس، تهدمت منها دور كثيرة، ومات من أهلها نحو من خمسة وأربعين ألفا وستة وتسعين نفسا.','3999','1003','0');
INSERT INTO [t8376] VALUES (1324,'وفيها توفي','4000','1323','0');
INSERT INTO [t8376] VALUES (1325,'ثم دخلت سنة ثلاث وأربعين ومائتين في ذي القعدة منها توجه المتوكل على الله من العراق قاصدا مدينة دمشق ليجعلها له دار إقامة ومحلة إمامة فأدركه عيد الاضحى بها، وتأسف أهل العراق على ذهاب الخليفة من بين أظهرهم، فقال في ذلك يزيد بن محمد المهلبي: أظن الشام تشمت بالعراق * إذا عزم الامام على انطلاق فإن يدع العراق وساكنيها * فقد تبلى المليحة بالطلاق وحج بالناس فيها الذي حج بهم في التي قبلها وهو نائب مكة.','4001','1003','0');
INSERT INTO [t8376] VALUES (1326,'وفيها توفي','4001','1325','0');
INSERT INTO [t8376] VALUES (1327,'ثم دخلت سنة أربع وأربعين ومائتين في صفر منها دخل الخليفة المتوكل إلى مدينة دمشق في أبهة الخلافة وكان يوما مشهودا، وكان عازما على الاقامة بها، وأمر بنقل دواوين الملك إليها، وأمر ببناء القصور بها فبنيت بطريق داريا، فأقام بها مدة، ثم إنه استوخمها ورأى أن هواءها بارد ندي وماءها ثقيل بالنسبة إلى هواء العراق ومائة، ورأى','4002','1003','0');
INSERT INTO [t8376] VALUES (1328,'وفيها توفي','4002','1327','0');
INSERT INTO [t8376] VALUES (1329,'ثم دخلت سنة خمس وأربعين ومائتين فيها أمر المتوكل ببناء مدينة الماحوزة وحفر نهرها، فيقال إنه أنفق على بنائها وبناء قصر الخلافة بها الذي يقال له " اللؤلؤة " ألفي ألف دينار.','4003','1003','0');
INSERT INTO [t8376] VALUES (1330,'وفيها توفي','4003','1329','0');
INSERT INTO [t8376] VALUES (1331,'وفيها توفي','4004','1329','0');
INSERT INTO [t8376] VALUES (1332,'ابن الراوندي الزنديق','4004','1329','0');
INSERT INTO [t8376] VALUES (1333,'ذو النون المصري','4005','1329','0');
INSERT INTO [t8376] VALUES (1334,'ثم دخلت سنة ست وأربعين ومائتين في يوم عاشوراء منها دخل المتوكل الماحوزة فنزل بقصر الخلافة فيها، واستدعى بالقراء ثم بالمطربين وأعطى وأطلق، وكان يوما مشهودا، وفي صفر منها وقع الفداء بين المسلمين والروم، ففدي من المسلمين نحو من أربعة آلاف أسير (١) .','4005','1003','0');
INSERT INTO [t8376] VALUES (1335,'وممن توفي فيها','4005','1334','0');
INSERT INTO [t8376] VALUES (1336,'ثم دخلت سنة سبع وأربعين ومائتين في شوال منها كان مقتل الخليفة المتوكل على الله على يد ولده المنتصر، وكان سبب ذلك أنه أمر ابنه عبد الله المعتز الذي هو ولي العهد من بعده أن يخطب بالناس في يوم جمعة، فأداها أداء عظيما بليغا، فبلغ ذلك من المنتصر كل مبلغ، وحنق على أبيه وأخيه، فأحضره أبوه وأهانه وأمر بضربه في رأسه وصفعه، وصرح بعزله عن ولاية العهد من بعد أخيه، فاشتد أيضا حنقه أكثر مما كان.','4007','1003','0');
INSERT INTO [t8376] VALUES (1337,'ترجمة المتوكل على الله','4008','1336','0');
INSERT INTO [t8376] VALUES (1338,'خلافة محمد المنتصر بن المتوكل','4010','1336','0');
INSERT INTO [t8376] VALUES (1339,'وفيها توفي','4011','1336','0');
INSERT INTO [t8376] VALUES (1340,'ثم دخلت سنة ثمان وأربعين ومائتين فيها أغزى المنتصر وصيفا التركي الصائفة لقتال الروم، وذلك أن ملك الروم قصد بلاد الشام، فعند ذلك جهز المنتصر وصيفا وجهز معه نفقات وعددا كثيرة، وأمره إذا فرغ من قتال الروم أن يقيم بالثغر أربع سنين، وكتب له إلى محمد بن عبد الله بن طاهر نائب العراق كتابا عظيما فيه آيات كثيرة في التحريض للناس على القتال والترغيب فيه (١) .','4012','1003','0');
INSERT INTO [t8376] VALUES (1341,'ج ١١','4015','0','0');
