INSERT INTO [t8376] VALUES (1347,'ثم دخلت سنة خمسين ومائتين من الهجرة فيما كان ظهور أبي الحسين يحيى بن عمر بن يحيى بن حسين (٤) بن زيد بن علي بن الحسين بن علي بن أبي طالب، وأمه أم الحسين فاطمة بنت الحسين بن عبد الله بن إسماعيل بن عبد الله بن جعفر بن أبي طالب.','4021','1341','0');
INSERT INTO [t8376] VALUES (1348,'وفيها توفي','4023','1347','0');
INSERT INTO [t8376] VALUES (1349,'ثم دخلت سنة إحدى وخمسين ومائتين فيها اجتمع رأي المستعين وبغا الصغير ووصيف على قتل باغر التركي، وكان من قواد الامراء الكبار الذين باشروا قتل المتوكل، وقد اتسع إقطاعه وكثر عماله، فقتل ونهبت دار كتابه دليل بن يعقوب النصراني، ونهبت أمواله وحواصله، وركب الخليفة في حراقة من سامرا إلى بغداد فاضطربت الامور بسبب خروجه، وذلك في المحرم.','4024','1341','0');
INSERT INTO [t8376] VALUES (1350,'سنة ثنتين وخمسين ومائتين " ذكر خلافة المعتز بالله بن المتوكل على الله بعد خلع المستعين نفسه "','4027','1341','0');
INSERT INTO [t8376] VALUES (1351,'وفيها توفي','4027','1350','0');
INSERT INTO [t8376] VALUES (1352,'ذكر مقتل المستعين','4028','1350','0');
INSERT INTO [t8376] VALUES (1353,'ثم دخلت سنة ثلاث وخمسين ومائتين في رجب منها عقد المعتز لموسى بن بغا الكبير على جيش قريب من أربعة آلاف ليذهبوا إلى قتال عبد العزيز بن أبي دلف بناحية همذان','4029','1341','0');
