INSERT INTO [t8376] VALUES (1357,'خلافة المهتدي بالله أبي محمد عبد الله محمد بن الواثق بن المعتصم بن هارون','4035','1356','0');
INSERT INTO [t8376] VALUES (1358,'وفيها توفي','4038','1356','0');
INSERT INTO [t8376] VALUES (1359,'ثم دخلت سنة ست وخمسين ومائتين في صبيحة يوم الاثنين الثاني عشر من المحرم قدم موسى بن بغا الكبير إلى سامرا فدخلها في جيش هائل','4039','1341','0');
INSERT INTO [t8376] VALUES (1360,'خلع المهتدي بالله وولاية المعتمد أحمد بن المتوكل','4040','1359','0');
INSERT INTO [t8376] VALUES (1361,'خلافة المعتمد على الله','4042','1359','0');
