INSERT INTO [t8376] VALUES (1363,'الامام محمد بن إسماعيل البخاري صاحب الصحيح','4043','1359','0');
INSERT INTO [t8376] VALUES (1364,'ثم دخلت سنة سبع وخمسين ومائتين فيها ولى الخليفة المعتمد ليعقوب بن الليث بلخ وطخارستان وما يلي ذلك من كرمان وسجستان السند وغيرها.','4047','1341','0');
INSERT INTO [t8376] VALUES (1365,'وفيها توفي','4048','1364','0');
INSERT INTO [t8376] VALUES (1366,'ثم دخلت سنة تسع وخمسين ومائتين في يوم الجمعة لاربع بقين من ربيع الآخر رجع أبو أحمد بن المتوكل من واسط إلى سامرا وقد استخلف على حرب الزنج محمد الملقب بالمولد، وكان شجاعا شهما.','4050','1341','0');
INSERT INTO [t8376] VALUES (1367,'وفيها توفي','4050','1366','0');
INSERT INTO [t8376] VALUES (1368,'سنة إحدى وستين ومائتين فيها انصرف الحسن بن زيد من بلاد الديلم إلى طبرستان وأحرق مدينة شالوس لممالاتهم يعقوب بن الليث عليه.','4051','1341','0');
INSERT INTO [t8376] VALUES (1369,'ثم دخلت سنة ستين ومائتين وفيها وقع غلاء شديد ببلاد الاسلام كلها حتى أجلي أكثر أهل البلدان منها إلى غيرها، ولم يبق بمكة أحد من المجاورين حتى ارتحلوا إلى المدينة وغيرها من البلاد، وخرج نائب مكة منها.','4051','1341','0');
INSERT INTO [t8376] VALUES (1370,'وفيها توفي','4051','1369','0');
INSERT INTO [t8376] VALUES (1371,'وفيها توفي','4052','1369','0');
INSERT INTO [t8376] VALUES (1372,'مسلم بن الحجاج صاحب الصحيح.','4052','1369','0');
INSERT INTO [t8376] VALUES (1373,'ثم دخلت سنة اثنتين وستين ومائتين فيها قدم يعقوب بن الليث في جحافل فدخل واسط قهرا فخرج الخليفة المعتمد بنفسه من سامرا لقتاله فتوسط بين بغداد وواسط فانتدب له أبو أحمد الموفق بالله أخو الخليفة، في جيش عظيم على ميمنته موسى بن بغا، وعلى ميسرته مسرور البلخي، فاقتتلوا في رجب من هذه السنة أياما قتالا عظيما، ثم كانت الغلبة على يعقوب وأصحابه، وذلك يوم عيد الشعانين.','4055','1341','0');
INSERT INTO [t8376] VALUES (1374,'ثم دخلت سنة ثلاث وستين ومائتين فيها جرت حروب كثيرة منتشرة في بلاد شتى فمن ذلك مقتلة عظيمة في الزنج لعنهم الله، حصرهم في بعض المواقف بعض الامراء من جهة الخليفة فقتل الموجودين عنده من آخرهم.','4055','1341','0');
INSERT INTO [t8376] VALUES (1375,'وفيها توفي','4055','1374','0');
INSERT INTO [t8376] VALUES (1376,'ثم دخلت سنة أربع وستين ومائتين في المحرم منها عسكر أبو أحمد وموسى بن بغا بسامرا وخرجا منها لليلتين مضتا من صفر، وخرج المعتمد لتوديعهما، وسار إلى بغداد.','4056','1341','0');
INSERT INTO [t8376] VALUES (1377,'وفيها توفي','4056','1376','0');
INSERT INTO [t8376] VALUES (1378,'ثم دخلت سنة خمس وستين ومائتين فيها كانت وقعة بين ابن ليثويه عامل أبي أحمد وبين سليمان بن جامع فظفر بها ابن ليثويه بابن جامع نائب صاحب الزنج، فقتل خلقا من أصحابه وأسر (٢) منهم سبعة وأربعين أسيرا، وحرق له مراكب كثيرة، وغنم منهم أموالا جزيلة.','4057','1341','0');
INSERT INTO [t8376] VALUES (1379,'وفيها توفي','4058','1378','0');
INSERT INTO [t8376] VALUES (1380,'يعقوب بن الليث الصفار','4058','1378','0');
INSERT INTO [t8376] VALUES (1381,'ثم دخلت سنة ست وستين ومائتين في صفر منها تغلب إساتكين على بلد الري وأخرج عاملها منها ثم مضى إلى قزوين فصالحه أهلها فدخلها وأخذ منها أموالا جزيلة، ثم عاد إلى الري فمانعه أهلها عن الدخول إليها فقهرهم ودخلها.','4059','1341','0');
INSERT INTO [t8376] VALUES (1382,'ثم دخلت سنة سبع وستين ومائتين فيها وجه أبو أحمد الموفق ولده أبا العباس في نحو من عشرة آلاف فارس وراجل في أحسن هيئة وأكمل تجمل لقتال الزنج، فساروا نحوهم فكان بينهم وبينهم من القتال والنزال في أوقات متعددات ووقعات مشهورات ما يطول بسطه، وقد استقصاه ابن جرير في تاريخه مبسوطا مطولا.','4060','1341','0');
INSERT INTO [t8376] VALUES (1383,'وفيها توفي','4060','1382','0');
INSERT INTO [t8376] VALUES (1384,'ثم دخلت سنة ثمان وستين ومائتين في المحرم منها استأمن جعفر بن إبراهيم المعروف بالسجان - وكان من أكابر صاحب الزنج وثقاتهم في أنفسهم - الموفق فأمنه وفرح به وخلع عليه وأمره فركب في سمرته فوقف تجاه قصر الملك فنادى في الناس وأعلمهم بكذب صاحب الزنج وفجوره، وأنه في غرور هو ومن اتبعه، فاستأمن بسبب ذلك بشر كثير منهم، وبرد قتال الزنج عند ذلك إلى ربيع الآخر.','4062','1341','0');
