INSERT INTO [t8376] VALUES (1396,'وفيها توفي','4072','1395','0');
INSERT INTO [t8376] VALUES (1397,'ثم دخلت سنة ثلاث وسبعين ومائتين فيها وقع بين إسحاق بن كنداج نائب الموصل وبين صاحبه ابن أبي الساج نائب قنسرين وغيرها بعد ما كانا متفقين، وكاتب ابن أبي الساج خمارويه صاحب مصر، وخطب له ببلاده وقدم خمارويه إلى','4073','1341','0');
