INSERT INTO [t8376] VALUES (1423,'خلافة المعتضد','4089','1418','0');
INSERT INTO [t8376] VALUES (1424,'ثم دخلت سنة ثمانين ومائتين من الهجرة في المحرم منها قتل المعتضد رجلا من أمراء الزنج كان قد لجأ بالامان ويعرف بسلمة (١) ، ذكر له أنه يدعو إلى رجل لا يعرف من هو، وقد أفسد جماعة، فاستدعي به فقرره فلم يقر، وقال: لو كان تحت قدمي ما أقررت به، فأمر به فشد على عمود ثم لوحه على النار تى تساقط جلده، ثم أمر بضرب عنقه وصلبه لسبع خلون من المحرم.','4091','1341','0');
